SET NAMES utf8;
INSERT IGNORE INTO `locales_page_text` (`entry`) SELECT `entry` FROM `page_text`;

UPDATE `locales_page_text` SET `Text_loc2` = 'Bonjour Morgan,\n\nLes affaires sont florissantes à Comté-de-l\'or, au point que je n\'ai pas eu le temps de vous envoyer la moindre cargaison ! \n\nJ\'ai demandé au porteur de ce message de vous remettre un grand paquet de bougies (vous savez, celles que les kobolds aiment porter sur le sommet du crâne ?) \n\nRemerciez ce messager et donnez-lui le paiement qui lui revient.' WHERE `entry` = 15;
UPDATE `locales_page_text` SET `Text_loc2` = 'À l\'honorable Maître Crillian,\n\nCher ancien maître, je vous écris pour vous informer de ce que devient votre apprenti. Suivant vos conseils, j\'ai décidé d\'augmenter mes connaissances et ma sagesse en voyageant loin des frontières de notre bien-aimée Hurlevent. Mes voyages m\'ont fait découvrir bien des lieux, mais j\'ai finalement décidé de m\'établir dans la magnifique ville de Ruisselune. Les champs qui entourent la marche de l\'Ouest sont plus beaux encore tandis que l\'époque de la moisson approche.' WHERE `entry` = 16;
UPDATE `locales_page_text` SET `Text_loc2` = 'À peine arrivé, j\'ai commencé à enseigner aux enfants des fermes voisines. Les leçons se sont si bien déroulées que le maire m\'a demandé de diriger une école. La construction du bâtiment a déjà commencé ! Des Pins argentés à Hurlevent, et maintenant Ruisselune, qui aurait pensé que j\'aurais exploré de si larges portions d\'Azeroth !\n\nMeilleures salutations,\n\nStalvan Mistmantle' WHERE `entry` = 17;
UPDATE `locales_page_text` SET `Text_loc2` = 'Monseigneur,\n\nJ\'ai entendu dire que vous cherchiez un précepteur pour vos enfants, ici, à Comté-de-l\'or, où je me suis établi à l\'auberge de la Fierté du lion. En raison des tristes événements qui agitent la région, j\'ai été contraint d\'abandonner ma position de directeur de l\'école de Ruisselune. Veuillez considérer avec bienveillance ma candidature au poste que vous cherchez à pourvoir. Le maître de l\'académie Crillian vous fournira si besoin toutes les recommandations voulues.' WHERE `entry` = 18;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je viendrai vous rencontrer en personne lorsque la saison des pluies sera achevée et que les routes permettront le déplacement.\n\nJe demeure en attendant votre dévoué\n\nStalvan Mantebrume des Pins argentés' WHERE `entry` = 19;
UPDATE `locales_page_text` SET `Text_loc2` = '...Giles, le garçon, semble un peu dissipé et ne sera pas facile à éduquer. Mais la fille aînée, Tilloa, semble exceptionnellement brillante. Je ne peux m\'empêcher de constater aussi combien elle est belle. Elle est presque une femme désormais. Il paraît que Monseigneur a arrangé son mariage pour l\'année prochaine. Mais je m\'éloigne du sujet. Cette semaine, je vais accompagner la famille à leur résidence d\'été, près du Camp de bûcherons du Val d\'est en Elwynn, non loin des Carmines. Je vous donnerai plus de nouvelles depuis là-bas.' WHERE `entry` = 20;
UPDATE `locales_page_text` SET `Text_loc2` = '... sentiment étrange et incontrôlable. Jamais je n\'avais ressenti cela. J\'aidais Giles dans sa leçon d\'Histoire, et je voyais Tilloa, par la fenêtre, s\'occuper du jardin. Quelques instants après, elle est entrée, elle a placé un bégonia écarlate dans ma paume ouverte et elle m\'a souri de telle manière que mon cœur s\'est mis à trembler dans ma poitrine...' WHERE `entry` = 21;
UPDATE `locales_page_text` SET `Text_loc2` = '... tout à fait certain qu\'elle partage désormais mes sentiments. Elle a même posé sa main sur la mienne ce matin. Lorsqu\'elle sourit, ses yeux s\'illuminent comme des diamants. Nous échangeons de longues conversations d\'un simple regard. Je sens sa présence dans mon cœur palpitant et dans mes veines.' WHERE `entry` = 22;
UPDATE `locales_page_text` SET `Text_loc2` = '...une colère, une furie dont j\'ignorais qu\'elles pussent exister ! Comment ose-t-elle ! J\'apprenais à Giles les merveilles de l\'arithmétique lorsque Tilloa est apparue devant moi avec un chevalier servant, la main dans la main ! Un jeune homme sans raffinement ! Au lieu de me présenter comme il convient, Tilloa s\'est simplement exclamée : "Oh, et voilà mon précepteur, Oncle Stalvan. C\'est un charmant vieillard." Vieillard ! En entendant ces mots, j\'ai senti mes joues rougir. J\'ai à peine quelques années de plus, et elle me trahit ainsi...' WHERE `entry` = 23;
UPDATE `locales_page_text` SET `Text_loc2` = '...en bas d\'une spirale de désespoir. Tout d\'abord elle se moque de moi puis la voilà fiancée ! Cette indélicate créature s\'est prétendue amoureuse alors qu\'elle ne souhaitait que me faire du mal. Un tourbillon noir croît chaque minute au plus profond de ma poitrine. Le sang que je verserai n\'est rien en comparaison des larmes que j\'ai pleurées...' WHERE `entry` = 24;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tommy Joe -\n\nÀ chaque heure qui passe, mon cœur gémit de ton absence. Oh, si seulement nos familles pouvaient oublier les torts qu\'elles se sont infligés et comprendre que le seul vrai crime est la haine. La haine consume, et je crains ces sombres heures où la haine fera bien d\'autres victimes que nos deux seules familles.\n\nSi les nôtres comprenaient, nous pourrions au moins être ensemble. C\'est ce jour que j\'attends dans l\'espérance et c\'est pour lui que je survis.\n\nTon amour,\nMaybell' WHERE `entry` = 25;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bonjour William,\n\nVoilà des années que nous n\'avons pas parlé, et j\'ai bon espoir que vous et votre frère vous portez bien, ainsi que vos affaires.\n\nJe dois vous demander une faveur, William. Pour faire court, mon petit-fils Tommy Joe s\'est épris de la jeune Maybell Maclure. Et bien qu\'ils s\'adorent, nos familles, ma foi, nos familles s\'affrontent depuis des années.' WHERE `entry` = 26;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bonjour William,\n\nVoilà des années que nous n\'avons pas parlé, et j\'ai bon espoir que vous et votre frère vous portez bien, ainsi que vos affaires.\n\nJe dois vous demander une faveur, William. Pour faire court, mon petit-fils Tommy Joe s\'est épris de la jeune Maybell Maclure. Et bien qu\'ils s\'adorent, nos familles, ma foi, nos familles s\'affrontent depuis des années.' WHERE `entry` = 27;
UPDATE `locales_page_text` SET `Text_loc2` = 'Argus, \n\ncomme vous savez, je suis jusqu\'au cou dans des demandes de réparation de l\'armée. Je ne me plains pas du travail, mais il vide mes réserves de fer.\n\nJe n\'en ai plus assez pour forger des fers à cheval. Je sais que vous en avez toujours un large stock. Je souhaiterais vous emprunter 50 paires de fers à cheval jusqu\'à ce qu\'arrive ma prochaine cargaison de fer.\n\nAvec toute ma gratitude,\n-Verner' WHERE `entry` = 28;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une note est attachée à la caisse. Elle dit :\n\nVerner - Désolé d\'apprendre que les Carmines connaissent de telles difficultés. Voici les fers à cheval que vous avez demandés. Veuillez me payer 100 pièces d\'argent lorsque vous le pourrez."\n\n"Vous pouvez si vous préférez me payer en écailles ventrales de jeunes dragons noirs (il paraît que ces dragons sont nombreux dans les Carmines). Comme nous sommes amis, 4 écailles suffiront. Tous mes remerciements —Argus"' WHERE `entry` = 29;
UPDATE `locales_page_text` SET `Text_loc2` = 'Monseigneur,\n\nLa guerre est sur nous. La malédiction frappe à nos portes.\n\nEn tant que Magistrat de la ville de Comté-du-lac, il est de mon devoir de vous faire savoir que des activités militaires ont lieu dans les colonies les plus à l\'est de Sa Majesté.\n\nDans ces pages, vous trouverez un rapport sur Comté-du-lac, niché dans les Carmines.' WHERE `entry` = 30;
UPDATE `locales_page_text` SET `Text_loc2` = 'La perte du donjon de Guet-de-pierre est très grave. Dans les précédents rapports, je vous ai fait connaître l\'alliance entre le clan Rochenoire des orcs et le démoniste nommé Morganth. Les orcs Rochenoire se sont battus avec une sauvagerie inouïe, probablement soutenus par la magie maléfique de Morganth. Nombre des vaillants chevaliers de Sa Majesté ont péri dans la défense du donjon, mais ce fut en vain.' WHERE `entry` = 31;
UPDATE `locales_page_text` SET `Text_loc2` = 'Étrangement, Morganth s\'est retourné contre les cohortes des orcs aussitôt après leur ignoble victoire. Le démoniste a construit une tour des arcanes dans le nord-est et il a utilisé ses pouvoirs pour assembler une petite armée de gnolls Sombrepoil destinés à servir ses vils desseins. Les orcs comme les humains ont été les victimes des ces brutes gluantes. Les raisons de cette rupture entre Morganth et Gath\'Ilzogg, le chef de guerre du clan Rochenoire, restent inconnues.' WHERE `entry` = 32;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il est regrettable de constater que les vils orcs semblent surtout intéressés par la lutte contre les citoyens de Sa Majesté. Au cours des quinze derniers jours, le clan Rochenoire a tenté plusieurs offensives brutales contre Comté-du-lac. Beaucoup d\'hommes braves y ont perdu la vie. Les orcs avancent si rapidement et avec tant de rage que le pont du lac Placide n\'est plus que ruines. Nos fournitures de guerre diminuent dangereusement. Nous manquons de bandages pour panser les plaies des blessés.' WHERE `entry` = 33;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je sollicite maintenant de Sa Majesté qu\'elle aide le bon peuple de Comté-du-lac en ces temps de désespoir. Nous avons besoin de troupes pour défendre notre territoire et de nouveaux engins de guerre pour remplacer ceux perdus au combat. La nourriture et les matériaux de construction nous font aussi défaut. Monseigneur, par pitié, veuillez nous aider à défendre le dernier bastion entre les humains et les orcs. L\'ennemi ne doit pas pénétrer dans le royaume de Hurlevent.' WHERE `entry` = 34;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai peur qu\'un mal plus grand et plus noir ne se prépare dans ce chaudron de corruption que sont devenues les terres du Nord. Il est impératif que le royaume de Hurlevent agisse à moins que nous ne désirions devenir les esclaves de l\'ennemi.\n\nSigné,\n\nMagistrat Salomon\nVille de Comté-du-lac\nRoyaume de Hurlevent' WHERE `entry` = 35;
UPDATE `locales_page_text` SET `Text_loc2` = 'Honorable magistrat Salomon :\n\nJe pleure avec vous la perte de nos braves. Je veillerai personnellement à ce que le roi soit informé de la situation et j\'ai grand espoir qu\'il envoie des renforts à Comté-du-lac. Sa Majesté s\'est faite discrète ces derniers temps et ses actes sont des plus étranges. Que ceci reste entre nous.\n\nJe reste votre serviteur,\n\nGénéral Marcus Jonathan\nRoyaume de Hurlevent' WHERE `entry` = 36;
UPDATE `locales_page_text` SET `Text_loc2` = 'Honorable magistrat Salomon :\n\nJe pleure avec vous la perte de nos braves. Je veillerai personnellement à ce que le roi soit informé de la situation et j\'ai grand espoir qu\'il envoie des renforts à Comté-du-lac. Sa Majesté s\'est faite discrète ces derniers temps et ses actes sont des plus étranges. Que ceci reste entre nous.\n\nJe reste votre serviteur,\n\nGénéral Marcus Jonathan\n\nRoyaume de Hurlevent' WHERE `entry` = 37;
UPDATE `locales_page_text` SET `Text_loc2` = 'Roidemantel—\n\nQuel culot d\'envoyer $gun étranger:une étrangère; discuter de la bande des Défias. Vous m\'avez naguère sauvé la vie, mais maintenant vous la mettez en grand péril.\n\nJe reste néanmoins votre débiteur. La Confrérie Défias est plus dangereuse que vous ne le pensez. De la marche de l\'Ouest à la forêt d\'Elwynn, toutes les mines sont sous leur contrôle. Des kobolds et des gnolls ont été engagés pour faire leur sale travail. Des gobelins construisent pour eux des monstres de métal pour que les habitants de la marche de l\'Ouest deviennent la proie des pires superstitions.' WHERE `entry` = 38;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ils ont construit un vaste réseau souterrain. Plus grand que vous ne le pourriez imaginer, Roidemantel. De Baie-du-Butin jusqu\'au donjon de Hurlevent. Vous êtes sans nul doute un courageux paladin mais vous vous leurrez si vous pensez que vous pouvez les abattre. Ils travaillent sur une arme de destruction massive. Après tout, s\'il y a quelque chose que savent faire les maçons, c\'est construire des choses énormes. C\'est bien autre chose que quelques saltimbanques batifolant dans les champs ! Je vous ai payé ma dette. Laissez-moi maintenant !\n\n—W' WHERE `entry` = 39;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ils ont construit un vaste réseau souterrain. Plus grand que vous ne le pourriez imaginer, Roidemantel. De Baie-du-Butin jusqu\'au donjon de Hurlevent. Vous êtes sans nul doute un courageux paladin mais vous vous leurrez si vous pensez que vous pouvez les abattre. Ils travaillent sur une arme de destruction massive. Après tout, s\'il y a quelque chose que savent faire les maçons, c\'est construire des choses énormes. C\'est bien autre chose que quelques saltimbanques batifolant dans les champs ! Je vous ai payé ma dette. Laissez-moi maintenant !' WHERE `entry` = 40;
UPDATE `locales_page_text` SET `Text_loc2` = 'Seigneur Roidemantel—\n\nSous la direction d\'Edwin VanCleef, la guilde des Maçons est devenue la plus expérimentée des associations de bâtisseurs parmi les hommes. Les maçons ont aidé à la reconstruction de la cité dévastée de Hurlevent, rasée par les orcs durant la Première Guerre. VanCleef et ses hommes se sont montrés sans égaux, et leur travail élégant apparaît dans des édifices comme la cathédrale de la Lumière et le donjon de Hurlevent.' WHERE `entry` = 41;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cependant, les nobles de Hurlevent se sont gravement endettés en étendant la présence militaire du royaume jusqu\'à Elwynn et Strangleronce. Cette énorme dette a sapé l\'économie du royaume et privé VanCleef et ses maçons de la récompense qu\'ils méritaient. Après des années de labeur pour reconstruire la glorieuse cité, les maçons n\'ont pas touché une pièce d\'argent, oubliés par les dirigeants corrompus de la ville.' WHERE `entry` = 42;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je connais personnellement Edwin VanCleef depuis toujours, je vous assure que l\'avoir comme ennemi est une mauvaise idée. C\'est un ami d\'enfance, et je lui ai personnellement appris les voies des Ténèbres, au cas où il envisagerait un jour une carrière à mes côtés. Si VanCleef rejoint la Confrérie des Défias, que la Lumière ait pitié de nous.\n\nMaître Mathias Shaw\nGuilde des Assassins de Hurlevent' WHERE `entry` = 43;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je connais personnellement Edwin VanCleef depuis toujours, je vous assure que l\'avoir comme ennemi est une mauvaise idée. C\'est un ami d\'enfance, et je lui ai personnellement appris les voies des Ténèbres, au cas où il envisagerait un jour une carrière à mes côtés. Si VanCleef rejoint la Confrérie des Défias, que la Lumière ait pitié de nous.\n\nMaître Mathias Shaw\nGuilde des Assassins de Hurlevent' WHERE `entry` = 44;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bon travail, matelot ! Maintenant il va falloir cingler vers l\'est. À l\'est, vers le haut des falaises, à l\'est vers la route. Cherchez les ruines des vieilles cheminées, au bord de la route. Là, vous trouverez un vieux tonneau qui contient l\'indice suivant.' WHERE `entry` = 45;
UPDATE `locales_page_text` SET `Text_loc2` = 'À partir de ce tonneau, tournez-vous vers le nord. Marchez droit comme une bande de cadets qui va au réfectoire, jusqu\'à ce que vous voyiez la cruche vide, à côté du moulin à vent solitaire, sur les falaises. Si vous farfouillez autour de cette cruche, vous pourrez bien trouver ce que vous cherchez.' WHERE `entry` = 46;
UPDATE `locales_page_text` SET `Text_loc2` = 'Onglenoirs--\n\nLa mer nous attend. Nous avons besoin de vos fournitures venant du Nord. Prenez tout : chaque poteau, chaque baquet. On a besoin de métal. Une cargaison devait venir de BB mais on l\'attend toujours. Venez à moi, Onglenoirs. Envoyez vos fournitures directement à la "grange" cette fois. Plus de temps à perdre.\n\n--VanCleef' WHERE `entry` = 48;
UPDATE `locales_page_text` SET `Text_loc2` = 'Seigneur Roidemantel—\n\nLa ville de Comté-du-lac sollicite humblement l\'aide de votre milice. Au moment où j\'écris ces lignes, nous sommes assiégés par les orcs Rochenoire et les gnolls Sombrepoil de Morganth. Je n\'enverrais pas cette requête si notre situation n\'était très grave. Nous attendons de l\'aide de Hurlevent mais jusqu\'à ce que ces renforts arrivent, je vous supplie de nous accorder votre assistance. Si le royaume cède, c\'est toute l\'humanité qui sera en péril.\n\nMagistrat Salomon' WHERE `entry` = 49;
UPDATE `locales_page_text` SET `Text_loc2` = 'Votre Honneur—\n\nLa marche de l\'Ouest pleure vos cruelles pertes, mais nous menons notre propre guerre. Les fermiers sont sous la menace de voleurs et de golems mécaniques. Hurlevent a retiré ses troupes, ce qui laisse les petites gens sans défense. J\'ai assemblé la Milice du Peuple au moment de l\'abandon de Hurlevent, pour sauver ce qui peut l\'être de ces terres. Envoyer la Milice dans les Carmines soumettrait la marche de l\'Ouest à un destin funeste.\n\nTous mes regrets,\nGryan Roidemantel' WHERE `entry` = 50;
UPDATE `locales_page_text` SET `Text_loc2` = 'Seigneur Bouclenoire--\n\nLa ville de Comté-du-lac demande humblement l\'aide des Veilleurs. Nous sommes assiégés à la fois par les orcs Rochenoire et les gnolls Sombrepoil de Morganth. Je n\'aurais pas envoyé de demande si notre situation n\'était pas si délicate. Nous attendons de l\'aide de Hurlevent, mais avant que cette aide n\'arrive, je vous supplie de bien vouloir nous accorder des renforts. Si le royaume est atteint, c\'est toute l\'humanité qui est menacée.\n\nMagistrat Salomon' WHERE `entry` = 51;
UPDATE `locales_page_text` SET `Text_loc2` = 'Votre Honneur—\n\nJe regrette de devoir vous informer que les Veilleurs sont incapables de quitter leur poste de Sombre-comté en ce moment. Vous ignorez peut-être que Hurlevent ne fournit plus de soutien militaire dans cette région. Les Veilleurs ont été formés et entraînés par le peuple de la forêt dans le but de défendre Sombre-comté en l\'absence de Hurlevent. Sans les Veilleurs, la ville tomberait. Je vous souhaite bonne chance.\n\n—Seigneur Ello Bouclenoire' WHERE `entry` = 52;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les lettres, sur cette note, semblent bouger et danser sur le papier. Il est impossible d\'en tirer le moindre sens.' WHERE `entry` = 53;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salutations, Ello Bouclenoire, maire de Sombre-comté. Je crains d\'avoir de mauvaises nouvelles pour votre ville.\n\nVoyez-vous, je suis un créateur. J\'ai trompé le porteur de ce message pour qu\'il m\'aide à réaliser ma dernière et plus audacieuse création : une créature mêlée de chair et de métal impie ! Au moment où vous lisez ces lignes, elle est probablement à l\'extérieur de mon humble demeure, aiguisant ses dents et attendant que je lui donne l\'ordre de commencer le massacre.\n\nMais vous verrez cela rapidement par vous-même.\n\n-L\'Embaumeur' WHERE `entry` = 54;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ceci est un acte notarié traitant d\'une extension immobilière dans la région de la marche de l\'Ouest. Il confère à la personne désignée la propriété de la zone considérée ainsi que les objets produits et les bâtiments construits dans ses limites.\n\nLe document est signé par :\n\n Theodore Furlbrow\n Verna Furlbrow' WHERE `entry` = 55;
UPDATE `locales_page_text` SET `Text_loc2` = '[Les mots ci-dessous ont été rapidement griffonnés au revers de l\'acte]\n\nNous avons fait pression sur Furlbrow et avons récupéré son titre de propriété, en pensant que ça pourrait vous être utile si vous vouliez en faire un pour chez vous. Les Furlbrow ne nous causeront pas de problèmes. La dernière fois que je les ai vus, ils étaient coincés sur la route de la marche de l\'Ouest avec un chariot brisé.' WHERE `entry` = 56;
UPDATE `locales_page_text` SET `Text_loc2` = '[Les mots ci-dessous ont été rapidement griffonnés au revers de l\'acte]\n\nNous avons fait pression sur Furlbrow et avons récupéré son titre de propriété, en pensant que ça pourrait vous être utile si vous vouliez en faire un pour chez vous. Les Furlbrow ne nous causeront pas de problèmes. La dernière fois que je les ai vus, ils étaient coincés sur la route de la marche de l\'Ouest avec un chariot brisé.' WHERE `entry` = 57;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une lune brille au-dessus de la vallée\nNoyant la jungle de ses lueurs\nC\'est là que de fiers Guerriers suivent l\'appel\nDe notre Pays et de ses terres sacrées. \n\nUne lune brille au-dessus de la Vallée\nBien au-dessus des clameurs du combat\nOù se verse le sang\nDe l\'ennemi et de nos camarades.' WHERE `entry` = 58;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et lorsque nos frères passent\nDans le royaume dont on ne sait rien\nL\'âme et l\'esprit se durcissent\nLoin sous la vallée.\n\nEt lorsque nos frères passent\nDans le Temple de la montagne\nNous protégeons leur esprit immortel\nEnchâssé dans le saint cristal bleu.\n\nEt lorsque nos frères passent\nUne lune brille au-dessus de la vallée.' WHERE `entry` = 59;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et lorsque nos frères passent\nDans le royaume dont on ne sait rien\nL\'âme et l\'esprit se durcissent\nLoin sous la vallée.\n\nEt lorsque nos frères passent\nDans le Temple de la montagne\nNous protégeons leur esprit immortel\nEnchâssé dans le saint cristal bleu.\n\nEt lorsque nos frères passent\nUne lune brille au-dessus de la vallée.' WHERE `entry` = 61;
UPDATE `locales_page_text` SET `Text_loc2` = 'Par la lune et par le feu,\nPar l\'os et par la chair,\nÉcrit avec le sang,\nGravé dans la pierre.\n\nAbandonnez ces lieux\nOu préparez-vous à la terreur\nLa mort monte la garde\nSur la tombe de l\'empereur.' WHERE `entry` = 62;
UPDATE `locales_page_text` SET `Text_loc2` = 'Maître Carevin,\n\nLe porteur de cette note s\'est montré digne de la Lumière, capable de battre les morts-vivants et les démons qui infestent les abords du bois de la Pénombre, et désire rejoindre la famille Carevin dans sa lutte pour la Lumière.\n\nJe vous suggère donc de confier d\'autres tâches à ce guerrier de la Lumière, comme enquêter sur certains membres de la communauté qui sont suspectés d\'être traîtres à la Lumière.\n\nBien amicalement,\nCalor' WHERE `entry` = 63;
UPDATE `locales_page_text` SET `Text_loc2` = 'Morgan Ladimore était un grand et noble chevalier qui s\'est battu en défense de l\'innocent, du pauvre, de l\'affligé. Pendant des années, il a arpenté sans faiblir les confins d\'Azeroth, apportant le réconfort à ceux qui souffraient et un prompt châtiment aux serviteurs du mal.\n\nIl avait épousé une jeune femme du nom de Lys dans l\'été de sa dix-huitième année. Ils étaient fort amoureux et eurent trois enfants, un garçon et deux filles.\n\nMorgan avait trente-deux ans lorsque la guerre éclata à' WHERE `entry` = 64;
UPDATE `locales_page_text` SET `Text_loc2` = 'Lordaeron. Morgan fut appelé aux côtés du légendaire paladin Uther, le Porteur de Lumière ; pour combattre contre les orcs et les morts-vivants. Il laissa femme et enfants en sûreté dans leur maison et partit pour la guerre.\n\nLes années passèrent et la guerre ne cessait point. Morgan fut le témoin de bien des horreurs, dont la déroute des paladins de la Main d\'argent, la mort d\'Uther et l\'extension du Fléau. Il n\'était retenu de sombrer dans la folie que par l\'espoir d\'être' WHERE `entry` = 65;
UPDATE `locales_page_text` SET `Text_loc2` = 'un jour réuni à sa femme et ses enfants.\n\nMorgan finit par rentrer au pays, mais le trouva bien différent de ce qu\'il avait laissé derrière lui. Les forêts naguère verdoyantes étaient corrompues par les morts-vivants et les forces du mal. Des maisons et des fermes détruites noircissaient le paysage à perte de vue et le cimetière près de la Colline-aux-Corbeaux avait pris d\'effroyables proportions. Choqué et dérouté, Morgan regagna finalement sa demeure, mais ne trouva que des ruines. Ignorant ce qui avait ravagé sa terre natale, il' WHERE `entry` = 66;
UPDATE `locales_page_text` SET `Text_loc2` = 'se mit en marche vers le village pour trouver des réponses et, espérait-il, sa femme et ses enfants.\n\nMorgan tenta de savoir ce qu\'il était advenu des siens, mais ne trouva pas de réponse. Un prêtre de Sombre-comté, comme ces terres s\'appelaient désormais, lui conseilla de chercher parmi les pierres tombales du cimetière de la Colline-aux-Corbeaux. Morgan refusa de croire à la mort de sa famille et continua de fouiller chaque ferme, chaque maison du bois de la Pénombre, mais sans résultat.\n\nMorgan galopa de Sombre-comté jusqu\'à Comté-du-lac, pensant que sa famille s\'y était peut-être réfugiée. En route,' WHERE `entry` = 67;
UPDATE `locales_page_text` SET `Text_loc2` = 'il décida malgré tout de faire halte au cimetière de la Colline-aux-Corbeaux. Morgan passa des heures à marcher parmi les pierres tombales. Il reconnut le nom de bien des gens qu\'il avait connus et sentit sa raison défaillir. Puis il les vit : un petit espace mal entretenu avec trois pierres tombales. En approchant, il se sentit saisi d\'effroi. Morgan brossa la poussière de la pierre tombale la plus grande pour en lire le nom. Les lettres grossièrement gravées sur la pierre confirmèrent' WHERE `entry` = 68;
UPDATE `locales_page_text` SET `Text_loc2` = 'ses pires craintes :\n\nLys Ladimore\nÉpouse et mère bien-aimée\n\nLes peurs de Morgan se transformèrent en consternation puis en douleur, et il tomba à genoux pour pleurer. Il resta des heures à contempler cette tombe, implorant la pierre froide de lui pardonner. Quelque chose finit par casser à l\'intérieur de lui. Il dégaina son épée et se mit à asséner des coups sur les pierres tombales avec des hurlements de douleur. Aveuglé par la rage, il se démenait sauvagement et finit par attirer l\'attention' WHERE `entry` = 69;
UPDATE `locales_page_text` SET `Text_loc2` = 'de trois fossoyeurs. Ces derniers tentèrent de l\'immobiliser, mais il se retourna contre eux, hurlant des accusations contre les innocents fossoyeurs. Puis il les tua tous les trois.\n\nPlus tard, sa rage passée, Morgan commença à réaliser ce qu\'il venait de faire : son épée sanglante était encore plantée dans la poitrine de l\'un des fossoyeurs. Désespéré, il dégaina son poignard et se l\'enfonça dans la poitrine. \n\nLes corps de Morgan Ladimore et de ses trois victimes furent retrouvés' WHERE `entry` = 70;
UPDATE `locales_page_text` SET `Text_loc2` = 'le lendemain. Le paladin fut enterré rapidement, sans cérémonie, dans une tombe creusée à la hâte aux abords du cimetière. Comme Morgan avait assassiné des innocents, ce qui était contraire à sa nature et à ses convictions, et en raison de la douleur qu\'il avait ressentie à n\'avoir pas su protéger sa famille, Morgan n\'eut pas droit à une mort paisible et fut condamné à hanter dans l\'affliction.\n\nQuelques jours plus tard, on constata que son corps ne se trouvait plus dans sa tombe. L\'être qui avait été Morgan erre' WHERE `entry` = 71;
UPDATE `locales_page_text` SET `Text_loc2` = 'maintenant dans le bois de la Pénombre, consumé par le chagrin d\'avoir perdu sa famille et par la haine qu\'il se porte à lui-même. Mor\'Ladim (tel est désormais son nom) arpente le bois de la Pénombre, rempli de haine et d\'un désir de vengeance. Il tue sans discernement.' WHERE `entry` = 72;
UPDATE `locales_page_text` SET `Text_loc2` = 'La terreur des dernières semaines est plus que je n\'en peux supporter. Mais je pense qu\'en écrivant ce que j\'ai vu, je pourrai trouver quelque réconfort. C\'est le seul que je puisse espérer en ces temps obscurs.' WHERE `entry` = 73;
UPDATE `locales_page_text` SET `Text_loc2` = 'PREMIER JOUR\n\nL\'apprenti et moi-même sommes arrivés aujourd\'hui à la vallée des Frigères. Il ne s\'est rien passé au cours de notre passage dans les grottes. Nous avons établi notre campement à bonne distance de la grotte où l\'on dit que les trolls se sont installés.\n\nDEUXIÈME JOUR\n\nLes hurlements d\'un satané loup m\'ont tenu éveillé toute la nuit.\n\nJ\'aurai un nouveau manteau en peau de loup dans quelques jours.\n\nLe gamin a eu maille à partir avec un trogg aujourd\'hui, il y a eu un combat, et' WHERE `entry` = 74;
UPDATE `locales_page_text` SET `Text_loc2` = 'PREMIER JOUR\n\nL\'apprenti et moi-même sommes arrivés aujourd\'hui à la vallée des Frigères. Il ne s\'est rien passé au cours de notre passage dans les grottes. Nous avons établi notre campement à bonne distance de la grotte où l\'on dit que les trolls se sont installés.\n\nDEUXIÈME JOUR\n\nLes hurlements d\'un satané loup m\'ont tenu éveillé toute la nuit.\n\nJ\'aurai un nouveau manteau en peau de loup dans quelques jours.\n\nLe gamin a eu maille à partir avec un trogg aujourd\'hui, il y a eu un combat, et' WHERE `entry` = 77;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ce bon permet d\'obtenir un élément d\'armure en cuir ou en étoffe à la demeure de Timberlain, dans le Camp de bûcherons du Val d\'est' WHERE `entry` = 78;
UPDATE `locales_page_text` SET `Text_loc2` = 'Voici la procédure et l\'organisation de la collecte d\'or dans les mines d\'Elwynn par les Défias, pour le bénéfice de notre quartier général de la marche de l\'Ouest.\n \nDélais :\n\nDimanche : 12h30\nMercredi : 12h30\n\nChaque jour spécifié, l\'or obtenu dans les mines d\'Elwynn sera rassemblé au champ de potirons des Saumepuits. L\'agent en charge de ces collectes, nommé "le Collecteur", s\'identifiera par l\'anneau gravé que je lui ai donné.' WHERE `entry` = 79;
UPDATE `locales_page_text` SET `Text_loc2` = 'Grelin,\n\nJ\'ai peu de temps et bien des affaires à régler, et j\'espère que votre enquête sur les trolls ne me causera pas de soucis supplémentaires. Je vous autorise donc à utiliser mon autorité dans l\'affaire des trolls de la façon que vous estimerez nécessaire, surtout si vous pouvez régler l\'affaire rapidement.\n\n\nMagni Barbe-de-bronze' WHERE `entry` = 80;
UPDATE `locales_page_text` SET `Text_loc2` = 'il a réussi à prendre l\'avantage sur l\'horrible brute, mais il a subi une méchante entaille au bras. Je devrai m\'occuper des corvées pour un moment.\n\nTROISIÈME JOUR\n\nIl fait froid. Il a un peu neigé vers midi.\n\nQUATRIÈME JOUR\n\nLe gamin semble en meilleure forme aujourd\'hui, alors nous sommes allés jeter un coup d\'œil sur les trolls. Des trolls Crins-de-givre, selon moi, à en juger par les scarifications et la bimbeloterie qu\'ils portent sur leur corps.' WHERE `entry` = 81;
UPDATE `locales_page_text` SET `Text_loc2` = 'CINQUIÈME JOUR\n\nJ\'ai laissé le gamin veiller sur le camp et je me suis glissé dans la grotte pour y jeter un coup d\'œil. Les effectifs des trolls sont inquiétants. Les Montagnards devront être mobilisés en masse s\'il faut nettoyer la grotte de l\'invasion des trolls.\n\nJe me rends compte que je n\'ai pas mentionné les troggs dernièrement. Ils se font plus nombreux dans la zone, mais ils sont si primitifs qu\'ils ne représentent pas une vraie menace. Le temps confirmera ou non cette impression.' WHERE `entry` = 82;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pas une goutte de bière depuis pratiquement une semaine.\n\nSIXIÈME JOUR\n\nIl fait de nouveau froid. Il neige. Trolls. Pas de bière.\n\nSEPTIÈME JOUR\n\nLa neige est si blanche,\nLe ciel si bleu,\nLes loups cherchent une revanche,\nC\'est de la bière que je veux.\n\nHUITIÈME JOUR\n\nLes trolls ne sont peut-être pas le problème que nous pensions initialement. Ils sont' WHERE `entry` = 83;
UPDATE `locales_page_text` SET `Text_loc2` = 'nombreux mais mal armés et peu organisés. Une petite démonstration de force devrait suffire pour régler la question.' WHERE `entry` = 84;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mon Journal\n\nLa terreur de ces dernières semaines est presque plus que je ne puis en supporter. Comment se fait-il qu\'en rédigeant ces quelques mots je puisse en quelque sorte tenir la folie à distance ? C\'est comme si je confessais mes fautes à quelque compagnon silencieux, ou libérais mes pensées torturées pour les confiner sur le papier.  \n\nJ\'ai commencé un journal avant celui-ci, mais il est resté à un endroit où je ne puis retourner. Je reprends donc tout du début. Mais du vrai début, cette fois-ci.' WHERE `entry` = 85;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapport sur les trolls Crins-de-givre dans la région de la vallée des Frigères\n\nPréparé par Grelin Blanchebarbe, Envoyé spécial du Sénat\n\nAprès avoir passé du temps à observer les mouvements des trolls Crins-de-givre dans la région de la vallée des Frigères, je suis en mesure de dire qu\'ils ne représentent pas une menace sérieuse pour les comptoirs des nains dans la zone. Bien plus, avec une petite aide de l\'armée, la question pourrait rapidement être réglée. Avec le soutien des Montagnards déjà présents dans la' WHERE `entry` = 86;
UPDATE `locales_page_text` SET `Text_loc2` = 'vallée des Frigères et de mercenaires (payés par les fonds débloqués avant mon départ par le Sénat), je suis convaincu que le problème sera résolu rapidement. \n\nCette action a été autorisée par le roi Barbe-de-bronze.' WHERE `entry` = 87;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapport sur les trolls Crins-de-givre dans la région de Dun Morogh\n\nLes trolls situés dans Dun Morogh sont essentiellement concentrés dans le Repaire des Crins-de-givre, un renfoncement montagneux à la frontière de l\'ouest. Ils sont en nombre suffisant pour causer une grave inquiétude, mais ils ne semblent pas avoir la velléité de sortir de leur antre. Probablement ne souhaitent-ils pas encourir de nouveau la colère des nains et risquer l\'extermination totale. Leurs actions restent confinées dans leur territoire, et je' WHERE `entry` = 88;
UPDATE `locales_page_text` SET `Text_loc2` = 'pense qu\'ils ne nous menacent pas directement, tant que nous ne mordons pas sur leur territoire. Cette situation n\'est pas confortable pour la population des nains, mais vu la dispersion des ressources militaires, il semble prudent de reléguer l\'extermination des trolls à un niveau moindre d\'importance et de continuer à nous concentrer sur la menace posée par les troggs et les Sombrefer.\n\nVous trouverez ci-joint un exemplaire du rapport de mon frère Grelin sur Courbenclume.\n\nSigné,\nSenir Blanchebarbe' WHERE `entry` = 89;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapport sur les trolls Crins-de-givre dans la région de la vallée des Frigères\n\nPréparé par Grelin Blanchebarbe, Envoyé spécial du Sénat\n\nAprès avoir passé du temps à observer les mouvements des trolls Crins-de-givre dans la région de la vallée des Frigères, je suis en mesure de dire qu\'ils ne représentent pas une menace sérieuse pour les comptoirs des nains dans la zone. Bien plus, avec une petite aide de l\'armée, la question pourrait rapidement être réglée. Avec le soutien des Montagnards déjà présents dans la' WHERE `entry` = 90;
UPDATE `locales_page_text` SET `Text_loc2` = 'vallée des Frigères et de mercenaires (payés par les fonds débloqués avant mon départ par le Sénat), je suis convaincu que le problème sera résolu rapidement. \n\nCette action a été autorisée par le roi Barbe-de-bronze.' WHERE `entry` = 91;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tarrel -\n\nExcusez-moi de vous écrire à la va-vite, mais nous n\'avons pas le luxe du temps.  \n\nL\'équipe de forage a fait des progrès significatifs, exhumant d\'importants artefacts des Titans. Mais nous avons également trouvé d\'autres objets antiques, en particulier des ossements de grande taille.' WHERE `entry` = 92;
UPDATE `locales_page_text` SET `Text_loc2` = 'Peu après l\'exhumation des ossements, nous avons été attaqués par des raptors. Les hommes du régiment de Tresse-longue ont combattu vaillamment, mais ils ont été submergés. Presque tout le monde a péri. Nous ne sommes que trois à avoir survécu. Un ouvrier du nom d\'Ormer s\'est montré fort héroïque, en nous protégeant, le prospecteur Whelgar et moi-même.' WHERE `entry` = 93;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous nous sommes réfugiés dans une caverne, apparemment hors de danger. Mais nous sommes piégés ici jusqu\'à ce qu\'une force puisse être envoyée pour reconnaître la position des raptors. Whelgar essaie de continuer le travail mais je suspecte qu\'il est toujours bien effrayé. Telle est notre situation, Tarrel. Veuillez presser Tresse-longue de nous dépêcher immédiatement un contingent de secours.\n\nMon amour vous reste acquis, même en ces heures difficiles.\n\n—Merrin' WHERE `entry` = 94;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les vertes collines de Strangleronce\n\nNotre première journée s\'est aussi bien déroulée qu\'on pouvait l\'espérer. Nous avons surtout oeuvré à préparer l\'établissement d\'un campement. J\'ai repéré un emplacement idéal près d\'une rivière. À en juger par les vieux quais abandonnés qui s\'y trouvent, ce site a été habité naguère. Quant aux anciens habitants, le temps nous dira peut-être ce qu\'ils sont devenus.' WHERE `entry` = 95;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les vertes collines de Strangleronce\n\nNotre première journée s\'est aussi bien déroulée qu\'on pouvait l\'espérer. Nous avons surtout oeuvré à préparer l\'établissement d\'un campement. J\'ai repéré un emplacement idéal près d\'une rivière. À en juger par les vieux quais abandonnés qui s\'y trouvent, ce site a été habité naguère. Quant aux anciens habitants, le temps nous dira peut-être ce qu\'ils sont devenus.' WHERE `entry` = 97;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans les années qui suivirent, après la trahison de la Guilde des Maçons par Hurlevent, sire Erlgadin devint aigri au sujet du rôle des nobles du royaume. Il ne souhaitait plus conserver la position que la lignée de son père lui réservait à la Maison des Nobles. Mais trêve de digressions. Le but de cette histoire n\'est pas de jouer au traité de science politique ni à la biographie. C\'est la narration de mes expériences de chasse dans les vertes vallées de Strangleronce.' WHERE `entry` = 98;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans les années qui suivirent, après la trahison de la Guilde des Maçons par Hurlevent, sire Erlgadin devint aigri au sujet du rôle des nobles du royaume. Il ne souhaitait plus conserver la position que la lignée de son père lui réservait à la Maison des Nobles. Mais trêve de digressions. Le but de cette histoire n\'est pas de jouer au traité de science politique ni à la biographie. C\'est la narration de mes expériences de chasse dans les vertes vallées de Strangleronce.' WHERE `entry` = 99;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors que j\'allais interroger Ajeck sur son manque d\'intérêt pour la stratégie de ce jour de chasse, elle s\'est précipitée vers son carquois, a sorti une flèche et a tiré en direction du pauvre Barnil. Mais ce n\'est pas Barnil qu\'Ajeck visait. Lorsque Barnil s\'est écarté, bouche bée, un grand crocilisque des rivières flottait à la surface, la flèche d\'Ajeck parfaitement plantée entre ses deux yeux.' WHERE `entry` = 100;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors que j\'allais interroger Ajeck sur son manque d\'intérêt pour la stratégie de ce jour de chasse, elle s\'est précipitée vers son carquois, a sorti une flèche et a tiré en direction du pauvre Barnil. Mais ce n\'est pas Barnil qu\'Ajeck visait. Lorsque Barnil s\'est écarté, bouche bée, un grand crocilisque des rivières flottait à la surface, la flèche d\'Ajeck parfaitement plantée entre ses deux yeux.' WHERE `entry` = 101;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au cours d\'un de ces faux-pas, Erlgadin a posé lourdement la main sur l\'épaule de Barnil. Ajeck et moi nous sommes lancé un regard, pensant qu\'il voulait simplement tancer Barnil. Mais Erlgadin a fait un geste lent de la tête vers un arbre mort, non loin. Deux yeux noirs nous fixaient au-dessus d\'une gueule emplie de crocs acérés.' WHERE `entry` = 102;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au cours d\'un de ces faux-pas, Erlgadin a posé lourdement la main sur l\'épaule de Barnil. Ajeck et moi nous sommes lancé un regard, pensant qu\'il voulait simplement tancer Barnil. Mais Erlgadin a fait un geste lent de la tête vers un arbre mort, non loin. Deux yeux noirs nous fixaient au-dessus d\'une gueule emplie de crocs acérés.' WHERE `entry` = 103;
UPDATE `locales_page_text` SET `Text_loc2` = 'La mise à mort a créé une atmosphère joviale parmi les membres de l\'expédition. Barnil a servi de l\'hydromel à tous. Mais la bonne humeur n\'a pas duré. Alors que nous préparions le cadavre pour le rapporter au campement, nous avons été surpris par un horrible grognement. De toute ma vie je n\'ai jamais rien entendu qui soit plus de nature à glacer le sang.' WHERE `entry` = 104;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sur un précipice rocheux découpé par le soleil couchant, j\'ai aperçu le plus gros félin que l\'on puisse imaginer. J\'ai tiré une salve maladroite avec mon arme, mais le félin n\'a pas bougé. Il a grogné à nouveau, plus fort que la première fois, et il a disparu.\n\nNous avons rassemblé nos affaires et nous sommes rentrés au campement.' WHERE `entry` = 105;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sur un précipice rocheux découpé par le soleil couchant, j\'ai aperçu le plus gros félin que l\'on puisse imaginer. J\'ai tiré une salve maladroite avec mon arme, mais le félin n\'a pas bougé. Il a grogné à nouveau, plus fort que la première fois, et il a disparu.\n\nNous avons rassemblé nos affaires et nous sommes rentrés au campement.' WHERE `entry` = 107;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ce jour-là, nous nous sommes aventurés vers le sud, à la suite de traces de panthère fraîches. Nous sommes arrivés à un ravin traversé par un immense pont de corde. Je n\'ai pu m\'empêcher de penser aux descriptions de Brann sur la région, quand j\'ai aperçu cette merveille surplombant le vide. On pense si souvent que les trolls sont une race primitive et sans éducation, mais en observant la parfaite construction du pont je ne pus m\'empêcher d\'admirer l\'habileté avec laquelle les bâtisseurs trolls avaient vaincu cette difficulté apparemment insurmontable.' WHERE `entry` = 108;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ce jour-là, nous nous sommes aventurés vers le sud, à la suite de traces de panthère fraîches. Nous sommes arrivés à un ravin traversé par un immense pont de corde. Je n\'ai pu m\'empêcher de penser aux descriptions de Brann sur la région, quand j\'ai aperçu cette merveille surplombant le vide. On pense si souvent que les trolls sont une race primitive et sans éducation, mais en observant la parfaite construction du pont je ne pus m\'empêcher d\'admirer l\'habileté avec laquelle les bâtisseurs trolls avaient vaincu cette difficulté apparemment insurmontable.' WHERE `entry` = 109;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin restaient aux aguets, canon levé vers les broussailles qui se hérissaient à la base de la frange mouvante des arbres. Le soleil de midi tapait dur sur nos épaules. Un mince filet de sueur coulait le long des tempes d\'Erlgadin lorsqu\'il a relevé le chien de son arme. Au cliquetis qui a retenti, la dense végétation s\'est ouverte et une panthère noire, un spécimen magnifique, est entrée dans la clairière.' WHERE `entry` = 110;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin restaient aux aguets, canon levé vers les broussailles qui se hérissaient à la base de la frange mouvante des arbres. Le soleil de midi tapait dur sur nos épaules. Un mince filet de sueur coulait le long des tempes d\'Erlgadin lorsqu\'il a relevé le chien de son arme. Au cliquetis qui a retenti, la dense végétation s\'est ouverte et une panthère noire, un spécimen magnifique, est entrée dans la clairière.' WHERE `entry` = 111;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son arme a failli lui échapper des mains. Le canon a dévié et est venu se placer sous le fusil d\'Ajeck. Ajeck a choisi ce moment précis pour tirer. Le coup est parti avec sa détonation caractéristique, mais dans la direction de la ligne d\'arbres. Des oiseaux se sont envolés dans toutes les directions avec des cris plaintifs. Un filet de fumée s\'est élevé de l\'arbre qui avait été touché. Stupéfaits, nous avons vu une énorme branche tomber sur la panthère en pleine course, lui brisant la colonne vertébrale.' WHERE `entry` = 112;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son arme a failli lui échapper des mains. Le canon a dévié et est venu se placer sous le fusil d\'Ajeck. Ajeck a choisi ce moment précis pour tirer. Le coup est parti avec sa détonation caractéristique, mais dans la direction de la ligne d\'arbres. Des oiseaux se sont envolés dans toutes les directions avec des cris plaintifs. Un filet de fumée s\'est élevé de l\'arbre qui avait été touché. Stupéfaits, nous avons vu une énorme branche tomber sur la panthère en pleine course, lui brisant la colonne vertébrale.' WHERE `entry` = 113;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous avons levé le camp à l\'aube et sommes partis vers le sud en traversant les ruines de Tkashi. Barnil craignait qu\'on ne rencontre des membres de la tribu Scalp-rouge. J\'ai rappelé à Barnil que les Scalps-rouges se concentraient surtout sur la destruction de leurs rivaux, les Casse-crâne. Cela, bien sûr, n\'a pas rassuré Barnil le moins du monde. Mais j\'avais un fusil chargé, des munitions, et trois chasseurs expérimentés pour me réconforter contre la menace d\'une embuscade.' WHERE `entry` = 114;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je me suis déjà tenu devant un immense infernal sur le champ de bataille, tandis que l\'armée de la Légion ardente avançait de toutes les directions. Une bande de trolls sans chef est aussi inoffensive qu\'un lapin dans les collines de Dun Morogh.\n\nNous avons traversé les ruines de Tkashi sans incident, au grand soulagement de Barnil. Le groupe s\'est ensuite dirigé vers l\'ouest, en direction de la Grande Mer, longeant les ruines de Zul\'Kunda au sud. C\'est alors que nous montions les escarpements du rivage que nous avons repéré notre premier raptor.' WHERE `entry` = 115;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je me suis déjà tenu devant un immense infernal sur le champ de bataille, tandis que l\'armée de la Légion ardente avançait de toutes les directions. Une bande de trolls sans chef est aussi inoffensive qu\'un lapin dans les collines de Dun Morogh.\n\nNous avons traversé les ruines de Tkashi sans incident, au grand soulagement de Barnil. Le groupe s\'est ensuite dirigé vers l\'ouest, en direction de la Grande Mer, longeant les ruines de Zul\'Kunda au sud. C\'est alors que nous montions les escarpements du rivage que nous avons repéré notre premier raptor.' WHERE `entry` = 117;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nos tirs mal ajustés ont suffi à couvrir la fuite de Barnil. Il a dévalé la colline pour nous rejoindre. Nous nous sommes précipités dans la jungle, entraînant derrière nous une meute de raptors flagellants.\n\nLes chasseurs étaient désormais les proies.' WHERE `entry` = 118;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai mené le groupe jusqu\'à la mer, espérant que le rivage nous fournirait une protection contre les raptors. Dans notre hâte, nous sommes allés trop loin vers le nord, jusqu\'à un promontoire dangereusement escarpé. Erreur fatale. Ma faute. Nous nous sommes retrouvés bloqués sur une corniche, les raptors à quelques pas derrière nous.' WHERE `entry` = 119;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je fis lentement un pas en avant, arme levée. J\'avais conduit ces braves chasseurs à leur mort. J\'étais prêt à mourir en tentant de les défendre. Les raptors flagellants sont particulièrement féroces, réputés pour leur inextinguible soif de sang. Ils étaient beaucoup plus nombreux que nous. Mais je n\'allais pas les laisser me tuer ainsi que mes camarades sans d\'abord verser un peu de leur sang.' WHERE `entry` = 120;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin ont préparé leurs armes, un à chacun de mes flancs, nos dos tournés vers la mer. Barnil a laissé échapper un soupir et a levé sa hache. Les flagellants étaient presque sur nous. Ils avançaient moins vite. Ils savaient que leurs proies étaient piégées.' WHERE `entry` = 121;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin ont préparé leurs armes, un à chacun de mes flancs, nos dos tournés vers la mer. Barnil a laissé échapper un soupir et a levé sa hache. Les flagellants étaient presque sur nous. Ils avançaient moins vite. Ils savaient que leurs proies étaient piégées.' WHERE `entry` = 152;
UPDATE `locales_page_text` SET `Text_loc2` = 'Notre première journée s\'est aussi bien déroulée qu\'on pouvait l\'espérer. Nous avons surtout œuvré à préparer l\'établissement d\'un campement. J\'ai repéré un emplacement idéal près d\'une rivière. À en juger par les vieux quais abandonnés qui s\'y trouvent, ce site a été habité naguère. Quant aux anciens habitants, le temps nous dira peut-être ce qu\'ils sont devenus.' WHERE `entry` = 153;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pour cette expédition, j\'ai réuni Ajeck Rouack et sire S. J. Erlgadin, ainsi que mon fidèle serviteur Barnil Potepierre. J\'ai combattu aux côtés du père d\'Ajeck dans bien des batailles au service de l\'Alliance. La voir grandir a été quelque chose de très particulier. Son père l\'a bien formée dans les arts du maniement d\'armes. Et en voyant son habileté à l’arc, je me demande si elle n’a pas du sang elfe dans les veines.' WHERE `entry` = 154;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sire S. J. Erlgadin est un aristocrate humain. Son père, le comte Erlgadin, était célèbre pour sa générosité. C\'est le comte qui s\'est battu pour améliorer les conditions de travail de la Guilde des Maçons pendant la reconstruction de Hurlevent après la Deuxième guerre.' WHERE `entry` = 155;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans les années qui suivirent, après la trahison de la Guilde des Maçons par Hurlevent, sire Erlgadin devint aigri au sujet du rôle des nobles du royaume. Il ne souhaitait plus conserver la position que la lignée de son père lui réservait à la Maison des Nobles. Mais trêve de digressions. Le but de cette histoire n\'est pas de jouer au traité de science politique ni à la biographie. C\'est la narration de mes expériences de chasse dans les vertes vallées de Strangleronce.' WHERE `entry` = 156;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous nous sommes levés à l\'aube. Barnil a commencé à préparer le repas du matin. J\'ai constaté qu\'Ajeck semblait distraite. La marche serait longue ce jour-là et la chasse s\'annonçait dangereuse. Il était vital que chacun reste concentré. Mais Ajeck me semblait incapable de détacher ses yeux de Barnil qui nettoyait ses affaires dans la rivière.' WHERE `entry` = 157;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors que j\'allais interroger Ajeck sur son manque d\'intérêt pour la stratégie de ce jour de chasse, elle s\'est précipitée vers son carquois, a sorti une flèche et a tiré en direction du pauvre Barnil. Mais ce n\'est pas Barnil qu\'Ajeck visait. Lorsque Barnil s\'est écarté, bouche bée, un grand crocilisque des rivières flottait à la surface, la flèche d\'Ajeck parfaitement plantée entre ses deux yeux.' WHERE `entry` = 158;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous sommes partis vers l\'ouest, à travers la jungle épaisse. Marchant lentement et précautionneusement, nous avons cherché nos proies dans l\'épaisse frondaison. La matinée s\'est écoulée dans un silence frustré. Rien ne frémissait dans la vallée, pas même la brise. Au début de l\'après-midi, nous étions fatigués et Barnil avait abandonné la démarche silencieuse du chasseur de proies. Il arpentait la région d\'un pas lourd, faisant craquer les feuilles mortes et les brindilles.' WHERE `entry` = 159;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au cours d\'un de ces faux-pas, Erlgadin a posé lourdement la main sur l\'épaule de Barnil. Ajeck et moi nous sommes lancé un regard, pensant qu\'il voulait simplement tancer Barnil. Mais Erlgadin a fait un geste lent de la tête vers un arbre mort, non loin. Deux yeux noirs nous fixaient au-dessus d\'une gueule emplie de crocs acérés.' WHERE `entry` = 160;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'était un tigre de Strangleronce, un mâle. Avant que j\'aie le temps de relever mon arme, Erlgadin a pointé son arbalète vers la bête et a fait feu. Le trait a manqué la tête et a mordu la bête dans le flanc gauche. Le tigre a fait une puérile tentative pour fuir, mais la blessure était trop grave. Il a oscillé quelques secondes et Barnil l\'a achevé d\'un coup de hache.' WHERE `entry` = 161;
UPDATE `locales_page_text` SET `Text_loc2` = 'La mise à mort a créé une atmosphère joviale parmi les membres de l\'expédition. Barnil a servi de l\'hydromel à tous. Mais la bonne humeur n\'a pas duré. Alors que nous préparions le cadavre pour le rapporter au campement, nous avons été surpris par un horrible grognement. De toute ma vie je n\'ai jamais rien entendu qui soit plus de nature à glacer le sang.' WHERE `entry` = 162;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sur un précipice rocheux découpé par le soleil couchant, j\'ai aperçu le plus gros félin que l\'on puisse imaginer. J\'ai tiré une salve maladroite avec mon arme, mais le félin n\'a pas bougé. Il a grogné à nouveau, plus fort que la première fois, et il a disparu.\n\nNous avons rassemblé nos affaires et nous sommes rentrés au campement.' WHERE `entry` = 163;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'avais promis à l\'équipe que nous passerions le jour suivant à chasser des panthères, car leur fourrure est très chère à Azeroth. Cela est juste, vu le nombre de trappeurs et de chasseurs qui ont perdu la vie au service de l\'Alliance.' WHERE `entry` = 164;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin étaient désireux d\'apprendre à chasser efficacement avec un fusil nain. J\'ai conseillé aux deux humains de laisser leurs armes primitives au campement. Barnil et moi leur avons fourni des armes à feu parmi les meilleures de Forgefer.' WHERE `entry` = 165;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ce jour-là, nous nous sommes aventurés vers le sud, à la suite de traces de panthère fraîches. Nous sommes arrivés à un ravin traversé par un immense pont de corde. Je n\'ai pu m\'empêcher de penser aux descriptions de Brann sur la région, quand j\'ai aperçu cette merveille surplombant le vide. On pense si souvent que les trolls sont une race primitive et sans éducation, mais en observant la parfaite construction du pont je ne pus m\'empêcher d\'admirer l\'habileté avec laquelle les bâtisseurs trolls avaient vaincu cette difficulté apparemment insurmontable.' WHERE `entry` = 166;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien vite, Ajeck repéra les traces d\'une panthère vers le sud-ouest. Nous avons avancé rapidement, les armes prêtes, avides de rencontrer notre proie. Un craquement de branchages a attiré notre attention. Il y avait quelque chose par là. Un coup d\'œil rapide à Barnil a confirmé mes suppositions. Barnil a lentement relevé son fusil. Cette proie n\'était pas pour nous, mais pour nos compagnons humains. D\'innombrables panthères avaient perdu la vie au bout de nos canons. C\'était au tour des humains.' WHERE `entry` = 167;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin restaient aux aguets, canon levé vers le feulement qui provenait de la frange mouvante des arbres. Le soleil de midi tapait dur sur nos épaules. Un mince filet de sueur coulait le long des tempes d\'Erlgadin lorsqu\'il a relevé le chien de son arme. Au cliquetis qui a retenti, la dense végétation s\'est ouverte et une panthère noire, un spécimen magnifique, est entrée dans la clairière.' WHERE `entry` = 168;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les humains ont pointé leur arme sur la panthère qui courait le long de la ligne des arbres. Les canons se déplaçaient en parallèles parfaites. Barnil m\'a lancé un coup d\'œil pressant mais j\'ai fait non de la tête. Cette chasse était pour les humains, pas pour Barnil ni pour moi. Erlgadin a tiré, mais a manqué la cible. Il n\'était visiblement pas préparé au violent recul de l\'arme.' WHERE `entry` = 169;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son arme a failli lui échapper des mains. Le canon a dévié et est venu se placer sous le fusil d\'Ajeck. Ajeck a choisi ce moment précis pour tirer. Le coup est parti avec sa détonation caractéristique, mais dans la direction de la ligne d\'arbres. Des oiseaux se sont envolés dans toutes les directions avec des cris plaintifs. Un filet de fumée s\'est élevé de l\'arbre qui avait été touché. Stupéfaits, nous avons vu une énorme branche tomber sur la panthère en pleine course, lui brisant la colonne vertébrale.' WHERE `entry` = 170;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au fur et à mesure que les semaines passaient, notre stock de peaux de panthère et de tigre grandissait. Je décidai qu\'il était temps pour l\'expédition de se concentrer sur un nouveau défi : les raptors.\n\nLes humains, tout en appréciant l\'expérience de Barnil et la mienne, décidèrent de ne pas se servir d\'armes à feu. Ajeck se sentait plus à l\'aise avec un arc et sire Erlgadin ne quittait jamais le camp sans une solide arbalète.' WHERE `entry` = 171;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous avons levé le camp à l\'aube et sommes partis vers le sud en traversant les ruines de Tkashi. Barnil craignait qu\'on ne rencontre des membres de la tribu Scalp-rouge. J\'ai rappelé à Barnil que les Scalps-rouges se concentraient surtout sur la destruction de leurs rivaux, les Casse-crâne. Cela, bien sûr, n\'a pas rassuré Barnil le moins du monde. Mais j\'avais un fusil chargé, des munitions, et trois chasseurs expérimentés pour me réconforter contre la menace d\'une embuscade.' WHERE `entry` = 172;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je me suis déjà tenu devant un immense infernal sur le champ de bataille, tandis que l\'armée de la Légion ardente avançait de toutes les directions. Une bande de trolls sans chef est aussi inoffensive qu\'un lapin dans les collines de Dun Morogh.\n\nNous avons traversé les ruines de Tkashi sans incident, au grand soulagement de Barnil. Le groupe s\'est ensuite dirigé vers l\'ouest, en direction de la Grande Mer, longeant les ruines de Zul\'Kunda au sud. C\'est alors que nous montions les escarpements du rivage que nous avons repéré notre premier raptor.' WHERE `entry` = 173;
UPDATE `locales_page_text` SET `Text_loc2` = 'La bête ne nous a pas entendus approcher. Tout ce qu\'elle a reçu, c\'est une balle entre les yeux. Sire Erlgadin a laissé échapper un cri de joie tandis qu\'Ajeck me lançait un coup de tête approbateur. J\'ai cherché ma pipe dans mon sac, pour célébrer cette victoire. Barnil a filé vers le haut de la colline pour retrouver le cadavre du raptor. J\'ai regardé la bête morte avec la satisfaction qui accompagne chaque joli tir.' WHERE `entry` = 174;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais je n\'ai pas profité longtemps de ce moment de gloire. Quand j\'ai tourné les yeux vers l\'horizon, j\'ai vu plusieurs silhouettes apparaître au sommet de la colline, juste au-dessus du pauvre Barnil.\n\n*Fuyez, Barnil !* ai-je hurlé. Ajeck, sire Erlgadin et moi avons envoyé une salve de balles et de flèches vers les nouveaux raptors. Dans la confusion, nous en avons abattu un autre.' WHERE `entry` = 175;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nos tirs mal ajustés ont suffi à couvrir la fuite de Barnil. Il a dévalé la colline pour nous rejoindre. Nous nous sommes précipités dans la jungle, entraînant derrière nous une meute de raptors flagellants.\n\nLes chasseurs étaient désormais les proies.' WHERE `entry` = 176;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai mené le groupe jusqu\'à la mer, espérant que le rivage nous fournirait une protection contre les raptors. Dans notre hâte, nous sommes allés trop loin vers le nord, jusqu\'à un promontoire dangereusement escarpé. Erreur fatale. Ma faute. Nous nous sommes retrouvés bloqués sur une corniche, les raptors à quelques pas derrière nous.' WHERE `entry` = 177;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je fis lentement un pas en avant, arme levée. J\'avais conduit ces braves chasseurs à leur mort. J\'étais prêt à mourir en tentant de les défendre. Les raptors flagellants sont particulièrement féroces, réputés pour leur inextinguible soif de sang. Ils étaient beaucoup plus nombreux que nous. Mais je n\'allais pas les laisser me tuer ainsi que mes camarades sans verser d\'abord un peu de leur sang.' WHERE `entry` = 178;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin ont préparé leurs armes, un à chacun de mes côtés, nos dos tournés vers la mer. Barnil a laissé échapper un soupir et a levé sa hache. Les flagellants étaient presque sur nous. Ils avançaient moins vite. Ils traquaient leurs proies maintenant qu\'ils savaient qu\'ils nous avaient piégés.' WHERE `entry` = 179;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et le miracle a eu lieu. Loin sur notre flanc, nous avons entendu le rugissement caractéristique et terrifiant du grand tigre blanc. Malgré leur nombre, les raptors ont fait demi-tour et se sont dispersés dans toutes les directions. Nous avons vu un bref éclair blanc : le tigre passait devant nous et se précipitait sur l\'un des raptors. Inutile de donner des ordres : nous savions tous que l\'heure de fuir avait sonné.' WHERE `entry` = 180;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous avons foncé jusqu\'au campement, sans ralentir. Au cours de la nuit, assis silencieusement autour du feu de camp, nous avons médité sur l\'étrange destin qui venait de nous sauver la vie. Tels sont les risques de la chasse. Nous jouons avec le destin. Chacun de nous, à un moment ou à un autre, rencontrera sa fin entre ses dents aiguisées. Le nain que je suis est simplement heureux que ce moment ne soit pas arrivé dans les vertes collines de Strangleronce.' WHERE `entry` = 181;
UPDATE `locales_page_text` SET `Text_loc2` = 'Estimés collègues et chercheurs de vérité\n\nLa fouille du site continue, mais elle est ralentie par les troggs que j\'ai mentionnés dans mon dernier rapport. Je suis sûr que l\'on peut s\'occuper d\'eux, même si certains de ces troggs sont plus agressifs que d\'ordinaire.\n\nD\'autres artefacts ont été exhumés dernièrement, mais dans tous les cas à l\'exception d\'un (dont je parlerai plus bas), les découvertes récentes ne sont pas du même intérêt que les précédentes.' WHERE `entry` = 182;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'exception est la suivante : les idoles étrangement gravées que nous avons trouvées dernièrement (j\'en ai communiqué une brève description dans mon dernier rapport) semblent affecter les troggs présents sur le site. Les marques sur la pierre les attirent, et certains troggs en deviennent fous furieux !\n\nD\'autres études sont nécessaires avant qu\'une conclusion ne soit atteinte en ce qui concerne ces idoles, mais j\'ai bon espoir qu\'elles révèlent un lien entre les troggs et les Titans.' WHERE `entry` = 183;
UPDATE `locales_page_text` SET `Text_loc2` = 'Enfin, je me dois de réitérer mes demandes de poudre. Mes stocks sont fort bas et cela fait peser un risque grave sur la réussite de ces fouilles. On m\'a affirmé que de la poudre était en route, mais je n\'ai toujours rien vu.\n\nSur quels délais dois-je compter ?\n\nRespectueusement,\nProspecteur Darteus Baguefer' WHERE `entry` = 184;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tout a commencé avec la découverte de cette maudite faux au Destin de Roland. Avant la faux, il n\'y avait rien de plus terrible ici que dans la vallée de Comté-du-nord.\n\nMais depuis que j\'ai trouvé la hampe de la faux dans un tas de déchets de la mine et que, malédiction sur moi, je l\'en ai extirpée, le Destin de Roland est devenu un lieu de mort impie !' WHERE `entry` = 185;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tout a commencé avec la découverte de cette maudite faux au Destin de Roland. Avant la Faux, il n\'y avait rien de plus terrible ici que dans la vallée de Comté-du-nord.\n\nMais depuis que j\'ai trouvé la hampe de la faux dans un tas de déchets de la mine et que, malédiction sur moi, je l\'en ai extirpée, le Destin de Roland est devenu un lieu de mort impie !' WHERE `entry` = 186;
UPDATE `locales_page_text` SET `Text_loc2` = 'Si j\'avais su ce qui allait se passer, je me serais coupé la main pour m\'empêcher de récupérer ce morceau de bois gravé de runes. Je suis bourrelé de remords. J\'ai toujours pensé que c\'était le privilège des vieux. Je sais maintenant que ce ne sont pas les vieux, ce sont les sans-espoir, qui portent le fardeau du regret, incapables d\'en débarrasser leurs misérables épaules.\n\nMais assez pleuré, je ne suis pas poète. Je dois continuer ma chronique...' WHERE `entry` = 187;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après l\'exhumation de la Faux, un changement s\'est opéré dans la mine. La lumière a tremblé au bout de nos torches, et la force de nos voix a paru échapper à notre contrôle. Parfois le murmure d\'un homme rugissait dans les tunnels, à en être assourdissant, parfois nos vociférations portaient à quelques pas seulement avant de s\'évanouir dans l\'air.\n\nEprouvant, certes, mais ces bizarreries ne sont rien en comparaison de ce qui nous a vraiment chassés de la mine. Les worgens.' WHERE `entry` = 188;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ils ont surgi de partout, sortant de trous à nos pieds, tombant d\'escarpements au-dessus de nos têtes. La moitié de nos hommes sont tombés dans ces premières minutes de panique. Les autres ont essayé de fuir. Moi aussi. Tout en courant, j\'ai vu nombre de nos frères se faire déchiqueter, entendu bien des cris sombrer dans le silence après un gargouillis.\n\nPour autant que je sache, je suis le seul à en avoir réchappé.' WHERE `entry` = 189;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ignore comment j\'ai survécu cette nuit-là. J\'ai toujours été prudent, toujours rapide à fuir les plaies et les bosses. On me traite de froussard depuis toujours. C\'est même pour ça qu\'on m\'a surnommé "La Tremblote". C\'est peut-être ce fort instinct de conservation qui m\'a sauvé...\n\nOu peut-être est-ce la Hampe que j\'ai arrachée aux décombres. Ce ne peut-être la Faux elle-même, je l\'ai perdue dans ma course éperdue. Mais si c\'est moi qui ai ramené les worgens dans le bois de la Pénombre, peut-être m\'ont-ils fait la grâce de me laisser survivre. Qu\'ils soient maudits !' WHERE `entry` = 190;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ou peut-être suis-je condamné à être le témoin des changements que j\'ai attirés sur le bois de la Pénombre. Peut-être est-ce mon destin de voir les worgens déchirer cette terre en lambeaux et la plonger dans les ténèbres.\n\nSi tel est vraiment mon destin, alors il est double. Car les worgens ne sont pas la seule puissance à vouloir le bois de la Pénombre : les misérables du défilé de Deuillevent ont également leurs projets.\n\nCe sera le prochain chapitre de ma narration, et je souhaite que ce soit le dernier...' WHERE `entry` = 191;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après avoir fui le Destin de Roland, je me suis caché dans une grange appartenant à un homme du nom de Sven. J\'ai passé quelques jours dans la grange et une telle horreur me possédait que je n\'ai jamais signalé ma présence à Sven ni à sa famille. Mais de ce que j\'ai vu depuis ma cachette, j\'ai compris que ces fermiers étaient d\'honnêtes gens. Si j\'étais sorti de mon recoin, je crois qu\'ils m\'auraient offert l\'hospitalité, mais je ne suis pas d\'un naturel confiant. Surtout depuis ce choc dans la mine.\n\nAlors je suis resté caché. Et cela a sauvé ma vie.' WHERE `entry` = 192;
UPDATE `locales_page_text` SET `Text_loc2` = 'Quelques jours après mon arrivée à la grange, Sven quitta sa ferme pour Sombre-comté. Il embrassa sa femme et ses enfants et promit de vite revenir avec des jouets et des bonbons. Pauvre vieux ! Ce fut la dernière fois qu\'il vit sa famille en un morceau.  \n\nAu moins se sont-ils quittés heureux. Au moins sa femme a-t-elle été la première à mourir, sans devoir assister au meurtre de ses enfants. Mais je n\'ai pas eu cette chance. J\'ai vu ce qui s\'est passé, et cela hantera mes rêves toute ma vie.' WHERE `entry` = 193;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mes mains tremblent tandis que je bats le rappel des souvenirs de cette nuit, Sven au loin et sa famille seule face aux Cavaliers Noirs. Les regrets se saisissent à nouveau de moi, car j\'aurais pu me dresser contre ces mécréants du défilé de Deuillevent. Mais c\'est un faux regret. Tout rescapé d\'une tragédie connaît les mêmes. Je le sais bien, si j\'avais quitté ma cachette, j\'aurais été tué aussi, mon corps dépecé, ses morceaux dispersés, mutilés, impossibles à reconnaître.' WHERE `entry` = 194;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais j\'ai beau savoir que je ne pouvais rien faire pour empêcher ce massacre, un vrai regret me taraude : c\'est moi qui ait attiré les Cavaliers Noirs jusqu\'à la ferme de Sven. Non seulement ma découverte de la Faux a ouvert aux worgens la porte du bois de la Pénombre, mais elle a aussi attiré les Cavaliers jusqu\'au défilé de Deuillevent.\n\nJe le sais parce que, juste avant de commencer leur boucherie, ils ont posé une question à la femme de Sven qui serrait ses enfants contre elle pour leur prodiguer le peu de réconfort qu\'elle pouvait avant une mort certaine.' WHERE `entry` = 195;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Faux d\'Elune ! hurla l\'un des Cavaliers d\'une voix dure et pourtant suraiguë, comme le feulement d\'un fer de hache contre une pierre. Et il glapit le dernier mot, Elune, comme s\'il butait dessus.\n\nLa panique s\'est emparé de moi lorsque j\'ai entendu cette voix, à cause de son timbre ignoble, et parce que... je savais de quelle Faux parlait le Cavalier. Ce ne pouvait être que l\'objet maudit que j\'avais arraché aux roches du Destin de Roland quelques jours plus tôt. C\'est cela que cherchaient les Cavaliers Noirs !\n\nEt c\'est cela qui scella le destin de la famille de Sven.' WHERE `entry` = 196;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ignore comment se nommait la femme de Sven, tant ils abusaient des "chérie", "mon amour" et autres "maman" dans la famille. Mais je voudrais le connaître. Je suis la mémoire vivante de ses gestes ce jour-là. Et bien qu\'elle ne fût qu\'une femme de fermier, je n\'ai jamais vu quiconque se comporter avec plus de bravoure.\n\nBien sûr, elle ne savait rien de la Faux, mais quand elle a compris que les Cavaliers le cherchaient, un plan a germé dans sa tête.\n\nC\'était audacieux et astucieux. Si seulement ça avait marché.' WHERE `entry` = 197;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Faux ? a-t-elle dit d\'une voix calme. "Je sais tout, bien sûr. Comme tout le monde ici." Elle fixa les Cavaliers d\'un regard posé et, si je n\'avais pas su la vérité, j\'aurais été convaincu qu\'elle savait tout de la Faux.\n\nSon pari a fonctionné. Le Cavalier qui avait posé la question se pencha légèrement vers elle et a croassé : "Où ?"\n\n"Je vais tous vous y conduire", a-t-elle répondu, et une lueur d\'espoir a dansé dans ses yeux.' WHERE `entry` = 198;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais c\'est loin d\'ici et mes enfants nous ralentiraient. Nous devons les laisser."\n\nSon plan était simple, mais ce sont les plans simples qui ont le plus de chances de fonctionner. Elle avait une chance d\'éloigner les Cavaliers de la ferme. Elle n\'avait aucun espoir pour sa propre vie mais ses enfants seraient sauvés. Il suffisait que les Cavaliers acceptent ses courageux mensonges.\n\nJe n\'ai jamais été un adepte de la Lumière, mais j\'ai prié pour la femme de Sven tandis qu\'elle faisait face aux terribles Cavaliers.\n\n"Par pitié", ai-je prié. "Faites qu\'ils mordent à l\'hameçon."' WHERE `entry` = 199;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ils attendaient, immobiles, et elle soutenait leur regard avec calme. Soudain, un cavalier a levé la tête, comme s\'il entendait un appel distant. Il a tiré d\'une poche une petite gemme et a regardé dedans. Puis il a fait un geste avec la verroterie vers la femme de Sven. Une lumière a jailli du cavalier et s\'est avancée vers la femme sous forme d\'une main blanche. La fermière a regardé la lumière sans bouger, mais une nuance d\'incertitude a fendillé son masque de confiance. Quand la main l\'a atteinte, ses doigts se sont ouverts au-dessus de son crâne.  \n\nPuis ils ont serré.' WHERE `entry` = 200;
UPDATE `locales_page_text` SET `Text_loc2` = 'La femme de Sven était comme pétrifiée et ses yeux se sont écarquillés. Ses lèvres se sont ouvertes pour pousser un cri, mais aucun son n\'est sorti de sa bouche. Après quelques instants de cette torture, la main l\'a relâchée et elle est tombée à genoux. Le Cavalier qui tenait la gemme se redressa sur sa selle et une voix forte retentit.\n\n"Cette femme ment", lâcha-t-il d\'une façon qui hante encore mes cauchemars. "Elle n\'a pas vu la Faux."' WHERE `entry` = 201;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après cela, les épaules du Cavalier se sont abaissées légèrement, comme si un esprit le quittait. Puis, de la voix stridente qu\'il avait à son arrivée, il a prononcé ces derniers mots :\n\n"Le Maître a parlé. Tuez-les."' WHERE `entry` = 202;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je ne peux décrire ce qui s\'est passé ensuite. C\'est précis dans mon esprit, mais même mon âme misérable ne peut mettre par écrit les événements des terribles minutes qui suivirent.\n\nJe ne peux que dire que la famille de Sven est morte. Peu après, Sven est revenu sur les lieux de cette scène atroce. Il a éclaté d\'une telle douleur que je n\'ai pas eu le courage de me montrer. Et de peur qu\'il ne me trouve, je me suis enfui de ma cachette dans la grange. J\'ignore ce qu\'il est advenu de Sven, mais je prie qu\'un jour il trouve la paix.' WHERE `entry` = 203;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je ne peux décrire ce qui s\'est passé ensuite. C\'est précis dans mon esprit, mais même mon âme misérable ne peut mettre par écrit les événements des terribles minutes qui suivirent.\n\nJe ne peux que dire que la famille de Sven est morte. Peu après, Sven est revenu sur les lieux de cette scène atroce. Il a éclaté d\'une telle douleur que je n\'ai pas eu le courage de me montrer. Et de peur qu\'il ne me trouve, je me suis enfui de ma cachette dans la grange. J\'ignore ce qu\'il est advenu de Sven, mais je prie qu\'un jour il trouve la paix.' WHERE `entry` = 204;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après l\'exhumation de la Faux, ils ont surgi de partout, sortant de trous à nos pieds, tombant d\'escarpements au-dessus de nos têtes. La moitié de nos hommes sont tombés dans ces premières minutes de panique. Les autres ont essayé de fuir. Moi aussi. Tout en courant, j\'ai vu nombre de nos frères se faire déchiqueter, et j\'ai entendu bien des cris sombrer dans le silence après un hoquet.' WHERE `entry` = 205;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ignore comment j\'ai survécu cette nuit-là. J\'ai toujours été prudent, toujours rapide à fuir les plaies et les bosses. On me traite de froussard depuis toujours. C\'est peut-être ce fort instinct de conservation qui m\'a sauvé...\n\nOu peut-être est-ce la Hampe que j\'ai arrachée aux décombres. Ce ne peut être la Faux elle-même, je l\'ai perdue dans ma course éperdue. Mais si c\'est moi qui ai ramené les worgens dans le bois de la Pénombre, peut-être m\'ont-ils fait la grâce de me laisser survivre. Qu\'ils soient maudits !' WHERE `entry` = 206;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après avoir fui le Destin de Roland, je me suis caché dans une grange appartenant à un homme du nom de Sven. J\'ai passé quelques jours dans la grange et une telle horreur me possédait que je n\'ai jamais signalé ma présence à Sven ni à sa famille. Mais de ce que j\'ai vu depuis ma cachette, j\'ai compris que ces fermiers étaient d\'honnêtes gens. Si j\'étais sorti de mon recoin, je crois qu\'ils m\'auraient offert l\'hospitalité, mais je ne suis pas d\'un naturel confiant. Surtout depuis ce choc dans la mine.\n\nAlors je suis resté caché.' WHERE `entry` = 207;
UPDATE `locales_page_text` SET `Text_loc2` = '<Le reste du livre est vierge>' WHERE `entry` = 208;
UPDATE `locales_page_text` SET `Text_loc2` = 'RAPPORT : Les kobolds\n\nL\'activité des kobolds s\'est accrue dans les pourtours de la vallée de Comté-du-nord. Ils ont établi un campement au nord de l\'abbaye et se rassemblent dans la mine d\'Echo en nombre effrayant. J\'ai fait prêter serment aux paladins, aux guerriers et aux habitants du cru pour qu\'ils m\'aident à éliminer cette vermine.\n\nTout se passe bien. Je donnerai plus de nouvelles quand la situation aura évolué.' WHERE `entry` = 209;
UPDATE `locales_page_text` SET `Text_loc2` = 'RAPPORT : Les voleurs\n\nPlus inquiétants que les kobolds, une bande humaine de voleurs a fait son apparition dans la vallée. Ils portent des foulards rouges et se désignent comme une confrérie. Une telle organisation peut apporter bien des ennuis, ici comme dans toute la forêt d\'Elwynn.\n\nNous avons identifié un de leurs chefs, un certain Garrick Ladétrousse, et nous espérons le présenter prochainement à la justice.' WHERE `entry` = 210;
UPDATE `locales_page_text` SET `Text_loc2` = 'RAPPORT : Les loups\n\nOn signale des attaques d\'animaux de plus en plus nombreuses à Comté-du-nord. Eagan pense que les loups de la vallée viennent du bois de la Pénombre, d\'où ils ont été effrayés.\n\nLes loups chassent rarement les humains, mais rien que leur nombre provoque des incidents avec les fermiers. Et de nombreuses bêtes ont disparu.' WHERE `entry` = 211;
UPDATE `locales_page_text` SET `Text_loc2` = 'RECOMMANDATION :\n\nLe porteur de ce document a été nommé adjoint dans l\'armée de Hurlevent, ayant servi Comté-du-nord avec dévouement et courage. Je ne doute pas que vous trouviez ses services utiles dans la forêt d\'Elwynn.\n\nSigné :\n-Maréchal Douglas McBride, \nArmée de Hurlevent, Comté-du-nord' WHERE `entry` = 212;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ragoût de la marche de l\'Ouest  \n\n3 morceaux de viande filandreuse de vautour \n3 groins de broche-tripes \n3 yeux de murlocs \n3 okras\n\nMélangez et faites bouillir. Faites cuire à feu doux pendant au moins deux heures avant de servir.' WHERE `entry` = 213;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chère Yvette,\n\nJe vais confier cette lettre à notre plus rapide messager. Je prie qu\'il trouve un passage à travers les troupes qui nous entourent. Et je prie pour que vous puissiez lire ces mots, qui sont les derniers que vous recevrez de moi.\n\nMon seul réconfort en ces jours sombres est mon soulagement à l\'idée que vous ne partagerez pas mon destin.' WHERE `entry` = 214;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chère Yvette,\n\nJe vais confier cette lettre à notre plus rapide messager. Je prie qu\'il trouve un passage à travers les troupes qui nous entourent. Et je prie pour que vous puissiez lire ces mots, qui sont les derniers que vous recevrez de moi.\n\nMon seul réconfort en ces jours sombres est mon soulagement à l\'idée que vous ne partagerez pas mon destin.' WHERE `entry` = 217;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je crains que la guerre entre les Champierreux et les Maclure ne finisse par tuer l\'amour naissant de Tommy Joe et de Maybell. En des temps comme ceux-ci, où les nouvelles sont noires et où la guerre menace, la jeunesse et l\'amour doivent être protégés.\n\nJe vous demande donc une faveur : utilisez vos connaissances pour concocter une potion capable d\'aider ces jeunes gens dans leur quête de vie commune.\n\nMerci, William. Et s\'il vous plaît, lorsque le travail vous laissera quelque répit, venez nous rendre visite. Nous évoquerons le bon vieux temps.\n\n-Mildred' WHERE `entry` = 218;
UPDATE `locales_page_text` SET `Text_loc2` = 'De la main de Baros Alexston,\nBureau de l\'Architecte de la cité\nHurlevent\n\nSire,\n\nPour de nombreuses raisons, j\'ai pensé qu\'un rapport devait être compilé et présenté sur les affaires récentes concernant la "Confrérie Défias" et ses activités dans le royaume.\n\nJe commencerai par un peu d\'histoire. Comme Votre Majesté le sait peut-être, mes services à la ville de Hurlevent ont commencé au sein de la Guilde des Maçons. Des années de travail ont permis la reconstruction de' WHERE `entry` = 219;
UPDATE `locales_page_text` SET `Text_loc2` = 'Hurlevent, après quoi les honoraires de la Guilde des Maçons n\'ont pas été acquittés.\n\nÀ l\'époque, Edwin VanCleef venait d\'être élu Maître de la Guilde des Maçons et il se fit le porte-parole des doléances de ses compagnons. En réponse, la Maison des Nobles de Hurlevent ordonna la dissolution de la Guilde des Maçons, ce qui, c\'est compréhensible, rendit VanCleef furieux. Dans une atmosphère d\'émeute, VanCleef mena les Maçons hors de la ville.\n\nAvant de continuer, voici quelques-uns des événements qui sont survenus durant' WHERE `entry` = 220;
UPDATE `locales_page_text` SET `Text_loc2` = 'ces temps troublés et sur lesquels j\'attire l\'attention de Votre Majesté.\n\nTout d\'abord, c\'est à cette époque que je me vis offrir le poste d\'architecte de la cité à condition de ne pas rejoindre VanCleef. En raison de certains divergences d\'opinion avec lui, je choisis de rester à Hurlevent.\n\nPendant les émeutes, le lieutenant de VanCleef, Bazil Thredd, fut capturé et jeté en prison, où il fut presque oublié, dans l\'attente d\'un procès.\n\nPour en revenir à VanCleef, après avoir conduit le restant des' WHERE `entry` = 221;
UPDATE `locales_page_text` SET `Text_loc2` = 'Maçons hors de Hurlevent, il profita des faibles défenses de la marche de l\'Ouest et s\'appuya sur ses troupes pour chasser nombre de fermiers avant de s\'emparer des mines d\'or.\n\nGrâce à ces ressources, VanCleef a préparé la vengeance contre le gouvernement de Hurlevent.\n\nCette information m\'est parvenue dernièrement grâce à l\'aide du porteur de ce document, qui a beaucoup contribué à mettre en lumière les dessous de cette vaste conspiration.' WHERE `entry` = 222;
UPDATE `locales_page_text` SET `Text_loc2` = 'Propagez l\'information selon les moyens qui vous semblent appropriés.\n\nCommandement régional de Tirisfal\nCapitaine Melrache\nCapitaine Vachon\nCapitaine Perrine\n\nPar l\'ordre du généralissime.\n\nCapitaine Perrine, fortifiez votre position à la tour du sud-ouest (comme indiqué). Des fournitures vous seront envoyées à une date ultérieure. En attendant, les matériaux voulus peuvent être obtenus dans les fermes voisines. De plus, des reconnaissances et des informations' WHERE `entry` = 223;
UPDATE `locales_page_text` SET `Text_loc2` = 'doivent impérativement nous renseigner sur l\'organisation des morts-vivants à Brill.\n\nCapitaine Vachon, il semble que les morts-vivants s\'agitent près de la tour du Nord. Vous devez vous occuper promptement de ces troubles.\n\nCapitaine Melrache, le niveau d\'organisation des morts-vivants cause des soucis près de la frontière des Maleterres. Des troupes fraîches seront affectées à votre position dans les semaines à venir.\n\nGloire et Lumière' WHERE `entry` = 224;
UPDATE `locales_page_text` SET `Text_loc2` = 'doivent impérativement nous renseigner sur l\'organisation des morts-vivants à Brill.\n\nCapitaine Vachon, il semble que les morts-vivants s\'agitent près de la tour du Nord. Vous devez vous occuper promptement de ces troubles.\n\nCapitaine Melrose, le niveau d\'organisation des morts-vivants cause des soucis près de la frontière des Maleterres. Des troupes fraîches seront affectées à votre position dans les semaines à venir.\n\nGloire et Lumière' WHERE `entry` = 228;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 1\n\nMon char est cassé, mais je sais que le minerai de chiron est quelque part dans ces collines. J\'ai dit à Martèlepied de rester et de surveiller les chars.\n\nJe continue les recherches seul. Si je ne reviens pas, alors ce journal constitue mon dernier testament.\n\n—Pilote Buron Hildelve\nBrigade des sapeurs de Forgefer' WHERE `entry` = 229;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 2\n\nMes recherches continuent, mais toujours pas de minerai. Je me demande si Engrepierre n\'a pas lancé la rumeur sur ce minerai pour plaisanter. Si c\'est le cas, il aura droit à une belle fissure crânienne quand je rentrerai au dépôt de Grillacier !\n\nCette nuit, en préparant mon bivouac, j\'ai entendu l\'écho d\'un grondement dans les canyons. \n\nCe n\'est pas un loup. Un ours, peut-être...' WHERE `entry` = 230;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 3\n\nLe grondement a continué toute la nuit et me suit aujourd\'hui, je ne peux plus me concentrer sur la recherche de minerai. Je pense qu\'un ours me suit !\n\nQu\'il approche ! Je lui planterai mon piolet dans la tête !' WHERE `entry` = 231;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 4\n\nMes provisions sont presque épuisées. J\'ai toujours de la nourriture, mais je n\'avais pas prévu de passer tant de temps éloigné de mon char et je n\'ai que deux tonnelets de bière avec moi.\n\nJe n\'ai pas fermé l\'œil au cours des deux dernières nuits, perturbé par le grognement permanent, et j\'ai presque asséché mes tonnelets !\n\nIl va falloir que je retourne vers Martèlepied et nos chars à vapeur dès demain. Je ne veux pas rester à errer dans les parages sans carburant.' WHERE `entry` = 232;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 5\n\nCe maudit ours s\'est enfin dévoilé ! Il m\'a attaqué à midi, il a chargé vers moi par derrière en grondant.\n\nJe l\'aurais senti venir s\'il avait chargé dans le vent, car il empestait horriblement ! Les relents de sa fourrure et de son haleine ont presque suffi à me faire m\'évanouir !\n\nJ\'ai repoussé la créature mais elle a eu le temps de me broyer la jambe. Je ne peux plus bouger, je n\'ai plus de bière et je n\'ai trouvé aucun minerai. Malédiction !' WHERE `entry` = 233;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 6\n\nL\'ours n\'est pas encore revenu, il a dû recevoir une bonne volée ! Mais j\'entends toujours son grognement. Je crois qu\'il attend que je meure !\n\nJ\'ai une requête pour quiconque trouvera ce livre. Tuez cet ours galeux ! Tuez-le et rapportez le livre à mon ami Martèlepied. Il voudra savoir ce qui s\'est passé.\n\nEt prenez mon armure. Vous en aurez besoin contre Griffe galeuse !' WHERE `entry` = 234;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salutations, Ello Bouclenoire, maire de Sombre-comté. J\'ai peur d\'avoir de mauvaises nouvelles pour votre ville.\n\nVoyez-vous, je suis un créateur. J\'ai trompé le porteur de ce message pour qu\'il m\'aide à réaliser ma dernière et plus audacieuse création : une créature mêlée de chair et de métal impie ! Au moment où vous lisez ces lignes, elle est probablement à l\'extérieur de mon humble demeure, aiguisant ses dents et attendant que je lui donne l\'ordre de commencer le massacre.\n\nMais vous verrez cela rapidement par vous-même.\n\n-L\'Embaumeur' WHERE `entry` = 235;
UPDATE `locales_page_text` SET `Text_loc2` = 'À travers l\'étude de différentes créatures fossilisées, j\'ai déduit que dans les temps anciens, une grande peste a ravagé les eaux du lac Lordamere. Quelle en fut la cause ? Nous ne le saurons peut-être jamais. Mais le taux de contamination paraît très élevé à en juger par la forte concentration de restes présents partout dans le lit du lac.' WHERE `entry` = 236;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans un effort pour comprendre le passé, j\'ai commencé à examiner les créatures du présent dans l\'espoir de trouver la chaînon manquant de ce mystère. Les furtifs et les rampants du lac sont des bêtes anciennes qui habitent les îles au centre du lac Lordamere. Une certaine mousse pousse sur leur corps qui évoque fortement certains éléments trouvés sur les fossiles. D\'autres recherches sont nécessaires avant que je ne puisse émettre une hypothèse.' WHERE `entry` = 237;
UPDATE `locales_page_text` SET `Text_loc2` = 'Seigneur Bethor Glacéclat,\n\nSuivant vos instructions, je vous envoie l\'agent que vous avez demandé. C\'est le porteur de ce message.\n\nCette personne s\'est révélée fiable et capable d\'obéir aux ordres, a su affronter les forces du Fléau, et son choix s\'impose pour votre mission.\n\nQue Son règne soit éternel,\n-Magistrat Sevren' WHERE `entry` = 244;
UPDATE `locales_page_text` SET `Text_loc2` = 'Porté disparu : caporal Keeshan\n\nPorté disparu depuis la sanglante bataille du donjon de Guet-de-pierre, le caporal Keeshan semble avoir été emporté par les orcs Rochenoire.\n\nOn pense que le caporal Keeshan est prisonnier au camp Rochenoire situé au nord de Comté-du-lac, à l\'ouest du col menant aux Steppes ardentes.\n\nPar mandat du Magistrat Salomon, quiconque aidera à retrouver le caporal Keeshan sera récompensé par le maréchal Marris.' WHERE `entry` = 245;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai passé les quelques semaines suivantes à errer de lieu en lieu, toujours saisi par la peur des Cavaliers noirs. Je me trouve actuellement dans la ville abandonnée de Colline-aux-Corbeaux, caché, comme toujours. Je ne suis pas de taille à affronter la force qu\'ils ont déployée contre la femme de Sven et je sais qu\'ils fouillent toujours le bois de la Pénombre à la recherche de la Faux. Je ne l\'ai plus, et j\'en remercie la Lumière, car si je l\'avais gardée, ils m\'auraient trouvé. Même aujourd\'hui, je sais au fond de moi qu\'ils finiront par me trouver.\n\nJe suis si fatigué.' WHERE `entry` = 246;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai passé les quelques semaines suivantes à errer de lieu en lieu, toujours saisi par la peur des Cavaliers noirs. Je me trouve actuellement dans la ville abandonnée de Colline-aux-Corbeaux, caché, comme toujours. Je ne suis pas de taille à affronter la force qu\'ils ont déployée contre la femme de Sven et je sais qu\'ils fouillent toujours le bois de la Pénombre à la recherche de la Faux. Je ne l\'ai plus, et j\'en remercie la Lumière, car si je l\'avais gardée, ils m\'auraient trouvé. Même aujourd\'hui, je sais au fond de moi qu\'ils finiront par me trouver.\n\nJe suis si fatigué.' WHERE `entry` = 247;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salutations, maître,\n\npardonnez-moi cette manière de vous envoyer un message. Mes mages de l\'ombre d\'Alterac sont plongés dans leur travail, ce qui me contraint à utiliser un messager étranger, choisi parmi nos nouveaux "alliés", pour vous apporter ce rapport. Mais j\'ai codé cette lettre avec l\'un de nos chiffres les plus ésotériques. Ces rustres n\'y comprendront rien.' WHERE `entry` = 248;
UPDATE `locales_page_text` SET `Text_loc2` = 'Valik,\nGardez l\'esclave jusqu\'à notre retour. Il est risqué de le garder ici plus longtemps. Emmenons-le plus au nord afin d\'être sûr que personne ne le verra. \n\nCe sont des animaux, et des brutes qui plus est. Les connaissant, nous avons des raisons de nous inquiéter. Mieux vaut éviter toute espèce de provocation. Nous ne voulons pas leur donner une raison de se regrouper et de nous attaquer.' WHERE `entry` = 249;
UPDATE `locales_page_text` SET `Text_loc2` = 'Un représentant du quartier général des Défias va contacter le Collecteur, après quoi il transférera l\'or collecté.\n\nAssurez-vous que cela se fait avec efficacité et discrétion. Le Collecteur est responsable du transfert de l\'or, mais au bout du compte il appartient à chaque membre de la Confrérie de s\'assurer qu\'il remplit son rôle avec attention et discipline.' WHERE `entry` = 250;
UPDATE `locales_page_text` SET `Text_loc2` = 'Souvenez-vous, mes frères, nous avons été naguère de fiers artisans. Nous accomplirons nos tâches actuelles avec la même précision que dans nos anciennes activités.\n\n-EVC' WHERE `entry` = 251;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai peur que mon père n\'ait commis une grave erreur quand il a décidé de rester pour défendre notre foyer. Les forces du Fléau ravagent les clairières de Tirisfal, et bien que les fortifications et les stocks soient sûrs, la traîtrise de mon frère Devlin nous prive de la force et des troupes nécessaires pour repousser leurs assauts constants.\n\nSi vous recevez cette lettre, Yvette, sachez bien que Devlin est un traître.' WHERE `entry` = 252;
UPDATE `locales_page_text` SET `Text_loc2` = 'Devlin a connu le désespoir et a fui notre infortune, et par là même il a fait un pacte avec le Fléau. Je ne sais pas quelle récompense il a reçue pour prix de sa lâcheté, mais je suis sûr que tout n\'était que mensonges.\n\nSi vous voyez mon frère, fuyez-le. C\'est une vile créature. Il y a de cela quelques nuits, il a assassiné deux de nos veilleurs et laissé quelques membres du Fléau franchir la brèche, là où nombre d\'entre nous dormaient.' WHERE `entry` = 253;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'attaque a été brutale. Ils ont tué le tiers d\'entre nous avant que nous ne puissions les abattre. Il nous reste à peine assez d\'hommes pour surveiller nos frontières. Ce n\'est qu\'une question de temps avant que notre surveillance ne se relâche et que le Fléau n\'attaque.\n\nEt bien que j\'aie repéré le visage de Devlin parmi les désespérés du Fléau cette nuit-là, il s\'est éclipsé avant que je ne puisse l\'atteindre. Il est peut-être mort, ou peut-être est-il un agent bien vivant du Fléau.\n\nDans les deux cas, il est un monstre.' WHERE `entry` = 254;
UPDATE `locales_page_text` SET `Text_loc2` = 'Devlin nous a avertis que si nous tentions de nous opposer au Fléau, nous étions perdus. Il nous l\'a dit quand ils sont entrés pour la première fois dans les clairières de Tirisfal, avant l\'encerclement de notre foyer. Il nous implorait peut-être de partir, ou peut-être se vantait-il.\n\nIl est difficile de comprendre les motivations de Devlin. Depuis son enfance, il est un mystère pour nous tous.' WHERE `entry` = 255;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais il avait raison. Nous sommes perdus. La lignée de la famille Agamand va s\'éteindre. Je suis toujours en vie, mais je sais que je n\'en ai plus pour longtemps.\n\nJe n\'ai pas peur et je ne regrette pas d\'être resté avec ma famille. Mais mes dernières pensées vont vers vous.\n\nVivez, ma bien-aimée Yvette, et je trouverai la paix,\n-Thurman' WHERE `entry` = 256;
UPDATE `locales_page_text` SET `Text_loc2` = 'Notre plan progresse bien. Comme je l\'ai mentionné, nous avons fait alliance avec un groupe d\'humains qui se fait appeler le Syndicat. Ses chefs sont les anciens nobles d\'Alterac et ils rêvent de retrouver leur position.\n\nIls ont donc fait un marché avec nous. Ils le regretteront.' WHERE `entry` = 257;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aujourd\'hui, le Syndicat n\'a guère de pouvoir sur ces terres, mais ils connaissent bien la région et ont assez d\'hommes pour les tâches à venir. Avec une aide appropriée, je pense que le Syndicat régnera de nouveau sur Alterac.\n\nDe plus, nous avons poussé le Syndicat à se diriger vers la cité de Stromgarde dans les hautes-terres d\'Arathi. La ville est un champ de bataille entre nos forces, les ogres et les humains de Stromgarde, mais nous pensons qu\'avec une tactique habile nous posséderons cette grande cité.' WHERE `entry` = 258;
UPDATE `locales_page_text` SET `Text_loc2` = 'Quand cela sera fait, nous passerons à l\'étape suivante de notre plan. Avec notre propre nation et une base puissante dans la région, nous préparerons la route pour le Troisième Ost.\n\nBientôt, les seigneurs de la Légion récompenseront notre travail en faisant pleuvoir le feu une fois de plus !' WHERE `entry` = 259;
UPDATE `locales_page_text` SET `Text_loc2` = 'Quand cela sera fait, nous passerons à l\'étape suivante de notre plan. Avec notre propre nation et une base puissante dans la région, nous préparerons la route pour le Troisième Ost.\n\nBientôt, les seigneurs de la Légion récompenseront notre travail en faisant pleuvoir le feu une fois de plus !' WHERE `entry` = 260;
UPDATE `locales_page_text` SET `Text_loc2` = 'Registre municipal de Hautebrande\n\nNous, les citoyens de Hautebrande, jurons solennellement foi et dévotion à l\'Alliance maintenue par les grands monarques, le roi Magni Barbe-de-bronze de Forgefer et le roi Anduin Wrynn de Hurlevent.\n\nIci repose le registre municipal pour le gouvernement de cette grande cité dans les contreforts des montagnes d\'Alterac et pour le recensement de ceux qui ont acquitté leurs impôts à leurs rois et à la puissante Alliance.' WHERE `entry` = 261;
UPDATE `locales_page_text` SET `Text_loc2` = 'Magistrat Rutherford Burnside\nDettes réglées.\n\nForgeron Avery Verringtan\nDettes réglées.\n\nGreffier Horrace Blancheval\nDettes réglées.\n\nConseiller Gillis\nDettes réglées.\n\nConseiller Hooks\nDettes réglées.\n\nFermier Getz\nDettes réglées.\n\nFermier Ray\nDettes en instance. Paiement en nature promis au moment de la moisson.\n\nFermier Lyion\nDettes en instance. Chassé des terres.' WHERE `entry` = 262;
UPDATE `locales_page_text` SET `Text_loc2` = 'Fermière Kalaba\nDettes en instance. Paiement en nature promis au moment de la moisson.\n\nCitoyen Wilkes\nDettes réglées.\n\nCitoyen Bonoan\nDettes réglées.\n\nMineur Wellty\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.\n\nMineur Sidney\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.\n\nMineur Hackett\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.' WHERE `entry` = 263;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mineur Orwell\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.\n\nMineur Fitzgerald\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.\n\nCitoyen Netherand\nDettes réglées.\n\nCitoyen May\nDettes réglées.\n\nContremaître Bonds\nDettes en instance. Paiement dû lors de la prochaine livraison de minerai en provenance de Veine-azur.' WHERE `entry` = 265;
UPDATE `locales_page_text` SET `Text_loc2` = 'Kegan Darkmar, chef d\'un petit groupe de morts-vivants qui est venu à nous à la recherche d\'un asile contre leurs "frères", a ébranlé nos certitudes concernant son espèce. Peut-être que sa peau pourrit et que son sang ne coule plus dans ses veines, mais il agit noblement et semble se préoccuper davantage de ses camarades que de lui-même.\n\nIl y a vraiment en lui une humanité qui fait défaut, je dois le confesser, à certains humains qui m\'entourent.' WHERE `entry` = 266;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pourquoi est-ce que j\'explique tout cela ? Pour donner du poids aux mots que je vais écrire, car ils viennent directement des lèvres de Kegan et j\'ai bon espoir que mes collègues, en lisant mon journal, sauront pourquoi je crois ce qu\'il a dit :\n\n"Ce qu\'il reste des Dieux très anciens demeure toujours dans les profondeurs du monde. De nouvelles forces cherchent à s\'assujettir cette puissance, et ceux qui y parviendront disposeront d\'une arme terrible contre leurs ennemis."' WHERE `entry` = 267;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'est ce que Kegan a dit en me tendant son pendentif de pierre de sang, et il y avait de la peur et comme une sorte de respect dans ses yeux. Et lorsque ses mains ont touché les miennes, elles ont frémi, comme s\'il était réticent à m\'abandonner le pendentif. Une vague de répulsion m\'a envahie, mais j\'ignore toujours aujourd\'hui si c\'est à cause de sa chair morte contre la mienne ou à cause du pendentif lui-même.\n\nCar j\'ai senti un pouvoir en lui. Un pouvoir enfoui, caché, affamé. Avide de s\'échapper.' WHERE `entry` = 268;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien que mes collègues de Dalaran aient hésité à étudier les pierres de sang, préférant mettre Kegan et ses compagnons en quarantaine avec tout ce qu\'ils portaient sur eux, la sincérité de Kegan m\'a contrainte à entamer l\'étude de son pendentif.' WHERE `entry` = 269;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je souhaitais vérifier si ce type de pierre possédait des propriétés magiques et faire comprendre à mes collègues que si nous, les sorciers de Dalaran, ne souhaitions pas exploiter la puissance des pierres de sang, nous devions au moins en apprendre les propriétés, car nos ennemis pourraient un jour les utiliser contre nous.\n\nMes études ont donc commencé.' WHERE `entry` = 270;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai commencé mes tests dans l\'idée que la pierre de sang était un morceau de roc, comme le quartz ou l\'obsidienne. J\'ai donc lancé une série de procédures pour déterminer : quels minéraux étaient contenus dans la pierre de sang, quelles forces étaient appliquées pour produire la couleur et la résistance, et les autres caractéristiques propres aux rochers et aux minerais. Mais, à ma plus grande frustration, le pendentif n\'a pas réagi aux procédures comme du minerai normal.' WHERE `entry` = 271;
UPDATE `locales_page_text` SET `Text_loc2` = 'En fait, il a souvent réagi de façon contraire à ce que j\'espérais. C\'était comme si le pendentif essayait délibérément de tromper mes expériences.\n\nComme s\'il pensait, comme s\'il vivait.\n\nEn colère mais pas découragée, je suis partie ensuite de l\'idée que la pierre de sang n\'était pas un morceau inerte de rocher, mais une chose vivante.  \n\nJ\'ai échoué de nouveau.' WHERE `entry` = 272;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aucun de mes nouveaux tests ne m\'a apporté de révélation sur les origines de la pierre de sang. La seule énigme que j\'ai résolue à l\'époque, c\'était que la pierre de sang n\'était ni vivante, ni morte !\n\nMais c\'est alors, au bord de l\'échec, que j\'ai fait une percée. Mon dernier test impliquait un vase de verre dont le bord était un peu ébréché. Après avoir terminé, sans que le test ne m\'apprenne rien, une fois de plus, j\'ai commencé à nettoyer mon matériel et je me suis coupée sur le vase.' WHERE `entry` = 273;
UPDATE `locales_page_text` SET `Text_loc2` = 'La coupure n\'était pas profonde mais saignait tout de même abondamment. Avant que je ne puisse bander mon doigt, une bonne partie de mon sang avait jailli sur le plan de travail.\n\nTandis que je nettoyais, j\'ai constaté quelque chose d\'étrange…' WHERE `entry` = 274;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le sang qui avait coulé près du pendentif de pierre de sang avançait doucement vers le bijou, comme si la gravité s\'inclinait vers la pierre de sang. Le sang qui touchait le pendentif semblait disparaître et la couleur rouge de la pierre devenait plus profonde au fur et à mesure que le sang était bu.' WHERE `entry` = 275;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après avoir vu cela, j\'ai senti ma tête s\'alléger, soit à cause de ma récente blessure (mais je ne pensais pas avoir perdu tant de sang), soit parce qu\'après toutes ces frustrations, j\'avais découvert une propriété de la pierre de sang. Je me suis assise et me suis mise à réfléchir. Pensées et questions s\'agitaient dans ma tête, me donnant le vertige et menaçant de me faire tomber à terre.  \n\nEst-ce que les pierres de sang boivent du sang ? Ont-elles soif de sang ? Sont-elles attirées par le sang ?' WHERE `entry` = 276;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ou sont-elles faites de sang ? Mais alors, le sang de qui ? Le mien ? Celui d\'un humain ? D\'un animal ?  \n\nOu peut-être la pierre de sang est-elle le sang de quelque chose d\'inconnu, la chose que Kegan avait à la fois l\'air de craindre et de respecter en me remettant son pendentif ?\n\nC\'est la question à laquelle il faut répondre. Voilà la clé.' WHERE `entry` = 277;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le feu s\'est rallumé en moi et j\'ai repris mes expériences. Cette fois, je ne suis partie d\'aucun a priori, j\'ai réalisé méthodiquement tous les tests à ma disposition. Cela a considérablement augmenté les efforts nécessaires, mais a accru les chances de faire une découverte.  \n\nEt bien que mon labo soit exigu et que je n\'aie pas d\'assistant, j\'ai découvert une autre propriété étrange de la pierre de sang.' WHERE `entry` = 278;
UPDATE `locales_page_text` SET `Text_loc2` = 'En plus du sang, des forces élémentaires sont mêlées dans la pierre. Le feu, l\'eau, la foudre et la pierre sont mélangés au sang (mais une fois encore, au sang de quoi ?) et bien que le mélange soit inerte extérieurement, toutes ces forces semblent s\'affronter à l\'intérieur. Cette extraordinaire substance soulevait tant d\'autres questions !' WHERE `entry` = 279;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais pour répondre à ces questions, d\'autres études, d\'autres expériences sont nécessaires. J\'ai peur que le camp d\'internement de Lordamere ne puisse fournir la main-d\'œuvre ni le matériel requis pour cette tâche. Alors j\'ai envoyé le pendentif de pierre de sang à Dalaran avec des instructions détaillées sur la manière de le tester, pour leur éviter mes frustrations initiales.' WHERE `entry` = 280;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tandis que j\'attendais les résultats des tests, j\'ai passé du temps à parler à Kegan. Je n\'ai cessé de l\'interroger sur ce qu\'il savait des pierres de sang, mais il ne m\'a rien dit de plus que le jour où il m\'a donné son pendentif. Et il n\'a guère parlé du temps qu\'il a passé au sein des "Réprouvés" (c\'est le nom qu\'il donne à son clan de morts-vivants).' WHERE `entry` = 281;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais Kegan avait grande envie de parler d\'autres sujets, notamment son enfance à Lordaeron avant sa chute.  \n\nIl a encore beaucoup d\'affection pour ce royaume perdu, même s\'il est aujourd\'hui en ruine, disparu.\n\nMa sympathie croissante pour Kegan m\'a fait patienter le temps que les résultats des tests me reviennent.' WHERE `entry` = 282;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais après des semaines sans réponse, ma patience a commencé à faiblir, et après avoir pris mes renseignements à Dalaran, j’ai appris que la pierre de sang n\'avait jamais atteint mes collègues. Mon messager a disparu en chemin et le pendentif avec lui !\n\nCe sont de bien graves nouvelles, car bien que Kegan et ses compagnons aient d\'autres spécimens de pierres de sang, j\'ai peur que le pendentif perdu ne tombe entre de mauvaises mains.' WHERE `entry` = 283;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai envoyé un autre messager à Dalaran, et j\'ai appris qu\'ils cherchent toujours le pendentif, dans les ruines qui entourent notre sphère protégée.\n\nJ\'espère qu\'il n\'est pas trop tard.' WHERE `entry` = 284;
UPDATE `locales_page_text` SET `Text_loc2` = 'Conte de Gri\'lek le vagabond\n\n[...Le début est effacé et illisible. Mais la fin est lisible...]\n\nGri\'lek a marché à travers la jungle. Et ses yeux crépitaient et sa poitrine était incendiée, car il avait de la colère en lui.  \n\nDans sa rage, il hurla vers les cieux et leva son bras. Il leva son bras gauche, devenu fort et sûr au cours des chasses sans son frère.  \n\nCar le bras droit de Gri\'lek n\'était plus, et ne devait plus revenir.' WHERE `entry` = 285;
UPDATE `locales_page_text` SET `Text_loc2` = 'LÉGENDES DES TROLLS, VOLUME III\nLa Pierre des marées\n\nDe la main de\nl\'archimage Ansirem Tisserune\nDalaran\n\nINTRODUCTION\n\nL\'ancien empire Gurubashi fut une source de bien des légendes étonnantes et fascinantes qui nous renseignent sur l\'environnement de ses sujets, car l\'examen de leurs croyances et de leurs pratiques sociales montre leur grand respect pour leur environnement naturel.\n\nJ\'ai détaillé les' WHERE `entry` = 286;
UPDATE `locales_page_text` SET `Text_loc2` = 'Kurdran Marteau-hardi\n\nChasseur de dragons réputé. Maître des griffons du Nid-de-l\'Aigle. Commandant des chevaucheurs de griffons attachés à l\'expédition de l\'Alliance qui a marché sur le monde des orcs, Draenor. Présumé mort.\n\nNous entendrons l\'appel de Ciel\'ree dans le vent. Nous entendrons le tonnerre de ton marteau résonner dans les montagnes. Chevauche hardiment dans l\'après-vie, frère ! Les salles de nos ancêtres t\'attendent.\n\n- Haut-thane Falstad Marteau-hardi' WHERE `entry` = 287;
UPDATE `locales_page_text` SET `Text_loc2` = 'Capitaine des forestiers Alleria Coursevent\n\nCélèbre chasseresse de trolls de Quel\'Thalas. Chef des éclaireurs et agent de renseignement attaché à l\'expédition de l\'Alliance qui a marché sur le monde des orcs, Draenor. Présumée morte.\n\nTon cœur était aussi droit qu\'une flèche fendant le vent, sœur. Tu fus la meilleure de notre ordre. Tu fus la meilleure des nôtres. \n\n- Sylvanas Coursevent - Général des forestiers de Quel\'Thalas' WHERE `entry` = 288;
UPDATE `locales_page_text` SET `Text_loc2` = 'Archimage Khadgar du Kirin Tor\n\nAncien apprenti de Medivh. Commandant suprême de l\'expédition de l\'Alliance qui a marché sur le monde des orcs, Draenor. Présumé mort.\n\nNul ne s\'est avec plus d\'altruisme plongé dans l\'étude de la magie et du combat. Nous sommes avec toi, audacieux guerrier, où que tu sois. \n\n- Antonidas - Archimage de Dalaran' WHERE `entry` = 289;
UPDATE `locales_page_text` SET `Text_loc2` = 'Général Turalyon\n\nAncien lieutenant du seigneur Anduin Lothar. Chevalier de la Main d\'argent. Général en chef de l\'Alliance attaché à l\'expédition de l\'Alliance qui a marché sur le monde des orcs de Draenor. Présumé mort.\n\nEsarus thar no\'Darador\' - Par le sang et l\'honneur nous servons. \nTu étais la main de la justice et de la vertu, vieil ami. Ton nom sera honoré à jamais.\n\n- Seigneur Uther le Porteur de Lumière - Chevalier de la Main d\'argent' WHERE `entry` = 290;
UPDATE `locales_page_text` SET `Text_loc2` = 'Danath Trollemort\n\nCommandant de la milice de Stromgarde. Conseiller stratégique du général Turalyon. Commandant des forces attachées à l\'expédition de l\'Alliance qui a marché sur le monde des orcs de Draenor. Présumé mort.\n\nNous honorons ta mémoire, ô mon neveu, et ton sacrifice. Depuis la fondation de notre glorieux empire, le chemin de la grandeur a toujours été ouvert par le sang des héros.\n\n- Thoras Trollemort, seigneur de Stromgarde' WHERE `entry` = 291;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ici repose Uther le Porteur de Lumière\nPremier des paladins - Fondateur de l\'Ordre de la Main d\'argent\n\nUther a vécu et est mort pour défendre le royaume de Lordaeron. Bien qu\'il ait été trahi par son apprenti préféré, nous pensons que son esprit demeure. Il continue à veiller sur nous, même aujourd\'hui que les ombres se referment sur nos terres. Sa lumière est celle de toute l\'humanité, et tant que nous honorerons son exemple, il ne périra jamais. \n									- Anonyme' WHERE `entry` = 292;
UPDATE `locales_page_text` SET `Text_loc2` = 'aspects de leur culte des serpents dans les précédents volumes de cette étude, mais je vais aborder ici l\'examen des rapports uniques et passionnants des trolls avec la mer.\n\n\nLA GRANDE MER\n\nL\'empire Gurubashi était entouré d\'océans sur trois frontières. Rien d\'étonnant à ce que l\'eau ait constitué un aspect important de leur société. Les trolls ont été capables de soumettre de larges pans de leur monde forestier, mais la mer leur a toujours posé problème. Elle était vaste, incommensurable, représentait' WHERE `entry` = 293;
UPDATE `locales_page_text` SET `Text_loc2` = 'sans doute un voisin déconcertant pour les puissants trolls.\n\nDe récentes découvertes suggèrent que les trolls ne se sentaient pas le désir d\'explorer les terres situées au-delà de la Grande Mer. Bien que des espèces de trolls soient disséminées partout dans tout Azeroth, Khaz Modan et Lordaeron, on n\'a guère trouvé de traces de leur civilisation dans les terres nouvellement découvertes de Kalimdor ni dans les îles des Mers du Sud. Cela démontre-t-il le désir des trolls' WHERE `entry` = 294;
UPDATE `locales_page_text` SET `Text_loc2` = 'de ne pas s\'éloigner de leur territoire terrestre ou une incapacité à développer la technologie nécessaire à entreprendre un tel voyage ? Cela demande d\'autres recherches, qui dépassent le cadre de mon étude.\n\nMais on peut difficilement ignorer une présence si large, et de nouvelles trouvailles dans les ruines de la vallée de Strangleronce montrent un aspect de leur relation avec la mer que nous ignorions jusque là.\n\n\nLA PIERRE DES MARÉES\n\nDe récentes découvertes pendant les fouilles dans' WHERE `entry` = 295;
UPDATE `locales_page_text` SET `Text_loc2` = 'les ruines trolles de Strangleronce ont montré des références à un objet nommé la "Pierre des marées". De nombreux fragments des légendes des trolls peuvent être assemblés pour obtenir une image assez précise de la pierre et de son importance dans l\'ancien empire Gurubashi.\n\nIl semble que la Pierre des marées ait permis à celui qui la portait de contrôler l\'eau dans ses nombreuses formes (rivières, pluie, marées). En raison des histoires narrant le fonctionnement de la Pierre des marées, je pense qu\'il s\'agit' WHERE `entry` = 296;
UPDATE `locales_page_text` SET `Text_loc2` = 'en fait d\'une manifestation physique des puissances du seigneur des Eaux, un puissant élémentaire des mers. De quelle manière cet objet permettait-il le contrôle du seigneur des Eaux et comment est-il tombé entre les mains des trolls, c\'est une autre question qui dépasse mes connaissances.\n\n\nLE PORTEUR DES MARÉES\n\nComme l\'eau sur les rivages, la Pierre des marées entrait dans le monde des trolls et en repartait, ne restant jamais entre les mains des trolls pour plus d\'une génération à la suite.' WHERE `entry` = 297;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans les légendes des trolls, on dit que la première fois que la Pierre des marées est entrée dans l\'empire Gurubashi, elle a été trouvée par un guerrier troll errant le long de la côte de Strangleronce. Il est tombé sur une mystérieuse pierre bleue dans laquelle flottaient des fibres d\'un blanc laiteux. Intrigué par la pierre, le guerrier la prit avec lui et continua sa route.\n\nAu cours des semaines et des mois qui suivirent, le guerrier découvrit que la pierre lui conférait le contrôle de l\'eau. Il pouvait invoquer des' WHERE `entry` = 298;
UPDATE `locales_page_text` SET `Text_loc2` = 'élémentaires d\'eau, des créatures formées entièrement d\'eau, égalant par là même les plus puissants mages du Kirin Tor.\n\nLe guerrier alla jusqu\'à Zul\'Gurub, au cœur de l\'Empire, pour faire la démonstration de ses capacités nouvelles à l\'Empereur. Il obtint facilement une audience après avoir démontré l\'étendue de ses pouvoirs au centre de la capitale impériale. Ses pouvoirs éblouirent l\'empereur Gurubashi, qui lui donna immédiatement une place d\'honneur à la cour, surnommant le guerrier "Porteur des marées", de sorte que son vrai nom' WHERE `entry` = 299;
UPDATE `locales_page_text` SET `Text_loc2` = 'ne nous est plus connu.\n\nPendant des années, le Porteur des marées servit l\'empire Gurubashi, invoquant ses élémentaires d\'eau au combat et manipulant les flux aquatiques de la vallée de Strangleronce au profit de l\'Empire. Mais alors que les années passaient, le Porteur des marées se fit plus solitaire, restant de longues périodes de temps loin de la cour.\n\nLe Porteur des marées cachait un secret aux yeux inquisiteurs de la Cour. Les capacités qui lui étaient conférées par la Pierre des marées étaient accompagnées d\'une malédiction. Au fur et à mesure du passage du temps,' WHERE `entry` = 300;
UPDATE `locales_page_text` SET `Text_loc2` = 'le Porteur des marées s\'effaçait. A chaque reflux de la marée, il disparaissait un peu plus, perdant sa forme corporelle progressivement. Sentant venir son agonie, il partit vers la plage où il avait trouvé la Pierre et disparut dans la mer.\n\nDes générations plus tard, la Pierre des marées réapparut sur les rivages de Strangleronce, et un autre Porteur des marées fut choisi tandis que les trolls rapportaient la Pierre à' WHERE `entry` = 301;
UPDATE `locales_page_text` SET `Text_loc2` = 'Zul\'Gurub. Cela continua ainsi pendant des générations, la Pierre venant avec la marée et le Porteur disparaissant avec elle.\n\n\nDes histoires contemporaines sur la Pierre des marées se font jour de temps en temps, mais on se demande toujours pourquoi un objet d\'une telle puissance apparaîtrait si régulièrement, et sur la volonté de qui.' WHERE `entry` = 302;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ordres de la Voile sanglante\n\nPrêtez une grande attention à ces mots, camarades, car on n\'a pas le droit à l\'erreur à Baie-du-Butin.\n\nJ\'ai envoyé le Vieux bourré reconnaître le nord et je lui ai dit d\'attendre notre signal.\n\nL\'Heureuse Demoiselle va jeter l\'ancre au cap sud pour gérer l\'intendance. Le capitaine Eau-plate, le petit nabot, doit préparer son équipage à embarquer le butin. Je suis sûr que le vieux Brutus sera à la hauteur.' WHERE `entry` = 303;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ordres de la Voile sanglante\n\nPrêtez une grande attention à ces mots, camarades, car on n\'a pas le droit à l\'erreur à Baie-du-Butin.\n\nJ\'ai envoyé le Vieux bourré reconnaître le nord et je lui ai dit d\'attendre notre signal.\n\nL\'Heureuse Demoiselle va jeter l\'ancre au cap sud pour gérer l\'intendance. Le capitaine Eau-plate, le petit nabot, doit préparer son équipage à embarquer le butin. Je suis sûr que le vieux Brutus sera à la hauteur.' WHERE `entry` = 305;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je veux que le capitaine Soulaquille amène le Courant aux rivages du sud-est. Nous enverrons son équipage à pied au signal.  \n\nSabot-de-sel peut emmener les hommes. Je veux qu\'on ait à l\'œil Revilgaz et Corne-de-mer. Vous pouvez les envoyer par cent brasses de fond si ça vous amuse.\n\nVous avez pour mission de récupérer tout ce qui peut être de quelque utilité pour notre projet avec VanCleef dans les Mortemines.' WHERE `entry` = 306;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je donnerai l\'ordre de tir à Oeil-de-fer. Le barrage d\'artillerie empêchera tout navire de Flots noirs d\'entrer dans le port ou d\'en sortir.\n\nQuand le « Voile cramoisi » aura déchargé ses canons, je le mènerai près du « Courant » en soutien et je lui donnerai les réserves nécessaires.\n\nUne fois que Sabot-de-sel et ses gamins auront incendié la ville et que tout le monde sera à son poste, nous ferons voile et offrirons une dernière bordée à la côte pour faire bonne mesure.\n\n—Amiral Firallon' WHERE `entry` = 307;
UPDATE `locales_page_text` SET `Text_loc2` = 'Fuir les Zanzil dans ce bateau pourrissant semblait sage sur le moment, mais je commence à me dire que la mort aurait été un destin plus favorable.\n\nJe pensais que mes ancêtres m\'avaient fait une faveur en me permettant d\'échapper à leur colère. Mais je commence à me demander quelle sagesse je vais acquérir si je survis à mon emprisonnement.' WHERE `entry` = 308;
UPDATE `locales_page_text` SET `Text_loc2` = 'La bête qui règne sur cette île est vraiment puissante. Crins-de-ciel ou jagueros, les autres créatures tremblent devant elle. Même aujourd\'hui, j\'entends ses rugissements et ils me font frémir.\n\nCes créatures arrêteront à coup sûr tous ceux qui s\'approcheront de l\'île. Je dois avoir foi en mes ancêtres.' WHERE `entry` = 309;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma tribu et mon foyer me manquent. J\'accueillerais avec joie les plaines ouvertes ou le crépuscule sur Thunder Bluff au milieu de la chaleur de cette jungle.\n\nJ\'aperçois l\'océan non loin, mais il ne rafraîchit ni mon corps ni mon humeur. Il ne fait que me rappeler mon emprisonnement.' WHERE `entry` = 310;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 1 :\n6H - 11H - camp supérieur\n11H - 11H30 - Pause, déjeuner\n11H30 - 16H00 - passe du canyon\n16H00 - 19H30 - camp supérieur\n\nJour 2 :\nIdem jour 1\n\nJour 3 :\nIdem\n\nJour 4 :\nIdem, à une exception. Juste avant le crépuscule, Thorsen m\'a demandé de le couvrir pendant qu\'il s\'enfonçait dans la passe du canyon. Je suppose qu\'il est allé rencontrer un de nos agents.\n\nMais il est revenu une heure plus tard. Cela m\'a surpris. J\'espérais qu\'il avait rencontré un de nos agents et rejoint Kurzen. Quelque chose ne va pas.' WHERE `entry` = 311;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 5 :\nThorsen était très agité aujourd\'hui. Quoi qu\'il ait vu ou qui qu\'il ait rencontré durant son excursion d\'hier, il a eu peur.\n\nJe suppose que nos agents lui ont parlé, mais lorsque j\'ai demandé à Thorsen où il était allé pendant que je le couvrais, il est resté silencieux. Il me pense son ami, et croit me protéger contre un dangereux secret.\n\nIl place mal sa loyauté, mais il est loyal. Quand il nous rejoindra, il sera un apport inestimable aux forces de Kurzen.' WHERE `entry` = 312;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 6 :\nTôt ce matin, Thorsen a longuement parlé avec le sergent Yohwa. J\'ai dû faire semblant de dormir et je n\'ai pu m\'approcher pour les écouter. Mais je pense qu\'ils parlaient de l\'excursion récente de Thorsen.\n\nJe l\'ai attentivement observé au déjeuner et j\'ai vu que Yohwa en faisait autant. Plus tard, Doren a convoqué Thorsen. Tous trois partagent un secret. Doren et Yohwa doivent savoir que Thorsen rencontre vos agents.\n\nIl espionne les rebelles.' WHERE `entry` = 313;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous ne devons plus donner de renseignements à Thorsen et nous devons le tuer. Il n\'est plus d\'aucune utilité pour notre colonel.\n\nJe m\'occuperai de Thorsen si j\'en ai la possibilité, mais si vous recevez cette lettre, vous saurez que je n\'en ai pas encore eu l\'occasion.  \n\nDans ce cas, je suggère que le prochain contact de Thorsen avec nos agents soit sa dernière heure.\n\nGloire à Kurzen,\n-M' WHERE `entry` = 314;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Conseil a été sage de vous nommer à la tête de la Veille d\'Argus, surveillant de leurs stratégies dans la terre des humains et des nains. Et je suis honoré d\'exécuter vos volontés dans Alterac.\n\nAu nom de Sargeras,\n\nNagaz, \nHiérophante d\'Argus' WHERE `entry` = 315;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pauvres idiots !\n\nMon plan nécessitait que les deux ponts soient détruits simultanément. Et qu\'avez-vous fait, avec vos cervelles spongieuses ? Vous n\'avez fait que la moitié du travail et vous avez alerté toute l\'Alliance de nos intentions. Mais quels idiots !\n\nIl vous reste une chance de vous racheter. Nous avons une cache d\'explosifs dans les hautes-terres d\'Arathi, non loin du pont.  \n\nFinissez le travail si vous tenez à vos pitoyables existences !\n\n-Kam Furie-du-fond' WHERE `entry` = 316;
UPDATE `locales_page_text` SET `Text_loc2` = 'S\'élevant en tour d\'eau au-dessus de l\'océan, Neptulon envoya les grands krakkens détruire I\'lalai. Si hautes étaient leurs formes que des jungles de varech palpitaient entre leurs côtes, et les léviathans bourdonnaient sur tout leur corps.  \n\nLe plus grand des Krakkens leva alors ses bras et frappa puissamment la mer, envoyant des raz-de-marée autour de lui. Puis tous se mirent en route vers I\'lalai.' WHERE `entry` = 324;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les Krakkens rugirent et leurs voix éclatèrent comme des typhons : \n\n"Nous venons."\n\nMin\'loth, imperturbable, invoqua sa magie. Les vagues envoyées vers I\'lalia se séparèrent et se perdirent dans les jungles côtières. Min\'loth ordonna alors à ses serviteurs de chanter des sorts de lien et des dizaines de voix de trolls s\'élevèrent.\n\nEt une voix se détacha des autres.' WHERE `entry` = 325;
UPDATE `locales_page_text` SET `Text_loc2` = 'Min\'loth vociféra et sa magie rassembla la force des sorts de ses serviteurs, pour la diriger vers les Krakkens qui approchaient.\n\nLes flots se séparèrent et le sort de Min\'loth jaillit vers les soldats de Neptulon. Des éclairs déchirèrent le ciel et le sort les atteignit, faisant bouillir l\'eau et creusant des cratères.\n\nMin\'loth chanta sa victoire, sachant que son sort allait abattre les grandes bêtes.' WHERE `entry` = 326;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais les Krakkens étaient vieux, très vieux. Ils se rappelaient le temps où la terre avait pour la première fois jailli de l\'eau.\n\nIls se rappelaient le règne des Anciens et leur chute face aux Voyageurs. Ils se souvenaient de la naissance de toute magie.\n\nIls étaient vieux et ils détenaient bien des secrets. Le sort de Min\'loth était puissant mais, tout comme les trolls, il était mortel.  \n\nC\'est ainsi qu\'il échoua.' WHERE `entry` = 327;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le sort ne put lier les Krakkens, mais il les rendit furieux. Aucun mortel ne leur avait causé de douleur depuis des siècles, et le sort des trolls était douloureux.\n\nIls se débarrassèrent des liens de Minloth puis s\'élancèrent pleins de rage.\n\nUn grondement s\'éleva tandis que de grandes vagues s\'élevaient des profondeurs et roulaient vers la terre. Quand ils atteignirent I\'lalai, ils couvrirent la ville de leur ombre.\n\nMais avant de la détruire, les Krakkens firent halte.' WHERE `entry` = 328;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les sorciers docteurs trolls tremblèrent et en appelèrent à leur maître. Min\'loth regarda les montagnes de la mer, méfiant et sombre. Il se tourna vers ses adeptes et murmura, et les trolls gravèrent ses derniers mots dans la pierre. Min\'loth fit alors face aux menaçants Krakkens.\n\nIl grimaça et lança violemment son bâton : son dernier acte de bravoure.\n\nLes Krakkens tournèrent alors leur rage contre Min\'loth, et un océan fondit sur I\'lalai.\n\nEt la ville disparut.' WHERE `entry` = 329;
UPDATE `locales_page_text` SET `Text_loc2` = 'Puis les eaux tombèrent sur la jungle, nettoyant tout sur leur passage. Trolls et bêtes hurlaient sous les cataractes qui les engloutissaient.\n\nNombre de Gurubashi se demandèrent pourquoi l\'océan les avalait, mais ils mouraient sans réponse.\n\nEnfin, lorsque les eaux atteignirent les montagnes, elles s\'arrêtèrent. Apaisées, elles revinrent au rivage, laissant derrière elles la mort et la désolation.\n\nElles s\'en allèrent, mais pas au point de rendre à la terre les ruines d\'I\'lalai, qui restèrent à jamais englouties.' WHERE `entry` = 330;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et le chef Var\'gazul, à l\'abri dans les montagnes de Zul\'Gurub, s\'enfonça dans la jungle et la trouva vidée de son peuple.\n\nIl fut pris de désespoir, car ses rêves de conquête étaient anéantis.\n\nJamais ne fut trouvé Min\'loth le Serpent.' WHERE `entry` = 331;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et le chef Var\'gazul, à l\'abri dans les montagnes de Zul\'Gurub, s\'enfonça dans la jungle et la trouva vidée de son peuple.\n\nIl fut pris de désespoir, car ses rêves de conquête étaient anéantis.\n\nJamais ne fut trouvé Min\'loth le Serpent.' WHERE `entry` = 332;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aux Schneider,\n\nSalutations. J\'espère que les affaires vont bien. Dans le quartier commerçant, c\'est l\'explosion.\n\nNous avons grand besoin de tricots, car nos stocks sont vides. Veuillez nous envoyer le chargement habituel dès que vous le pourrez.\n\nJe vous souhaite bonne fortune et j\'espère que les études de votre fils Thurman progressent bien,\n\n--Harlan Bagley' WHERE `entry` = 336;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ordres officiels pour la flotte de Kul Tiras\n\nMoi, amiral Portvaillant, je mènerai le premier débarquement sur les rivages de Kalimdor.\n\nLe lieutenant Benedict commandera en mon absence. Toutes les affaires militaires locales de Kul Tiras seront placées sous son autorité. Si je ne donne pas de nouvelles du donjon de Tiragarde dans les 12 mois, le lieutenant Benedict conduira la seconde vague destinée à s\'emparer des lieux.' WHERE `entry` = 337;
UPDATE `locales_page_text` SET `Text_loc2` = 'En attendant, j\'envoie le lieutenant Alverold en mission d\'exploration autour du continent de Kalimdor. Ses forces doivent accomplir leur mission et nous rejoindre au donjon de Tiragarde.\n\nIl est prévu que la mission du lieutenant Alverold dure quelque temps. Les nouvelles terres semblent vastes et dangereuses. Mais si le malheur me poursuit ou s\'abat sur Benedict, les troupes d\'Alverold reprendront le donjon.\n\nNous enverrons vague après vague jusqu\'à ce que la forteresse soit nôtre !\n\n--Amiral Portvaillant' WHERE `entry` = 338;
UPDATE `locales_page_text` SET `Text_loc2` = 'En attendant, j\'envoie le lieutenant Alverold en mission d\'exploration autour du continent de Kalimdor. Ses forces doivent accomplir leur mission et nous rejoindre au donjon de Tiragarde.\n\nIl est prévu que la mission du lieutenant Alverold dure quelque temps. Les nouvelles terres semblent vastes et dangereuses. Mais si le malheur me poursuit ou s\'abat sur Benedict, les troupes d\'Alverold reprendront le donjon.\n\nNous enverrons vague après vague jusqu\'à ce que la forteresse soit nôtre !\n\n--Amiral Portvaillant' WHERE `entry` = 339;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">Registre de conscription de la Horde</H1>\n<BR/>\n<P>Cette personne a montré de grands talents au service de la Horde. Il faut immédiatement l\'affecter à la Croisée.</P>\n<BR/><BR/>\n<P>Respectueusement :</P>\n<BR/><BR/><BR/>\n<P>Ça m\'a l\'air parfait.<BR/>\n- Takrin Cherchevoie</P>\n</BODY>\n</HTML>' WHERE `entry` = 340;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">Registre de conscription de la Horde</H1>\n<BR/>\n<P>Cette personne a montré de grands talents au service de la Horde. Il faut immédiatement l\'affecter à la Croisée.</P>\n<BR/><BR/>\n<P>Respectueusement :<BR/>\n- Kargal Balafre-glorieuse</P>\n<BR/><BR/>\n<P>Ça m\'a l\'air parfait.<BR/>\n- Takrin Cherchevoie</P>\n</BODY>\n</HTML>' WHERE `entry` = 341;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'Excavation de Bael Modan\n\nL\'équipe de surveillance envoyée par la Ligue des explorateurs avait raison. Le sol contient des objets d\'une valeur inestimable pour notre peuple. Il est certain que nous trouverons de nombreuses réponses en fouillant le sable et les pierres de Bael Modan.\n\nLa géologie de la région nécessite cependant d\'être très prudent. La plupart des dépôts de sable se sont solidifiés sous l\'effet de conditions climatiques difficiles et changeantes.' WHERE `entry` = 342;
UPDATE `locales_page_text` SET `Text_loc2` = 'La solution est cependant assez simple. En utilisant de la pulpe de bois en guise d\'absorbant, nous pouvons combiner les traces de nitroglycérine avec du nitrate de sodium afin de développer une puissante charge explosive, capable de transpercer même les masses les plus denses.\n\nLe travail sera bruyant et causera des perturbations mais notre recherche est de loin plus importante que le confort de la population locale. En fait, nous avons déjà dû déplacer une bande d\'hommes taureaux, qui se sont avérés être une nuisance.' WHERE `entry` = 343;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le fait que nous ayons déjà rencontré des problèmes avec quelques éléments imprévisibles me laisse à penser que nous aurons sans doute besoin de l\'aide de l\'armée du roi. Non seulement l\'excavation nécessite un soutien militaire, mais il me semble, autant que je puisse en juger, que l\'emplacement de Bael Modan représente une importance stratégique pour l\'Alliance, si on considère la situation politique instable.\n\nMais je m\'égare. Il y a un travail énorme à accomplir sous la roche. J\'ai passé trop de temps à écrire alors que j\'aurais dû l\'employer à creuser...' WHERE `entry` = 344;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le fait que nous ayons déjà rencontré des problèmes avec quelques éléments imprévisibles me laisse à penser que nous aurons sans doute besoin de l\'aide de l\'armée du roi. Non seulement l\'excavation nécessite un soutien militaire, mais il me semble, autant que je puisse en juger, que l\'emplacement de Bael Modan représente une importance stratégique pour l\'Alliance, si on considère la situation politique instable.\n\nMais je m\'égare. Il y a un travail énorme à accomplir sous la roche. J\'ai passé trop de temps à écrire alors que j\'aurais dû l\'employer à creuser...' WHERE `entry` = 349;
UPDATE `locales_page_text` SET `Text_loc2` = 'La console de contrôle du samophlange peut être utilisée pour réguler automatiquement les flux de carburant, de gaz, de liquides, et tout ce qui est lié aux machines. Une fois la configuration en place, tout devrait fonctionner avec un minimum de maintenance.\n\nListe sommaire des éléments de la console de contrôle (de la gauche vers la droite)\n\nI. Levier d\'alimentation principal\n\nPermet de faire fonctionner le samophlange. Le mécanisme de contrôle ne se désengage pas automatiquement quand le levier est en position off. Cela initie seulement' WHERE `entry` = 350;
UPDATE `locales_page_text` SET `Text_loc2` = 'La console de contrôle du samophlange peut être utilisée pour réguler automatiquement les flux de carburant, de gaz, de liquides, et tout ce qui est lié aux machines. Une fois la configuration en place, tout devrait fonctionner avec un minimum de maintenance.\n\nListe sommaire des éléments de la console de contrôle (de la gauche vers la droite)\n\nI. Levier d\'alimentation principal\n\nPermet de faire fonctionner le samophlange. Le mécanisme de contrôle ne se désengage pas automatiquement quand le levier est en position off. Cela initie seulement' WHERE `entry` = 351;
UPDATE `locales_page_text` SET `Text_loc2` = 'Superviseur Vibrouage, veuillez trouver ci-joint la liste des équipements que vous avez réquisitionnés, approuvée par le président Razdunk.\n\nIl m\'a demandé de vous rappeler les intérêts que la KapitalRisk a investis dans les opérations de Kalimdor. Nos postes de minage et de forage dans les Tarides se sont montrés efficaces et avantageux, mais nous attendons de meilleurs chiffres de Mulgore, votre juridiction.\n\nLe conseil d\'administration comprend la difficulté qu\'il y a à établir' WHERE `entry` = 352;
UPDATE `locales_page_text` SET `Text_loc2` = 'une opération de grande envergure si près des terres des taurens, mais ne pense pas qu\'ils posent un problème majeur à nos plans d\'exploitation dans la région.\n\nNous sommes heureux d\'apprendre que le minage a commencé dans les riches plaines de Mulgore. C\'est la raison pour laquelle le conseil a décidé d\'approuver votre demande d\'équipement. En plus du minage, nous pensons que Mulgore a également beaucoup à offrir en ce qui concerne le bois, vous recevrez en conséquence plusieurs de nos nouveaux modèles de découpeuses et des matériaux bruts pour construire une' WHERE `entry` = 353;
UPDATE `locales_page_text` SET `Text_loc2` = 'scierie.' WHERE `entry` = 354;
UPDATE `locales_page_text` SET `Text_loc2` = 'le processus de refroidissement. Il faut ensuite une à deux minutes (en fonction de la situation) pour que l\'appareil s\'éteigne.\n\nREMARQUE IMPORTANTE : le levier d\'alimentation principal ne peut être contrôlé que lorsque les trois valves de contrôle de la machine ont été désengagées. Ces valves peuvent être désactivées dans n\'importe quel ordre.\n\nLa première est la VALVE DE CONTRÔLE PRINCIPALE, qui se trouve à la base de la grande cheminée verticale. Ensuite, la VALVE DE CONTRÔLE DU CARBURANT se trouve sur le grand tuyau qui va des réservoirs' WHERE `entry` = 355;
UPDATE `locales_page_text` SET `Text_loc2` = 'de carburant (il devrait y en avoir trois) à la cheminée. Enfin, la VALVE DE RÉGLAGE se trouve sur le plus court des deux grands réservoirs cylindriques sur le bord de la cheminée.\n\nII. Jauges de carburant\n\nAffichent la pression actuelle dans les réservoirs de carburant principal, secondaire et tertiaire.\n\nIII. Jauge de flux\n\nAffiche l\'état actuel du flux dans le système.' WHERE `entry` = 356;
UPDATE `locales_page_text` SET `Text_loc2` = 'IV. Contrôles du flux\n\nPermettent d\'augmenter ou de baisser le taux du flux. Contrôlé automatiquement par le mécanisme de contrôle du samophlange.\n\nV. Jauge de température\n\nAffiche la température de fonctionnement de tout le système.' WHERE `entry` = 357;
UPDATE `locales_page_text` SET `Text_loc2` = 'Depuis longtemps les enfants des étoiles s\'étaient établis auprès des eaux miroitantes du Puits éternel. Chacun savait qu\'Elune, lumière du crépuscule éternel, déesse de la lune, se reposait en son sein de son labeur passé. Sur les rivages du Puits les enfants des étoiles, favoris d\'Elune, bâtirent leurs maisons, les yeux toujours perdus dans la nuit baignée de clarté lunaire.\n\nIl y avait bien des villes et bien des foyers construits' WHERE `entry` = 358;
UPDATE `locales_page_text` SET `Text_loc2` = 'La terre trembla alors que les arbres antiques de la forêt enchantée étaient déracinés. Les bosquets et les prairies des fils et des filles de Cénarius et les tours de pierre des enfants des étoiles furent bouleversés. Notre reine demeurait radieuse même dans le désespoir et dans le chaos des combats. Le ciel enchanté changeait de couleur avec les décharges de magie, avec les explosions qui menaçaient de déchirer le monde.\n\nLe frère combattait le frère. L\'Élu combattait' WHERE `entry` = 359;
UPDATE `locales_page_text` SET `Text_loc2` = 'La terre trembla alors que les arbres antiques de la forêt enchantée étaient déracinés. Les bosquets et les prairies des fils et des filles de Cénarius et les tours de pierre des enfants des étoiles furent bouleversés. Notre reine demeurait radieuse même dans le désespoir et dans le chaos des combats. Le ciel enchanté changeait de couleur avec les décharges de magie, avec les explosions qui menaçaient de déchirer le monde.\n\nLe frère combattait le frère. L\'Élu combattait' WHERE `entry` = 360;
UPDATE `locales_page_text` SET `Text_loc2` = 'Maître Delgren,\n\nC\'est ce que vous craigniez. Votre théorie sur la Tour d\'Althalaxx s\'avère vraie. Un culte se rassemble dans la Tour d\'Althalaxx, comptant bien des démonistes et même un satyre. Ils ont pris le nom de Culte de la Rive noire. Je ne puis imaginer quels vils desseins les rassemblent, mais cela m\'inquiète.\n\nVeuillez excuser le laconisme de cette missive, mais le danger rôde dans la forêt qui m\'entoure et je dois décrire la situation en peu de mots. Je' WHERE `entry` = 361;
UPDATE `locales_page_text` SET `Text_loc2` = 'Maître Delgren,\n\nC\'est ce que vous craigniez. Votre théorie sur la Tour d\'Althalaxx s\'avère vraie. Un culte se rassemble dans la Tour d\'Althalaxx, comptant bien des démonistes et même un satyre. Ils ont pris le nom de Culte de la Rive noire. Je ne puis imaginer quels vils desseins les rassemblent, mais cela m\'inquiète.\n\nVeuillez excuser le laconisme de cette missive, mais le danger rôde dans la forêt qui m\'entoure et je dois décrire la situation en peu de mots. Je' WHERE `entry` = 363;
UPDATE `locales_page_text` SET `Text_loc2` = 'sur les bords du lac, Ameth\'Aran et Bashal\'Aran étaient les plus éminents, Azshara, la reine bien-aimée des Kaldorei, ayant eu part à leur création. Ses serviteurs favoris, parmi les Bien-nés, elle les mena dans les villes jumelles pour y résider. Pour les gouverner, elle choisit Athrikus Narassin, qui allait pourtant passer la plupart de ses journées dans la tour d\'Althalaxx.\n\nSon second, un mage connu pour ses prouesses dans la magie des arcanes comme pour ses capacités physiques,' WHERE `entry` = 364;
UPDATE `locales_page_text` SET `Text_loc2` = 'était Asterion, qui vécut parmi les gens de Bashal\'Aran, et alla maintes fois à Ameth\'Aran, pour y veiller aux besoins de son peuple.\n\nEt c\'est ainsi que sur les rives du Puits d\'éternité, les villes jumelles grandirent et prospérèrent, tandis que le monde sous les étoiles sombrait lentement dans la folie.' WHERE `entry` = 365;
UPDATE `locales_page_text` SET `Text_loc2` = 'le béni. Tous les Bien-nés ne combattaient pas. Certains restaient debout, pétrifiés, tandis que la folie s\'emparait d\'eux. De puissantes cités et d\'humbles villages tombaient à l\'identique tout autour.\n\nDans Ameth\'Aran, tout n\'était plus que ruines. Les habitants se cramponnaient au sol comme si cela pouvait leur épargner la destruction. Ce fut alors qu\'Athrikus Narassin, favori d\'Azshara, apparut pour les guider vers la sécurité.\n\nAvec un sort il pouvait les protéger, affirmait-il. Tandis qu\'autour d\'eux périssaient frère, sœur, père et mère,' WHERE `entry` = 366;
UPDATE `locales_page_text` SET `Text_loc2` = 'ils auraient la vie éternelle et n\'auraient rien à craindre de la Fracture qui allait survenir.' WHERE `entry` = 367;
UPDATE `locales_page_text` SET `Text_loc2` = 'ils auraient la vie éternelle et n\'auraient rien à craindre de la Fracture qui allait survenir.' WHERE `entry` = 371;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'Étoile de Xil\'yeh\n\nGrel\'borg l\'Avare a l\'Étoile. C\'est un ogre cupide qui passe ses journées dans les montagnes d\'Alterac, au coeur des ruines d\'Alterac, à chercher des babioles. La majeure partie de sa collection est sans intérêt, mais un des objets, l\'Étoile de Xil\'yeh, a des capacités intéressantes.' WHERE `entry` = 372;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Main de Dagun\n \nDes textes anciens affirment que les griffes de Dagun sécrètent un poison qui non seulement tue sa proie, mais la fait muter en quelque chose d\'autre, en un membre des vieilles races. Sa valeur est évidente pour nous.\n\nDagun vit dans la mer profonde, mais il est régulièrement attiré à la surface par une tribu de murlocs Aileron boueux du le marécage d\'Âprefange. Leurs oracles l\'invoquent avec du varech enchanté. Si vous tuez assez d\'oracles, vous trouverez le varech. Placez-le ensuite sur l\'autel des murlocs et Dagun apparaîtra.' WHERE `entry` = 373;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Coeur de l\'héritage\n\nMogh l\'Immortel est un sorcier-docteur troll de Strangleronce. Il possède le Coeur de l\'héritage, qui a réputation d\'éloigner la mort de ceux qui savent découvrir son secret. Vous trouverez Mogh dans les ruines de Zul\'Mamwe. Battez-le, si vous pouvez, et apportez-moi le Coeur de l\'héritage.' WHERE `entry` = 374;
UPDATE `locales_page_text` SET `Text_loc2` = 'Apportez-moi ces trois objets, l\'Etoile, la Main et le Coeur, et le Sommaire de Yagyin sera à vous.\n\n-G.B.' WHERE `entry` = 375;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'Étoile de Xil\'yeh\n\nUn ogre d\'Alterac, Grel\'borg l\'Avare, fut le dernier possesseur connu de cette gemme qui, dit-on, donne à celui qui la détient la possibilité de communiquer avec d’autres mondes. D’après nos sources, Grelborg erre dans les ruines d\'Alterac, au cœur des montagnes d\'Alterac, fouillant les décombres à la recherche d\'autres reliques.' WHERE `entry` = 376;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Main de Dagun\n \nDagun est une créature des profondeurs, adorée par les murlocs du marécage d\'Âprefange Ils l’attirent à la surface avec un varech spécial, enchanté par leurs chamans. Chassez les murlocs, trouvez leur varech enchanté, puis placez-le sur l\'autel des murlocs. Dagun viendra, et vous l\'attendrez.\n\nJe veux cette main.' WHERE `entry` = 377;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Cœur de l\'héritage\n\nLes anciens textes disent que celui qui possède le Cœur de l\'héritage n\'a plus à craindre la tombe. C\'est peut-être vrai, car l\'actuel possesseur du Cœur, le troll Mogh, est connu sous le nom de Mogh l\'immortel. Sorcier-docteur de la tribu des Casse-crâne, Mogh se trouve à Strangleronce dans les ruines de Zul\'Mamwe.' WHERE `entry` = 378;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapportez-moi ces objets, l\'Étoile, la Main et le Cœur, et je vous donnerai un exemplaire de l\'Abrégé de Yagyin.\n\n-Le Conservateur' WHERE `entry` = 379;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapport de mission des nécrotraqueurs\n\nAgents : Rane Yorick, Quinn Yorick, Erland McKree\n\nMission principale : faire une reconnaissance dans la région nord de la forêt des Pins argentés et déterminer le degré de menace représenté par la faune et par le Fléau.' WHERE `entry` = 380;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les agents ont commencé leur exploration et ont découvert une présence significative de worgs. Il est recommandé que des équipes de chasse soient envoyées pour éliminer le problème.\n\nDes gnolls morts-vivants ont été trouvés dans la ferme de la région nommée le Champ des morts. La raison de leur présence en ces lieux est inconnue, mais leurs déplacements et leurs actes suggèrent une action militaire imminente. Au cours de nos observations, aucun chef n\'a été repéré parmi les gnolls. On suppose qu\'ils attendent l\'arrivée d\'un commandant ou de renforts.' WHERE `entry` = 381;
UPDATE `locales_page_text` SET `Text_loc2` = 'Durant leur progression, nos agents ont été retenus à la ferme d\'Ivar. Ivar, la plupart de sa famille et ses travailleurs ont succombé au Fléau et sont devenus ses serviteurs.\n\nNos agents ont été attaqués et bien qu\'ils aient réussi à vaincre leurs assaillants, certains d\'entre eux ont été sérieusement blessés, notamment l\'agent Quinn.' WHERE `entry` = 382;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une position défensive a été adoptée dans la vieille maison d\'Ivar, et tandis que Rane montait la garde, Erland a continué la reconnaissance.\n\nErland a alors été cloué dans un verger du Nord par des worgs et n\'a réussi à s\'échapper qu\'avec l\'aide d\'un autre agent des Réprouvés. Cet agent est la personne qui apporte ce rapport.' WHERE `entry` = 383;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il faut noter que le messager a été essentiel au succès de notre mission, mais a également battu Ivar l\'Infect, secouru Erland et accepté de porter ce rapport.\n\nNous lui sommes très reconnaissants et nous recommandons au commandement de récompenser cette glorieuse conduite à hauteur de ce qu\'elle mérite.\n\n-Nécrotraqueur Rane Yorick,\nChef de mission' WHERE `entry` = 386;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors il a erré et cherché. Et son bras restait perdu. Alors il a rugi, maudit, marché.  \n\nMais Gri\'lek avait depuis longtemps détourné ses oreilles des esprits, et ils étaient furieux et n\'écoutaient pas ses malédictions.\n\nGri\'lek était condamné. Condamné à errer sans bras.' WHERE `entry` = 387;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors il a erré et cherché. Et son bras restait perdu. Alors il a rugi, maudit, marché. \n\nMais Gri\'lek avait depuis longtemps détourné ses oreilles des esprits, et ils étaient furieux et n\'écoutaient pas ses malédictions.\n\nGri\'lek était condamné. Condamné à errer sans bras.' WHERE `entry` = 393;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une force maléfique a créé ceci,\nEn haut du Pic de Brume-funeste il est assis,\nIl rit de toi, de tes amis,\nSes élémentaires toujours en vie,\n\nBientôt tu affronteras sa magie,\nCar seule la mort mettra fin à sa furie.' WHERE `entry` = 394;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'Autel de Mel\'Thandris\n\n"Que la lumière d\'Elune brille éternellement sur cette vallée et illumine les voies de la forêt comme elle l\'a fait depuis des temps immémoriaux."' WHERE `entry` = 395;
UPDATE `locales_page_text` SET `Text_loc2` = 'Elune a exaucé mes vœux. Le sacrifice de mes camarades décédés n\'aura pas été vain, et je vengerai leur mort en utilisant la Faux d\'Elune. C\'est un objet ancien, une branche de bois tordu qui pourrait bien être plus ancienne encore que les Kaldorei.\n\nJ\'ai examiné cet outil offert par la déesse durant des heures. Ce n\'est pas une arme ordinaire, cela est sûr, ni un simple instrument magique. Avec lui, c\'est comme si les barrières du temps et de l\'espace s\'effaçaient.\n\nLa Faux en main,' WHERE `entry` = 396;
UPDATE `locales_page_text` SET `Text_loc2` = 'j\'ai reçu une vision du chaos. Des hommes-loups... les worgens... en lutte contre une incroyable armée. Les worgens se battaient sauvagement, comme il convient à leur race primitive, mais l\'ennemi ne cédait pas : c\'étaient les seigneurs de la Flamme d\'émeraude.\n\nEt c\'est là que j\'ai compris le vrai pouvoir de la Faux. En me concentrant sur lui, je suis devenu capable de communiquer avec les worgens. Je n\'ai pas utilisé de mots pour cela, c\'était quelque chose de différent... Je suis incapable de l\'expliquer.\n\nL\'important, c\'est que les worgens m\'ont entendu' WHERE `entry` = 397;
UPDATE `locales_page_text` SET `Text_loc2` = 'et m\'ont compris. En canalisant la puissance de la Faux, j\'ai pu altérer davantage encore les barrières et j\'ai pu conduire les worgens dans notre monde. Belle réussite pour un premier essai. Ils m\'ont suivi au combat, et fort de leur brutale puissance, nous nous sommes attaqués aux démons de Gangrebois.\n\nElune a répondu à mes prières. La forêt sera purifiée.\n\n\nQue les grâces d\'Elune me comblent jusqu\'à ce que ma tâche soit accomplie.' WHERE `entry` = 398;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tout va bien. J\'ai invoqué de grandes bandes de worgens pour combattre à mes côtés. Ils ne sont pas intelligents, mais ils ont une capacité remarquable à coordonner leurs attaques et à agir en groupe.\n\nJ\'ai identifié des chefs dans leurs meutes. Je leur ai donné le commandement de petits groupes de combattants. Nous frappons au même instant à plusieurs endroits de la forêt et ma campagne contre les démons porte ses fruits.\n\n\nQue les grâces d\'Elune me comblent jusqu\'à ce que ma tâche soit accomplie.' WHERE `entry` = 399;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai perdu une meute. J\'ai envoyé les worgens dans la forêt pour y reconnaître les zones d\'infestation démoniaque, mais ils ne sont pas revenus. Je commence à m\'inquiéter au sujet des chefs à qui j\'ai confié le commandement. Ils sont peu intelligents et pas nécessairement à la hauteur du peu de responsabilité que je leur ai confiée. Suis-je en train de perdre le contrôle ? Peut-être que je dois cesser d\'invoquer plus de worgens pour l\'heure.\n\n\nQue les grâces d\'Elune me comblent jusqu\'à ce que ma tâche soit accomplie.' WHERE `entry` = 400;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien que je n\'aie pas invoqué de worgens supplémentaires, leur nombre ne cesse de croître. C\'est comme si la Faux n\'avait plus besoin de mon intervention pour poursuivre le processus d\'invocation. C\'est troublant. J\'ai rassemblé autant de meutes que j\'ai pu en trouver, et je leur ai donné l\'ordre de demeurer à l\'Autel de Mel\'Thandris.\n\nEn attendant, j\'ai fouillé dans les bibliothèques de Darnassus et consulté le Cercle des Anciens à Sombrivage. Pas la moindre information sur les worgens. J\'ai entendu des rumeurs concernant' WHERE `entry` = 401;
UPDATE `locales_page_text` SET `Text_loc2` = 'un sorcier du Kirin Tor nommé Arugal. Apparemment, il aurait lui aussi invoqué des worgens. Je vais consulter ce sorcier, aussi déplaisant que cela soit, pour tâcher de découvrir ce qu\'il sait.\n\nLes worgens sont dangereux et je dois reprendre le contrôle. A l\'aube, je partirai pour le port des Tarides et me rendrai au Nouveau Monde.\n\n\nQue les grâces d\'Elune me comblent jusqu\'à ce que ma tâche soit accomplie.' WHERE `entry` = 402;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai commencé ce journal pour garder une trace de ma quête au service d\'Orneval. Certains événements survenus il y a peu restent mystérieux, et j\'ai bon espoir d\'en apprendre davantage dans le but d\'aider mon peuple.\n\nCommençons par rédiger les faits afin que je m\'en souvienne toujours.\n\nJ\'ai récemment découvert qu\'il y avait du vrai dans les rumeurs concernant le sorcier Dartol.' WHERE `entry` = 403;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai cru naguère grotesque l\'idée qu\'un humain vienne dans nos terres apprendre nos moeurs, mais ce sorcier, cet homme maléfique, est venu jusqu\'à nous. Il a survécu et appris une partie de notre savoir. Je ne suis pas certain de savoir comment...\n\nJe n\'ai pas compris ses objectifs lors de mon enquête, mais son plan semblait simple : en utilisant la magie druidique, il voulait se lier avec les furbolgs puis les contrôler. J\'espère apprendre avant que mon heure vienne si c\'est pour s\'en prendre à mon espèce ou pour quelque autre dessein maléfique.' WHERE `entry` = 404;
UPDATE `locales_page_text` SET `Text_loc2` = 'Malgré cela, il a apparemment réussi à mener ses plans à bien. A-t-il essayé ses machinations sur les furbolgs avant de les tenter sur mon peuple ? Est-ce que les furbolgs possédaient quelque chose qu\'il convoitait ? Qui sait ?\n\nEn utilisant un objet qu\'il a conçu (un genre de bâton ou de bâtonnet), il a essayé d\'infiltrer les tribus de furbolgs à Orneval. A un certain moment, et tous les points ne sont pas encore très clairs, il fut démasqué par les furbolgs alors qu\'il essayait de les monter' WHERE `entry` = 405;
UPDATE `locales_page_text` SET `Text_loc2` = 'les uns contre les autres. Tout comme les elfes l\'auraient été, les furbolgs ont été pris de rage. Mais ils ont réagi de façon plus primaire et ont tué Dartol.\n\nLes furbolgs, à cette époque du moins, n\'étaient pas les créatures corrompues qu\'elles sont devenues. Ils étaient rationnels et intelligents, leurs relations avec notre peuple restaient amicales.\n\nJ\'espère qu\'en recréant le bâtonnet avec l\'aide d\'une dryade nommée Shael\'dryn, je pourrai utiliser sa puissance pour aider notre peuple... et les furbolgs.' WHERE `entry` = 406;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tous les furbolgs n\'ont pas été corrompus. Certains vivent encore et tentent de protéger la forêt. Sous l\'effet de la corruption, certains des furbolgs ont fui, beaucoup ont été tués, d\'autres ont été chassés de leurs tribus.\n\nJ\'ai trouvé un vénérable furbolg de la tribu Vile-sylve qui vit au-dessus du lac Mystral. Il s\'appelle Krolg.\n\nJe l\'ai observé des jours durant, et je pense que si je pouvais l\'approcher sous le déguisement d\'un furbolg, je pourrais l\'aider et découvrir un moyen de recréer un lien entre nos deux peuples.' WHERE `entry` = 407;
UPDATE `locales_page_text` SET `Text_loc2` = 'En plus de tuer Dartol, les furbolgs ont brisé le bâtonnet en trois parties pour qu\'on ne puisse plus l\'utiliser contre eux. Ce fut difficile, mais je crois que je sais maintenant où se trouvent les trois fragments.\n\nLa Gemme\nLa première partie du bâtonnet, une gemme luminescente, a été cachée dans un autel au nord-ouest d\'Orneval. Je pense que cet autel est dans les ruines au centre du lac Falathim. Les Sentinelles m\'ont dit que des murlocs y habitent désormais.' WHERE `entry` = 408;
UPDATE `locales_page_text` SET `Text_loc2` = 'Si je ne peux trouver la gemme cachée dans le sanctuaire, c\'est qu\'elle est sûrement en possession d\'un des murlocs.\n\nLa Hampe\nCe morceau du bâtonnet a été donné aux tréants près de ce qui s\'appelle maintenant Gangrebois. Ni les furbolgs ni mon peuple n\'auraient pu imaginer que de si magnifiques créatures pourraient succomber à la corruption qui s\'est emparée de nos terres.\n\nMes recherches me disent que les gardiens conservent ce morceau dans une petite clairière, à l\'intérieur d\'un coffre. Pour en savoir plus, il faudra que je parle à Shael\'dryn.' WHERE `entry` = 409;
UPDATE `locales_page_text` SET `Text_loc2` = '1. Trouver un homme ou huit\n2. Taper fort l\'homme\n3. Taper l\'homme plus fort\n4. Mettre l\'homme dans le feu\n5. Manger l\'homme' WHERE `entry` = 410;
UPDATE `locales_page_text` SET `Text_loc2` = 'Shael\'dryn est une dryade qui protège le puits de lune au nord-est du lac Iris. Elle refuse de quitter la forêt ou de l\'abandonner à la corruption. Je sais qu\'elle a la puissance et la connaissance nécessaires pour réparer le bâtonnet ou au moins pour me dire comment procéder.\n\nLe Pommeau\nLe Pommeau du bâtonnet a été donné aux druides de Dor\'danil. Un problème : les druides sont morts.\n\nLeurs esprits habitent maintenant la région et ne sont pas hospitaliers.' WHERE `entry` = 411;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cela mérite une enquête plus approfondie. Je ne suis pas sûr d\'être assez puissant pour pouvoir combattre les esprits de mon peuple... encore moins pour m\'enfoncer dans les profondeurs de Dor\'danil et trouver le pommeau.\n\n(Remarque : durant mes préparatifs à Astranaar, j\'ai récemment rencontré un chasseur nain qui a passé une bonne partie de son temps à Orneval pour tenter d\'y aider mon peuple. Il vient d\'un lieu nommé Hurlevent.\n\nNous avons parlé autour de quelques bières une nuit, et ce qu\'il m\'a dit m\'a donné un nouvel espoir de pouvoir retrouver le pommeau.' WHERE `entry` = 412;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il m\'a dit qu\'il avait traversé tout Dor\'danil, et qu\'il n\'a jamais vu de coffre susceptible de contenir le pommeau. \n\nIlo m\'a dit également que des gelées pourrissantes avaient vécu dans la région, dévorant tout ce qui était plus petit qu\'elles. Il les a vues manger des tables, des sculptures, des ours, et même des gnomes !\n\nLes gelées semblent affectionner tout particulièrement ce qui est magique. Si le coffre contenant le pommeau a été enchanté, il est possible que les gelées l\'aient dévoré.' WHERE `entry` = 413;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'est un pari, mais je n\'ai pas plus d\'informations à cette heure...\n\nQuand j\'en saurai plus, je le noterai ici. Mais en attendant, je dois me mettre à la recherche des morceaux du bâtonnet pour aider Orneval.' WHERE `entry` = 414;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jin\'Zil,\n   Je ne vous ai plus donné de nouvelles depuis longtemps, mais j\'ai pensé que vous souhaiteriez être informé que la dernière des Plumesang a été tuée. Ne me remerciez pas, la personne qui délivre ce message est l\'assassin. Si vous le souhaitez, je peux vous faire livrer la tête de la harpie, une fois que la caravane aura terminé sa tournée dans les Tarides. Je sais que vous recherchez ce genre de choses pour exercer votre magie vaudou.\n   Prenez soin de vous, vieil ami.\n\n-Darsok Vivedague' WHERE `entry` = 415;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jin\'Zil,\n   Je ne vous ai plus donné de nouvelles depuis longtemps, mais j\'ai pensé que vous souhaiteriez être informé que la dernière des Plumesang a été tuée. Ne me remerciez pas, la personne qui délivre ce message est l\'assassin. Si vous le souhaitez, je peux vous faire livrer la tête de la harpie, une fois que la caravane aura terminé sa tournée dans les Tarides. Je sais que vous recherchez ce genre de choses pour exercer votre magie vaudou.\n   Prenez soin de vous, vieil ami.\n\n-Darsok Vivedague' WHERE `entry` = 416;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bon, les détonateurs et les explosifs NG-5 doivent être prêts à fonctionner. Je les ai marqués pour vous... Le bleu est pour la scierie. Le rouge pour la mine des Cisailles.\n\nJ\'y suis allé en reconnaissance et vous ne devriez pas avoir de problème à aller placer les explosifs et à ressortir avant de les faire exploser avec le détonateur.\n\nSouvenez-vous, le but est de faire diversion. Éloignez-vous des chariots avant que les gobelins ne viennent voir ce qui s\'est passé et faufilez-vous discrètement derrière eux pour entrer.' WHERE `entry` = 417;
UPDATE `locales_page_text` SET `Text_loc2` = 'Surtout faites attention. Le Nitromirglyceronium peut vous réduire en poussière. J\'ai enfermé le liquide dans des tubes en cuivre, il devrait rester stable et sans danger.\n\nJ\'ai utilisé le parchemin que Collin vous avait donné sur les boîtes des détonateurs. Ils enverront un message silencieux aux explosifs NG-5 une fois que vous les aurez placés. J\'ai utilisé un mécanisme Stalthwargon simple pour être sûr que la conductivité est optimale et que l\'écoulement de la Nitroglycérine entre les fluides différentiels est meilleur que la moyenne.' WHERE `entry` = 418;
UPDATE `locales_page_text` SET `Text_loc2` = 'Avant que je ne rentre dans les détails de mon plan, laissez-moi vous expliquer où vous devrez placer les explosifs.\n\nPlan Alpha - la scierie :\nIl y a un chariot à l\'extérieur en face de la scierie (c\'est au nord-est du site de déboisement et de stockage de bois). Je pense que les plans d\'ingénierie sont dans la scierie.\n\nPour placer la charge, allez derrière le chariot. Une fois qu\'elle est placée, éloignez-vous au plus vite.' WHERE `entry` = 419;
UPDATE `locales_page_text` SET `Text_loc2` = 'Lorsque vous êtes prêt, appuyez sur le détonateur.\n\nJe me suis assuré que le signal serait bon en utilisant des bobines de cuivre et d\'argent avec une antenne melthusienne déployée sur le boîtier. Ça devrait fonctionner à bonne distance. Méfiez-vous des gobelins. Ils n\'apprécieront pas de vous trouver à cet endroit.\n\nAu fait, des fois que vous n\'ayez pas compris, éloignez-vous du chariot avant de le faire exploser... Ça fait mal.' WHERE `entry` = 420;
UPDATE `locales_page_text` SET `Text_loc2` = 'Plan Bêta - Mine des Cisailles :\n\nJ\'ai vu les gobelins utiliser un autre chariot d\'explosifs au nord de l\'entrée de la mine. Je pense que cet endroit conviendra très bien pour y placer la charge d\'explosif NG-5 (rouge). Après avoir placé la charge à l\'arrière du chariot, éloignez-vous et appuyez sur le bouton. \n\nSi les gobelins ne viennent pas voir ce qui s\'est passé, c\'est que la montagne leur est tombée sur la tête.' WHERE `entry` = 421;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les lettres de la KapitalRisk sont à l\'extérieur de la mine... Sur certaines des caisses au niveau inférieur, je crois.\n\nBonne chance, et souvenez-vous...\n\nExplosif bleu : la scierie\nExplosif rouge : la mine\n\nEt détruisez cette lettre une fois la mission accomplie. D\'ailleurs, je devrais peut-être penser à trouver un moyen pour que ces lettres se détruisent toutes seules, une fois qu\'elles sont lues. Ce n\'est pas bête, ça...' WHERE `entry` = 422;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les lettres de la KapitalRisk sont à l\'extérieur de la mine... Sur certaines des caisses au niveau inférieur, je crois.\n\nBonne chance, et souvenez-vous...\n\nExplosif bleu : la scierie\nExplosif rouge : la mine\n\nEt détruisez cette lettre une fois la mission accomplie. D\'ailleurs, je devrais peut-être penser à trouver un moyen pour que ces lettres se détruisent toutes seules, une fois qu\'elles sont lues. Ce n\'est pas bête, ça...' WHERE `entry` = 423;
UPDATE `locales_page_text` SET `Text_loc2` = '--Premier jour--\n\nMon périple m\'a conduit depuis Forgefer à Kalimdor !\n\nLe prospecteur Khazgorm de Bael Modan progresse bien. Il a aujourd\'hui découvert un fossile que les érudits du site pensent être un fragment d\'os d\'un ancien dieu. Khazgorm est convaincu que ce fossile relève d\'un mystère plus grand encore. Pendant qu\'il supervise le site de fouilles, il m\'a chargé de découvrir d\'autres fossiles afin de comprendre toute l\'histoire. \n\nMa tâche est d\'aller vers le sud à la recherche de davantage d\'indices.' WHERE `entry` = 424;
UPDATE `locales_page_text` SET `Text_loc2` = '--Deuxième jour--\n\nLes Tarides sont vastes et étendues. Mon périple a été long et difficile et je n\'ai rien pu glaner pour les archives de la Ligue des explorateurs. \n\nLe seul incident notable a eu lieu sur la Route de l\'or. Au loin, j\'ai remarqué d\'étranges créatures. Elles semblaient être mi-cochon, mi-homme. Je me suis vite caché et j\'ai laissé passer ces viles créatures. Quelle puanteur elles ont laissé derrière elles !' WHERE `entry` = 425;
UPDATE `locales_page_text` SET `Text_loc2` = '--Troisième jour--\n\nQuelle découverte merveilleuse !\n\nEn me promenant aujourd\'hui, j\'ai trouvé quelque chose d\'exceptionnel : les restes immenses de ce qui semble être une créature porcine. Son crâne imposant faisait plusieurs fois ma taille. Chacune de ses dents était plus haute qu\'un nain. En raison de leur gigantisme, je pense que ces restes sont ceux de l\'un des Dieux très anciens légendaires.' WHERE `entry` = 426;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je me souviens d\'une conférence que le Chef archéologue Grisemoustache avait donnée il y a quelque temps, au quartier général de la Ligue des explorateurs à Forgefer. Ce vieux professeur mentionnait un tel dieu, un de ceux qui étaient vénérés par les étranges elfes de Darnassus pour son légendaire altruisme.--\n\nAh...! Un instant. J\'ai entendu grogner à l\'extérieur de la tente. Sûrement un sanglier des chardons qui essaie de me voler une de mes pommes...' WHERE `entry` = 427;
UPDATE `locales_page_text` SET `Text_loc2` = '--Jour 4--\n\nCapturé !\n\nJe dois... le noter... rapidement. \n\nAttaqué par des hommes sangliers de la tribu de Tranchebauge. Je crois qu\'ils m\'emmènent à ce qu\'il reste de ce grand dieu sanglier déchu. À moins qu\'ils ne contournent les grands fossiles et se dirigent directement vers l\'ouest du crâne géant, de l\'autre côté de la Route de l\'or.' WHERE `entry` = 428;
UPDATE `locales_page_text` SET `Text_loc2` = 'Entraîné de force dans cet endroit infect et infesté de bosquets épineux appelé Kraal de Tranchebauge. Jeté dans une hutte grossière et retenu captif avec un elfe de la nuit en piteux état.\n\nJe n\'ai rien pu garder à part ce journal, ma plume et ma montre de poche... \n\nQuelqu\'un approche...' WHERE `entry` = 429;
UPDATE `locales_page_text` SET `Text_loc2` = '--Jour 5--\n\nMon compagnon de cellule elfe de la nuit souffre de graves blessures. Les hommes sangliers l\'ont fouetté à plusieurs reprises et je pense qu\'il ne survivra pas, à moins qu\'une équipe de secours n\'arrive par miracle dans cet endroit redoutable. Il perd régulièrement connaissance. Quand il se réveille, je lui donne le peu d\'eau que j\'ai.' WHERE `entry` = 430;
UPDATE `locales_page_text` SET `Text_loc2` = 'La nuit dernière, il a pu parler pour la première fois. C\'est un druide appelé Heralath. Il a été envoyé ici pour étudier les rumeurs sur le dieu sanglier déchu. \n\nD\'une voix très faible, il m\'a dit qu\'il croyait que le crâne du grand sanglier se trouvait à l\'endroit où le grand dieu sanglier Agamaggan est tombé en combattant la Légion ardente il y a plus de dix mille ans, durant la guerre des Anciens. Son corps massif s\'est écrasé sur le sol et son sang s\'est répandu dans la terre. Des ronciers torturés ont poussé là où son sang a coulé...' WHERE `entry` = 431;
UPDATE `locales_page_text` SET `Text_loc2` = 'Heralath et son peuple cherchent à découvrir la source de la corruption sur ce site. Agamaggan était considéré comme un dieu pur, m\'a-t-il dit. La raison pour laquelle ces créatures immondes et ces épines nocives défigurent ce pays demeure un mystère.\n\nAvant que je n\'arrive à obtenir de plus amples informations, Heralath sombra de nouveau dans un sommeil agité...' WHERE `entry` = 432;
UPDATE `locales_page_text` SET `Text_loc2` = '--Jour 6--\n\nLa santé d\'Heralath empire.\n\nSes yeux se sont ouverts une seule fois aujourd\'hui. Il a parlé avec angoisse et tristesse mais son message était clair. Si je réussis à m\'enfuir de cet enfer, ici au Kraal de Tranchebauge, je dois aller rapporter à Falfindel Gardevoie à Thalanaar que Charlga Trancheflanc lève des troupes dans le sud des Tarides. Sa puissance augmente de jour en jour. Il faut arrêter la mégère avant que le fléau de la corruption ne s\'étende depuis le Kraal.' WHERE `entry` = 433;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pauvre Heralath, il est pâle comme la mort. Sa fièvre ne cesse d\'augmenter de minute en minute. Pendant ce temps-là, les hommes sangliers préparent de grandes manœuvres militaires. Si je ne tente pas de m\'échapper bientôt, je risque de connaître le même sort que mon compagnon de cellule elfique.\n\nMon plan consiste à me faufiler à l\'extérieur lorsque les hommes sangliers partiront à la chasse. Mes gardes sont paresseux et sont souvent endormis à leur poste.' WHERE `entry` = 434;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une fois libéré du Kraal, j\'irai à Mille pointes puis vers l\'ouest jusqu\'à Thalanaar qui se trouve à la frontière de la mystérieuse forêt de Féralas. Là-bas, je chercherai le druide Falfindel Gardevoie.\n\nCe qui à l\'origine était une simple tentative pour en savoir plus concernant le passé de mon peuple, est devenu quelque chose de bien plus important. L\'avenir de l\'Alliance repose entre mes mains...' WHERE `entry` = 435;
UPDATE `locales_page_text` SET `Text_loc2` = 'NUMÉRO DE COMMANDE : "45TK-3\nACHETEUR : Kravel Koalbarbe\n\nFACTURE :\n\nRoue à rochet hydroélectrique (3)\nBoulons à répétition (50)\nTubes de cuivre diamètre 20 30"(12)\nTabac à priser, marque Furie-du-fond (1)\nPrisme de premier choix (3)"' WHERE `entry` = 436;
UPDATE `locales_page_text` SET `Text_loc2` = 'vous aurais remis ce message moi-même, mais je préfère rester où je suis, de peur de manquer un événement important.\n\nSi vous voulez en savoir plus sur mes observations concernant la Tour d\'Althalaxx, le porteur de ce message m\'a été d\'une aide précieuse pendant mes recherches et je pense que l\'on peut lui faire confiance.\n\nEn hâte,\nBalthule Frappelombre' WHERE `entry` = 437;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nazz Steamboil est en réalité le fils d\'un prince marchand gobelin (quel prince ? Cette information vaut de l\'or).\n\nIl semble qu\'il préfère dépenser l\'argent familial plutôt que d\'en gagner par lui-même. Il a dérobé une petite fortune à sa famille et est parti se cacher dans les Salines.\n\nSi son père savait où il était, il irait le chercher et le ramènerait à la maison... amputé d\'un orteil pour son insolence.' WHERE `entry` = 438;
UPDATE `locales_page_text` SET `Text_loc2` = 'M. Fizzlebub,\n\nLe porteur de cette lettre et moi-même avons un projet commercial auquel nous vous proposons de vous joindre. \n\nComme vous le savez, j\'ai passé beaucoup de temps dans les Salines. Le projet évoqué concerne les courses qui s\'y déroulent.\n\nSi vous participez aux courses, et plus particulièrement si parier sur ces courses vous intéresse, continuez la lecture de ce document. Je suis certain que votre connaissance de Strangleronce sera très utile pour notre plan.' WHERE `entry` = 439;
UPDATE `locales_page_text` SET `Text_loc2` = 'M. Fizzlebub,\n\nLe porteur de cette lettre et moi-même avons un projet commercial auquel nous vous proposons de vous joindre. \n\nComme vous le savez, j\'ai passé beaucoup de temps dans les Salines. Le projet évoqué concerne les courses qui s\'y déroulent.\n\nSi vous participez aux courses, et plus particulièrement si parier sur ces courses vous intéresse, continuez la lecture de ce document. Je suis certain que votre connaissance de Strangleronce sera très utile pour notre plan.' WHERE `entry` = 440;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mon bon Martek,\n\nj\'ai des soucis et j\'ai besoin de votre aide.\n\nMon frère et moi-même sommes en train de construire un char de course et nous avons besoin de pistons solides. Je ne trouve rien qui soit assez résistant. Rien n\'est à la hauteur des performances de notre nouveau char. Le moteur casse sans arrêt !\n\nVous êtes un expert de la forge du métal. Vous devez nous aider. Vous êtes le seul à pouvoir le faire.' WHERE `entry` = 441;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pour faire face à Gueule-glacée, vous devez l\'attirer avec la viande de sa proie favorite.\n\nRendez-vous dans les montagnes d\'Alterac en Azeroth, et chassez un lion des montagnes massif. Tuez-le et rapportez sa carcasse à la Caverne stérile, un endroit sacré pour le wendigo bestial.\n\nPlacez la carcasse sur la Flamme d\'Uzel et l\'odeur de la viande en train de cuire se répandra.\n\nGueule-glacée ne tardera pas.' WHERE `entry` = 442;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai toujours été surpris de voir comment il était simple de manipuler les gens en exploitant la haine qu\'ils ont développée à l\'encontre des autres races. Les orcs et les trolls furent les premiers, la haine qu\'ils éprouvent pour l\'Alliance en faisait des sujets privilégiés et attentifs au message de la Rive noire. Sont-ils au courant des desseins qu\'ils servent ?\n\nCe paladin exaspérant, Delgren le Purificateur, a mis son nez dans nos affaires dans la forêt d\'Orneval, pendant que son laquais, Balthule, espionnait ma tour en pensant que je n\'étais au courant de rien. Delgren pense que nous représentons une menace' WHERE `entry` = 443;
UPDATE `locales_page_text` SET `Text_loc2` = 'mineure, comme tant d\'autres cultes de la mort. Il n\'a pas la moindre idée que nous sommes l\'une des... branches du Conseil des ombres.\n\nQuand j\'étais enfant, on m\'a parlé d\'un insecte dont la morsure était mortelle, bien qu\'il soit de la taille d\'une mouche, une tache à peine reconnaissable. Ce sont les choses que l\'on néglige qui vous infligent les pires souffrances.' WHERE `entry` = 444;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les chariots explosifs qui disputent les courses des salines nécessitent des réglages et un entretien très pointus. Il n\'est donc pas étrange d\'apercevoir les équipages dans les environs.\n\nIls testent leurs véhicules régulièrement et font des réglages lorsqu\'une modification est nécessaire. C\'est le seul moyen de conserver les véhicules en très bon état et performants.\n\nDonc... Nous recherchons un moyen de distraire les équipages afin qu\'ils relâchent leur vigilance sur les véhicules ; sans entretien, ils seront vite hors compétition.' WHERE `entry` = 445;
UPDATE `locales_page_text` SET `Text_loc2` = 'Si nous pouvons décider quels chariots sont en bon état et lesquels ne le sont pas, inutile de vous expliquer que nous aurons un sérieux avantage.\n\nDonc, mon bon Fizzlebub, nous avons besoin de quelque chose qui pousserait les équipages des chariots à faire des erreurs.\n\nEst-ce que Strangleronce possède des plantes, des herbes ou une boisson que nous pourrions utiliser pour perturber l\'esprit des gnomes et des gobelins ?\n\n--K. Koalbeard' WHERE `entry` = 446;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai menÃ© de grandes recherches concernant les Aspects et leurs crÃ©ateurs titanesques, plus qu\'aucun homme ne peut en mener en toute une vie. Il y avait cinq Aspects lorsque les Titans quittÃ¨rent ce monde. C\'Ã©taient de puissants dragons dont la tÃ¢che consistait Ã  protÃ©ger le monde d\'Azeroth. Les rÃ©cits les concernant sont nombreux et variÃ©s. Maintenant, encore, malgrÃ© toutes les informations que j\'ai pu rÃ©unir, je suis convaincu qu\'il reste tout Ã  apprendre Ã  propos de ces magnifiques crÃ©atures.' WHERE `entry` = 447;
UPDATE `locales_page_text` SET `Text_loc2` = 'Apothicaire Lydon,\n\nJ\'espÃ¨re que cette lettre et son porteur arriveront jusqu\'Ã  vous dans de bonnes conditions. Un Ã©vÃ©nement est survenu dans la lointaine Kalimdor et je crois que vos compÃ©tences sont requises.\n\nJe serai brÃ¨ve.\n\nUne crise a lieu dans les Serres-Rocheuses, non loin de Mulgore, la patrie de nos alliÃ©s taurens. Les gobelins de la KapitalRisk exploitent le minerai et le bois dans ces montagnes en rendant furieux les esprits qui y demeurent.' WHERE `entry` = 448;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je dois reconnaÃ®tre que la diversion faite par les esprits que contrÃ´laient les chamans et les druides taurens a eu de l\'effet. Les taurens considÃ¨rent la prÃ©sence de la KapitalRisk dans les Serres-Rocheuses comme une terrible menace.\n\nMenace ou non, nous devons aider nos alliÃ©s.\n\nLydon, vous Ãªtes expert en toxines. Pourriez-vous en trouver une qui nous permettrait de contaminer les gobelins des Serres-Rocheuses ?\n\nVotre dÃ©vouÃ©e,\nP. Zamah' WHERE `entry` = 449;
UPDATE `locales_page_text` SET `Text_loc2` = 'La plupart des connaissances que je possÃ¨de maintenant, je n\'aurais pas pu les apprendre tout seul. C\'est pour cela que je suis extrÃªmement reconnaissant aux elfes de la nuit. C\'est grÃ¢ce Ã  eux que j\'ai pu obtenir autant de dÃ©tails. Suite Ã  nos Ã©changes, je suis de plus en plus intimement convaincu qu\'au commencement ils Ã©taient bien plus proches des Aspects que je ne le pensais. Mais leurs secrets sont si bien gardÃ©s que je ne peux rien affirmer.' WHERE `entry` = 450;
