UPDATE `locales_page_text` SET `Text_loc2` = 'Est-ce que je deviens fou ? Aujourd\'hui j\'ai coupé la tête de l\'un de mes plus fidèles patrouilleurs afin d\'avoir un meilleur aperçu de son âme. Alors que je regardais la trachée sectionnée, je me disais que je n\'avais pas mangé depuis des jours !' WHERE `entry` = 1153;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mathias Shaw a envoyé un garçon de courses pour me tuer. <texte illisible>\nIl patrouille dehors maintenant. Pantins sans âme et sans vie.\n\nHahahah !' WHERE `entry` = 1154;
