UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">\nCombinaisons de cristal est\n</H1>\n<BR/>\n<P align = "left">Il y a deux effets possibles : l\'un fournit un bonus d\'esprit, l\'autre crée un bouclier de dégâts.</P>\n<BR/>\n<P align = "left">FORCE CRISTALLINE : nécessite des cristaux bleus et verts.<BR/>Vous fournit une amélioration de l\'esprit.</P>\n<BR/>\n<P align = "left">FLECHE CRISTALLINE : nécessite des cristaux bleus et jaunes.<BR/>Crée un bouclier qui inflige des dégâts aux attaquants.\n</P>\n</BODY>\n</HTML>' WHERE `entry` = 1515;
