UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES MILITAIRES DE LA HORDE &amp; DE L\'ALLIANCE </H1>\n<BR/>\n<BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRankHorde" align = "left"/>\n<BR/>\n<P align = "right">Throm\'ka! Vous</P>\n<P align = "right">trouverez dans ce </P>\n<P align = "right"> livre la liste détaillée </P>\n<P align = "right">des grades militaires</P>\n<P align = "right">de la Horde et de la</P>\n<P align = "right">misérable Alliance.</P>\n<P align = "right">Nous traiterons d\'abord</P>\n<P align = "right">de la Horde, puis de</P>\n<P align = "right"> l\'Alliance, en indiquant chaque fois les grades d\'officiers et de les grades d\'hommes de troupe. Comme il se doit, les plus forts sont en haut, et les faibles en bas.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2661;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES D\'OFFICIER DE LA HORDE </H1><BR/>\n<P align = "center">Part 1</P>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank14" align = "left"/><BR/>\n<P align = "right"> Grand seigneur de guerre </P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank13" align = "left"/><BR/>\n<P align = "right"> Seigneur de guerre </P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank12" align = "left"/><BR/>\n<P align = "right">Général</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank11" align = "left"/><BR/>\n<P align = "right">Lieutenant-général</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank10" align = "left"/><BR/>\n<P align = "right">Champion</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2662;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES D\'OFFICIER DE LA HORDE </H1><BR/>\n<P align = "center">Part 2</P>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank09" align = "left"/><BR/>\n<P align = "right">Centurion</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank08" align = "left"/><BR/>\n<P align = "right">Légionnaire</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank07" align = "left"/><BR/>\n<P align = "right">Garde de sang</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank06" align = "left"/><BR/>\n<P align = "right">Garde de pierre</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2663;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES DES RECRUES DE LA HORDE </H1><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank05" align = "left"/><BR/>\n<P align = "right">Adjudant</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank04" align = "left"/><BR/>\n<P align = "right"> Sergent-chef</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank03" align = "left"/><BR/>\n<P align = "right">Sergent</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank02" align = "left"/><BR/>\n<P align = "right">Grunt</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank01" align = "left"/><BR/>\n<P align = "right">Eclaireur</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2664;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES D\'OFFICIER DE L\'ALLIANCE </H1><BR/>\n<P align = "center">Part 1</P>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank14" align = "left"/><BR/>\n<P align = "right">Connétable</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank13" align = "left"/><BR/>\n<P align = "right">Grand maréchal</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank12" align = "left"/><BR/>\n<P align = "right">Maréchal</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank11" align = "left"/><BR/>\n<P align = "right">Commandant</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank10" align = "left"/><BR/>\n<P align = "right">Lieutenant-commandant</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2665;
UPDATE `locales_page_text` SET `Text_loc2` = 'Simona la Séductrice :\n\nSimona empoisonne de sa présence le cratère d\'Un\'Goro. Ne vous laissez pas tromper par son apparence. Approchez-vous d\'elle avec prudence et défiez-la.' WHERE `entry` = 2671;
UPDATE `locales_page_text` SET `Text_loc2` = 'Klinfran l\'Enragé :\n\nKlinfran erre dans les Steppes ardentes. Approchez-vous de lui avec prudence et obligez-le à se montrer sous sa vraie forme.' WHERE `entry` = 2672;
UPDATE `locales_page_text` SET `Text_loc2` = 'Solenor le Tueur :\n\nC\'est dans le paysage aride de Silithus que se tapit le démon Solenor le Tueur. Il est sans doute déguisé et il est extrêmement dangereux. Approchez-vous de lui et obligez-le à se transformer.' WHERE `entry` = 2673;
UPDATE `locales_page_text` SET `Text_loc2` = 'Artorius le Néfaste :\n\nArtorius n\'apporte que la corruption et la ruine à tout ce qu\'il touche au Berceau-de-l\'Hiver. Trouvez-le et tuez-le.' WHERE `entry` = 2674;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">GRADES MILITAIRES DE L\'ALLIANCE</H1><BR/>\n<P align = "center">OFFICIERS</P><BR/>\n<P align = "center">Connétable</P>\n<P align = "center">Grand Maréchal</P>\n<P align = "center">Maréchal</P>\n<P align = "center">Commandant</P>\n<P align = "center">Lieutenant-commandant</P>\n<P align = "center">Chevalier-champion</P>\n<P align = "center">Chevalier-capitaine</P>\n<P align = "center">Chevalier-lieutenant</P>\n<P align = "center">Chevalier</P><BR/>\n<P align = "center">RECRUES</P><BR/>\n<P align = "center">Sergent-Major</P>\n<P align = "center">Sergent-chef</P>\n<P align = "center">Sergent</P>\n<P align = "center">Caporal</P>\n<P align = "center">Soldat</P>\n</BODY>\n</HTML>' WHERE `entry` = 2676;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">GRADES MILITAIRES DE LA HORDE</H1><BR/>\n<P align = "center">OFFICIERS</P><BR/>\n<P align = "center">Grand seigneur de guerre</P>\n<P align = "center">Seigneur de guerre</P>\n<P align = "center">Général</P>\n<P align = "center">Lieutenant-général</P>\n<P align = "center">Champion</P>\n<P align = "center">Centurion</P>\n<P align = "center">Légionnaire</P>\n<P align = "center">Garde de sang</P>\n<P align = "center">Garde de pierre</P><BR/>\n<P align = "center">RECRUES</P><BR/>\n<P align = "center">Adjudant</P>\n<P align = "center">Sergent-chef</P>\n<P align = "center">Sergent</P>\n<P align = "center">Grunt</P>\n<P align = "center">Eclaireur</P>\n</BODY>\n</HTML>' WHERE `entry` = 2677;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pour vous lier à un Destrier de l\'effroi, vous devez faire les trois choses suivantes :\n\nCréer un Cercle d\'invocation supérieure.\n\nA l\'intérieur du Cercle, ouvrez une porte vers Xoroth et sortez-en un Destrier de l\'effroi.\n\nTriomphez du Destrier de l\'effroi, puis rendez-vous maître de son esprit.\n\nLes pages suivantes vous expliqueront comment accomplir chacune de ces actions. Ce ne sera pas facile, mais vous avez prouvé votre valeur. Montrez-vous habile, et je ne doute pas que le Destrier de l\'effroi vous appartiendra.\n\nPoursuivez votre lecture,\n-Mor\'zul Porte-sang' WHERE `entry` = 2691;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les instruments du Rituel\n\nAvant de commencer, il vous faut les instruments magiques suivants :\n\nLe bocal de J\'eevee\nUne Magnétite noire\nGlyphes de Xoroth\n\nMon serviteur Gorzeeki vous les fournira, si vous payez. Ne tentez d\'accomplir aucune des étapes du rituel si vous n\'avez pas tous ces instruments. Chacun est très important.\n\nDans les pages qui suivent, j\'expliquerai comment chaque instrument doit être utilisé.' WHERE `entry` = 2692;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cercle de lien supérieur\n\nVous devez créer un Cercle de lien supérieur à un endroit où la magie est forte. Un tel lieu existe loin dans les ruines d\'Eldre\'Thalas, que l\'on appelle aussi Hache-tripes. Un être très puissant est emprisonné à Eldre\'Thalas : Immol\'thar. C\'est sur le socle de sa prison que vous devez accomplir le rituel pour créer le Cercle.\n\nCombattez pour atteindre le Socle, puis laissez J\'eevee s\'échapper de son bocal.' WHERE `entry` = 2693;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Cloche, la Roue et la Chandelle\n\nQuand vous aurez libéré J\'eevee, il mettra en place la Cloche, la Roue et la Chandelle et un cercle apparaîtra. C\'est alors que commence le rituel. Prenez garde : les trois objets véhiculent des énergies très puissantes et peuvent cesser de fonctionner. Lorsque cela se produit, utilisez vite votre Magnétite noire pour les remettre en route avant que votre rituel n\'échoue complètement.\n\nSi les objets cessent tous les trois de fonctionner avant que vous ne les ayez remis en route, le rituel est interrompu et il vous faudra tout recommencer.' WHERE `entry` = 2694;
UPDATE `locales_page_text` SET `Text_loc2` = 'La Cloche, la Roue et la Chandelle ne véhiculent pas seulement les énergies du rituel mais possèdent aussi des propriétés spécifiques.\n\nLorsqu\'elle sonne, la Cloche de Dethmoora confère vigueur et énergie aux démonistes se trouvant à l\'intérieur du cercle.\n\nLorsqu\'elle tourne, la Roue de la Marche noire protège ceux qui se trouvent à l\'intérieur du cercle.\n\nLorsqu\'elle brûle, la Chandelle de l\'apocalypse projette une énergie surnaturelle sur les ennemis qui pénètrent dans le cercle.\n\nEn raison de ces bienfaits, il est très important que les trois objets fonctionnent pendant le rituel.' WHERE `entry` = 2695;
UPDATE `locales_page_text` SET `Text_loc2` = 'Notez également que la Magnétite noire, qui permet de remettre en route la Cloche, la Roue et la Chandelle si elles cessent de fonctionner, nécessite des fragments d\'âme. Chaque fois que vous remettez en route un objet du rituel à l\'aide de la Magnétite, un de vos fragments d\'âme sera consommé. Veillez donc à en avoir suffisamment avant de commencer le rituel.' WHERE `entry` = 2696;
UPDATE `locales_page_text` SET `Text_loc2` = 'La fin du Rituel\n\nVous pouvez surveiller la progression du rituel à l\'aide des runes magiques autour du cercle. Le rituel est terminé lorsque neuf runes ont apparu, et vous verrez l\'énergie s\'élever du Cercle.\n\nVous pouvez dès lors invoquer les Glyphes de Xoroth, ouvrir une porte vers Xoroth et en sortir un Destrier de l\'effroi.\n\nTriomphez du Destrier de l\'effroi et libérez son esprit. Affrontez l\'esprit et soumettez-le, et vous serez récompensé par le secret de son invocation.' WHERE `entry` = 2697;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<BR/>\n<P align = "left">Ici repose Durotan, premier Chef du clan Loup-de-givre et père de notre honoré Chef de guerre, Thrall. Il était le représentant le plus valeureux de notre peuple. Trahi par ceux qui allaient nous conduire à l\'esclavage, Durotan donna sa vie pour que nous puissions gagner notre liberté. Nous l\'honorons ainsi que l\'héritage qu\'il nous a transmis par son fils.</P>\n<BR/>\n<P align = "left">Drek\'Thar, prophète des Loups-de-givre </P>\n</BODY>\n</HTML>' WHERE `entry` = 2731;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">GRADES MILITAIRES DE LA HORDE</H1><BR/>\n<P align = "center">OFFICIERS</P><BR/>\n<P align = "center">Grand seigneur de guerre</P>\n<P align = "center">Seigneur de guerre</P>\n<P align = "center">Général</P>\n<P align = "center">Lieutenant-général</P>\n<P align = "center">Champion</P>\n<P align = "center">Centurion</P>\n<P align = "center">Légionnaire</P>\n<P align = "center">Garde de sang</P>\n<P align = "center">Garde de pierre</P><BR/>\n<P align = "center">RECRUES</P><BR/>\n<P align = "center">Adjudant</P>\n<P align = "center">Sergent-chef</P>\n<P align = "center">Sergent</P>\n<P align = "center">Grunt</P>\n<P align = "center">Eclaireur</P>\n</BODY>\n</HTML>' WHERE `entry` = 2770;
UPDATE `locales_page_text` SET `Text_loc2` = 'Peler l\'oignon\nComment démanteler les Foudrepiques \n-par Drek\'Thar\n\nVous trouverez dans ces pages de nombreuses informations d\'ordre tactique et politique. Le soldat érudit est celui qui se penche sur l\'épaule de l\'expérience. Que ce livre vous serve de guide dans votre bataille pour la Vallée d\'Alterac.' WHERE `entry` = 2771;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 1 - La première ligne\n\nDémanteler l\'armée des Foudrepiques est tout à fait comparable à peler un oignon. Pour parvenir jusqu\'au coeur, il faut commencer par enlever les couches extérieures.\n\nLes premières lignes, principalement constituées de Montagnards Foudrepique et de Sentinelles de l\'Alliance, sont liées au fortin du capitaine. C\'est le capitaine Balinda Stonehearth qui commande ces unités et envoie des renforts quand les lignes sont assiégées. Frappez le capitaine et les forces de la première ligne s\'effondreront.' WHERE `entry` = 2772;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 2 - Les tours jumelles\n\nNe vous y trompez pas. Vanndar Foudrepique est très loin d\'être stupide. Une fois que la première ligne aura été percée, il faudra encore détruire les défenses secondaires. C\'est depuis les tours jumelles de Stonehearth et de l\'Aile de glace que sont commandés les gardes Foudrepiques à l\'extérieur de Dun Baldar et les patrouilles Foudrepiques.' WHERE `entry` = 2773;
UPDATE `locales_page_text` SET `Text_loc2` = 'Prenez garde, soldat, car ces deux tours sont très bien gardées par des troupes sous les ordres de l\'un des commandants d\'élite de Vanndar. Si vos forces pénètrent dans les fortifications, assurez-vous que le commandant à l\'intérieur a été... réduit au silence. Cela aussi fait partie du démantèlement des Foudrepiques.' WHERE `entry` = 2774;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 3 - Les quatre commandants\n\nLa troisième couche de l\'oignon Foudrepique est constituée par les quatre commandants. À cet égard, Vanndar a copié nos glorieuses défenses Loup-de-givre. Bien sûr, il vous dira le contraire... Mais je m\'égare. \n\nLes quatre commandants contrôlent les mouvements des gardes Foudrepiques qui protègent férocement Dun Baldar. Réduisez-les au silence et les gardes Foudrepiques tomberont. \n\nIl ne restera alors plus qu\'une couche à peler.' WHERE `entry` = 2775;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 4 - Les fortins de Dun Baldar\n\nComme vous l\'avez sans doute remarqué, les couches défensives des Foudrepiques sont placées de sorte à empêcher qu\'une puissante attaque ne détruise les défenses de l\'armée entière. Inutile de demander : oui, c\'est exactement comme cela que nos propres forces sont positionnées, et non, je ne changerai pas l\'organisation de notre défense. Ce sont eux qui nous ont copiés, pourquoi devrions-nous changer ?' WHERE `entry` = 2776;
UPDATE `locales_page_text` SET `Text_loc2` = 'Où en étais-je ? Ah oui, les défenseurs Foudrepique - les unités d\'élite les plus fidèles de Vanndar - sont commandés depuis les fortins de Dun Baldar (situés à Dun Baldar). Détruisez ces deux tours et les renforts destinés à aider Vanndar n\'existeront plus.' WHERE `entry` = 2777;
UPDATE `locales_page_text` SET `Text_loc2` = 'Epilogue\n\nQuand vous aurez fait tout cela, il vous restera le coeur de l\'oignon. Vanndar Foudrepique se retrouvera seul et sans défenses. Le code de conduite militaire nous oblige à lui demander sa reddition avant de procéder à toute exécution. N\'oubliez pas de lui demander de se rendre quand vous le verrez... puis tuez-le.' WHERE `entry` = 2778;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'artichaut Frostwolf : Récits glorieux des Stormpike\n-par Vanndar Stormpike\n\nQu\'est-ce que les Frostwolf ? La réponse est simple : les Frostwolf sont des sauvages qui essayent de briser notre élan impérialiste et notre impératif souverain.\n\nComme un artichaut, les Frostwolf possèdent des défenses hérissées de piquants, et comme un artichaut, une fois qu\'on a ôté la couche extérieure, le coeur apparaît, prêt à être mangé avec une bonne petite sauce à l\'aïl.\n\nDélicieux !' WHERE `entry` = 2779;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 1 - La première ligne\n\nLes premières lignes des défenses des Frostwolf sont constituées de guerriers Frostwolf. Les guerriers sont déployés vers les premières lignes depuis la forteresse du capitaine Galvangar : la garnison de Glace-sang. Le moyen le plus efficace de briser cette couche de leurs défenses est de s\'attaquer à la garnison de Glace-sang.\n\nAssiégez la forteresse et débarrassez-vous du capitaine Galvangar. Quand ce sera fait, les premières lignes s\'effondreront.' WHERE `entry` = 2780;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 2 - Glace-sang et la Halte de la tour\n\nUne fois la première couche tombée, la deuxième couche de l"artichaut" sera à nu. Détruire les tours Frostwolf qui se dressent près des premières lignes handicapera lourdement les patrouilles et les légionnaires qui sont à l\'arrière.\n\nLes tours sont très bien gardées et chacune abrite l\'un des commandants d\'élite de Drek\'Thar. Ils doivent être tués. Cela accélérera le démantèlement des défenses des Frostwolf.' WHERE `entry` = 2781;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 3 - Les quatre commandants\n \nSi vous avez suivi les conseils de ce guide, deux des commandants de Drek\'Thar sont maintenant morts. Bon travail, soldat ! Ils commencent déjà à perdre de leur détermination. Vous devez maintenant achever le démantèlement de la chaîne de commandement en tuant les deux commandants restants.\n\nQuand les quatre commandants seront tués, les légionnaires Frostwolf qui gardent le donjon Frostwolf seront éparpillés, sans personne pour les commander. Prêts à être cueillis !' WHERE `entry` = 2782;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chapitre 4 - Les tours Frostwolf occidentales et orientales\n\nIl n\'y a maintenant plus qu\'une couche qui vous sépare de la victoire : les unités les plus puissantes et les plus fidèles de Drek\'Thar, les Gardiens Frostwolf.\n\nLes Gardiens Frostwolf viennent des sections de réserve qui se tiennent dans les tours Frostwolf occidentales et orientales. Détruisez ces tours et regardez les Gardiens Frostwolf restants fuir dans le déshonneur.' WHERE `entry` = 2783;
UPDATE `locales_page_text` SET `Text_loc2` = 'Epilogue\n\n"Couvrez le coeur de l\'artichaut de beurre d\'aïl et de mayonnaise. Accompagnez le repas d\'une enivrante bière vieillie."\n\nDrek\'Thar est maintenant seul, ses deux chiens à ses côtés. Prenez la hutte de guérison Frostwolf pour vous aider dans votre bataille et finissez-en avec Drek\'Thar. Quand le général tombera, cette terre reviendra enfin aux mains de ceux à qui elle appartient : les Stormpike.' WHERE `entry` = 2784;
UPDATE `locales_page_text` SET `Text_loc2` = 'Viens à nous, Zanza de Zuldazar.\n\nBénis ceux qui te demandent de l’aide, loa Zanza. Bénis ceux qui cherchent à s’allier avec le peuple\nzandalarien.\n\nAide-nous dans cette heure de péril. Aide-nous, loa. Donne-nous le pouvoir d’abattre nos ennemis. Donne-nous le pouvoir de vaincre une nouvelle fois le Dieu sanglant.' WHERE `entry` = 2785;
UPDATE `locales_page_text` SET `Text_loc2` = 'Concours de pêche à Strangleronce!\n\n Dimanche après-midi de 14 h à 16 h !\n\nBaladez-vous le long de la côte de Strangleronce et guettez les bancs de courbines qui apparaîtront le long de la côte ! Envoyez votre flotteur dans le banc, et vous prendrez des poissons inhabituels au lieu des variétés normales de Strangleronce. Le premier à pêcher quarante courbines et à se présenter au maître de pêche Bassbait, à Baie-du-Butin, recevra un prix ! \n\nMême si vous n\'êtes pas le premier à prendre quarante courbines, vous pouvez toujours vous présenter pour les revendre. Et puis, il existe aussi des poissons spéciaux, pour lesquels il y a des récompenses particulières !' WHERE `entry` = 2786;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son nom apporta la Lumière dans les ténèbres d\'Azeroth.\n\nSa valeur renversa les murs de la souffrance.\n\nLa Main d\'argent n\'aurait pu accomplir plus grand bienfait\n\nque donner au monde le chevalier qui se sacrifia.' WHERE `entry` = 2787;
UPDATE `locales_page_text` SET `Text_loc2` = 'La renommée n\'était pas ce qu\'il cherchait,\n\nni le pouvoir sur son peuple.\n\nIl choisit plutôt de lutter pour Lordaeron\n\ndans l\'espoir que les siens n\'auraient plus à se battre.' WHERE `entry` = 2788;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'histoire tragique de ce chevalier si saint\n\njamais ne se terminera par sa terrible mort.\n\nNous l\'honorons, et lui rendons grâce !\n\nUther connaîtra toujours la gloire et la Lumière !' WHERE `entry` = 2789;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son nom ne mourra pas.$BSon sacrifice servira toujours$Bà nous montrer la voie.$B$BLes chaînes qui autrefois$Bétouffaient notre honneur$Bne nous lient plus.' WHERE `entry` = 2790;
UPDATE `locales_page_text` SET `Text_loc2` = 'Entendez-vous son cri ?$BUn hurlement de bataille pour la Horde :$BLa victoire ou la mort !$B$BN\'oublions jamais$Bsa force face à la mort.$BSon rêve, à présent réalité.' WHERE `entry` = 2791;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le danger est partout !$BLes ennemis cherchent à nous$Bremettre dans les fers.$B$BQuand nous combattons, pensez à$Bcelui qui fit son devoir.$BHellscream, pour toujours !' WHERE `entry` = 2792;
UPDATE `locales_page_text` SET `Text_loc2` = 'Près de mille ans se sont écoulés depuis la “Guerre des sables changeants”. Pendant ce conflit, Fandral Forteramure a été frappé par une terrible tragédie, au cœur du désert de Silithus. Depuis, il porte un lourd fardeau. On suppose que le tempérament désagréable de l’archidruide vient en grande partie des événements décrits ici.' WHERE `entry` = 2793;
UPDATE `locales_page_text` SET `Text_loc2` = 'À un moment donné, une ancienne race insectoïde, les Qiraji, tenta de s’emparer de presque tout Kalimdor. Il revint à Forteramure, l’impérialiste pompeux, et à son armée d’elfes de la nuit, de les affronter sur le champ de bataille.' WHERE `entry` = 2794;
UPDATE `locales_page_text` SET `Text_loc2` = 'Père et fils se battirent vaillamment pour contenir les essaims à l’intérieur de Silithus. Un flot ininterrompu de silithides jaillissait de la forteresse d’Ahn’Qiraj pour renforcer les forces d’invasion des Qiraji. Tout Silithus était assiégé.' WHERE `entry` = 2795;
UPDATE `locales_page_text` SET `Text_loc2` = 'Forteramure l’aîné le regretta par la suite, mais il donna l’ordre au jeune Forteramure, Valstann, de défendre l’avant-poste de Sudevent. Hélas, Valstann fut tué ce même jour, lorsque Sudevent succomba devant les armées d’Ahn’Qiraj.' WHERE `entry` = 2796;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le cœur lourd, la tête pleine de pensées de vengeance, Fandral Forteramure ne renonça pas. Une défaite en Silithus aurait représenté une perte tragique pour la nation des elfes de la nuit, et peut-être pour le monde entier.\n\nLa bataille se poursuivit pendant des mois. Les Qiraji conquirent tout Silithus. Forteramure et son armée furent repoussés jusqu’à Tanaris, et l’arrogance qui poussait Fandral Forteramure jusque-là disparut complètement. Ses troupes étaient décimées. D’innombrables innocents avaient trouvé la mort.' WHERE `entry` = 2797;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son orgueil anéanti, Forteramure ressentit, pour la seconde fois de son existence, ce frisson au centre de son être qui ne pouvait venir que de la peur. C’est en Tanaris qu’il décida d’approcher Anachronos, progéniture de Nozdormu, et de demander l’aide du Vol de bronze.' WHERE `entry` = 2798;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans un premier temps, Anachronos refusa d’impliquer le Vol de bronze dans les affaires des races inférieures. Il fallut que les Qiraji osent attaquer les Grottes du temps pour qu’Anachronos accepte de s’allier aux elfes de la nuit.' WHERE `entry` = 2799;
UPDATE `locales_page_text` SET `Text_loc2` = 'Anachronos ne tarda pas à réaliser que même le puissant Vol de bronze ne parvenait pas à empêcher la progression des armées des Qiraji. À chaque fois que son souffle anéantissait cinquante insectes, cent autres prenaient leur place. Les lignes des elfes de nuit se délitèrent. Confronté à la possibilité d’une retraite dans les étendues sauvages de Féralas, Anachronos décida d’avertir les enfants des Aspects.' WHERE `entry` = 2800;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il avertit d’abord le vol draconique d’Ysera. Merithra du Rêve répondit à son appel. Ensuite, le vol draconique d’Alexstrasza et le vol draconique de Malygos furent informés.' WHERE `entry` = 2801;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les dragons se rencontrèrent dans la stratosphère, très loin au-dessus de Silithus et de la forteresse d’Ahn’Qiraj.\n\nÉtaient présents Anachronos, enfant de Nozdormu, Aspect du Temps.\n\nDu Rêve d’Emeraude vint Merithra, enfant d’Ysera, Aspect de la Nature.\n\nCaelestrasz, enfant d’Alexstrasza, Aspect de la Vie, représentait le vol draconique rouge.\n\nArygos, enfant de Malygos, Aspect de la Magie, représentait le vol draconique bleu.' WHERE `entry` = 2802;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les dragons observèrent, et virent un flot incessant de silithides et de Qiraji jaillir d’Ahn’Qiraj. Il leur apparut vite que la force seule ne suffirait pas à mettre un terme à cette guerre. À l’intérieur du temple principal, des dizaines de milliers de soldats Qiraji attendaient, et d’autres naissaient sans cesse. Après une brève éducation, ils étaient prêts à être lâchés sur le monde.' WHERE `entry` = 2803;
UPDATE `locales_page_text` SET `Text_loc2` = 'Arygos fut le premier à sentir les étranges émanations. Le temple monolithique situé au sud d’Ahn’Qiraj irradiait une magie faible, mais très différente de celle des Qiraji. En dépit de leurs efforts, aucun des dragons ne parvint à pénétrer plus avant dans le temple ou à découvrir la source de cette énergie. Seule une créature d’une puissance immense pouvait se dissimuler aussi bien. Peut-être… peut-être que quelque chose d’autre contrôlait les insectes, y compris les Qiraji.' WHERE `entry` = 2804;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les dragons comprirent qu’ils devaient réagir rapidement, sous peine de voir le monde souffrir d’une nouvelle et terrible catastrophe. Après avoir pesé leurs différents choix, ils décidèrent que la meilleure tactique sera d’arrêter l’armée ennemie près de sa source.' WHERE `entry` = 2805;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une immense barrière serait érigée en puisant à la fois dans l’essence des elfes de la nuit et dans celle des vols draconiques. Cette barrière magique retiendrait les silithides et leurs maîtres à l’intérieur des murs d’Ahn’Qiraj, évitant ainsi de futures incursions. Ahn’Qiraj deviendrait une prison.' WHERE `entry` = 2806;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 1$bNotre pèlerinage est terminé. Nous avons finalement rejoint Silithus, où nous devrons effectuer la prise de contact. Nous sommes venus de bien des endroits différents pour mettre nos efforts en commun, et mener à bien cette entreprise monumentale.$b$bUne extraordinaire atmosphère de fébrilité règne parmi nous. Demain, nous consacrerons toute notre énergie à la construction de ce qui nous permettra de communiquer avec EUX.$b$bDemain nous érigerons une pierre des vents !' WHERE `entry` = 2807;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 2$bL’emplacement de la Pierre des vents a été le sujet d’un grand débat. Même les plus sages d’entre nous sont incapables de déterminer avec une grande certitude l’endroit exact auquel se réfère la vieille prophétie.$b$bJe suis resté avec le groupe nord d’origine, et le temps prouvera indubitablement que nous avons raison. Soutenir que les lignes d’énergie se croisent à cet endroit selon des angles défavorables est idiot et naïf.$b$bNous avons déjà rassemblé tous les matériaux de construction qui nous sont nécessaires.' WHERE `entry` = 2808;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 15$bNotre dévouement a fini par porter ses fruits. Nous sommes le premier camp à avoir terminé la construction d’une pierre des vents. Si les autres imbéciles étaient restés avec nous, nous aurions achevé notre tâche en moins d’une semaine !$b$bDemain, nous commencerons les rituels qui invoqueront le baron Kazum en personne. Il a demandé d’être le premier à tester le système, et nous n’avons pas pu le contredire, car il s’emporte facilement.$b$bJe pense qu’il sera très content de nous.' WHERE `entry` = 2809;
UPDATE `locales_page_text` SET `Text_loc2` = 'À diffuser immédiatement auprès de tous les membres du Marteau du crépuscule.\n\nLa traque du traître Ortell progresse à une allure plus que satisfaisante. Nos éclaireurs ont pu réduire le champ des recherches à un réseau de cavernes dans la marche de l\'Ouest.\n\nLa trahison d\'Ortell ne sera pas pardonnée. Sa fuite représente un risque majeur pour la sécurité de notre organisation.\n\nTout individu qui se rendrait coupable de dissimulation d\'informations concernant le lieu où se trouve Ortell subira un châtiment exemplaire.\n\n-Seigneur du crépuscule Everun' WHERE `entry` = 2810;
UPDATE `locales_page_text` SET `Text_loc2` = 'À diffuser immédiatement auprès de tous les membres du Marteau du crépuscule.\n\nEn raison de la situation actuelle à Silithus, toute diffusion des textes du Vrai croyant devra être effectuée sous l\'escorte de pas moins de quatre gardes armés du Marteau du crépuscule.\n\nTout membre qui contreviendrait à cette nouvelle directive sera puni sans délai.\n\n-Seigneur du crépuscule D\'Sak' WHERE `entry` = 2811;
UPDATE `locales_page_text` SET `Text_loc2` = 'À diffuser immédiatement auprès de tous les membres du Marteau du crépuscule.\n\nLes informations suivantes décrivent l\'utilisation des pierres des vents pour communiquer avec nos maîtres du Conseil abyssal.\n\nToute utilisation frivole des pierres des vents sera punie dans la douleur.\n\n-Gardien du crépuscule Havunth' WHERE `entry` = 2812;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les Templiers des abysses\n\nEn tant qu\'initié, vous êtes autorisé à établir contact avec des Templiers des abysses pour des rituels de routine. Souvenez-vous de toujours porter les vêtements appropriés lorsque vous utilisez les pierres des vents afin d\'éviter d\'activer nos enchantements défensifs.\n\nEn cas d\'urgence, si aucun surveillant n\'est présent, vous avez la permission d\'invoquer un Templier des abysses.\n\nPour davantage de détails au sujet des rituels d\'invocation concernés, consultez le Vrai Croyant du mois dernier.' WHERE `entry` = 2813;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les ducs des abysses\n\nSeuls les acolytes en possession d\'un médaillon de statut sont autorisés à établir contact avec des ducs des abysses. Un médaillon de statut doit être utilisé uniquement par la personne pour laquelle il a été issu à l\'origine.\n\nLes membres du Marteau du crépuscule qui se rendraient coupables d\'une communication inappropriée avec les ducs des abysses ou d\'un usage non autorisé d\'un médaillon de statut seront châtiés par moi en personne.' WHERE `entry` = 2814;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Haut conseil des seigneurs des abysses\n\nSeuls les seigneurs des abysses sont autorisés à entrer en contact avec le prince Skaldrenox, le haut maréchal Trombe, le baron Kazum ou le seigneur Skwol. \n\nLes membres de faible rang du Marteau du crépuscule ne sont autorisés à initier toute forme de communication avec le Haut conseil en aucune circonstance. Tous ceux qui ne lui appartiennent pas qui utiliseraient une pierre des vents de cette manière seront réduits en cendres sur le champ.' WHERE `entry` = 2815;
UPDATE `locales_page_text` SET `Text_loc2` = 'État du Culte, Volume 92\n\nC\'est le cœur réjoui que je vous accueille, mes frères, car notre culte ne pourrait mieux se porter.  Nous sommes légion et nos objectifs n\'ont jamais été plus prêts de leur aboutissement.\n\nDe nouvelles recrues continuent d\'affluer.  Elles chantent les louanges de nos seigneurs à Sombrivage, elles s\'inclinent dans l\'allégresse dans la gorge des Vents brûlants, elles embrassent le serviteur de nos maîtres, Aku\'mai, dans les Profondeurs de Brassenoire.  Je vous le dis, mes frères et sœurs, bientôt un essaim de fidèles recouvrira tout Azeroth !' WHERE `entry` = 2816;
UPDATE `locales_page_text` SET `Text_loc2` = 'État du Culte, Volume 127\n\nEn vérité, aujourd\'hui est un jour heureux ! Nous avons été mis à l\'épreuve à Silithus et nous avons réussi !\n\nLes nouvelles de la destruction de notre camp le plus au nord pourront troubler les cœurs de ceux qui manquent de foi, mais ce massacre ne doit pas être considéré comme une tragédie. Non !' WHERE `entry` = 2817;
UPDATE `locales_page_text` SET `Text_loc2` = 'Que notre sang soit versé par l\'un des membres du Haut conseil abyssal est une bénédiction ! Nous devrions envier ceux de nos camarades qui sont tombés, car leurs esrpits demeurent à présent dans le ventre d\'une bête des plus vertueuses ! \n\nGloire aux Dieux très anciens ! Gloire à leurs serviteurs ! Gloire au Marteau du crépuscule !' WHERE `entry` = 2818;
UPDATE `locales_page_text` SET `Text_loc2` = 'État du Culte, Volume 233\n\nUne nouvelle occasion pour nous, mes frères et sœurs! Le Cercle cénarien, une vile secte d\'adorateurs de la nature, envoie à présent ses agents contre nous. Ils veulent probablement nous empêcher d\'invoquer des pierres d\'attraction rouges. Nous ne le permettrons pas !\n\nLes pierres rouges sont d\'une importance vitale pour nos nouveaux alliés. Nous ne devons pas laisser les valets du Cercle cénarien parvenir à leurs fins. Soyez fermes, mes fidèles amis. Soyez fermes !' WHERE `entry` = 2819;
UPDATE `locales_page_text` SET `Text_loc2` = 'Comment savons-nous qu\'ils nous aiment ?\n\nCertains se demandent peut-être comment les Dieux très anciens et leurs serviteurs pourraient nous aimer. Est-ce qu\'ils ne nous tuent pas ? Est-ce qu\'ils ne nous causent pas à tous douleurs et souffrances ? Comment des êtres ayant un penchant si prononcé pour le mal pourraient-ils connaître l\'amour dans leurs cœurs ?\n\nLa réponse à cette question est très simple. Si simple qu\'il n\'est pas nécessaire de la prononcer.' WHERE `entry` = 2820;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<IMG src = "Interface\\Pictures\\21037_crudemap_256"/>\n</BODY>\n</HTML>' WHERE `entry` = 2828;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\n\nAujourd’hui, mon ennemi juré, Narain Divinambolesque, a tenté de déjouer mes plans en m’envoyant quelques sbires prendre sa place lors de l’exécution. De SON exécution. Tu imagines ?\n\nJe me suis donné un mal fou pour mettre au point un plan génial afin de l’attirer dans cette fichue hutte, et il me fait un coup pareil ? La barbe… je suis trop énervé pour écrire. À plus tard.' WHERE `entry` = 2829;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\n\nQuand j’ai quitté ma cachette après avoir rédigé ma dernière note, j’étais dans une rage folle. Vraiment, vraiment furieux ! J’ai décidé de redessiner l’uniforme de mes sbires pour qu’il reflète mes souffrances intérieures. Je me sens un peu mieux, mais il me manque encore quelque chose. Je reviens…' WHERE `entry` = 2830;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\n\nVoilà, je me sens beaucoup mieux. J’ai battu l’un de mes sbires jusqu’à ce qu’il pleure comme une petite fille.\n\nEuh… non, en fait, je me sens un peu mal à l’aise. Ah mais minute, je suis un génie du MAL ! Du mal, tu vois ? Je me déçois beaucoup. À plus.' WHERE `entry` = 2831;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\nJe l’ai serré dans mes bras et je lui ai dit qu’il s’en sortait bien et qu’il devait continuer comme ça. Je me sens mieux. Mais ça reste entre nous, d’accord ?\n\nOù en étais-je ? Ah oui, NARAIN ! ARRGH !!! Je le hais. Quand nous étudiions au Bricolo-institut de Gnomeregan, ses résultats étaient si bons qu’il fallait redéfinir toutes les moyennes en fonction de lui.\n\nEt du coup, ils m’ont chassé de l’école parce que j’étais en dessous des nouvelles normes. En ce qui me concerne, Gnomeregan a eu ce qu’elle méritait !\n\nBon, ça suffit pour aujourd’hui. Bonne nuit, mon cher journal !' WHERE `entry` = 2832;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\n\nJe dois vieillir. J’ai complètement oublié ce que je te racontais à propos de Narain et de la manière dont il a triché. J’ai si furieux de voir un leurre déguisé en Narain que j’ai demandé à Numéro Deux de massacrer cet imbécile !\n\nAprès, j’ai couru jusqu’à ma machine volante et j’ai commencé à déchirer son livre bien-aimé, stupide et idiot sur le draconique. Quel prétentieux ! Qui le lit le draconique ? Je le hais tant !' WHERE `entry` = 2833;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cher journal,\n\nDésolé, je me suis endormi. J’étais fou de rage et je jetais les chapitres de ce livre stupide un peu partout. Quand j’ai vu un démon en dessous de moi, dans la gorge du Sombre murmure, j’ai décidé de faire une tornade magique avec le premier chapitre. Elle l’a déchiqueté en un million de petits morceaux ! Ça m’a soulagé. Magie + papier = rigolo.\n\nJ’étais tellement content que j’ai decidé de faire un tour dans les Royaumes de l’est. Mon premier arrêt a été pour le Cœur du Magma. Narain en parlait souvent… « Le Cœur du Magma ceci, Ragnaros cela… »' WHERE `entry` = 2834;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tu sais quoi ? S’il aime tant son fichu livre, il pourra toujours le reconstituer en allant au Cœur du Magma et en récupérant un chapitre dans un tas de cendres ! Ah !\n\nEt tant que j’étais dans le coin, je me suis dit que certains des techniciens baveux du labo de ce bon vieux Victor Nefarius auraient peut-être l’usage d’un peu de draconique. Et hop, sitôt dit, sitôt largué !' WHERE `entry` = 2835;
UPDATE `locales_page_text` SET `Text_loc2` = 'C’était en train de devenir un passe-temps à part entière. Je prenais beaucoup de plaisir à découper ce fichu livre en morceaux !\n\nLes largages suivants ont été simples.\n\nFossoyeuse, demeure des morts-vivants fétides et pestilentiels (mais aussi du meilleur chili du monde) en a eu un. Hurlevent, capitale du fromage, un autre. Dis, cher journal, as-tu déjà pensé à mettre du fromage de Hurlevent sur du chili de Fossoyeuse ? HÉ HO ? Ce serait intéressant…' WHERE `entry` = 2836;
UPDATE `locales_page_text` SET `Text_loc2` = 'À Toutes les Personnes Concernées,\n\nComme j\'aime ces petits jeux auxquels nous nous adonnons. \n\nJe suppose que si vous lisez cette lettre, c\'est que vous êtes parvenus, d\'une manière ou d\'une autre, par miracle ou grâce à une intervention divine, à me vaincre. C\'est grotesque, je le sais, mais des choses bien plus étranges sont déjà advenues dans l\'histoire de ce monde.' WHERE `entry` = 2838;
UPDATE `locales_page_text` SET `Text_loc2` = 'Comme dans toutes les choses de la vie, il existe une part éclatante et glorieuse de tragédie, de chaos et de mal dans ce triste récit. Même si vous avez réussi à me tuer, vous l\'avez fait d\'une façon si maladroite et si désordonnée que j\'ai eu le temps d\'écrire cette lettre APRÈS avoir détruit votre dernier espoir de sauver ce monde. Cette poudre de cristal rouge est tout ce qui reste de votre fragment de sceptre.' WHERE `entry` = 2839;
UPDATE `locales_page_text` SET `Text_loc2` = 'Appréciez à leur juste valeur les tenues de Némésis et de Stormrage que vous trouverez peut-être dans mes coffres. Et croyez-moi, j\'éprouve un immense plaisir en sachant que j\'ai réussi là où mon père avait échoué...\n\n\nTrès sincèrement,\n\nSeigneur Victor Nefarius\n"Nefarian"' WHERE `entry` = 2840;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au nom des Gourmandises Fumebois, nous vous remercions d’avoir accepté d’enquêter. Nous ne savons pas avec certitude quel groupe détient Metzen. Vous pouvez donc commencer par l’un ou par l’autre, à votre guise.$B$BLe temps, c’est de l’argent, mais dans ce cas précis, nous en manquons. Soyez rapide, car ces deux bandes sont capables du pire.$B$BVoici les deux demandes de rançon que nous avons reçues. Bonne chance.' WHERE `entry` = 2841;
UPDATE `locales_page_text` SET `Text_loc2` = 'Si vous voulez retrouver le renne vivant, dépêchez-vous de payer la rançon. Les pirates des mers du Sud n’aiment pas les retards de paiement, et nous savons que l’animal vous rend de grands services dans VOTRE forme de piraterie.$B$BApportez 1 000 pièces d’or à l’entrée de la crique des Gréements, à Tanaris. Toute tentative pour libérer le renne nous obligera à lui faire subir le supplice de la planche, avec une toute petite planche suivie d’une très longue chute.$B$BYeargh !$B$BLes pirates des mers du Sud.' WHERE `entry` = 2842;
UPDATE `locales_page_text` SET `Text_loc2` = 'Votre animal bien-aimé supporte assez mal la chaleur incessante de la gorge des Vents brûlants… vous avez intérêt à payer la rançon sans tarder.$B$BNous demandons 700 pièces d’or et cinq rubis étoilés. Vous les placerez dans une boîte dépourvue de signes distinctifs que vous laisserez à l’entrée du passage de Formepierre d’ici une semaine.$B$BSi vous n’obtempérez pas, Metzen deviendra le plat principal du traditionnel festin sombrefer du Voile d’hiver.$B$BMmmm… du renne….' WHERE `entry` = 2843;
UPDATE `locales_page_text` SET `Text_loc2` = 'Plus rapide que des aigles sont ses coursiers,$B$BEt il siffle, et il crie, et il les encourage :$B$B« Hue, Mercer ! Hue, Chilton ! Hue, Jordan et Kaplan !$B$BAllons, Nagle ! Allons, Pardo ! Allons, Goodman et Metzen !$B$BEn haut des murailles ! En haut des toits !$B$BEn avant ! En avant ! En avant, tous ! »' WHERE `entry` = 2844;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'espère de tout mon cœur que mes recherches permettront de découvrir une source d\'énergie magique complémentaire dans laquelle mon peuple pourra puiser sans danger. Avec la disparition du puits de soleil, nous devons trouver un moyen de perpétuer notre mode de vie sans succomber aux pièges de la magie des arcanes.\n\nJe crois que dans l\'avenir, les sin\'dorei deviendront, une fois encore, un exemple éclatant que tous pourront suivre !' WHERE `entry` = 2846;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aucun succès jusqu\'à présent. Le peu de magie dont je peux disposer doit être canalisé par la flèche. J\'ai quand même eu quelques idées intéressantes. J\'ai chargé mes apprentis les plus brillants de suivre ces pistes. Nous verrons bien ce qu\'il en sort.' WHERE `entry` = 2847;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je n\'ai rien écrit ici depuis un moment. Toujours rien de prometteur. J\'ai appris hier que l\'un des disciples de l\'île de Haut-soleil, un certain Felendren, avait ignoré les conseils de ses mentors et avait succombé au mal.\n\nJe vais redoubler d\'efforts.' WHERE `entry` = 2848;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rien, mais je ne dois pas perdre espoir. Les Sin\'dorei ne peuvent pas se permettre de dépendre de la magie au moment où nous sommes assiégés depuis le sud.\n\nJ\'ai envisagé une approche entièrement nouvelle. Si elle réussit, elle nous permettra de filtrer les « impuretés » de certaines sources d\'énergie corrompues et gangrenées que j\'avais mises de côté jusqu\'à présent. Je dois me montrer prudent.' WHERE `entry` = 2849;
UPDATE `locales_page_text` SET `Text_loc2` = 'Extraordinaire ! Nous avons remporté quelques succès, bien que la quantité de magie que nous avons été capable d\'extraire soit encore très minime. Je vais demander à la plupart des apprentis d\'interrompre leurs études pour se concentrer sur cette nouvelle approche si prometteuse.\n\nAvec un peu de chance, nous serons bientôt capables d\'affiner le processus. Cela nous permettrait de faire d\'une pierre deux coups : nous procurer une source de magie abondante ET un moyen de contrer les magies gangrenées que nous risquons de rencontrer à l\'avenir !' WHERE `entry` = 2850;
UPDATE `locales_page_text` SET `Text_loc2` = 'Non ! J\'expérimentais le nouveau processus avec mon apprenti principal, Telethayon. Soudain, il s\'est flétri sous mes yeux, succombant au mal qui afflige les miens. J\'ai essayé de le sauver, mais il était trop gravement touché. Je n\'ai pu que mettre un terme à ses souffrances.\n\nTel est le prix de la découverte, un prix qui me semble déjà trop lourd.' WHERE `entry` = 2851;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'est difficile à supporter… deux autres apprentis sont morts. Nous avons été si prudents… Je ne comprends pas ce qui a mal tourné.\n\nIl va falloir que j\'abandonne cette piste, et que nous recommencions depuis le début.' WHERE `entry` = 2852;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je suis arrivé trop tard. À mon insu, un troisième apprenti s\'était ouvert de cet axe de recherche désastreux à certains de ses confrères. Je vais essayer d\'endiguer la crise, mais je dois d\'abord éloigner les apprentis qui ne sont pas affectés.\n\nJe vais leur annoncer à tous mon départ en congé sabbatique à la retraite des Pérégrins, dans l\'espoir de trouver une nouvelle approche au problème.\n\nJe dois trouver un moyen de racheter cette horrible erreur.' WHERE `entry` = 2853;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">GRADES D\'OFFICIER DE LA HORDE</H1><BR/>\n<P align = "center">Deuxième partie</P>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank09" align = "left"/><BR/>\n<P align = "right">Centurion</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank08" align = "left"/><BR/>\n<P align = "right">Légionnaire</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank07" align = "left"/><BR/>\n<P align = "right">Garde de sang</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank06" align = "left"/><BR/>\n<P align = "right">Garde de pierre</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2854;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES DES RECRUES DE LA HORDE </H1><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank05" align = "left"/><BR/>\n<P align = "right">Adjudant</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank04" align = "left"/><BR/>\n<P align = "right"> Sergent-chef</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank03" align = "left"/><BR/>\n<P align = "right">Sergent</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank02" align = "left"/><BR/>\n<P align = "right">Grunt</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank01" align = "left"/><BR/>\n<P align = "right">Eclaireur</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2855;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES D\'OFFICIER DE L\'ALLIANCE </H1><BR/>\n<P align = "center">Part 2</P>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank09" align = "left"/><BR/>\n<P align = "right">Chevalier-champion</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank08" align = "left"/><BR/>\n<P align = "right"> Chevalier-capitaine</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank07" align = "left"/><BR/>\n<P align = "right"> Chevalier-lieutenant</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank06" align = "left"/><BR/>\n<P align = "right">Chevalier</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2856;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center"> GRADES DES RECRUES DE L\'ALLIANCE </H1><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank05" align = "left"/><BR/>\n<P align = "right">Sergent-major</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank04" align = "left"/><BR/>\n<P align = "right"> Sergent-chef</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank03" align = "left"/><BR/>\n<P align = "right">Sergent</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank02" align = "left"/><BR/>\n<P align = "right">Caporal</P><BR/><BR/>\n<IMG src = "Interface\\PvPRankBadges\\PvPRank01" align = "left"/><BR/>\n<P align = "right">Soldat</P><BR/><BR/>\n</BODY>\n</HTML>' WHERE `entry` = 2857;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">\nAnthony Ray Stark\n</H1>\n<H2 align = "center">\n1961 - 2005\n</H2>\n</BODY>\n</HTML>' WHERE `entry` = 2858;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">LE BRASERO D’INVOCATION : MODE D’EMPLOI</H1>\n<H3 align = "center">Où trouver des lieux hantés</H3>\n\n<H3 align = "center">INTRODUCTION</H3>\n\n<P>Ces pages vous expliqueront où trouver les âmes et les esprits déchus des personnes impliquées dans l’incident de l’amulette du seigneur Valthalak. Bien que vous ayez déjà sauvé mes anciens compagnons, je soupçonne que les échos répugnants du mal qui a corrompu leurs esprits résonnent toujours dans ces lieux hantés. Tel est le pouvoir maléfique de l’esprit du seigneur Valthalak.</P>\n\n<P>Le brasero peut aussi servir à invoquer le seigneur Valthalak mais comme vous avez déjà pu vous en rendre compte, il vaut mieux faire preuve de prudence à ce propos.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2859;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">INTRODUCTION, suite...</H1>\n\n<P>Le brasero lui-même fonctionne comme le brasero de signal. Placez-le aux alentours de l’un des cinq lieux hantés et il invoquera l’esprit des morts.</P>\n\n<P>Accessoirement et parce que je sais que cela vous intéresse, la bannière de provocation peut être utilisée d’une manière similaire dans l’arène des profondeurs de Rochenoire. À votre prochaine visite, et si possible à toutes les suivantes, infligez donc à Theldren et à ses brutes une bonne leçon de ma part.</P>\n\n<P>Si vous voulez en apprendre davantage, continuez à lire.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2860;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">ISALIEN</H1>\n<H3 align = "center">Elfe de la nuit, prêtresse de la Lune.</H3>\n\n<P>Lorsque Isalien décida de quitter la compagnie, elle avait l’intention de se rendre en pèlerinage à Hache-tripes, l’antique cité en ruine que les elfes connaissaient naguère sous le nom d’Eldre’Thalas. Il s’y trouve encore une bibliothèque, un sanctuaire consacré au savoir, que quelques elfes de la nuit ont réussi à préserver. Elle comptait trouver des réponses concernant à la fois le passé de son peuple et les morceaux de l’amulette. Notez qu’elle était très amie avec Falrin Sculpteflore.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2861;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">ISALIEN, suite...</H1>\n\n\n<P>Malheureusement, au moment où elle entrait dans la ville, elle fut victime d’Alzzin le Modeleur et de ses séides. Son hippogriffe apprivoisé, Empyréen, succomba également. Isalien tomba et ne se releva pas. Il est peut-être préférable qu’elle ne soit pas parvenue à découvrir les secrets de la magie maléfique dont le seigneur Valthalak s’était servi pour enfermer une partie de son essence dans l’amulette.</P>\n\n<P>La salle découverte où réside Alzzin est l’un des lieux hantés.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2862;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">MOR SABOT-GRIS</H1>\n<H3 align = "center">Druide tauren, il fut le premier à succomber.</H3>\n\n\n<P>Mor Sabot-gris était l’un des plus vaillants compagnons au sein de la Lame ardente. Tragiquement, il a été la première victime des séides maléfiques du seigneur Valthalak.</P>\n\n<P>Lorsque nous avons fui la colère de Valthalak, nous étions poursuivis par une véritable armée d’assassins et de traqueurs spectraux. Notre départ du pic Rochenoire fut passablement désordonné. Hélas, au cours du trajet, Mor perdit pied. À moins que quelqu’un ne l’ait poussé… Quoi qu’il en soit, il tomba dans les ténèbres de la partie inférieure du pic.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2863;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">MOR SABOT-GRIS, suite...</H1>\n\n\n<P>Plus tard, nous avons découvert que Mor avait survécu à sa chute. Hélas, mon vieil ami a connu une fin affreuse. Il fut capturé par les trolls de Tazz\'Alaor. Peu après, alors que le maître de guerre Voone était en train de le torturer, il fut achevé par un groupe d’assassins au service de Valthalak.</P>\n\n<P>Si vous souhaitez consulter l’écho de l’esprit de Mor Sabot-gris, la chambre du maître de guerre Voone est l’un des lieux hantés.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2864;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">JARIEN ET SOTHOS</H1>\n<H3 align = "center">Une fratrie d\'aspirants à la Croisade écarlate</H3>\n\n\n<P>Jarien et Sothos étaient parmi les gens les plus intolérants que j’aie croisés. Mais ils étaient des combattants exceptionnels, et issus d’une famille riche. Pour ces raisons Anthion, le chef de la Lame voilée, les tolérait.</P>\n\n<P>Ils ont fini par se laisser séduire par les enseignements et la philosophie de la Croisade écarlate. Ils avaient toujours fait preuve de beaucoup de zèle dans l’extermination des créatures du Fléau qui croisaient notre chemin, et nous soupçonnions leur famille d’avoir des liens avec cette organisation.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2865;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">JARIEN ET SOTHOS, suite...</H1>\n\n<P>Ils se retrouvèrent donc confrontés aux épreuves réservées à ceux qui souhaitent rejoindre la Croisade. Ils convoitaient le titre de chevalier, ce qui apparemment requiert des épreuves plus compliquées. Nous avons su plus tard que Sothos avait échoué. Jarien refusa de l’admettre, et soutint que son frère devait la rejoindre parmi les chevaliers. Pour cette raison, le Grand croisé Dathrohan finit par les tuer tous les deux au milieu de ses appartements.</P>\n\n<P>Pour cette raison, le Trône cramoisi, au cœur du Bastion écarlate de Stratholme, est un autre lieu hanté.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2866;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">KORMOK</H1>\n<H3 align = "center">Ogre nécromancien bicéphale</H3>\n\n\n<P>Que dire de Kormok ? C’était un ogre bicéphale, de la variété qui lance des sorts, mais il n’était pas mauvais bougre. Il nous a rejoints quand nous l’avons capturé au cours d’un boulot dans les Steppes ardentes. L’affaire a dégénéré, il nous a aidé à nous en tirer, et il a fini par devenir l’un des principaux mages de la compagnie.</P>\n\n<P>Bizarrement, ses deux têtes se comportaient de manière complètement différente. La première était joviale et pas futée, l’autre était matoise et calculatrice, avec un regard mort vaguement effrayant.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2867;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">KORMOK, suite...</H1>\n\n<P>Au cours de nos voyages, Kormok découvrit le versant nécromantique de la magie. Malheureusement, sa tête effrayante avait un don pour ces pratiques. Elle y prenait un plaisir pervers, surtout lorsqu’elle parvenait à faire apparaître les esprits exilés de l’île de la Purification pour les asservir.</P>\n\n<P>Après la dissolution de la compagnie, Kormok entra à la Scholomance pour « s’instruire ». Les assassins spectraux n’eurent aucun mal à pénétrer dans l’école. Ils le tuèrent aux pieds de son nouveau maître, Ras Murmegivre.</P>\n\n<P>La chambre de Ras Murmegivre est l’un des lieux hantés où il est possible d’utiliser le brasero.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2868;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">LE SEIGNEUR VALTHALAK</H1>\n<H3 align = "center">Noble draconique et ancien général du pic Rochenoire</H3>\n\n<P>Avant le général Drakkisath, il y avait le seigneur Valthalak, un démoniste d’une puissance immense. La direction du pic change assez souvent, et malheureusement pour nous, nous avons été impliqués dans les derniers bouleversements.</P>\n\n<P>Nous avons été engagés par un sorcier-contrebandier des environs de Baie-du-Butin, Gremnik Rafistocade, pour voler le livre de sorts de Valthalak. Nous ne savons pas s’il voulait l’étudier, le vendre ou les deux, cela n’a plus d’importance. Nous avons infiltré le sommet du pic et nous avons tué Valthalak dans son repaire. C’est là que ça s’est gâté.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2869;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">LE SEIGNEUR VALTHALAK, suite...</H1>\n\n<P>Nous ignorions que le seigneur Valthalak était passé maître dans la manipulation des âmes. Sinon, nous n’aurions pas pris ce boulot. Mais nous l’avons accepté, et ce fut notre dernier.</P>\n\n<P>Nous venions de prendre son livre de sorts au cadavre de Valthalak lorsque Kormok a senti quelque chose à propos de son amulette. Il a insisté pour qu’elle devienne sa part du butin. Nous nous sommes disputés, et nous avons bien failli en venir aux mains. Nous ne savions pas que l’amulette renfermait une bonne part de l’esprit de Valthalak. Nous l’avons découvert plus tard, dans la douleur.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2870;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">LE SEIGNEUR VALTHALAK, suite...</H1>\n\n<P>Avant de fuir, poursuivis par le fantôme de Valthalak et une armée d’assassins spectraux, nous avons divisé l’amulette en plusieurs morceaux. Notre dispute sur l’amulette avait attiré l’attention des défenseurs restants du pic !</P>\n\n<P>Nous sommes presque tous sortis du pic, et nous avons cru que nous étions à l’abri. Mais même de l’au-delà, Valthalak nous envoyait ses sbires, où que nous nous cachions. La Lame voilée a éclaté peu après cette mission et nous sommes devenus des cibles encore plus faciles.</P>\n\n<P>Comme vous le savez certainement, la résidence du seigneur Valthalak, qui est aujourd’hui la chambre de la Bête dans le pic Rochenoire, est le dernier des lieux hantés.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2871;
UPDATE `locales_page_text` SET `Text_loc2` = 'À tous ceux en état de porter les armes :\n\nLe Fléau est de retour et nous devons oublier nos différends pour faire face à cette menace. L’Aube d’argent ne refusera aucun de ceux qui souhaitent prendre les armes pour lutter contre le Fléau.\n\nLa mobilisation pour la défense de nos contrées s’effectue à la chapelle de l’Espoir de Lumière, dans les Maleterres de l’est. Ensemble, nous vaincrons.\n\nLe gardien des registres\nL’Aube d’argent' WHERE `entry` = 2872;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma chère Emily,\n\nIl y a à peine quelques jours que sur ordre du roi, nous avons levé le camp pour quitter cet endroit perdu et rentrer chez nous. J’avais le cœur léger malgré la désolation environnante, car je savais qu’au bout de la marche glacée jusqu’au rivage et du long et pénible voyage, je trouverais le réconfort de vos bras.\n\nAujourd’hui, nous sommes arrivés sur la côte pour constater que nos bateaux, notre seul moyen de retour, n’étaient plus que des coques carbonisées. Nous ne pouvons plus rentrer et notre seule alternative est de traverser le cœur de cet épouvantable désert.\n\nJe suis allé jusqu’à l’extrême bout du monde pour votre sécurité, Emily... et je regrette à présent de tout mon être de ne pas être resté en Lordaeron avec vous.\n\nJe ne cesse à chaque instant de penser à vous. Vous êtes mon réconfort sur cette terre gelée, la seule parcelle de chaleur que rien ni personne ne peut m’enlever.\n\nMaxwell' WHERE `entry` = 2878;
UPDATE `locales_page_text` SET `Text_loc2` = 'Reuben,\n\nJ’écris cette lettre en sachant que tu ne la liras peut-être jamais. Mais je ne peux pas rester les bras croisés, en écoutant les bruits sourds qui ébranlent constamment les murs d’Âtreval. Les morts-vivants assiègent notre village sans interrompre un instant leurs assauts, et nous avons été chargés de défendre les villageois jusqu’à l’arrivée des renforts.\n\nJe me suis cassé la jambe lors de la dernière charge, et je dois rester assis, inutile, l’épée au côté au cas où nos défenses seraient débordées. On n’entend aucun bavardage… seuls les bruits des combats et de la mort. L’air empeste la peur.\n\nLe prince Arthas est là, combattant sur le front avec les hommes. Sans lui, nous serions tombés depuis longtemps. Son amour pour cette terre et ses habitants est contagieux ; jambe cassée ou pas, je ne peux pas rester ici. Chaque épée est nécessaire. J’espère que tu liras cette lettre en des temps meilleurs.\n\nTon ami,\nLeagrem' WHERE `entry` = 2880;
UPDATE `locales_page_text` SET `Text_loc2` = 'Heliana,\n\nJe suis au regret de vous annoncer que contrairement à vos vœux, je suis encore en vie. Je déteste me traîner dans cette campagne boueuse avec une armure ridicule.\n\nNos ordres nous ont conduit vers Andorhal pour aller chercher du blé, ou un autre truc stupide de ce genre. Je me moque des réserves de nourriture d’Andorhal, ce trou perdu. Toute cette histoire d’engagement dans l’armée est grotesque.\n\nSoyez bien sûre, tante Heliana, que je reviendrais chercher votre héritage. Même si je devais être grièvement blessé et abandonné sans nourriture, je trouverais toujours dans votre contrariété la force de continuer.\n\nVargus' WHERE `entry` = 2881;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma chère Amelia,\n\nCette nuit, j’ai vu des choses qui me hanteront jusqu’à la fin des temps.\n\nStratholme est la proie des flammes et nous en sommes responsables.\n\n\nNotre prince nous a conduit dans les rues, ce soir, nous a ordonné de forcer les portes des maisons et de tuer les habitants dans leur sommeil. Selon le seigneur Arthas, ils étaient contaminés par le Fléau, et nous devions les tuer avant qu’ils ne nous tuent.\n\nCe fut un vrai massacre. Des centaines de personnes sont mortes en silence, passées au fil de l’épée par leurs protecteurs. Je n’ai pas pu le supporter ; je me suis enfui.\n\nJe suis peut-être un déserteur, mais je ne peux commettre de telles atrocités. Je voyais ton visage et celui de nos enfants sur les traits des agonisants. Si refuser d’obéir à ces ordres signifie que je suis un traître, qu’il en soit ainsi.\n\nJ’espère que je trouverai mon chemin pour te rejoindre, mais les routes ne sont pas sûres. Embrasse bien les enfants pour moi.\n\nJames' WHERE `entry` = 2882;
UPDATE `locales_page_text` SET `Text_loc2` = 'Samla,\n\nNous suivons le seigneur Arthas vers le nord, encore et encore, et mon cœur s’alourdit de jour en jour. La Lumière resplendissait autrefois sur le jeune paladin, mais je sens aujourd’hui que son esprit s’obscurcit. Son zèle est terni par de sombres ruminations, un cauchemar dans son âme que je ne peux deviner…\n\nNous allons bientôt débarquer  dans les steppes gelées. Alors qu’un grand nombre de ses hommes tombent malades à cause du froid et des batailles qu’ils mènent contre des bêtes putrides, Arthas nous a affirmé que ce qu’il cherchait dans les glaces changera le cours de la bataille. Mais ses paroles ne m’ont pas réconforté... Le sourire sinistre figé sur ses lèvres dédaigneuses après son discours m’a glacé plus profondément que ne l’aurait fait n’importe quel blizzard.\n\nPrie pour nous, Samla, et prie pour notre monde.\n\nTorgal' WHERE `entry` = 2883;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma chère Pamela,\n\nDemain, nous livrerons enfin bataille à Andorhal, et j’ai bien peur que ce soit la dernière lettre que je puisse t’envoyer, mon amour. Les morts-vivants ne se fatiguent jamais, et nous ne sommes que de pauvres êtres humains. Nous ne pourrons pas tenir contre eux. Mais ne te fais pas trop de souci, car même si nous sommes certains de périr, nous gardons espoir. La Lumière vaincra !\n\nEt, mon cœur, je trouve un grand réconfort en sachant que lorsque ces sombres terreurs emporteront nos murs et nous écraseront sous leur nombre, mes dernières pensées ne seront pas tournées vers la mort. Je penserai à toi dans mes bras, et je connaîtrai enfin la paix.\n\nChristoph' WHERE `entry` = 2884;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salut, $N. Eh bien, il semblerait que j’aie réussi à dénicher quelques informations à partir de cette note.\n\nJe crains de ne pas avoir trouvé grand-chose sur James. Cependant, j’ai fait quelques découvertes sur Amelia.\n\nNi elle ni ses enfants n’ont survécu à la guerre, mais son frère, un certain Alexander Calder, est toujours en vie. Vous pourrez le trouver dans la Caverne lugubre de Forgefer, où il étudie la dangereuse magie de la Légion ardente.\n\nAmelia avait aussi une cousine dont elle était très proche, nommée Deze Plaie-des-neiges, qui recrute pour la bataille du bassin d’Arathi à Orgrimmar.\n\nVous devriez rendre visite à l’un d’entre eux quand vous sera dans le coin. Je suis certain qu’ils seront contents d’entendre parler de leur défunte Amelia, à défaut de mieux.' WHERE `entry` = 2885;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien le bonjour, $N. Je pense que vous serez $gintéressé:intéressée; par les résultats de mes recherches.\n\nIl semblerait que des membres de la famille de Reuben et de Leagrem aient survécu... Enfin, c’est une façon de parler. Même s’ils connaissent déjà le destin des soldats tombés, leur rendre visite en vaudra la peine.\n\nVous pourrez trouver l’ex-épouse de Reuben, Joanna Blancmanoir, dans le Quartier de la guerre de Fossoyeuse, où elle passe le plus clair de son temps. Je ne sais pas trop si elle appréciera vraiment de recevoir des nouvelles, mais vous verrez bien…\n\nQuant à Leagrem, sa tante est devenue la gouvernante de l’orphelinat de Hurlevent, un poste admirable.\n\nQuelle que soit la voie que vous suivrez, je vous souhaite bonne chance dans votre entreprise.' WHERE `entry` = 2886;
UPDATE `locales_page_text` SET `Text_loc2` = '$N, j\'espère que cette lettre vous trouvera en bonne santé. Après avoir fouillé dans mes archives, j\'ai découvert quelques informations sur les personnes qui ont écrit la note que vous avez récupérée.$B$BLe père de Samla dirige les Réprouvés et les trolls qui font le pèlerinage vers les Pitons du Tonnerre, en digne représentant de sa foi. Il se nomme Miles Welsh, et vous pourrez le trouver avec ses amis morts-vivants aux Bassins de la vision.$B$BLa cousine de Torgal, Elissa Dumas, a survécu. D\'après ce que j\'ai compris, elle mène une existence de recluse, et passe ses journées au Temple de la lune à Darnassus.$B$BJe vous conseille de rendre visite à l\'un d\'entre eux, $N. Ils n\'ont peut-être jamais appris ce qui était arrivé à Torgal ou Samla.$B$B' WHERE `entry` = 2887;
UPDATE `locales_page_text` SET `Text_loc2` = '“Le Gardien m’a transmis la lettre que vous avez découverte, $N, et j’ai pensé que vous apprécieriez peut-être quelques nouvelles.\n\nOn a retrouvé la trace du dernier survivant de la famille du "Vargus" mentionné dans le courrier en Silithus, au Fort cénarien. Il se nomme Garon Hutchins.\n\nVous devriez lui parler durant votre prochaine excursion dans le désert. Il sera peut-être heureux que vous lui relatiez ce que nous avons appris."' WHERE `entry` = 2888;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salutations. J’ai enfin trouvé le temps d’examiner la missive que vous avez trouvée sur le Fléau. Bien que cela m’ait donné un peu de fil à retordre, je pense avoir découvert une parente du défunt encore en vie.\n\nLa sœur de Maxwell, une certaine Zarena Cromwind, dirige une petite affaire de vente d’armes à Baie-du-Butin. Je ne sais pas vraiment si elle était très proche de son frère, mais si vous passez dans la région, vous devriez lui rendre une petite visite. Pouvoir mettre un point final à ce genre d’affaire est une chose précieuse et rare, de nos jours.' WHERE `entry` = 2889;
UPDATE `locales_page_text` SET `Text_loc2` = 'J’ai enfin réussi à déterrer quelques informations sur l’ancien soldat que vous avez combattu. La piste était difficile à suivre, car cette branche de l’arbre généalogique de la famille n’était pas à jour. Mais je suis tout de même parvenu à trouver un de ses parents.\n\nAuriez-vous cru que le conservateur Alen, à la chapelle de l’Espoir de Lumière, est en réalité le fils de Christoph ? Recevoir des nouvelles de son père pourrait l’apaiser. Si vous vous retrouvez une nouvelle fois au service de l’Aube d’argent dans les Maleterres, je vous en prie, allez lui parler. Ce serait un acte de pure bonté.' WHERE `entry` = 2890;
UPDATE `locales_page_text` SET `Text_loc2` = 'À la mémoire du commandant suprême de l’Alliance, Anduin Lothar. Un homme prêt à tout sacrifier pour défendre son roi, son peuple, son foyer...\n\nQue nos ennemis se souviennent de nos noms. Que nos alliés honorent notre mémoire. Nous sommes les fils de Lothar.\n\n- Général Turalyon' WHERE `entry` = 2891;
UPDATE `locales_page_text` SET `Text_loc2` = 'En mémoire de notre cher Jesse Morales' WHERE `entry` = 2892;
UPDATE `locales_page_text` SET `Text_loc2` = 'Voici une jarre de cendres. Ce sont les cendres de ma raison, de ma passion, et de mon énergie. Toutes choses qui se sont elles-mêmes entièrement détruites. Que tous ceux qui contemplent les paysages désolés des flammes infernales se rappellent de ce péon disparu. Il a donné son sang pour l\'Alliance, il s\'est sacrifié pour la Horde, pour finalement sombrer dans la folie en voyant les viles créatures sans âmes dévorer ce qu\'il avait de plus cher. Puissent-ils subir sa colère comme ils se sont repus de ses labeurs. Peut-être pas dans ce monde, mais dans tous ceux qui suivront. Je le proclame, j\'en fais solennellement le serment, je le promets pour l\'éternité. Je vengerai mes souffrances.' WHERE `entry` = 2905;
UPDATE `locales_page_text` SET `Text_loc2` = 'Inspectez la totalité des îles et revenez me faire votre rapport.$B$BSouvenez-nous, ne vous faites pas prendre !$B$BNe me décevez pas, Candress !$B$BS.' WHERE `entry` = 2906;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le dessin dépeint Shadra’Alor et ses trois temples, avec un certain nombre de marques en forme de « X » correspondant à l’emplacement des œufs volés. Les marques apparaissent à l’extrémité sud du temple oriental, près d’une hutte au nord-est du temple oriental, près d’un autel à l’est du lac central, et sur la pente du temple méridional près du mot « exilé ».\n\nLes notes de Barbe-de-plumes indiquent qu’il avait l’intention de livrer ces informations à Falstad Marteau-hardi. Il avait bon espoir que ce dernier envoie une troupe de guerriers pour récupérer les œufs.' WHERE `entry` = 2907;
UPDATE `locales_page_text` SET `Text_loc2` = 'Diathorus,\n\nCette lettre devrait bien finir par te parvenir. Ces démons n\'ont pas de processus cognitif supérieur. Ils n\'ont en tête que leurs ongles, leurs fouets, ou leur désir d\'éventrer quelque chose avec leurs pointes frontales. Et je n\'ose pas faire appel à un infernal, de crainte qu\'il ne soit réduit en cendres !\n\nHélas, je songe avec regret aux jours heureux où nous pouvions utiliser des orcs. Au moins ils avaient un minimum de jugeote et ils savaient obéir aux ordres. Peut-être existe-t-il une opportunité de trouver quelques nouveaux esclaves à proximité ?' WHERE `entry` = 2909;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les rapports sur les elfes de la nuit portant secours aux draeneï sont vrais. Ils essayent d\'établir une nouvelle base au Chant des forêts, au nord-est de Satyrnaar. Nous devrions nous entendre pour combiner nos forces, et attaquer avant la prochaine pleine lune de ce monde misérable. Si nous les écrasons maintenant, ils n\'arriveront jamais à mettre un pied sur le seuil de notre porte !\n\nCordialement,\n\nGorgannon\n\nP.S. : Cessez d\'utiliser toute l\'encre de sang pour écrire des lettres d\'amour à mes flagellantes ! De nos jours, les vierges se font rares chez les elfes de la nuit.' WHERE `entry` = 2910;
UPDATE `locales_page_text` SET `Text_loc2` = 'Diathorus,\n\nCette lettre devrait bien finir par te parvenir. Ces démons n’ont pas de processus cognitif supérieur. Ils n’ont en tête que leurs ongles, leurs fouets, ou leur désir d’éventrer quelque chose avec leurs pointes frontales. Et je n’ose pas faire appel à un infernal, de crainte qu’il ne soit réduit en cendres !\n\nHélas, je songe avec regret aux jours heureux où nous pouvions utiliser des orcs. Au moins ils avaient un minimum de jugeote et ils savaient obéir aux ordres. Peut-être existe-t-il une possibilité de trouver quelques nouveaux esclaves à proximité ?' WHERE `entry` = 2911;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au seuil de la défaite,$BIl garde son courage,$BTire l\'épée d\'une traite,$BEt fonce en plein carnage.$B$BDepuis ce jour sauvage,$BFlèche ou lame, pas moyen$BDe trouver un passage$BJusqu\'au seigneur des nains.' WHERE `entry` = 2913;
UPDATE `locales_page_text` SET `Text_loc2` = '« … mais elle le savait déjà. Elle s\'était résignée à vivre en paria après avoir marchandé son âme, fragment par fragment. La vengeance en valait la peine, ou tout du moins, c\'est ce qu\'elle pensait.$B$BMais elle avait maintenant à sa disposition une occasion de regagner son humanité, et tout ce que l\'étranger lui demandait en échange, c\'était de lui faire confiance.$B$B"Je le ferai, avait dit Katreyn. Je n\'ai rien à perdre." »' WHERE `entry` = 2914;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vazruden,\n\nDepuis que la Porte des ténèbres a été rouverte, le bastion de l’Honneur et Thrallmar ont reçu d’importants renforts venus d’Azeroth. J’ai même lu des rapports suggérant qu’ils préparent une attaque contre la citadelle, alors que nous avons déjà à faire face à la Légion ardente. C’est inacceptable !\n\nNe mettez pas en péril l’opération à la Fournaise du sang ! Les gangr’orcs produits là-bas restent la clé de notre succès. Fortifiez les défenses des remparts et placez immédiatement plus de gardes sur le périmètre !\n\nCroyez-moi, vous n’avez pas envie que je vienne vous rendre visite.\n\n— Illidan.' WHERE `entry` = 2916;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vazruden,\n\nDepuis que la Porte des ténèbres a été rouverte, le bastion de l’Honneur et Thrallmar ont reçu d’importants renforts venus d’Azeroth. J’ai même lu des rapports suggérant qu’ils préparent une attaque contre la citadelle, alors que nous avons déjà à faire face à la Légion ardente. C’est inacceptable !\n\nNe mettez pas en péril l’opération à la Fournaise du sang ! Les gangr’orcs produits là-bas restent la clé de notre succès. Fortifiez les défenses des remparts et placez immédiatement plus de gardes sur le périmètre !\n\nCroyez-moi, vous n’avez pas envie que je vienne vous rendre visite.\n\n—Illidan.' WHERE `entry` = 2917;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'est moi qui l\'ai fait. Je ne m\'en souviens pas, mais cela n\'a pu se passer qu\'ainsi.\n\nJ\'ai déjà commis de nombreuses fautes au cours de mon existence. J’ai même tué un homme qui ne l’avait pas mérité. Mais ceci est différent.\n\nJ’ai changé.\n\nJe n’ai pas choisi de commettre ces meurtres. Une bête vit en moi.\n\nJe serai pourchassé et ostracisé. Il faut que je me cache. Que je m’enfuie. Que j’enterre les corps.\n\nJ’ai commencé à marcher dehors quand j’ai compris ce qui me troublait vraiment.\n\nJe n’éprouve aucun remords.' WHERE `entry` = 2918;
UPDATE `locales_page_text` SET `Text_loc2` = 'Serviteur,\n \nLes projets conçus depuis des années sont anéantis. Les draeneï et leur pitoyable chef ont une fois de plus échappé au Maître ! Pour couronner le tout, ils ont volé MON vaisseau et l\'ont fait s\'écraser sur Azeroth ! \n\nL\'agent érédar du Maître a certes échoué, mais elle a pu nous transmettre la position de l\'Exodar. Par chance, son identité est toujours inconnue des draeneï. Nous pouvons encore l\'utiliser pour accomplir nos objectifs.' WHERE `entry` = 2919;
UPDATE `locales_page_text` SET `Text_loc2` = 'Contactez notre espionne et préparez une attaque à grande échelle contre les draeneï. Tuez-les tous et reprenez possession de mon vaisseau. Vous êtes mon seul contact sur Azeroth qui soit capable de réparer les dégâts que ces brutes ont causés sur l\'Exodar.\n\nN\'échouez pas, gnome. Si votre désir de richesses allait à l\'encontre de mes ordres, vous passeriez l\'éternité à prier. À prier pour que la mort vienne et vous délivre des tortures que je vous infligerai si vous me décevez.\n\n-Roi Haut-soleil' WHERE `entry` = 2920;
UPDATE `locales_page_text` SET `Text_loc2` = 'Très estimés chefs de la Horde,\n\nMoi, Lor’themar Theron, seigneur régent de Quel’Thalas, ai le grand plaisir de vous faire part de la chute du commandant du Fléau connu sous le nom de Dar’Khan Drathir.\n\nLe fait que Dar’Khan ait pu maintenir une tête de pont sur nos terres a servi à de nombreuses reprises d’argument à ceux qui critiquaient les sind’orei lors de précédentes négociations. Nous espérons que cet événement abolira une fois pour toutes les éventuelles objections qu\'auraient pu entretenir Vos Grâces.\n\n<Signé>Lor’themar Theron' WHERE `entry` = 2923;
UPDATE `locales_page_text` SET `Text_loc2` = 'Très estimés chefs de la Horde,\n\nMoi, Lor’themar Theron, seigneur régent de Quel’Thalas, ai le grand plaisir de vous faire part de la chute du commandant du Fléau connu sous le nom de Dar’Khan Drathir.\n\nLe fait que Dar’Khan ait pu maintenir une tête de pont sur nos terres a servi à de nombreuses reprises d’argument à ceux qui critiquaient les sind’orei lors de précédentes négociations. Nous espérons que cet événement abolira une fois pour toutes les éventuelles objections qu\'auraient pu entretenir Vos Grâces.\n\n<Signé>Lor’themar Theron' WHERE `entry` = 2924;
UPDATE `locales_page_text` SET `Text_loc2` = 'P.S. - Concernant la question de Thrall, notre Grand magistère a reçu des informations d’Outreterre. La réponse est assurément oui..' WHERE `entry` = 2925;
UPDATE `locales_page_text` SET `Text_loc2` = 'P.S. - Concernant la question de Thrall, notre Grand magistère a reçu des informations d’Outreterre. La réponse est assurément oui..\n\n<Cette lettre porte aussi le sceau de dame Sylvanas Coursevent.>' WHERE `entry` = 2926;
UPDATE `locales_page_text` SET `Text_loc2` = '$N,\n\nLa création de la corsèque à la trempe de sang nécessite plusieurs matériaux rares et instables, qui ensemble donnent une arme redoutable d\'excellente facture. Je vais faire une liste des matériaux, du plus facile au plus difficile à obtenir.\n\nN\'oubliez pas que vous devez aussi vous procurer votre insigne de chevalier de sang pour que les matériaux soient acceptés et que le processus de fabrication puisse commencer.' WHERE `entry` = 2930;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sang du Courroucé - Pour tremper comme il convient le métal de votre arme, il faut la plonger dans le sang du Courroucé. Bien que le sang de la plupart des démons contienne une quantité substantielle de puissance, le sang ordinaire ne suffirait pas pour forger votre corsèque.\n\nDes enquêtes discrètes auprès des démonistes d\'Orgrimmar se sont montrées fructueuses pour situer une source de sang. Les sectateurs de la Lame brûlante à l\'intérieur du Gouffre de Ragefeu possèdent un orbe rempli du type de sang qu\'il nous faut pour votre arme. On m\'a dit que l\'orbe est gardé par un orc qui se fait appeler Jergosh l\'Invocateur.\n\nMontrez-vous $gprudent:prudente;, comme toujours lorsque vous approchez les puissances démoniaques. On ne sait jamais ce qui peut arriver lorsqu\'on manipule une substance si puissante.' WHERE `entry` = 2931;
UPDATE `locales_page_text` SET `Text_loc2` = 'Caisse de lingots de forge-sang - Seul, l\'acier est un très bon matériau, mais une arme forgée dans ce métal est quelconque. Les lingots de forge-sang représentent un excellent matériau créé selon un procédé dont le secret est bien gardé. La rumeur dit que tout forgeron assez téméraire pour manipuler et travailler ce métal est frappé d\'une malédiction, mais nos artisans ont prouvé qu\'il n\'en est rien.\n\nLa réputation de ce métal en a accru la rareté, mais les Réprouvés ont signalé qu\'un stock pourrait exister à l\'intérieur du donjon d\'Ombrecroc. Un talentueux forgeron du nom de Landen Morpuits était assez curieux pour introduire une caisse de lingots dans le donjon. On ignore où Morpuits la cache, mais notre informateur nous dit que le forgeron est détenu prisonnier dans les cachots du donjon. Employez tous les moyens à votre disposition pour obtenir les lingots.' WHERE `entry` = 2932;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une gemme de Kor corrompue - Les tomes des magistères rapportent que ces gemmes sont utilisées par certains lanceurs de sorts pour contenir l\'énergie dont leur magie a besoin, ce qui la rend plus mortelle encore. Des versions purifiées de la gemme peuvent être utilisées sans danger par le commun des lanceurs de sorts, mais elles sont trop faibles pour nos desseins.\n\nDans les cavernes des Profondeurs de Brassenoire, des lanceurs de sorts naga ont imprégné des gemmes de Kor de leurs propres sombres pouvoirs, amplifiant leur puissance et les rendant ainsi parfaites pour votre corsèque. Tuez-les sans pitié jusqu\'à ce que vous ayez obtenu la gemme dont vous aurez besoin.' WHERE `entry` = 2933;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'insigne que vous porterez en tant qu\'adepte a été porté en dernier par un chevalier de sang courageux et sans peur de la famille Aubétoile. Son dévouement exemplaire est de bon augure pour votre propre vie au sein de l\'ordre.\n\nLe chevalier de sang Aubétoile mena une attaque audacieuse, qui hélas échoua, sur la forteresse du Fléau de Mortholme. Son corps repose là où il mourut, enfermé avec nos pires ennemis. Trouvez-le, $N, et rapportez-moi son insigne. Votre succès honorera ce chevalier, ainsi que vous-même et tous les vrais chevaliers de sang.' WHERE `entry` = 2934;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vous avez découvert l\'emplacement du sanctuaire ! En procédant à un examen plus approfondi, vous sentez battre le pouls de l\'étrange puissance qui s\'est emparée de l\'île. Vous vous sentez mal à l\'aise près du sanctuaire... et peut-être un peu $gtroublé:troublée;.\n\nUn panneau de bronze, sur le côté du monument, dit :\n\n"Modeste tribut à un noble elfe, ce sanctuaire est dédié à la mémoire de Dath\'Remar. Que tous ceux qui le contemplent se souviennent de son sacrifice face au Fléau et de sa dévotion à la cause de notre survie. Tous ceux qui prospèrent en Quel\'Thalas ont une dette envers lui."' WHERE `entry` = 2936;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je les observais pendant qu\'ils le battaient sans pitié. Leur brute de lieutenant se comportait comme si tant de cruauté était complètement normal. Comme les coups pleuvaient, je sentais que je craquais.$B$BLe redresseur de torts Saruan encaissait tout ça, et je me suis mis à pleurer. Je n\'avais pas peur... j\'étais triste. Voir un triumvir draeneï traité de la sorte. Si seulement j\'avais pu me libérer de mes liens. Si seulement..' WHERE `entry` = 2937;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après ce qui m\'a semblé une éternité, le redresseur de torts a perdu conscience. La douleur était trop forte, même pour lui. Le plus cruel, que les autres appelaient Matis, a essayé de réveiller Saruan en éclaboussant son corps brisé d\'eau croupie par la contagion. Mais Saruan n\'a pas repris conscience.' WHERE `entry` = 2938;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je voulais hurler mais seul un faible hoquet est sorti de ma bouche. J’ai aussitôt reçu une volée de coups pour cet acte de rébellion. Je me suis retrouvé étendu au sol, et j’ai vu que Matis faisait les cent pas devant le corps de Saruan. Il avait l’air visiblement inquiet. Il a dit que Sironas, ou Sirona, ou une entité de ce type lui arracherait la chair des os si Saruan était mort à cause d’eux.' WHERE `entry` = 2939;
UPDATE `locales_page_text` SET `Text_loc2` = 'Que cette entité Sironas avait des projets pour Saruan…\n\n<L’écriture s’arrête brusquement sur une longue ligne en travers de la page. Ce draeneï est probablement mort pendant qu’il était en train d’écrire.>' WHERE `entry` = 2940;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dame Liadrin,\n\nLes récents incidents concernant la créature captive ont endommagé une partie de notre équipement parmi le plus important. Pour reprendre contrôle sur elle, à la fois mes magistères et mes ressources ont été durement sollicités. J\'ai envoyé des rapports au sujet de l\'incident au Grand magistère Rommath, mais il tarde à répondre. Je crains que sans les matériaux suivants, nous ne soyons contraints de réduire nos opérations, et vous savez ce que cela signifierait pour vos chevaliers.\n\nAu cas où vous pourriez faire quelque chose pour nous aider, j\'ai inclus une liste des matériaux sur la page suivante. Toute assistance que vous ordre pourrait fournir serait grandement appréciée.\n\nMagistère Astalor Ligessang' WHERE `entry` = 2941;
UPDATE `locales_page_text` SET `Text_loc2` = '* 40 Etoffes runiques\n* 6 Barres d\'arcanite\n* 10 Soleillettes\n* 5 Runes ténébreuses\n* 150 pièces d\'or' WHERE `entry` = 2942;
UPDATE `locales_page_text` SET `Text_loc2` = 'Déclaration du roi-soleil\n\nÉperviers du soleil, vous mes précieux et fidèles enfants, votre roi vous félicite. Votre présence sur Azeroth est de la plus haute importance pour moi-même et pour le Maître.\n\nBientôt, le portail du soleil sera prêt et pourra transporter directement les renforts du Donjon de la tempête vers l’île de Brume-sang.\n\nNous récupérerons l’Exodar et nous massacrerons les bâtards à la peau bleue partout où nous les trouverons. Velen paiera pour ses crimes.\n\n- Roi Kael\'thas Haut-soleil.' WHERE `entry` = 2943;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<IMG src = "Interface\\Pictures\\24475_gordawg_256"/>\n</BODY>\n</HTML>' WHERE `entry` = 2944;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<BR/>\n<H1 align = "center">LES VERTES COLLINES DE STRANGLERONCE</H1>\n<BR/>\n<H3 align = "center">par Hemet Nesingwary</H3>\n</BODY>\n</HTML>' WHERE `entry` = 2945;
UPDATE `locales_page_text` SET `Text_loc2` = 'Notre première journée s\'est aussi bien déroulée qu\'on pouvait l\'espérer. Nous avons surtout oeuvré à préparer l\'établissement d\'un campement. J\'ai repéré un emplacement idéal près d\'une rivière. À en juger par les vieux quais abandonnés qui s\'y trouvent, ce site a été habité naguère. Quant aux anciens habitants, le temps nous dira peut-être ce qu\'ils sont devenus.' WHERE `entry` = 2946;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pour cette expédition, j\'ai réuni Ajeck Rouack et sire S. J. Erlgadin, ainsi que mon fidèle serviteur Barnil Potepierre. J\'ai combattu aux côtés du père d\'Ajeck dans bien des batailles au service de l\'Alliance. La voir grandir a été quelque chose de très particulier. Son père l\'a bien formée dans les arts du maniement d\'armes. Et en voyant son habileté à l’arc, je me demande si elle n’a pas du sang elfe dans les veines.' WHERE `entry` = 2947;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sire S. J. Erlgadin est un aristocrate humain. Son père, le comte Erlgadin, était célèbre pour sa générosité. C\'est le comte qui s\'est battu pour améliorer les conditions de travail de la Guilde des Maçons pendant la reconstruction de Hurlevent après la Deuxième guerre.' WHERE `entry` = 2948;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dans les années qui suivirent, après la trahison de la Guilde des Maçons par Hurlevent, sire Erlgadin devint aigri au sujet du rôle des nobles du royaume. Il ne souhaitait plus conserver la position que la lignée de son père lui réservait à la Maison des Nobles. Mais trêve de digressions. Le but de cette histoire n\'est pas de jouer au traité de science politique ni à la biographie. C\'est la narration de mes expériences de chasse dans les vertes vallées de Strangleronce.' WHERE `entry` = 2949;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous nous sommes levés à l\'aube. Barnil a commencé à préparer le repas du matin. J\'ai constaté qu\'Ajeck semblait distraite. La marche serait longue ce jour-là et la chasse s\'annonçait dangereuse. Il était vital que chacun reste concentré. Mais Ajeck me semblait incapable de détacher ses yeux de Barnil qui nettoyait ses affaires dans la rivière.' WHERE `entry` = 2950;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alors que j\'allais interroger Ajeck sur son manque d\'intérêt pour la stratégie de ce jour de chasse, elle s\'est précipitée vers son carquois, a sorti une flèche et a tiré en direction du pauvre Barnil. Mais ce n\'est pas Barnil qu\'Ajeck visait. Lorsque Barnil s\'est écarté, bouche bée, un grand crocilisque des rivières flottait à la surface, la flèche d\'Ajeck parfaitement plantée entre ses deux yeux.' WHERE `entry` = 2951;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous sommes partis vers l\'ouest, à travers la jungle épaisse. Marchant lentement et précautionneusement, nous avons cherché nos proies dans l\'épaisse frondaison. La matinée s\'est écoulée dans un silence frustré. Rien ne frémissait dans la vallée, pas même la brise. Au début de l\'après-midi, nous étions fatigués et Barnil avait abandonné la démarche silencieuse du chasseur de proies. Il arpentait la région d\'un pas lourd, faisant craquer les feuilles mortes et les brindilles.' WHERE `entry` = 2952;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au cours d\'un de ces faux-pas, Erlgadin a posé lourdement la main sur l\'épaule de Barnil. Ajeck et moi nous sommes lancé un regard, pensant qu\'il voulait simplement tancer Barnil. Mais Erlgadin a fait un geste lent de la tête vers un arbre mort, non loin. Deux yeux noirs nous fixaient au-dessus d\'une gueule emplie de crocs acérés.' WHERE `entry` = 2953;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'était un tigre de Strangleronce, un mâle. Avant que j\'aie le temps de relever mon arme, Erlgadin a pointé son arbalète vers la bête et a fait feu. Le trait a manqué la tête et a mordu la bête dans le flanc gauche. Le tigre a fait une puérile tentative pour fuir, mais la blessure était trop grave. Il a oscillé quelques secondes et Barnil l\'a achevé d\'un coup de hache.' WHERE `entry` = 2954;
UPDATE `locales_page_text` SET `Text_loc2` = 'La mise à mort a créé une atmosphère joviale parmi les membres de l\'expédition. Barnil a servi de l\'hydromel à tous. Mais la bonne humeur n\'a pas duré. Alors que nous préparions le cadavre pour le rapporter au campement, nous avons été surpris par un horrible grognement. De toute ma vie je n\'ai jamais rien entendu qui soit plus de nature à glacer le sang.' WHERE `entry` = 2955;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sur un précipice rocheux découpé par le soleil couchant, j\'ai aperçu le plus gros félin que l\'on puisse imaginer. J\'ai tiré une salve maladroite avec mon arme, mais le félin n\'a pas bougé. Il a grogné à nouveau, plus fort que la première fois, et il a disparu.\n\nNous avons rassemblé nos affaires et nous sommes rentrés au campement.' WHERE `entry` = 2956;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'avais promis à l\'équipe que nous passerions le jour suivant à chasser des panthères, car leur fourrure est très chère à Azeroth. Cela est juste, vu le nombre de trappeurs et de chasseurs qui ont perdu la vie au service de l\'Alliance.' WHERE `entry` = 2957;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin étaient désireux d\'apprendre à chasser efficacement avec un fusil nain. J\'ai conseillé aux deux humains de laisser leurs armes primitives au campement. Barnil et moi leur avons fourni des armes à feu parmi les meilleures de Forgefer.' WHERE `entry` = 2958;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ce jour-là, nous nous sommes aventurés vers le sud, à la suite de traces de panthère fraîches. Nous sommes arrivés à un ravin traversé par un immense pont de corde. Je n\'ai pu m\'empêcher de penser aux descriptions de Brann sur la région, quand j\'ai aperçu cette merveille surplombant le vide. On pense si souvent que les trolls sont une race primitive et sans éducation, mais en observant la parfaite construction du pont je ne pus m\'empêcher d\'admirer l\'habileté avec laquelle les bâtisseurs trolls avaient vaincu cette difficulté apparemment insurmontable.' WHERE `entry` = 2959;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien vite, Ajeck repéra les traces d\'une panthère vers le sud-ouest. Nous avons avancé rapidement, les armes prêtes, avides de rencontrer notre proie. Un craquement de branchages a attiré notre attention. Il y avait quelque chose par là. Un coup d\'œil rapide à Barnil a confirmé mes suppositions. Barnil a lentement relevé son fusil. Cette proie n\'était pas pour nous, mais pour nos compagnons humains. D\'innombrables panthères avaient perdu la vie au bout de nos canons. C\'était au tour des humains.' WHERE `entry` = 2960;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin restaient aux aguets, canon levé vers le feulement qui provenait de la frange mouvante des arbres. Le soleil de midi tapait dur sur nos épaules. Un mince filet de sueur coulait le long des tempes d\'Erlgadin lorsqu\'il a relevé le chien de son arme. Au cliquetis qui a retenti, la dense végétation s\'est ouverte et une panthère noire, un spécimen magnifique, est entrée dans la clairière.' WHERE `entry` = 2961;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les humains ont pointé leur arme sur la panthère qui courait le long de la ligne des arbres. Les canons se déplaçaient en parallèles parfaites. Barnil m\'a lancé un coup d\'œil pressant mais j\'ai fait non de la tête. Cette chasse était pour les humains, pas pour Barnil ni pour moi. Erlgadin a tiré, mais a manqué la cible. Il n\'était visiblement pas préparé au violent recul de l\'arme.' WHERE `entry` = 2962;
UPDATE `locales_page_text` SET `Text_loc2` = 'Son arme a failli lui échapper des mains. Le canon a dévié et est venu se placer sous le fusil d\'Ajeck. Ajeck a choisi ce moment précis pour tirer. Le coup est parti avec sa détonation caractéristique, mais dans la direction de la ligne d\'arbres. Des oiseaux se sont envolés dans toutes les directions avec des cris plaintifs. Un filet de fumée s\'est élevé de l\'arbre qui avait été touché. Stupéfaits, nous avons vu une énorme branche tomber sur la panthère en pleine course, lui brisant la colonne vertébrale.' WHERE `entry` = 2963;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au fur et à mesure que les semaines passaient, notre stock de peaux de panthère et de tigre grandissait. Je décidai qu\'il était temps pour l\'expédition de se concentrer sur un nouveau défi : les raptors.\n\nLes humains, tout en appréciant l\'expérience de Barnil et la mienne, décidèrent de ne pas se servir d\'armes à feu. Ajeck se sentait plus à l\'aise avec un arc et sire Erlgadin ne quittait jamais le camp sans une solide arbalète.' WHERE `entry` = 2964;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous avons levé le camp à l\'aube et sommes partis vers le sud en traversant les ruines de Tkashi. Barnil craignait qu\'on ne rencontre des membres de la tribu Scalp-rouge. J\'ai rappelé à Barnil que les Scalps-rouges se concentraient surtout sur la destruction de leurs rivaux, les Casse-crâne. Cela, bien sûr, n\'a pas rassuré Barnil le moins du monde. Mais j\'avais un fusil chargé, des munitions, et trois chasseurs expérimentés pour me réconforter contre la menace d\'une embuscade.' WHERE `entry` = 2965;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je me suis déjà tenu devant un immense infernal sur le champ de bataille, tandis que l\'armée de la Légion ardente avançait de toutes les directions. Une bande de trolls sans chef est aussi inoffensive qu\'un lapin dans les collines de Dun Morogh.\n\nNous avons traversé les ruines de Tkashi sans incident, au grand soulagement de Barnil. Le groupe s\'est ensuite dirigé vers l\'ouest, en direction de la Grande Mer, longeant les ruines de Zul\'Kunda au sud. C\'est alors que nous montions les escarpements du rivage que nous avons repéré notre premier raptor.' WHERE `entry` = 2966;
UPDATE `locales_page_text` SET `Text_loc2` = 'La bête ne nous a pas entendus approcher. Tout ce qu\'elle a reçu, c\'est une balle entre les yeux.\n\nSire Erlgadin a laissé échapper un cri de joie tandis qu\'Ajeck me lançait un coup de tête approbateur. J\'ai cherché ma pipe dans mon sac, pour célébrer cette victoire. Barnil a filé vers le haut de la colline pour retrouver le cadavre du raptor. J\'ai regardé la bête morte avec la satisfaction qui accompagne chaque joli tir.' WHERE `entry` = 2967;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mais je n\'ai pas profité longtemps de ce moment de gloire. Quand j\'ai tourné les yeux vers l\'horizon, j\'ai vu plusieurs silhouettes apparaître au sommet de la colline, juste au-dessus du pauvre Barnil.\n\n*Fuyez, Barnil !* ai-je hurlé. Ajeck, sire Erlgadin et moi avons envoyé une salve de balles et de flèches vers les nouveaux raptors. Dans la confusion, nous en avons abattu un autre.' WHERE `entry` = 2968;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nos tirs mal ajustés ont suffi à couvrir la fuite de Barnil. Il a dévalé la colline pour nous rejoindre. Nous nous sommes précipités dans la jungle, entraînant derrière nous une meute de raptors flagellants.\n\nLes chasseurs étaient désormais les proies.' WHERE `entry` = 2969;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai mené le groupe jusqu\'à la mer, espérant que le rivage nous fournirait une protection contre les raptors. Dans notre hâte, nous sommes allés trop loin vers le nord, jusqu\'à un promontoire dangereusement escarpé. Erreur fatale. Ma faute. Nous nous sommes retrouvés bloqués sur une corniche, les raptors à quelques pas derrière nous.' WHERE `entry` = 2970;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je fis lentement un pas en avant, arme levée. J\'avais conduit ces braves chasseurs à leur mort. J\'étais prêt à mourir en tentant de les défendre. Les raptors flagellants sont particulièrement féroces, réputés pour leur inextinguible soif de sang. Ils étaient beaucoup plus nombreux que nous. Mais je n\'allais pas les laisser me tuer ainsi que mes camarades sans verser d\'abord un peu de leur sang.' WHERE `entry` = 2971;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ajeck et sire Erlgadin ont préparé leurs armes, un à chacun de mes côtés, nos dos tournés vers la mer. Barnil a laissé échapper un soupir et a levé sa hache. Les flagellants étaient presque sur nous. Ils avançaient moins vite. Ils traquaient leurs proies maintenant qu\'ils savaient qu\'ils nous avaient piégés.' WHERE `entry` = 2972;
UPDATE `locales_page_text` SET `Text_loc2` = 'Et le miracle a eu lieu. Loin sur notre flanc, nous avons entendu le rugissement caractéristique et terrifiant du grand tigre blanc. Malgré leur nombre, les raptors ont fait demi-tour et se sont dispersés dans toutes les directions. Nous avons vu un bref éclair blanc : le tigre passait devant nous et se précipitait sur l\'un des raptors. Inutile de donner des ordres : nous savions tous que l\'heure de fuir avait sonné.' WHERE `entry` = 2973;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous avons foncé jusqu\'au campement, sans ralentir. Au cours de la nuit, assis silencieusement autour du feu de camp, nous avons médité sur l\'étrange destin qui venait de nous sauver la vie. Tels sont les risques de la chasse. Nous jouons avec le destin. Chacun de nous, à un moment ou à un autre, rencontrera sa fin entre ses dents aiguisées. Le nain que je suis est simplement heureux que ce moment ne soit pas arrivé dans les vertes collines de Strangleronce.\n\n—Fin—' WHERE `entry` = 2974;
UPDATE `locales_page_text` SET `Text_loc2` = 'Stupide,\n\nTrouver bouffe. Bleu avoir bon goût. Lantresor dire être bon. Rapporter nourriture à Gros-tas. Gros-tas manger d’abord, puis Stupide manger. Compris ? Aller vite ou Gros-tas manger toi.\n\n-Gros-tas' WHERE `entry` = 2975;
UPDATE `locales_page_text` SET `Text_loc2` = '(1) Raliq l’Ivrogne - Ogre. Ne quitte jamais la cantine. Cruel. Laid. Gros. Cruel. Et aussi très gros.\n\n[Page suivante]' WHERE `entry` = 2976;
UPDATE `locales_page_text` SET `Text_loc2` = '(2) Coosh\'coosh. Petit homme-spore énervant du marécage de Zangar. A un jardin de champignons au bord du marais et de Terokkar. Doit beaucoup de pièces d\'or à Sal’salabim, perdues en jouant à « Quelle est cette odeur ? ». A tenté de payer Sal’salabim avec de stupides champignons Chapeluisant. Dit que ça va autant que l’or ! Hah !\n\n[Page suivante]' WHERE `entry` = 2977;
UPDATE `locales_page_text` SET `Text_loc2` = '(3) Floon. Doit beaucoup de pièces d\'or à Sal’salabim. Sal’salabim a tué l’homme-oiseau car il n’a pas payé. Maintenant un fantôme homme-oiseau se promène dans le coin nord-ouest du désert des Ossements.' WHERE `entry` = 2978;
UPDATE `locales_page_text` SET `Text_loc2` = 'La racine d\'olemba est plutôt commune dans la forêt de Terokkar. Cherchez des racines déterrées près des arbres olemba qui constituent l\'essentiel de la forêt.\n\nLes canneberges ne poussent que dans le marécage de Zangar, et seulement au bastion Ango\'rosh, situé dans la région nord-ouest du marais. Prenez garde aux ogres qui gardent férocement ces précieuses baies.\n\nLes palmes telaari sont sûrement la plante la plus facile à se procurer de la liste. Vous en trouverez au sud de Garadar, dans le bassin de Telaari, au bord de l\'eau.\n\nLa dernière plante de la liste ne pousse que dans les Tranchantes. Ces montagnes se situent au nord du marécage de Zangar. L\'Épine-de-dragon pousse au bout des concrétions rocheuses déchiquetées qui entourent les canyons des Tranchantes. Vous saurez de quoi je parle quand vous verrez pour la première fois les Tranchantes. Les formations rocheuses ressemblent à un empilement de dents pointues et acérées.' WHERE `entry` = 2979;
UPDATE `locales_page_text` SET `Text_loc2` = 'Afin que la personne qui avale la potion puisse acquérir la vision des morts, il faut réunir certains composants matériels.\n\nTout d\'abord, un globe oculaire de gronn des montagnes du Nagrand occidental, non loin des camps de forge maudits de la Légion ardente, au sud de la colline des Cogneguerre.\n\nEn Nagrand occidental, on trouve également des venterocs supérieurs. Il en faudra absolument un sans défaut !\n\nEnfin, dans l\'ouest et le sud-ouest du Nagrand, il vous faudra vous procurer de la graisse de vieux sabot-fourchu.\n\nRassemblez ces objets et apportez-les-moi ! Hâtez-vous !\n\n-Nitrin' WHERE `entry` = 2983;
UPDATE `locales_page_text` SET `Text_loc2` = '<Après avoir parcouru plusieurs pages assommantes de pseudo-science qui semblent n\'avoir ni queue ni tête, vous voyez une note griffonnée en bas de la dernière page....>\n\nDésolé, chef. On dirait que Raz-de-Néant va bientôt sauter. Il va probablement emporter le reste de l\'Outreterre avec lui.\n\nJe n\'en suis pas sûr à 100%, mais je pense que le problème est lié à ce que ces elfes de sang fabriquent aux manaforges.\n\nC\'est une bonne chose que vous construisiez cette fusée !\n\n-- Pokernikus\n\nP.S. - Vous croyez que je pourrai avoir une place dans la fusée ? Vous aurez certainement besoin d\'un néantologue pendant que vous voyagerez dans le Néant distordu !' WHERE `entry` = 2984;
UPDATE `locales_page_text` SET `Text_loc2` = 'Général,$B$BNous tenons la ligne à l\'escalier du Destin, mais la Légion envoie vague après vague contre nos rangs. Ils doivent posséder non loin une zone de regroupement, que nos éclaireurs n\'ont pas encore réussi à situer. Bien que les forces de l\'Alliance soient ici à nos côtés, je crains qu\'en raison de sa supériorité numérique la Légion ne déborde nos défenses. Envoyez-nous toute l\'aide que vous pourrez, sans quoi la Porte pourrait tomber - et nous resterions piégés dans ce monde de cauchemar pour toujours.$B$BGénéral de division Orion' WHERE `entry` = 2985;
UPDATE `locales_page_text` SET `Text_loc2` = '« Quand Nahni s\'approcha lestement, le guerrier grisonnant lui décocha un regard de pierre. « Je suppose que vous êtes venue chercher la récompense pour avoir tué ces murlocs ? », dit-il.\n\nSes yeux vagabondèrent jusqu\'à la grosse épée brillante qu\'il portait sur lui. « Mais cela dépend de ce que vous proposez comme récompense, Marcus. », insinua-t-elle. Elle jouait à entortiller les longues boucles de ses cheveux, affectant de ne pas remarquer la gêne qu\'il éprouvait en sa présence. « Peut-être que je n\'en voudrai pas. », affirma-t-elle.' WHERE `entry` = 2986;
UPDATE `locales_page_text` SET `Text_loc2` = '« Marcus fit un pas dans sa direction, bouillonnant de peur et de colère. « La récompense n\'est pas négociable ! », s\'écria-t-il. Il se figea un instant, rassemblant tout son courage, et se colla contre sa silhouette minuscule. Leurs lèvres brûlantes se joignirent fougueusement, faisant fondre son sort d\'armure de givre dans un torrent de vapeur torride.\n\n« Alors, y a-t-il une étape suivante à cette quête ? », demanda-t-elle sur un ton provocant, les yeux brillants d\'excitation.\n\n<Et ça continue pendant des centaines et des centaines de pages, sans faire avancer l\'intrigue.> »' WHERE `entry` = 2987;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cet étrange objet semble être une sorte d\'appareil, mais il ne ressemble à rien que vous ayez déjà vu.\n\nÀ en juger par son apparence, il semble qu\'il ait déjà servi.\n\nEt d\'après la disposition des corps étalés autour, on pourrait même supposer qu\'il a servi de manière meurtrière, quelle que soit sa fonction.' WHERE `entry` = 2988;
UPDATE `locales_page_text` SET `Text_loc2` = 'Commandant,\n\nLa Formule doit être livrée à Zuluhed. Il en aura la garde jusqu\'à nouvel ordre.\n\n-Seigneur Illidan Hurlorage' WHERE `entry` = 2989;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<H1 align = "center">COMPLAINTE DES BIEN-NÉS</H1>\n<BR/>\n<IMG src = "Interface\\FlavorImages\\BloodElfLogo-small"/>\n</BODY>\n</HTML>' WHERE `entry` = 2990;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>Traduction approximative...</P>\n<BR/>\n<P>Par la lumière, par la lumière du soleil,</P>\n<P>Enfants du sang,</P>\n<P>Nos ennemis forcent l\'entrée.</P>\n<P>Enfants du sang,</P>\n<P>Par la lumière,</P>\n<P>Défaillants enfants du sang,</P>\n<P>Ils forcent l\'entrée.</P>\n<P>Ô enfants du sang</P>\n<P>Par la lumière du soleil.</P>\n<P>Défaillants enfants du sang,</P>\n<P>Ils forcent l\'entrée.</P>\n<P>Ô enfants du sang,</P>\n<P>Par la lumière du soleil,</P>\n<P>Du soleil.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2991;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>Version thalassienne...</P>\n<BR/>\n<P>Anar\'alah, anar\'alah belore,</P>\n<P>Sin\'dorei,</P>\n<P>Shindu fallah na.</P>\n<P>Sin\'dorei,</P>\n<P>Anar\'alah,</P>\n<P>Shindu sin\'dorei,</P>\n<P>Shindu fallah na.</P>\n<P>Sin\'dorei,</P>\n<P>Anar\'alah belore.</P>\n<P>Shindu sin\'dorei,</P>\n<P>Shindu fallah na.</P>\n<P>Sin\'dorei,</P>\n<P>Anar\'alah belore,</P>\n<P>Belore.</P>\n</BODY>\n</HTML>' WHERE `entry` = 2992;
UPDATE `locales_page_text` SET `Text_loc2` = 'Mon cher chancelier,\n\nJe vous charge de rallier les géants d’Ombrelune à notre cause. En bon elfe de sang, servez-vous de tout votre charme pour les convaincre de la justesse de notre position. À défaut, exterminez-les jusqu’au dernier.\n\nSeigneur Illidan Hurlorage' WHERE `entry` = 2993;
UPDATE `locales_page_text` SET `Text_loc2` = 'En toutes choses, un paladin doit réfléchir la Lumière qui amplifie nos forces. Il faut s’efforcer d’approcher la divinité. Cela ne veut pas dire qu’il faut devenir un dieu, juste faire en sorte que toutes nos actions soient empreintes de bonté.' WHERE `entry` = 2994;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les temps sont durs, et vous devez lutter contre le mal, mais souvenez-vous que c’est en aidant les autres que vous vous distinguerez vraiment de la multitude. La compassion, la patience, la bravoure… toutes ces choses sont aussi importantes pour un paladin que la force au combat.\n\nN’oubliez jamais cela.' WHERE `entry` = 2995;
UPDATE `locales_page_text` SET `Text_loc2` = '« DÉCHARGE DE RESPONSABILITÉ.\n\nLE PILOTE D’ESSAI A PLEINEMENT CONSCIENCE ET RECONNAÎT QU’IL COURT UN RISQUE DE BLESSURE, DE DÉMEMBREMENT OU DE MORT SOUS LA FORME DE : RÉDUCTION EN POUSSIÈRE, ÉLECTROCUTION, TRAUMATISME INFLIGÉ À SES ORGANES VITAUX, CONSOMMATION PAR LA FAUNE INDIGÈNE OU PARADOXES SPATIOTEMPORELS AU COURS DE L’UTILISATION DU ZEPHYRIUM CAPACITORIUM (« L’APPAREIL »). LE PILOTE D’ESSAI DÉCLARE QU’IL EST VOLONTAIRE ET QU’IL ASSUME TOUS LES RISQUES ÉNUMÉRÉS CI-DESSUS.\n\nLE PILOTE D’ESSAI RENONCE À TOUTE POURSUITE OU DEMANDE DE DOMMAGES ET INTÉRÊTS À L’ENCONTRE DE TALLY ROSSECOQUIN ET DE SA FAMILLE. DANS L’ÉVENTUALITÉ D’UN DÉCÈS, IL LÈGUE SES RESTES À LA FAMILLE ROSSECOQUIN POUR ÉTUDE.\n\nsigné,\nX :__$N »' WHERE `entry` = 2996;
UPDATE `locales_page_text` SET `Text_loc2` = 'Salutations, docte Antonivitch,\n\nJ\'espère que cette missive vous trouvera à Thrallmar, attelé à de productives recherches. Ma propre installation à Brise-échine suffit à mes expériences, mais je rêve d‘un retour à Fossoyeuse, et des moyens que j’y aurais pour m’aider...\n\nAh, la complainte du trépassé.\n\nPlus sérieusement, j\'ai quelques nouvelles. Le sang récupéré par $N contient une concentration de corruption démoniaque très proche de celle des gangr\'orcs originels d\'Azeroth... la souillure de Mannoroth.' WHERE `entry` = 2997;
UPDATE `locales_page_text` SET `Text_loc2` = '$N,\n\nLa formule de damnation est effectivement une incantation fort puissante. Cette formule ancienne et chaotique est tenue pour responsable de bien des tragédies dans l\'histoire de nos mondes. En d\'autres termes, elle n\'est pas cantonnée à Draenor.$B$BLe symbole de feu que vous avez vu était la marque de Kael\'thas. Sa connaissance des mots anciens explique bien des choses.$B$BVotre découverte a éveillé l\'intérêt des naaru. A\'dal a réclamé votre présence à Shattrath. Si vous deviez choisir de venir, vous me trouverez sur la Terrasse de la Lumière.\n\nHumblement vôtre,\n\nKhadgar' WHERE `entry` = 2998;
UPDATE `locales_page_text` SET `Text_loc2` = 'Antonivitch,\n\nLaissez-moi tout d\'abord vous dire que votre dernier agent, $N, a accompli sa tâche à mon entière satisfaction. J\'ai pris possession de son corps et je vous écris ce message de sa main. Croyez bien que cela m\'amuse énormément. J\'espère avoir un jour le plaisir de vous posséder, très excellent apothicaire !\n\nMais venons-en au compte-rendu que vous attendez de moi : la citadelle des Flammes infernales est hantée par un démon plus puissant que tous ceux que j\'ai déjà croisés. J\'imagine que Mannoroth, le célèbre seigneur des abîmes de la Troisième Grande Guerre, possédait une force similaire, mais c\'est rarissime. Je perçois toutefois comme un carcan autour de ce démon. Je ne serais pas surpris s\'il agissait contre sa volonté, ou s\'il était prisonnier, ou sous l\'influence d\'un pouvoir encore plus puissant.\n\nL\'avenir nous le dira...' WHERE `entry` = 2999;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'avenir nous le dira, selon l\'expression consacrée, car j\'ai également senti une altération du destin dans les murs de la citadelle.\n\nVous autres immigrants d\'Azeroth, cet endroit vert, luxuriant et affreux où les êtres tels que moi s’égarent dans un enfer de vie et de Lumière, pourrez voir le visage du démon. Vous comprendrez alors que les maléfices des Flammes infernales ne sont guère que le sommet, si vous me passez cette métaphore cavalière, d\'un vaste iceberg.\n\nQue de batailles glorieuses nous attendent ! J’aimerais y être pour goûter la peur et la douleur qui les accompagneront !\n\nMais je vais couper là mes envolées lyriques et vous laisser réfléchir à ceci, cher apothicaire : votre corps est déjà mort, mais votre âme est encore en parfait état, et c\'est d’elle que je me délecterai le jour où vous baisserez votre garde... et que je m\'inviterai en vous !\n\nJ\'attendrai jusqu\'au quatrième jour du Jugement s\'il le faut, mais vous serez à moi !\n\nBonne soirée,\n-D' WHERE `entry` = 3000;
UPDATE `locales_page_text` SET `Text_loc2` = '<Il ne reste aucun mot lisible sur cette page largement brûlée.>' WHERE `entry` = 3001;
UPDATE `locales_page_text` SET `Text_loc2` = 'Depuis votre départ du Bosquet éternel, j’ai poursuivi mes recherches sur le Livre du Corbeau. Je dispose d’informations qui vous aideront à vaincre le dieu corbeau.\n\nLibérer les esprits les a liés à votre destin, je pense donc qu\'ils choisiront d\'eux-mêmes de vous aider dans votre combat contre lui.\n\nLorsque vous relâcherez les esprits, ils seront en état de sommeil, comme ils le sont depuis des siècles. En utilisant des sortilèges de soin sur la durée, vous pouvez les imprégner d’énergie, les ramener à la vie, et obtenir leur aide en combat. Quand la magie s’estompera, ils retourneront à leur torpeur.\n\nVoici un résumé de mes notes sur chaque esprit oiseau.' WHERE `entry` = 3002;
UPDATE `locales_page_text` SET `Text_loc2` = 'L’esprit de l\'épervier\nL’esprit de l’épervier possède une incroyable vitesse et une excellente agilité, et est très apprécié par les arakkoa pour son habileté de chasseur. Il vous octroiera en partie sa rapidité pour le lancement des sorts et les combat de mêlée, si vous lui donnez de l’énergie grâce à un sort de soin sur la durée.' WHERE `entry` = 3003;
UPDATE `locales_page_text` SET `Text_loc2` = 'L’esprit de l’aigle\n\nÀ en croire les traditions arakkoa, l’esprit de l’aigle n’est pas aussi rapide que celui de son frère l’épervier, mais est le chasseur le plus doué de tous. Il utilisera ses talents contre vos ennemis si l’éveillez de son sommeil grâce à un sort de soin sur la durée.' WHERE `entry` = 3004;
UPDATE `locales_page_text` SET `Text_loc2` = 'L’esprit du faucon\n\nLa tablette multiplie les louanges pour le faucon, comme s’il avait été autrefois l’allié du corbeau. Ancien symbole arakkoa de vengeance, l’esprit du faucon est souvent invoqué par les victimes d’injustice. Si vous utilisez un sortilège de soin sur la durée pour l’éveiller, il partagera ses pouvoirs avec vous, et blessera ceux qui vous font du mal.' WHERE `entry` = 3005;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au nom de notre grand maître... Pour la gloire d\'Illidan !\n\nMor\'ghor,\n\nJe t\'envoie ce misérable en tant que serviteur d\'Illidan. Bien qu\'il n\'ait aucun avenir dans la chevalerie de la mort, tu lui trouveras peut-être des compétences appréciables. Bats-le comme plâtre, soumets-le, et fais-en un guerrier pour les Illidari.\n\nEt s\'il échoue... S\'il fait preuve de faiblesse... relègue-le. Dans les fosses !\n\nSeigneur Teron Fielsang' WHERE `entry` = 3006;
UPDATE `locales_page_text` SET `Text_loc2` = 'Deux têtes valent mieux qu\'une ! Vous aurez toujours au moins un ami.' WHERE `entry` = 3010;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nombres porte-bonheur : 13 9 17 4 n' WHERE `entry` = 3014;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vous êtes quelqu\'un de spécial, quoi qu\'en disent vos amis, votre famille, vos collègues, de parfaits inconnus ou votre propre conscience.' WHERE `entry` = 3016;
UPDATE `locales_page_text` SET `Text_loc2` = 'Qui veut voyager loin ne mange pas sa monture.' WHERE `entry` = 3017;
UPDATE `locales_page_text` SET `Text_loc2` = 'Heureux les soûlographes, car le royaume des cieux leur appartient.' WHERE `entry` = 3018;
UPDATE `locales_page_text` SET `Text_loc2` = 'Avertissement : cette entraille est impropre à la consommation humaine.' WHERE `entry` = 3019;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au nom de notre grand maître... Pour la gloire d\'Illidan !\n\nOn peut se procurer les composants les plus communs du canon à âme en quelques étapes. Les ingénieurs et mineurs devraient être à même de vous fournir les barres de gangracier, la batterie en khorium et la structure en adamantite.\n\nPour l\'essence arcanique sans défaut, il faudra vous rendre dans la forêt de Terokkar. Au milieu de la forêt, au-dessus du camp Horde des Brise-pierres, se trouve la demeure de Sar\'this, un arakkoa hérétique. L\'endroit s\'appelle le lac Jorune.\n\nAllez trouver Sar\'this et expliquez-lui ce que vous cherchez. Pensez bien à lui dire que c\'est moi qui vous envoie, car il me doit une faveur ou deux.\n\nSuivez les instructions de Sar\'this, et vous aurez l\'essence.\n\n-Balthas' WHERE `entry` = 3028;
UPDATE `locales_page_text` SET `Text_loc2` = 'La dernière fois que je me trouvais dans les salles, le roi-serre Ikiss portait la rémige de Terokk comme un symbole de son autorité. Il la porte probablement encore, pour convaincre ses sujets qu\'il est la réincarnation de Terokk.$B$BMon frère, le tisseur d\'ombre Syth, portait le masque de Terokk lorsqu\'il dirigeait les rituels sethekk.$B$BEn ce qui concerne la saga de Terokk, elle est conservée dans un cercle de runes, dans la grande chambre voûtée contiguë aux appartements du roi-serre Ikiss.' WHERE `entry` = 3029;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'influence de Mannoroth n’a pu atteindre l\'Outreterre, d\'autant que la bête est morte voici des années, mais un autre seigneur des abîmes pourrait y être en activité. De la même trempe que Mannoroth... capable de corrompre les orcs indigènes.\n\nJe poursuis mon étude du sang de l\'Orbite-sanglante, mais je vous encourage à suivre cette piste. Je crains qu\'elle ne vous mène aux profondeurs de la citadelle des Flammes infernales.\n\nAvec ma plus érudite considération,\nApothicaire Albreck' WHERE `entry` = 3030;
UPDATE `locales_page_text` SET `Text_loc2` = 'Fais gaffe, Budd !\n\nC\'est la dernière fois que j\'t\'envoie un pigeon tant qu\'tu m\'auras pas payé tout c\'que tu m\'dois.\n\nAssez traîné !\n\nEskrokar' WHERE `entry` = 3031;
UPDATE `locales_page_text` SET `Text_loc2` = 'Garn,\n\nInutile de vous rappeler l\'importance de votre mission : vous devez protéger votre précieuse cargaison à tout prix. Comme promis, succès et discrétion seront richement récompensés.\n\nAssurez-vous que vos « associés » gardent bien leurs langues dans leurs poches, souciez-vous de l\'équipage et tout ira bien. J\'attends la nouvelle de votre réussite.' WHERE `entry` = 3032;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je vous ai envoyé une troupe considérable, ancien, mais vous devez prendre garde à son utilisation. Votre première priorité est de stopper l\'avancée de ces corniauds de l\'Alliance sur nos terres. Quelques humains des plus audacieux ont entrepris de construire un grand bâtiment à la frontière des Tarides.\n\nFaites-leur comprendre qu\'ils ne sont pas les bienvenus hors de leur maudite Theramore. Pour finir, une mise en garde, ancien : ne laissez rien transpirer de vos plans chez les ogres de Mur-de-Fougères. Ces brutes sont incapables de la moindre discrétion.\n\n- Agashem' WHERE `entry` = 3033;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'une des créatures les plus effrayantes que j\'aie vues de mes propres yeux est Tethyr. Je n\'ai aperçu ce monstre qu\'une fois, et je compte déjà comme un miracle le fait d\'être encore en vie !\n\nC\'était pendant une sombre nuit d\'orage. Le phare de Theramore était à peine visible par-delà la mer tourbillonnante et les trombes de pluie. Nous faisions de notre mieux pour garder le cap vers cette lueur, quand soudain le monstre a surgi des vagues devant notre vaisseau !' WHERE `entry` = 3034;
UPDATE `locales_page_text` SET `Text_loc2` = 'La bouche de cette immense créature s\'est ouverte, expédiant des traits d\'eau glacée vers notre embarcation. Ses terribles mâchoires se sont refermées sur la proue du bateau, l\'arrachant au reste de la coque. Équipage et cargaison furent projetés à la baille, alors que les restes du navire disparaissaient entre les vagues.\n\nLa dernière chose dont je me souviens, c\'est de mon vol plané sur la mer d\'encre et de mon plongeon sous sa surface. Le monde s\'est obscurci et je croyais ma dernière heure venue. Lorsque je me suis réveillé à l\'infirmerie de Theramore, j\'ai compris que quelqu\'un devait raconter notre histoire.' WHERE `entry` = 3035;
UPDATE `locales_page_text` SET `Text_loc2` = 'La bouche de cette immense créature s\'est ouverte, expédiant des traits d\'eau glacée vers notre embarcation. Ses terribles mâchoires se sont refermées sur la proue du bateau, l\'arrachant au reste de la coque. Équipage et cargaison furent projetés à la baille, alors que les restes du navire disparaissaient entre les vagues.\n\nLa dernière chose dont je me souviens, c\'est de mon vol plané sur la mer d\'encre et de mon plongeon sous sa surface. Le monde s\'est obscurci et je croyais ma dernière heure venue. Lorsque je me suis réveillé à l\'infirmerie de Theramore, j\'ai compris que quelqu\'un devait raconter notre histoire.' WHERE `entry` = 3036;
UPDATE `locales_page_text` SET `Text_loc2` = 'Enfants : Joel et Gina. Femme : Suzannah' WHERE `entry` = 3037;
UPDATE `locales_page_text` SET `Text_loc2` = 'Des runes inquiétantes sont gravées sur le côté de la caisse...' WHERE `entry` = 3038;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ne pleure pas sur mon tombeau,\nJe n’y suis pas, je ne dors pas.\n\nJe suis les mille vents boréaux\nQui en Norfendre soufflent le froid.\n\nJe suis l’averse de douce pluie,\nSur la Marche et ses blonds épis\n\nJe suis le calme du blanc matin\nSur Strangleronce verte et profuse.\n\nJe suis le grondement sans fin\nQu’en Nagrand les sabots diffusent.\n\nJe suis les étoiles lumineuses\nSur Darnassus la douce rêveuse.\n\nJe suis dans le chant des oiseaux,\nDans la beauté autour de toi.\n\nNe pleure pas sur mon tombeau,\nJe n’y suis pas, je ne meurs pas.' WHERE `entry` = 3039;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>OK, $N, voilà la liste de tous les trucs dont je vais avoir besoin pour notre hyper-secrète nouvelle, heu..., super-arme. Pour le moment, appelons-la simplement « Pétoire Raccourcissante Proto-Typique Ultra-Avancée de Fufute ».</P>\n<BR/>\n<P>Faites-moi confiance, ça va être drôlement plus révolutionnaire que cette vieille rosse d\'agrandisseur de monde !</P>\n<BR/>\n<P>Mais peu importe, ne vous mettez pas martel en tête pour le moment. Contentez-vous de me rapporter tout ça et on va rouler sur l\'or !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3040;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<H1 align = "center">Mes outils</H1>\n<BR/>\n<P>Commençons par le commencement... J\'ai besoin de mes outils. Pour un projet aussi complexe, pas moyen de se contenter de pis-aller !</P>\n<BR/>\n<P>Quand on a dû décamper de la station de pompage pour sauver nos peaux - mais c\'est encore une autre histoire - je n’ai pas eu le temps de les ramasser. Faut me comprendre, bon sang, j\'avais la mort aux trousses ! Mais je me souviens que je les ai laissés sous la station de pompage, à l’est. </P>\n</BODY>\n</HTML>' WHERE `entry` = 3041;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<H1 align = "center">Un condensateur surchargé</H1>\n<BR/>\n<P>Tout ingénieur digne de ce nom pourra vous en bricoler un, si vous ne savez pas le faire vous-même. Bon sang, je le ferais bien moi-même, mais je n’ai plus d’outils pour l’instant, vous vous souvenez ?</P>\n<BR/>\n<P>Alors débrouillez-vous pour m’en rapporter. Sans, le projet n’ira nulle part. Si besoin est, partez pour les continents du sud et épluchez l’hôtel des ventes jusqu’à ce que vous en trouviez un !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3042;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<H1 align = "center">Une poignée de sable de Rocnar</H1>\n<BR/>\n<P>Bon, ça risque d’être un poil complexe à récupérer, mais il nous faut du sable extrafin pour polir la lentille. Ce qui veut dire qu’il faut le récupérer sur un élémentaire particulièrement costaud.</P>\n<BR/>\n<P>Rocnar fera parfaitement l’affaire. Je sais qu’il a été vu au sud-est, dans les confins Gelés, entre Unu’pe et la ziggourat Gémissante. Allez donc briser sa croûte de glace et rapportez-moi une poignée de sable de son noyau.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3043;
UPDATE `locales_page_text` SET `Text_loc2` = 'Premier quart, septième cloche\nEncore trouvé Cédric dans la réserve de rhum. Mesure disciplinaire.\n\nPremier quart, huitième cloche\nChangement de quart. Tout va bien.\n\nSecond quart, première cloche\nTrouvé Cédric tentant de forcer le bar du capitaine. Mesure disciplinaire.\n\nSecond quart, deuxième cloche\nCédric monté dans les gréements, emmêlé et tombé. Soigné par le chirurgien de bord. Envoyé en bas pour dessoûler.\n\nSecond quart, quatrième cloche\nBouteille de rhum découverte dans le pantalon de Cédric. Mesure disciplinaire.\n\nSecond quart, cinquième cloche\nTrouvé les vêtements de Cédric. Trouvé Cédric, séparément. Mesure disciplinaire.\n\nSecond quart, sixième cloche\nChant bruyant de Cédric. Réveil du capitaine. Mesure disciplinaire administrée par le capitaine.\n\nSecond quart, huitième cloche\nChangement de quart. Tout est calme. Envoyé deux hommes pour localiser Cédric.\n\nQuart du matin, première cloche.\nIncendie dans les quartiers du capitaine. Branle-bas général. Réserve de poudre jetée par-dessus bord.' WHERE `entry` = 3044;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cela fait bien longtemps que l\'on raconte l\'histoire du cor de fureur élémentaire autour des feux de camp des taunkas. Des générations de guerriers ont grandi en écoutant ces histoires et ont cherché, en vain, à trouver la cachette du cor. Comme les autres, le vaillant Sabot-tempête avait juré de partir en quête du cor, laissant derrière lui son village et sa famille.\n\nBravant le froid mordant et les féroces élémentaires du pic de Givrelame, Sabot-tempête partit seul pour gravir la montagne. Arrivé au sommet, il fut accueilli par le Vent du Nord en personne. Sachant que le vent ne céderait pas le cor sans se battre, le puissant guerrier fit retentir son cri de défi.' WHERE `entry` = 3054;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pendant cinq jours, Sabot-tempête lutta contre le vent. À l\'aube du sixième jour, il parvint à bannir le vent du pic de Givrelame et prit possession du cor. Meurtri et blessé, il quitta la montagne pour retourner à son village.\n\nLe Vent du Nord rassembla une armée de petits élémentaires et lança une attaque contre Sabot-tempête alors qu\'il approchait de son foyer. Le guerrier affaibli fut incapable de résister à l\'assaut et le Vent du Nord prit sa revanche. Il brisa le cor en deux morceaux et les enferma dans des pièces de verroterie brillante qu\'il offrit ensuite à deux puissants chefs Gorloc comme symbole de leur autorité.\n\nAujourd\'hui encore, les morceaux du cor sont en possession du chef Brouillegargouille et du chef Grouillebarbouille.' WHERE `entry` = 3055;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">AVIS DE RECHERCHE : HEMET NESINGWARY</H1>\n<H1 align = "center">POUR CRIMES CONTRE LA NATURE !</H1>\n<BR/>\n<IMG src = "Interface\\Pictures\\obj_nesingwary_256" align = "center"/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<BR/>\n<H1 align = "center">Interprétation de l\'artiste de « l\'Extinctionator » dans son habitat naturel : les flammes infernales du monde souterrain.</H1>\n<BR/>\n<H1 align = "left">Localisation du dernier holocauste de vie sauvage : Nagrand, Outreterre</H1>\n<BR/>\n<H1 align = "left">Également appelé : le Chasseur de grands fauves, le Boucher des Terres ingrates, l\'Éventreur de Strangleronce, le Vieux faucheur, Hemet, Nessie, Vieux salopard, le Donneur de primes, Extinctionator.</H1>\n<BR/>\n</BODY>\n</HTML>' WHERE `entry` = 3057;
UPDATE `locales_page_text` SET `Text_loc2` = 'Fidèles serviteurs des seigneurs élémentaires, LE TEMPS EST VENU.$b$bDepuis trop longtemps, nous nous morfondons dans l\'ombre en attendant notre heure, et servons nos maîtres en œuvrant pour cette fin que nous savons écrite. Aujourd\'hui, alors que les flammes visant à apaiser la fureur de Ragnaros illuminent la nuit, nous avons entre nos mains le pouvoir de semer la guerre et le chaos à une échelle cataclysmique !$b$bLe seigneur du Feu est emprisonné en notre monde. Il ne dispose pas de tout son potentiel, mais sa puissance est considérable. S\'il venait à croiser le chemin d\'un adversaire à la puissance tout aussi considérable, le choc qui en découlerait marquerait l\'avènement de la grande guerre des élémentaires qui nous amènera cette fin pour laquelle nous œuvrons.' WHERE `entry` = 3065;
UPDATE `locales_page_text` SET `Text_loc2` = 'Neptulon compte parmi ses serviteurs un grand seigneur du Givre du nom d\'Ahune. En ce moment-même, Ahune rassemble ses forces pour partir en guerre contre Ragnaros. Il ne lui manque qu\'une porte d\'accès vers notre monde. Cet accès, nous allons le lui offrir. Nous avons les alliés. Nous avons l\'endroit. Nous avons la force, et la volonté.$b$bLes ultimes négociations avec ces nouveaux alliés qui sont nos invités auront lieu dans un endroit sûr et retiré, au nord-ouest de notre camp de base à Orneval.$b$bAvant que cette « fête » des masses aveugles ne touche à sa fin, Ahune affrontera Ragnaros dans l\'ombre de Rochenoire. Les forces déchaînées à cette occasion feront trembler le monde.' WHERE `entry` = 3066;
UPDATE `locales_page_text` SET `Text_loc2` = 'Goramosh,\n\nJe vous envoie un représentant de l’Ethereum. L’ambassadeur Duyheen m’a dit que d’autres membres de son groupe aimeraient se joindre à notre cause. Sondez-le, voyez si vous pouvez lui trouver une utilité.\n\nS’il peut nous servir, j’envisagerai d’en recruter d’autres.\n\nNe soyez pas stupide, brûlez cette lettre dès que vous l\'aurez lue !\n\nM' WHERE `entry` = 3067;
UPDATE `locales_page_text` SET `Text_loc2` = 'Lok\'tar ogar ! La victoire ou la mort – ces mots me lient à la Horde, car ils sont la vérité la plus profonde et la plus sacrée que puisse entendre un guerrier de la Horde.\n\nJe donne librement ma chair et mon sang au Chef de guerre. Je suis l’instrument des volontés du Chef de guerre. Je suis l’arme que le Chef de guerre brandit à sa guise.\n\nDès cet instant et jusqu’à la fin de mes jours, je vis et je meurs pour la Horde !' WHERE `entry` = 3068;
UPDATE `locales_page_text` SET `Text_loc2` = '$N,\n\nSi vous lisez cette lettre, vous êtes encore en vie et un seul morceau – ou du moins, vous avez encore l’usage de vos yeux.\n\nToutes mes excuses pour le secret. Agmar exige que tout le courrier soit lu avant d’être remis – trop de voleurs et de traîtres, il suffit de lire les avis de recherche pour s’en rendre compte. Des questions délicates sont en jeu, qui pourraient être mal interprétées par la jeune garde.\n\nPour un soldat de la Horde, l’échec est un absolu. L’échec, c’est la mort. On ne finasse pas avec la mort, on ne négocie pas avec elle. On ne peut qu’espérer que ses circonstances seront honorables.' WHERE `entry` = 3069;
UPDATE `locales_page_text` SET `Text_loc2` = 'Quant à la victoire… La victoire peut signifier bien des choses. Comme vous l’avez sans doute remarqué, les kor\'krons sont présents en force. Le Chef de guerre a envoyé sa garde d’élite pour remporter la victoire en Norfendre. Avec votre aide et celle d’autres héros, ils poussent le roi-liche et ses forces vers l’inévitable fin. À chaque nouveau triomphe, vous êtes un peu plus près de débarrasser notre monde d’Arthas et du Fléau.\n\nEt c’est là que réside notre dilemme. Car il se trouve que nos forces en Norfendre combattent sous les ordres du jeune Hurlenfer. Chaque nouvelle victoire améliore le moral des soldats de la Horde et retentit dans le reste d’Azeroth.' WHERE `entry` = 3070;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il est par conséquent regrettable que Hurlenfer emploie des tactiques aussi sauvages. Au fur et à mesure que la victoire approche, les méthodes de Hurlenfer paraissent de plus en plus justifiées. Et cela nous rapproche d’un lieu où nous n’avons plus été depuis bien des années, un lieu sombre.\n\nJ’ai envoyé mon fils prendre le commandement des forces au Portail du Courroux. Je sais qu’elles se battront avec honneur. J’espère que leur ténacité et leur courage seront remarqués et imités par le reste de nos troupes. Il est mon cœur et ma force en un lieu où je ne peux être… Quant à vous, vous serez mes yeux et mes oreilles. Ensemble, nous redresserons tout cela.\n\nSang et tonnerre… puisse votre arrivée causer l’un et l’autre. \n\nSaurcroc' WHERE `entry` = 3071;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tous nos efforts tendaient vers cet instant. Nos maîtres se dresseront l\'un contre l\'autre en un glorieux combat qui déchirera Azeroth.$b$bNous vivons à l\'heure du crépuscule, mes frères. Ne relâchez pas vos efforts. Bientôt, nous serons vainqueurs !' WHERE `entry` = 3072;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma reine,\n\nNous avons découvert que l\'intersection de lignes telluriques dans les jardins de Repos-de-lune a été ouverte par les dragons du Vol bleu. Ils ont utilisé un capteur tellurique pour détruire la chape de pierre mais, au lieu de renvoyer l\'énergie vers le Nexus, ils ont utilisé des focalisateurs pour l\'amplifier et maladroitement la détourner vers le Sanctuaire azur.\n\nBien entendu, partout où l\'énergie magique est passée, les conséquences sont terribles. Les Anciens de la forêt, les Kalu\'aks et d\'autres créatures de la région ont été affectés. Par chance, $N, $gle:la:r; noble $r qui se présente à vous, a joué un rôle crucial dans la résolution de ces problèmes.\n\nD\'après moi, $N pourrait être d\'une grande aide pour régler les problèmes rencontrés au temple du Repos du ver. Je vous demande humblement de $gle:la; considérer comme $gun allié:une alliée; qui m\'a été $gprécieux:précieuse;.\n\nRespectueusement vôtre,\n\nModera\nArchimage et membre des Six' WHERE `entry` = 3073;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ma reine,$b$bNous avons découvert que le croisement tellurique des jardins de Repos-de-lune a été ouvert par les dragons du Vol bleu. Ils ont utilisé un capteur tellurique pour détruire la chape de pierre mais, au lieu de renvoyer l\'énergie vers le Nexus, ils ont utilisé des focalisateurs pour l\'amplifier et maladroitement la détourner vers le Sanctuaire azur.$b$bBien entendu, partout où l\'énergie magique est passée, les conséquences sont terribles. Les Anciens de la forêt, les Kalu\'aks et d\'autres créatures de la région ont été affectés. Par chance, $N, $gle:la:r; noble $r qui se présente à vous, a joué un rôle crucial dans la résolution de ces problèmes.$b$bD\'après moi, $N pourrait être d\'une grande aide pour régler les problèmes rencontrés au temple du Repos du ver. Je vous demande humblement de $gle:la; considérer comme $gun allié:une alliée; qui m\'a été assez utile.$b$bRespectueusement,$b$bAethas Saccage-soleil$bArchimage et membre des Six' WHERE `entry` = 3074;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bonjour !\n\nEcrire une lettre, c\'est pas mon fort, alors la directrice, elle m\'a aidé à vous écrire. Je veux juste vous dire MERCI, parce que vous êtes $gun héros:une héroïne;, et un jour moi aussi je veux sauver des gens comme vous.\n\nOh, je dois sortir pour aller jouer, alors MERCI ENCORE pour avoir été $ggentil et fort:gentille et forte;, et vous êtes aussi très $gbeau:belle; aussi !\n\n—Votre admirateur secret.' WHERE `entry` = 3075;
UPDATE `locales_page_text` SET `Text_loc2` = '<Il ne reste aucun mot lisible sur cette page largement brûlée.>' WHERE `entry` = 3076;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ah\'tusa avançait silencieusement dans l’énorme tunnel souterrain, en jetant des regards nerveux par-dessus son épaule à cause du tram qui allait arriver à grande vitesse. « Où est-il... », grommela-t-elle, furieuse, en s’arrêtant brusquement, car quelque chose avait bougé derrière elle. Elle plissa les yeux tout en faisant glisser sa dague hors de son fourreau.\n\nUne voix rauque résonna dans le tunnel : « Je croyais que les voleurs aimaient être derrière leur cible. » Elle se retourna en un éclair et planta sa lame dans un bouclier à la force impénétrable. « Et moi, je croyais que les paladins étaient censés rester chastes. », répliqua-t-elle d’un air narquois.' WHERE `entry` = 3077;
UPDATE `locales_page_text` SET `Text_loc2` = 'Penchée en avant, elle compta à voix haute jusqu’à ce que la bulle invisible se dissipe avec un « pop » sonore. « Douze secondes ? Je sais que tu as des techniques qui durent bien plus longtemps que ça. »\n\nSes doigts osseux se glissèrent sans difficulté sous l’armure du paladin et défirent sa cuirasse, exposant sa peau au froid de l’air. « N’aie pas peur, mon cher Marcus, on verra plus tard pour le coup de grâce... Je ne fais que commencer. » Il l’enlaça en passant ses bras autour de ce qui restait de sa taille et la fit glousser d’excitation en lui mordillant les vertèbres du cou.\n\n<Plusieurs passages du reste du livre sont apparemment très usés à force d’avoir été lus.>' WHERE `entry` = 3078;
UPDATE `locales_page_text` SET `Text_loc2` = 'Journal du Grand général Abbendis' WHERE `entry` = 3079;
UPDATE `locales_page_text` SET `Text_loc2` = 'Table des matières\n\nChapitre 1 - Les morts et vous\n\nChapitre 2 - Garder sa silhouette de goule : sept exercices faciles\n\nChapitre 3 - Prestance et confort : comment choisir un cercueil assorti à votre carnation\n\nChapitre 4 - Sûreté du sépulcre : cinquante vrais signes d\'amour\n\n<Le livre est en parfait état, comme si personne ne l\'avait jamais lu>' WHERE `entry` = 3083;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<H1 align = "center">Journal du grand général Abbendis</H1>\n<BR/>\n<IMG src = "Interface\\FlavorImages\\ScarletCrusadeLogo"/>\n</BODY>\n</HTML>' WHERE `entry` = 3092;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>La voix murmura : « Viens à moi ». Depuis le début, je sais que la Sainte Lumière me parle dans mes rêves. Enfin ! Après toutes ces années passées en prière et en bonnes actions. Après avoir effacé la lèpre morte-vivante de la face d’Azeroth. Après tous ces échecs et ces résurrections.</P>\n<BR/>\n<P>Enfin !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3093;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Cela s\'est à nouveau produit. « Viens à moi... », m\'a ordonné la Lumière.</P>\n<BR/>\n<P>Cette fois-ci, je me suis réveillée frigorifiée, mais il ne faisait pas froid dans la pièce. Je vais redoubler d\'efforts ! Je dirai demain au grand abbé que je veux plus de prières. Plus de demi-mesures !</P>\n<BR/>\n<P>La Sainte Lumière a remarqué nos bons offices. Je le sens !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3094;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Cette fois j\'étais éveillée ! C\'était saisissant, et pendant plus d\'une minute, malgré la chaleur de cette journée ensoleillée, mon souffle s\'est chargé d\'une buée glacée. L\'un des prêtres l\'a remarqué et est tombé à genoux, en prière.</P>\n<BR/>\n<P>Par contre, nul autre n\'a entendu la voix. Ce témoin me prouve au moins que je ne suis pas en train de devenir folle. Peut-être devrais-je demander à Langrain de prier pour moi à ce sujet ?</P>\n<BR/>\n<P>Je veillerai à ce que Jordan et Delarue examinent le recrutement demain. Nos rangs sont gonflés d\'incroyants motivés uniquement par la destruction des morts-vivants. Ce n\'est pas suffisant !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3095;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Le commandant et l’évêque étaient disposés à m’écouter. Ils n’avaient pas le choix. L’évêque Delarue avait l’air particulièrement enthousiaste. Il a parlé d’une nouvelle croisade, et a juré de chasser les tièdes de nos rangs.</P>\n<BR/>\n<P>J’ai insisté pour qu’il procède plus doucement. Nous ne sommes pas en train de détruire la Croisade, juste de former une troupe d’élite composée des plus fidèles des fidèles pour accomplir l’œuvre de la Lumière en Norfendre. Je crains que son amitié pour LeCraft ne lui fasse aucun bien. Tous les deux ont leur utilité, cela dit.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3096;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Je vais laisser la plupart des Croisés ici pour continuer les opérations de nettoyage contre les morts-vivants dans notre arrière-cour. J\'imagine qu\'une fois qu\'ils auront fini ce que nous avons commencé, ils seront nombreux à quitter les rangs de la Croisade et à retourner chez eux pour vivre en paix.</P>\n<BR/>\n<P>Cela semble un juste retour des choses. À notre apogée, nous avons toujours été le sel de la terre, nous soulevant pour récupérer nos foyers de la corruption du Fléau et rendre à notre Lordaeron toute sa gloire passée. Celle d\'avant le Fléau, avant Arthas et le régicide... Avant le roi-liche.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3097;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Des rumeurs circulent parmi les hommes à propos d\'un jour proche qui changerait tout pour la Croisade écarlate. L\'évêque Delarue l\'appelle l\'Aube cramoisie.</P>\n<BR/>\n<P>Je vais y réfléchir, même si je sens jusque dans mes os que c\'est la vérité. Je prie pour que tout ceci nous apporte le bonheur plutôt que le malheur.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3098;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>La Lumière m’a parlé de nouveau. Je suis sortie du rêve avec une impression d’urgence, d’impatience. Je ne La décevrai pas. Plus de retards ! Nous devons partir bientôt !</P>\n<BR/>\n<P>Par une coïncidence miraculeuse, cet après-midi, le capitaine Shely nous a trouvé plusieurs vaisseaux neufs pour le voyage. J’ai dû faire un effort pour dissimuler mon soulagement. Est-ce bien une coïncidence ? Les voies de la Sainte Lumière sont impénétrables.</P>\n<BR/>\n<P>Mon vaisseau amiral sera la Folie du pécheur. Je trouve ce nom très approprié.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3099;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Maintenant je comprends pourquoi la Lumière m\'a poussée à me mettre en route. En pleine nuit, une nécropole du Fléau est apparue dans le ciel au-dessus de nous et a répandu les serviteurs de l\'enfer !</P>\n<BR/>\n<P>Un chevalier de la mort d\'une nouvelle race dirige l\'assaut. Déjà, les pertes que nous avons subies sont catastrophiques. Avec un Fléau capable de nous attaquer n\'importe où et n\'importe quand par en haut, il semble impossible de monter une défense adaptée.</P>\n<BR/>\n<P>Je crains que l\'expédition prévue pour le Norfendre ne soit terminée avant d\'avoir commencé.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3100;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>J\'ai été informée qu\'Âtreval et la zone alentour ont commencé à rassembler une foule. Le haut-commandant Galvar Pursang lui-même veut les diriger pour nous porter secours. Ses efforts seront vains.</P>\n<BR/>\n<P>Je dois m\'assurer que mes meilleurs messagers réussissent à passer les lignes ennemies pour le prévenir de renoncer. Âtreval doit se préparer à se retrancher et à rallier le reste de la Croisade.</P>\n<BR/>\n<P>Avec un peu de chance, ils pourront être là-bas avant la tombée de la nuit.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3101;
UPDATE `locales_page_text` SET `Text_loc2` = 'En hommage au roi Varian Wrynn,\nconnu par certains sous le nom de LO\'GOSH le Loup fantôme.\n\nSon retour miraculeux de longues années d\'exil et de souffrances a allumé une flamme nouvelle dans l\'Alliance, une flamme de bravoure.\n\nNous marcherons vaillamment dans ces terres périlleuses, avec dans nos cœurs l\'exemple de sa ténacité et de sa valeur.\n\n\n - Généralissime Bolvar Fordragon' WHERE `entry` = 3102;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 1 : la vermine humaine semble béatement inconsciente de ma présence, comme prévu. Ils sont mûrs pour un bombardement, et ce n\'est qu\'une question de temps avant que j\'aie cartographié leur position.\n\nJour 2 : carte terminée - une besogne bien triviale pour quelqu\'un d\'aussi accompli que moi. Je n\'arrive pas à saisir pourquoi j\'ai écopé de ce travail, ils auraient dû y coller J-Y C. Cet accent ridicule devrait suffire à justifier sa désignation.\n\nJour 3 : le premier souffle d\'air commence à être un peu éventé. Je devrais peut-être faire momentanément surface dans quelques jours pour en prendre un autre. Je m\'ennuie toujours à mourir ; où est ce maudit signal ?\n\n<Les divagations continuent...>' WHERE `entry` = 3103;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 12 : ah, le délicieux soulagement. La seconde respiration avait le goût des larmes d\'orphelins les plus pures que j\'aie connues. Le signal est toujours bizarrement absent, et je pourrais jurer que l\'un des crabes essaye de jouer du tambour sur mon gros orteil.\n\n<Les divagations se font plus décousues...>' WHERE `entry` = 3104;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 36 : j\'ai finalement expiré mon troisième souffle, et les veines de mon épaule gauche se sont encore remplies de bulles pendant ma remontée. Le tambourinement des crabes est devenu un accompagnement constant, le décor sonore de mon petit coin d\'enfer obscur. À l\'évidence, ces misérables m\'ont oublié. Si J-Y était ici, ils ne l\'auraient pas oublié, lui ! Ils n\'abandonneraient pas Hans ou Sylvia ! Alors pourquoi moi ?\n\n<L\'écriture se transforme en gribouillages cafardeux...>' WHERE `entry` = 3105;
UPDATE `locales_page_text` SET `Text_loc2` = 'Au grand général Abbendis\nNouvelle-Avalon, Campagne écarlate\n\nGrand général Abbendis,\n\nJ\'espère que cette lettre vous trouvera en bonne santé. Mon nom est Dansel Adams, chasseur de métier, croisé écarlate dans le sang. Je vous écris cette lettre pour vous informer que j\'ai été témoin d\'un miracle.' WHERE `entry` = 3110;
UPDATE `locales_page_text` SET `Text_loc2` = 'Lors d\'une purge de routine des sauvages dans un Pestebois infesté par le Fléau, mon groupe et moi avons été interrompus par un cri assourdissant venant du ciel. Quand nous avons regardé vers les cieux, en direction de Naxxramas, nous avons été sidérés. Je vous le dis, grand général, c\'était une vision incroyable ! L\'effroyable citadelle était en train de se déplacer. Lentement au début, puis dans un grondement retentissant - pouf - elle avait disparu !\n\nC\'est exact, Grand général, Naxxramas s\'est volatilisée ! Sans laisser de traces ! Pourrait-ce s\'agir d\'autre chose qu\'un miracle de la Lumière ? Je ne crois pas !' WHERE `entry` = 3111;
UPDATE `locales_page_text` SET `Text_loc2` = 'Que les barbares impurs et sales soient jugés et exécutés ensuite. Louée soit la Lumière !\n\nFaites connaître ce miracle à nos frères et sœurs, grand général.\n\nQue la Lumière vous garde pour toute l\'éternité afin que vous répandiez la bonne parole jusqu\'à la fin des temps.\n\nRespectueusement,\n\nD. Adams\n« Le consacré »' WHERE `entry` = 3112;
UPDATE `locales_page_text` SET `Text_loc2` = '1. Laissez-vous pousser une barbe fournie, car elle cache les imperfections du visage à la Lumière. \n\n2. Purifiez vos mains au lait de jument, pour qu\'elles soient douces et souples lorsque vous manipulez l\'offertoire. \n\n3. Baignez-vous dans les rivières, pour permettre aux cadeaux naturels de la Lumière de nettoyer votre peau.' WHERE `entry` = 3113;
UPDATE `locales_page_text` SET `Text_loc2` = '4. Remplacez régulièrement vos vêtements saints, pour que votre apparence et vos odeurs n\'offensent pas les serviteurs de la Lumière. \n\n5. Maintenez à tout instant une bonne posture - ne laissez pas la fatigue ou la paresse vous faire courber l\'échine. \n\n<La liste de recommandations continue encore sur de nombreuses pages.>' WHERE `entry` = 3114;
UPDATE `locales_page_text` SET `Text_loc2` = 'Note trouvée sur le bureau du grand abbé Langrain : \n\nOn pourrait croire en cette sombre période de lutte et de désarroi qu\'il faut s\'occuper de soi, protéger sa famille et ses êtres chers à l\'exception de tout le reste. Mais ces sentiments sont erronés - sans la balise d\'espoir qu\'est l\'Église, le désespoir inondera rapidement les pores de l\'âme de votre famille. Les estomacs pleins, les pieds au chaud et les sourires heureux que les biens matériels peuvent acheter ne sauveront pas votre famille de la sensation de vide croissante causée par la fuite de vos responsabilités fiduciaires à la Lumière.' WHERE `entry` = 3115;
UPDATE `locales_page_text` SET `Text_loc2` = 'Donnez à l\'Église. Les difficultés endurées à cause de l\'absence de biens matériels forgent l\'âme, brûlent les impuretés et vous font accéder à un niveau de conscience supérieur. N\'ayez pas honte du grondement d\'un estomac vide, de la morsure d\'un caillou pointu sur votre pied nu, ou de la morsure du vent d\'hiver. Ce ne sont rien d\'autre que les moyens par lesquels la Lumière communique son inspiration à l\'âme. Ces sacrifices permettent que les hymnes offerts par l’or, glorifiant la Lumière qui vous inspire tant lors de vos dévotions, continuent à être chantés. \n\nÉvitez les conforts de la chair, et rapprochez-vous encore plus de la Lumière.' WHERE `entry` = 3116;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le sujet des grâces est un sujet difficile, mais à certains moments de nos vies, il devient un mal nécessaire. Une erreur de jugement, un mot dur trop vite lancé, un coup de poing ou de pied donné sous l\'effet de la colère... toutes ces choses sont des défauts des hommes mortels, et l\'Église doit les confesser.\n\nCependant, une telle confession a un prix. Pour des péchés de mots durs, une simple poignée d\'argent nettoiera l\'âme. Pour les péchés de force physique, une poignée d\'or purgera les défauts de la chair mortelle. Pour les autres péchés, une confession complète et une contribution plus généreuse sera nécessaire.' WHERE `entry` = 3117;
UPDATE `locales_page_text` SET `Text_loc2` = 'Coup donné :\n Au visage : 2 pièces d\'or\n À l\'aine : 3 pièces d\'or\n À la poitrine : 1 pièces d\'or et 45 pièces d\'argent\n \n<Une liste détaillée de prix et de péchés se poursuit sur des pages et des pages>' WHERE `entry` = 3118;
UPDATE `locales_page_text` SET `Text_loc2` = 'Purifiez votre corps par le travail. Purifiez votre âme par la prière. Purifiez vos ennemis par le feu. La douleur n\'est pas votre ennemie mais une alliée que vous devez embrasser, garder à vos côtés et faire subir aux incroyants comme aux fidèles. \n\nLa résistance aux voies de la Lumière est un signe d\'impureté, qui devrait être arraché du cœur de la communauté.' WHERE `entry` = 3119;
UPDATE `locales_page_text` SET `Text_loc2` = 'Grand général,\n\nLes armées d\'Âtreval et de Tirisfal sont à moins d\'un jour de voyage de la Nouvelle-Avalon. Nous chevauchons avec la Lumière éclairant notre chemin et le vent nous portant. Bientôt le Fléau devra affronter la pleine puissance de la Croisade écarlate !\n\nQue la Lumière vous bénisse,\n\nHaut-commandant Galvar Pursang.' WHERE `entry` = 3120;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<H1 align = "center">Le Chemin de la rédemption</H1>\n<BR/>\n<IMG src = "Interface\\FlavorImages\\ScarletCrusadeLogo"/>\n</BODY>\n</HTML>' WHERE `entry` = 3121;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>La voix murmura : « Viens à moi ». Depuis le début, je sais que la Sainte Lumière me parle dans mes rêves. Enfin ! Après toutes ces années passées en prière et en bonnes actions. Après avoir effacé la lèpre morte-vivante de la face d’Azeroth. Après tous ces échecs et ces résurrections.</P>\n<BR/>\n<P>Enfin !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3122;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>Cela s\'est à nouveau produit. "Viens à moi...", m\'a ordonné la Lumière.</P>\n<BR/>\n<P>Cette fois-ci, je me suis réveillée frigorifiée, mais il ne faisait pas froid dans la pièce. Je vais redoubler d\'efforts ! Je dirai demain au grand abbé que je veux plus de prières. Plus de demi-mesures !</P>\n<BR/>\n<P>La Sainte Lumière a remarqué nos bons offices. Je le sens !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3123;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>Cette fois, j\'étais réveillée ! C\'était très réaliste, et pendant plus d\'une minute, en plein milieu d\'un beau jour chaud et ensoleillé, mon souffle était une buée glacée. L\'un des prêtres l\'a remarqué et est tombé à genoux en prière.</P>\n<BR/>\n<P>Mais personne d\'autre n\'a entendu la voix. Au moins, ce témoin prouve que je ne deviens pas folle. Je devrais peut-être demander à Langrain de prier à ce sujet ?</P>\n<BR/>\n<P>Je vais demander à Jordan et Delarue de bien examiner les futures recrues demain. Nos rangs sont pleins d\'incroyants qui n\'ont soif que de détruire les morts-vivants. Ce n\'est pas suffisant !</P>\n<BR/>\n</BODY>\n</HTML>' WHERE `entry` = 3124;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le commandant et l\'évêque étaient réceptifs tous les deux. Non pas qu\'ils aient eu le choix. L\'évêque Delarue en particulier semblait très enthousiaste. Il parlait de nouvelle croisade et jurait de découvrir ceux dont la foi était faible dans nos rangs.\n\nJe lui ai dit d\'y aller doucement. Nous ne détruisions pas la Croisade mais formions un groupe d\'élite des plus croyants pour exécuter la volonté de la Lumière en Norfendre. Je crains que l\'amitié de l\'homme avec LeCraft soit en train de le changer lentement. Ils ont tous les deux leur utilité, cependant.\n\nNéanmoins, les discours de Delarue sur une nouvelle croisade m\'ont fait réfléchir. Je crois que quand cette nouvelle force sera assemblée, je lui donnerai un nouveau nom. Il devra évoquer notre nouvelle mission, et également fournir un lien avec le passé. Je prierai avec le grand abbé pour cela.' WHERE `entry` = 3125;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>Nos prières ont été entendues. Le grand abbé a été particulièrement ému par la force de la voix de la Lumière, sa clarté et sa détermination.</P>\n<BR/>\n<P>Ceux d\'entre nous qui vont au nord - les plus loyaux - seront connus sous le nom d\'Assaut écarlate.</P>\n<BR/>\n<P>Et nous serons un véritable assaut sur Norfendre ! Le cancer du Fléau menace de déborder dans la couronne du monde et de nous noyer tous. Il est temps d\'amener les combats directement sur le terrain du roi-liche.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3126;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<BR/>\n<P>C\'est avec émoi que je regarde la Nouvelle-Avalon, je pense pour la dernière fois. Le destin de notre cause se trouve en Norfendre. Je ressens un étrange sentiment d\'inquiétude pour une raison inconnue. La mission à venir devrait faire disparaître toutes ces préoccupations. Je me les sortirai de l\'esprit.</P>\n<BR/>\n<P>L\'Aube cramoisie est proche.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3129;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Je n\'ai aucune nouvelle de mes messagers, ce matin. Il est clair qu\'aucun d\'entre eux n\'a réussi à atteindre Âtreval. Les Maleterres sont perdues. Pursang arrivera avec ses forces et ils seront annihilés en terrain découvert.</P>\n<BR/>\n<P>Cet après-midi, j\'ai reçu une vision de la Lumière. J\'y ai vu la destruction totale de tout ce que nous avons construit ici. Le message était clair : on me disait d\'emmener les fidèles survivants et d\'abandonner la croisade à son triste destin.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3143;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Langrain m\'a dit plus tard qu\'il avait eu la même vision. Je ne comprends pas comment la Lumière peut nous demander d\'accomplir une action aussi déshonorante, mais je n\'ai pas à remettre ses ordres en question. Je dois obéir. Alors j\'obéirai.</P>\n<BR/>\n<P>C\'est avec émoi que je contemple la Nouvelle-Avalon, sans doute pour la dernière fois. Notre destin se trouve en Norfendre. Je ressens un étrange sentiment d\'inquiétude, sans raison précise. Notre prochaine mission devrait faire disparaître toutes ces préoccupations. Je les chasserai de mon esprit.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3144;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Peut-être qu\'avec de la chance, le haut-commandant Pursang parviendra d\'une façon ou d\'une autre à résister et rassemblera les survivants. Je suis lâche... Comme un chien s\'enfuyant la queue entre les jambes !</P>\n<BR/>\n<P>On me dit que ce voyage va durer deux mois. Les autres navires ne sont pas construits pour la vitesse comme la Folie. Ils transportent la plupart de nos forces et de notre équipement, et ne sont rien de plus que des simples navires cargo, mais ils arriveront là-bas sans encombre.</P>\n<BR/>\n<P>Je n\'ai pas vraiment hâte, mais pour la Lumière, j\'affronterai le mal de mer. Je dois juste faire en sorte que les autres ne me voient pas.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3145;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Je n\'ai rien écrit depuis un moment. Tout ce que je peux faire, c\'est me tenir debout et essayer de ne pas être malade. Les hommes commencent à se demander pourquoi je m\'enferme presque tout le temps dans ma cabine. Ce ne serait pas bon pour leur moral de me voir ainsi.</P>\n<BR/>\n<P>Ce ne devrait plus être long maintenant. Je prie pour cela. Six semaines se sont déjà écoulées et chaque jour, on dirait que le temps empire. Je ne peux qu\'espérer que Norfendre n\'aura pas un aussi mauvais temps. Je ne suis pas faite pour le froid.</P>\n<BR/>\n<P>La Sainte Lumière est silencieuse depuis très longtemps.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3146;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Nous avons été attaqués par surprise ! Des géants dans d\'énormes drakkars à rames ont surgi de la brume comme des fantômes ! Ils étaient aussi silencieux que la mort.</P>\n<BR/>\n<P>J\'ai perdu un navire et tous les hommes qu\'il transportait. Nous avons combattu avec beaucoup de courage et d\'adresse, si l\'on considère que nous n\'avons reçu qu\'un entraînement des plus rudimentaires en combat naval.</P>\n<BR/>\n<P>Après l\'attaque, les cris de nos hommes capturés par les géants ont retenti sur l\'eau. Au bout d\'un moment, le silence s\'est fait. L\'évêque Delarue a dirigé la prière de nos hommes.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3147;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Je me suis réveillée ce matin alors que j\'étais en train de faire des calculs sur mes cartes. La Lumière guidait ma main, me montrant exactement où nous devions aller.</P>\n<BR/>\n<P>Nous sommes près du but !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3148;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Terre !</P>\n<BR/>\n<P>En descendant de la chaloupe, j\'ai planté notre bannière sur la plage et j\'ai été submergée par la Sainte Lumière, qui a parlé à travers moi. Aujourd\'hui est le jour de l\'Aube cramoisie, le grand jour que nous attendions tous. Cet endroit sera le site de la Nouvelle-Âtreval. Désormais, nous ne sommes plus la Croisade écarlate mais l\'Assaut écarlate !</P>\n<BR/>\n<P>À l\'assaut du Norfendre ! Le cancer du Fléau menace de déborder du toit du monde et de nous dévorer tous. Le temps est venu de porter la guerre sur le territoire du roi-liche !</P>\n</BODY>\n</HTML>' WHERE `entry` = 3149;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Presque un mois s\'est écoulé maintenant et la construction a continué rapidement. J\'étais trop occupée pour écrire. Mes éclaireurs me disent que cette terre est pleine de dragons et d\'autres bêtes étranges. Nous allons rester entre nous jusqu\'à ce que nous soyons prêts.</P>\n<BR/>\n<P>Aujourd\'hui, au milieu du service, le grand abbé a déclaré qu\'un visiteur arriverait bientôt : un visiteur que la Lumière nous envoyait pour nous mener à la victoire.</P>\n<BR/>\n<P>Je ne sais qu\'en penser. Pourquoi la Lumière ne m\'a-t-elle rien dit ? N\'ai-je pas servi loyalement ? Vais-je maintenant être remplacée par un étranger ?</P>\n</BODY>\n</HTML>' WHERE `entry` = 3150;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>L\'amiral Barean Ponevent s\'est présenté à ma porte ce soir ! D\'après tous les récits que nous connaissions, il serait mort sur ces rivages il y a bien longtemps.</P>\n<BR/>\n<P>Il n\'avait pas l\'air suffisamment âgé, et pourtant je savais que c\'était lui. Il m\'a affirmé qu\'il était le seul rescapé de sa flotte maudite, et qu\'il n\'avait survécu que grâce à la faveur de la Lumière.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3151;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Nous avons parlé toute la nuit et une partie de la matinée. Il m\'a assuré qu\'il n\'avait pas l\'intention de prendre ma place, mais que la Lumière lui avait ordonné de traverser la grande Désolation des dragons et de me servir de conseiller, et de commandant en titre seulement. Il a affirmé qu\'un grand changement allait se produire en Norfendre. L\'Alliance et la Horde allaient débarquer en masse, pour contrecarrer une grande peste que le roi-liche était sur le point de déclencher.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3152;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Les hommes se sont pris d\'une fervente admiration pour le grand amiral, en particulier le grand abbé Langrain et l\'évêque Delarue. Apparemment, la Sainte Lumière a murmuré une nouvelle bénédiction à l\'amiral pendant son sommeil, qu\'il a dévoilée à Langrain. Certains de nos hommes se sont convertis à la prêtrise et se font désormais appeler les « prêtres du corbeau ».</P>\n<BR/>\n<P>Seul Jordan ne semble pas impressionné. Je suppose que c\'est compréhensible. Il pense probablement que sa position est menacée si la mienne l\'est.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3153;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Quelque chose ne va pas. Je ne sais pas pourquoi, mais je n\'arrive pas à faire totalement confiance à l\'amiral. Il n\'a rien fait de mal. Bien au contraire ! Cependant, je dois me fier à mon intuition.</P>\n<BR/>\n<P>Je prierai pour comprendre. La Lumière nous a envoyé l\'amiral pour nous mener à la victoire et je n\'ai pas à mettre en doute ses décisions. Je continuerai donc à obéir. Je suis loyale.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3154;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Deux autres mois ont passé. Nous avons beaucoup avancé dans la construction de la Nouvelle-Âtreval. Le mur est quasiment terminé ainsi que les casernes. Les hommes de Kaleiki sont des travailleurs extraordinaires.</P>\n<BR/>\n<P>Je n\'ai pas eu le cœur à écrire. J\'ai soigneusement évité de mettre mes pensées par écrit, pour une raison ou pour une autre. Dernièrement, la Lumière ne m\'a pas rendu visite, pourtant l\'amiral m\'assure qu\'il n\'y a aucune raison de s\'inquiéter.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3155;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>Mes hommes me rapportent qu\'un petit groupe de Réprouvés a installé un campement à flanc de colline, au nord. De l\'autre côté de la colline, l\'Alliance a apparemment commencé à construire une base bien plus grande.</P>\n<BR/>\n<P>L\'amiral affirme que nous devrions les laisser faire. D\'autres forces de la Horde se sont rassemblées à l\'ouest, et elles viendraient sûrement à leur aide si nous les attaquions. Je n\'aime pas ça, mais je peux comprendre la logique de son raisonnement.</P>\n</BODY>\n</HTML>' WHERE `entry` = 3156;
UPDATE `locales_page_text` SET `Text_loc2` = 'Skor\'zad jeta un coup d\'œil avec nervosité au-delà de la colonne. Comme il l\'avait prévu, il la vit flotter, seule dans sa chambre. Était-ce vraiment une bonne idée de se rendre dans la chambre de la banshee si tard le soir ? Les nécromanciens le remettraient sûrement en terre s\'ils le découvraient. Le pire, c\'est qu\'il ne savait même pas si ses sentiments étaient réciproques, et Madame Sidnari était connue pour son mauvais caractère.' WHERE `entry` = 3157;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les quelques doutes que le zombie avait furent rapidement oubliés quand il eut un aperçu complet de sa beauté. Ses traits distinctement elfiques - un souvenir de sa forme dans une vie passée - étaient désormais mis en valeur par les plaies suintantes et les croûtes qui parsemaient son visage. Sa peau purpurine presque transparente était sommairement couverte par des voiles nécrotiques qui moulaient sa silhouette d\'enfant perdue. La chair morte se fondait parfaitement avec l\'éphémère, son torse sans jambes posé sur une sphère d\'énergie bleutée, flottant à quelques centimètres du sol de granit glacé. Skor\'zad se détendit un peu et essuya la sueur et l\'ichor qui coulaient en alternance de son front proéminent.' WHERE `entry` = 3158;
UPDATE `locales_page_text` SET `Text_loc2` = 'Apparemment inconsciente de sa présence, elle glissa vers la seule fenêtre de la pièce et regarda le champ de bataille. La désolation s\'étendait devant ses yeux. Moins de six heures avant, les cris stridents des enfants humains avaient retenti dans l\'air, mais maintenant... il ne restait plus que le doux silence de la mort.' WHERE `entry` = 3159;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le vent se faufila dans ses cheveux, joua avec les boucles emmêlées semblables à des vers, les amenant autour de ses yeux creusés dans leurs orbites. Skor\'zad, tout à coup conscient qu\'il se penchait en avant avec désir, se recula derrière la colonne, terrifié à l\'idée que la noire pleureuse l\'ait vu. Elle l\'avait bien vu - mais il n\'avait aucune crainte à avoir.' WHERE `entry` = 3160;
UPDATE `locales_page_text` SET `Text_loc2` = '« Skor\'zad », dit-elle. « Je vous attendais. Vous avez bien combattu ajourd\'hui. » La banshee regarda timidement par-dessus son épaule droite en décomposition, laissant ses voiles se desserrer légèrement. « Il est temps pour vous de... toucher votre récompense. »' WHERE `entry` = 3161;
UPDATE `locales_page_text` SET `Text_loc2` = '<Les quelques pages suivantes semblent être collées.>' WHERE `entry` = 3162;
UPDATE `locales_page_text` SET `Text_loc2` = 'Tartek l\'Enragé. L\'une des plus puissantes abominations que le Fléau a jamais créée. Créé par le liche Amnennar le Porte-froid pour aider à mettre en place une base aux Souilles de Tranchebauge, Tartek a abattu à lui seul plus de 1200 hurans avant d\'être finalement vaincu. Son sacrifice a fourni des cadavres à Amnennar pour qu\'il puisse constituer rapidement sa propre armée de hurans morts-vivants, et former finalement l\'avant-poste du Fléau le plus important de Kalimdor. Les restes de Tartek ont été réanimés et utilisés pour créer Glouton, l\'actuel lieutenant d\'Amnennar.' WHERE `entry` = 3163;
UPDATE `locales_page_text` SET `Text_loc2` = 'Obrahiim, le Voyageur. Brillant architecte, clé de voûte de la conception de Naxxramas, et cerveau derrière la création d\'Achérus, il a rapidement gravi les échelons de modeste péon squelette jusqu\'à devenir l\'un des plus fidèles conseillers de Kel\'Thuzad.\n\nToujours en quête de connaissances, Obrahiim a voyagé à travers le monde, trouvant notamment son inspiration première dans l\'architecture des Nérubiens et la magie des démonistes orcs. Il a été vu pour la dernière fois il y a plusieurs années, se dirigeant vers l\'ouest pour aller étudier les ruines de Zin-Azshari à Kalimdor.' WHERE `entry` = 3164;
UPDATE `locales_page_text` SET `Text_loc2` = 'Diodor le Damné. Nécromancien humain, c\'est l\'un des premiers membres du Culte des damnés, et un héros décoré de la Première Guerre. Il a mené avec succès un contingent du Fléau à la victoire lors du premier et deuxième assaut de Thermidor contre le Monastère écarlate. Tué au combat lors du troisième (et dernier) assaut de Thermidor, qui a été repoussé avec succès par les agents de la Croisade écarlate, qui avaient enfin compris son modus operandi.' WHERE `entry` = 3165;
UPDATE `locales_page_text` SET `Text_loc2` = 'Kirkessen le Zélé. Puissante liche sous l\'autorité directe du roi-liche en personne. Vétéran décoré ayant participé à d\'innombrables batailles, Kirkessen a conçu et mené le Siège cramoisi, l\'un des assauts organisés les plus importants contre la chapelle de l\'Espoir de Lumière. Contre toute attente, son attaque fut rapidement contrée, et ses forces subirent d\'énormes dégâts. Kirkessen lui-même reçut un coup critique du seigneur Maxwell Tyrosus, et n\'est plus capable d\'assumer une forme corporelle.' WHERE `entry` = 3166;
UPDATE `locales_page_text` SET `Text_loc2` = 'NORFENDRE OUEST - Des rapports de l\'escadron de surveillance aérienne dans le glacier de la Couronne de glace indiquent que les communications avec le seigneur-liche, Frisshiver, ont été brusquement interrompues. "Frisshiver a été vu pour la dernière fois pilotant la nécropole, Talramas, au-dessus de la toundra Boréenne pour une mission confidentielle", rapporte l\'ingénieur du Fléau, Karomon, officier de renseignement en chef de l\'escadron. "Nos diagnostics montrent que ses cristaux de communication sont en parfait état de fonctionnement... C\'est juste... le silence total."' WHERE `entry` = 3167;
UPDATE `locales_page_text` SET `Text_loc2` = 'BRÈCHE-DE-MORT, ROYAUMES DE L\'EST - L\'instructeur Razuvious a fait un discours un peu plus tôt cette semaine, répétant sa politique en matière de mutinerie et d\'insubordination. "Tout chevalier de la mort qui osera déserter le Fléau connaîtra un destin pire que la mort. Ses souffrance seront infinies - j\'y veillerai personnellement."' WHERE `entry` = 3169;
UPDATE `locales_page_text` SET `Text_loc2` = 'DÉSOLATION DES DRAGONS - Le prince Valanar recherche un chevalier de la mort exceptionnel pour se rendre à la Désolation des dragons, la plaine glacée centrale du Norfendre, et pour diriger une troupe de soldats d\'élite contre le vol draconique rouge. "Votre entraînement est presque terminé", a-t-il déclaré lors d\'un récent discours public. "Le temps est venu d\'attaquer ceux qui protègent les vivants ! Nous allons leur montrer le véritable sens du nom Désolation des dragons !"' WHERE `entry` = 3171;
UPDATE `locales_page_text` SET `Text_loc2` = 'Alexstrasza, la reine des dragons rouges et peut-être l\'ennemie la plus puissante du Fléau en Norfendre, a dernièrement été vue en train de monter sa propre armée, même si on pense que ses efforts sont plutôt dirigés contre le vol draconique bleu ennemi.' WHERE `entry` = 3172;
UPDATE `locales_page_text` SET `Text_loc2` = 'FOSSOYEUSE - Nos sources dans Fossoyeuse rapportent que la Horde se mobilise pour organiser un assaut sur Norfendre. "Les troupes de la Horde, la plupart revenant d\'un déploiement en Outreterre, se rassemblent maintenant et se préparent à attaquer la Couronne de glace."' WHERE `entry` = 3173;
UPDATE `locales_page_text` SET `Text_loc2` = 'De plus, nos agents infiltrés rapportent que l\'activité à la Société royale des apothicaires réprouvée a augmenté dernièrement. "Bien que rien ne soit confirmé pour le moment, de nombreuses rumeurs affirment que la Société a fait une avancée dans ses recherches sur la peste. Encore une fois, ce ne sont que des on-dit, mais il est conseillé à toutes les forces d\'éviter les alchimistes réprouvés."' WHERE `entry` = 3174;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les chevaliers de la mort d\'Acherus\n\nNom : Jayde\nStatut : En vie\nCommentaire : Sanguinaire' WHERE `entry` = 3175;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Sixen\nStatut : En vie\nCommentaire : Loquace' WHERE `entry` = 3176;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Munch\nStatut : En vie\nCommentaire : Destructeur' WHERE `entry` = 3177;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Cobalt\nStatut : Décédé\nCommentaire : A succombé à sa mort. Pas grand-chose là-haut. Ça lui a probablement évité une vie de souffrance.' WHERE `entry` = 3178;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Harmonie\nStatut : Décédée\nCommentaire : Le nom de ce chevalier de la mort était en violation avec la décence publique du Fléau. Abattue par le roi-liche.' WHERE `entry` = 3179;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Melt\nStatut : En vie*\nCommentaire : Transformé en goule.' WHERE `entry` = 3180;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Milton\nStatut : En vie\nCommentaire : Pérore' WHERE `entry` = 3181;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Minitalis\nStatut : En vie\nCommentaire : Voit des choses qui n\'existent pas.' WHERE `entry` = 3182;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : $N\nStatut : En vie\nCommentaire : Plus de clochettes.\n\n<Le livre continue ainsi sur des centaines de pages.>' WHERE `entry` = 3183;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les chevaliers de la mort d\'Acherus\n\nNom : Jayde\nStatut : En vie\nCommentaire : Imparable.' WHERE `entry` = 3184;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Sixen\nStatut : Décédé\nCommentaire : Mort avec la bouche ouverte.' WHERE `entry` = 3185;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Munch\nStatut : En vie\nCommentaire : En colère. Plein de haine. Fera de bonnes choses.' WHERE `entry` = 3186;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Cobalt\nStatut : Décédé\nCommentaire : A succombé à sa mort. Pas grand-chose là-haut. Ça lui a probablement évité une vie de souffrance.' WHERE `entry` = 3187;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Harmonie\nStatut : Décédée\nCommentaire : Le nom de ce chevalier de la mort était en violation avec la décence publique du Fléau. Abattue par le roi-liche.' WHERE `entry` = 3188;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Melt\nStatut : Décédé\nCommentaire : Transformé en goule. Tué par un paysan écarlate.' WHERE `entry` = 3189;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Milton\nStatut : Porté disparu\nCommentaire : Gnome couard.' WHERE `entry` = 3190;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : Minitalis\nStatut : En vie\nCommentaire : Chasse les papillons.' WHERE `entry` = 3191;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nom : $N\nStatut : En vie\nCommentaire : Est personnellement responsable du massacre de centaines de croisés écarlates. En veut encore.\n\n<Le livre se poursuit sur des centaines de pages.>' WHERE `entry` = 3192;
UPDATE `locales_page_text` SET `Text_loc2` = 'Corpuleux, grand cuisinier d\'Achérus qui fait bonne cuisine pour vous. Lui fier de nourrir puissant Fléau et rendre plus fort. Pas fâcher Corpuleux, lui pas vous nourrir.\n\nRègles :\n\nChevalier de la mort manger d\'abord, goule attendre son tour.\n\nPas manger goule près de vous. Assiette propre d\'abord.\n\nD\'accord ajouter limaces et poux personnels à nourriture. Corpuleux pas vexé.\n\nDe la vermine ? Apporter à Corpuleux !\n\nD\'accord manger humain ou elfe. Elfe autre viande blanche.' WHERE `entry` = 3211;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rapport de réanimation d\'un wyrm de givre\n\nLes os massifs étaient presque ensevelis sous la neige, mais on ne pouvait se tromper devant la griffe sinistre qui dépassait, tordue dans une agonie solennelle. Les nécromanciens s\'assemblèrent sans dire un mot, formant un cercle autour du corps du dragon. Ils restèrent immobiles pendant un moment, alors que le vent soufflait des bourrasques de neige tourbillonnant autour d\'eux. Puis le rituel commença.' WHERE `entry` = 3213;
UPDATE `locales_page_text` SET `Text_loc2` = 'Des rayons de lumière impie émanaient du centre des os gelés, perçant à travers la neige et la glace jusqu\'à ce que l\'immense dépouille soit totalement exposée. Sur un geste du grand nécro-seigneur Antiok, les os tremblèrent et firent une embardée au-dessus du sol, avant de se mettre à tourner lentement sur eux-mêmes.' WHERE `entry` = 3214;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les incantations des nécromanciens enflèrent alors qu\'ils commençaient à donner conscience par la force aux restes de la créature. De violentes contorsions secouaient le corps tandis que la conscience du wyrm luttait contre sa réanimation corrompue. Un faible cri perça l\'air et la bête fut soumise. Une lueur glacée s\'alluma entre ses côtes béantes, se répandit le long de ses membres et prêta une lueur de vie étrange à ses orbites.' WHERE `entry` = 3215;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le grand nécro-seigneur s\'avança et dit : « Le roi-liche a jugé utile de vous réanimer pour servir le Fléau. Vous serez notre plus noble instrument de mort, faisant pleuvoir la douleur sur les villages de nos ennemis, vous régalant des vivants, et transportant nos meilleurs chevaliers de la mort. »' WHERE `entry` = 3216;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le wyrm de givre les regarda et baissa légèrement la tête en signe d\'accord. La conversion était terminée. Se dressant sur ses pattes arrières, il battit de ses ailes semblables à un éventail de couteaux dans le ciel.' WHERE `entry` = 3217;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il a été porté à mon attention que certains des nouveaux chevaliers de la mort sont parfois troublés par des souvenirs de leur ancienne vie. Je demande instamment à ces esprits agités de ne plus les ressasser, car personne n\'est plus dégoûté que moi de ces erreurs passées. Cependant, au sein du Fléau, j\'ai trouvé l\'absolution.' WHERE `entry` = 3218;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bienvenue dans le Fléau. Maintenant que vous avez pris votre véritable service, vous trouverez peut-être un immense soulagement dans votre libération des divers inconvénients des mortels. Désormais, vous ne perdrez plus votre temps en sommeil, vous n\'aurez plus besoin de vous abriter des éléments et même de vous couper cheveux et ongles ! Comme c\'est délicieux !' WHERE `entry` = 3226;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cependant, il y a quelques petits détails, quasiment insignifiants, à connaître quand on est mort-vivant :\nSurveillez votre pourrissement. Un peu de pourriture est parfait pour effrayer vos ennemis, mais un bras qui tombe alors que vous allez frapper quelqu\'un est tout simplement embarrassant.\nApprenez à aimer votre puanteur. Certains disent fétides, nous disons rawrr.\nÉvitez les animaux vivants, en particulier les chiens.' WHERE `entry` = 3227;
UPDATE `locales_page_text` SET `Text_loc2` = 'Faites attention à votre inintelligibilité. Si vous égarez votre mâchoire inférieure, vous serez sans doute $gréassigné:réassignée; à un poste où votre gargouillement ne sera pas une gêne. Et peu d\'entre eux ont un prestige quelconque.\nL\'alcool, ce n\'est vraiment plus la même chose, désormais. Mes excuses.\nUn bain de sang par jour tient le vivant éloigné (ajouter des bulles est mal vu). \nÉvitez les couleurs pastel. Elles ne vont pas du tout avec votre teint.\nVous n\'aurez plus jamais l\'air $ggros:grosse; dans vos pantalons.' WHERE `entry` = 3228;
UPDATE `locales_page_text` SET `Text_loc2` = 'Entretien des goules\nManuel de débridement et de recyclage' WHERE `entry` = 3229;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bandages\n\nDes blessures saignant plus de trois fois par jour devraient être bandées. Quand vous sélectionnez un type d\'étoffe pour vos bandages, choisissez celle que vous préférez. De nombreuses goules préfèrent l\'étoffe runique pour sa couleur pourpre naturelle qui devient d\'un marron noir quand elle est imbibée de sang. On ne peut cependant pas ignorer les propriétés de jaunissement suprêmement élégantes du simple lin. Le mieux, c\'est d\'expérimenter !' WHERE `entry` = 3230;
UPDATE `locales_page_text` SET `Text_loc2` = 'Croûtes et plaies\n\nUne plaie purulente et croûtée est un moyen formidable d\'affirmer son style qui vous aide vraiment à communiquer vos changements d\'humeur. Cependant, pas de laisser-aller ! Ne pas gratter une croûte, c\'est manquer une belle occasion de briller.' WHERE `entry` = 3231;
UPDATE `locales_page_text` SET `Text_loc2` = 'Insectes\n\nOh, laissez-les un peu tranquilles ! Ils ne mangent pas grand-chose et ils contribuent efficacement à votre apparence livide et à votre puanteur atroce. Cependant, si vous étiez colonisé par des abeilles ou des guêpes, rendez-vous immédiatement à l\'abattoir le plus proche.' WHERE `entry` = 3232;
UPDATE `locales_page_text` SET `Text_loc2` = 'Membres perdus\n\nEn plein combat, ce n\'est pas vraiment le moment ou le lieu pour remplacer un membre perdu. Veuillez continuer avec ceux qu\'il vous reste. Le bras ou la jambe manquant sera remplacé avec le membre le plus ressemblant que nous puissions trouver dès qu\'un acolyte pourra jeter un œil sur vous. À la rigueur, empruntez-en un à un collègue !' WHERE `entry` = 3233;
UPDATE `locales_page_text` SET `Text_loc2` = 'Cannibalisme\n\nPour éviter que le personnel du Fléau ne soit à nouveau mangé de manière prématurée, vous pouvez seulement manger quelque chose de mort qui est sur le sol depuis plus de 5 secondes ! Les couchettes des acolytes ne sont pas considérées comme étant « sur le sol » !' WHERE `entry` = 3234;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je vous avais prévenus que la prochaine campagne où je passerais plus de temps à nettoyer mes bottes qu\'à frapper des enfants, des têtes tomberaient ! Et donc, les tours au nettoyage des latrines sont doublés en fréquence ET en durée pour tous les geists à partir de maintenant !\n\n—Instructeur Razuvious' WHERE `entry` = 3239;
UPDATE `locales_page_text` SET `Text_loc2` = 'Voyez la plus belle des ironies. Les vivants prêtent à la non-mort tous les attributs les plus répugnants de leurs propres vies : servitude sans espoir, sauvagerie, inertie de l\'esprit. Nous servons ! Les vivants servent eux aussi, mais ils servent des rois, seigneurs de guerre, druides, prêtres, dieux, hommes et animaux. Nous, nous ne servons que la puissante et unificatrice volonté du roi-liche, qui nous porte à la prospérité et à l\'unité !' WHERE `entry` = 3240;
UPDATE `locales_page_text` SET `Text_loc2` = 'Malgré tout l\'acharnement de l\'humanité, son histoire n\'apporte qu\'une interminable énumération d\'actes tragiques ou cruels, de traîtrises et d\'actes d\'égoïsme. Pour le Fléau, il n\'est question que d\'efficacité et d\'entièreté. Servez le roi-liche dans la vie, ou servez-le dans la mort. Sur le chemin qui est le sien, les routes ne mènent qu\'à l\'accomplissement de soi !' WHERE `entry` = 3241;
UPDATE `locales_page_text` SET `Text_loc2` = 'Si la chair en est froide, l\'être y est habité d\'une chaleureuse unité : les créatures du puissant Fléau forment une seule nation, un seul esprit, un seul être. Le vrai froid est celui qui hante les regards des gardes qui ferment les frontières d\'un pays voisin à des réfugiés malades en temps de guerre ! Les hommes ne sont-ils pas une seule et même espèce ? Non, car leurs cœurs glacés les divisent.' WHERE `entry` = 3242;
UPDATE `locales_page_text` SET `Text_loc2` = 'Portez haut nos idéaux, et mettez à bas nos ennemis ! Incorporez leurs pertes dans nos rangs jusqu\'à ce que tous servent le Trône de glace !' WHERE `entry` = 3243;
UPDATE `locales_page_text` SET `Text_loc2` = 'J\'ai enfin obtenu la faveur du chapitre et gagné mon entrée à la Scholomance ! Aujourd\'hui, mon ascension finale vers la glorieuse non-mort commence !' WHERE `entry` = 3244;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aujourd\'hui, je suis devenu nécromancien Thuzadin à Stratholme ! Je me demande un peu pourquoi personne n\'éteint les incendies, et j\'ai été mortifié de découvrir par quel absurde système les ziggourats sont liées aux portes de l\'abattoir. Si nous devons empêcher les gens d\'approcher de l\'abattoir, pourquoi est-ce qu\'on ne verrouille pas tout simplement la porte " Définitivement "' WHERE `entry` = 3248;
UPDATE `locales_page_text` SET `Text_loc2` = 'Aujourd\'hui, premier jour de travail dans une usine de guerre du puissant Fléau ! Naxxramas grouille d\'activité. J\'ai été attristé d\'apprendre qu\'encore une fois on avait recours à d\'idiots petits casse-tête mécaniques pour alimenter des téléporteurs d\'une importance capitale, mais, en dehors de cela, tout semble tourner très correctement. J\'ai pu tourner mes pensées vers des recherches profitables, jusqu\'à ce que je tombe sur un de ces pervers nérubiens...' WHERE `entry` = 3252;
UPDATE `locales_page_text` SET `Text_loc2` = 'Après mes échecs répétés au service du Fléau, j\'ai bien compris que les principes auxquels je m\'astreins sont bien plus élevés que ceux des individus qui occupent même les plus hauts échelons de cette hiérarchie. Étant donné l\'inflexibilité inhérente aux structures de pouvoir social, il n\'est pas aisé de faire cohabiter les tentatives d\'instiguer le changement d\'un côté, et les défis sociaux qui perdurent de l\'autre.' WHERE `entry` = 3256;
UPDATE `locales_page_text` SET `Text_loc2` = 'Ceeeerrrrveauuuuux. Ceeeeeeerrrrrrrveauuuuux. CEEEEEEERRRRVEEEEAAAUUUUX !!!\n\n<Et ça continue comme ça sur plus de quarante pages.>' WHERE `entry` = 3260;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le décret du Fléau\nChapitre 1\nPar Kel\'Thuzad\n\nLes racines du Fléau plongent dans la folie de toutes les races. Notre accession au pouvoir est le produit des péchés de ceux qui veulent notre fin. Les soi-disant « héros » de la terre tombent devant nous en ayant recours à une mesure de nos idéaux pour parvenir à la victoire qu\'ils ont imaginée. Leur victoire véhicule une prise de conscience du fait que sans ce qu\'ils décrivent comme déplorable pour l\'obtenir, nous les aurions alors vaincus. Dans les deux cas, victoire ou défaite, le Fléau gagne invariablement. Quand on accepte les faits, la soumission à notre volonté est inévitable.' WHERE `entry` = 3269;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Maître, notre roi-liche, est né sous cette même doctrine.\n\nConquérir, c\'est corrompre. Corrompre, c\'est prendre ce qui est droit, plein d\'espoir et vivant, et en faire tout le contraire par l\'ensemble des moyens disponibles dans notre arsenal. Les attributs des vivants sont tous synonymes de défauts qui causent leur perte : l\'espoir est dogme, la droiture est fanatisme, la vie est empathie. Découvrez ce qui donne envie de vivre aux vivants et retournez-le.' WHERE `entry` = 3270;
UPDATE `locales_page_text` SET `Text_loc2` = 'Connaître le chemin le plus direct vers la corruption est le summum de la sagesse. Une simple faiblesse du cœur, un arrachage des organes vitaux ou une maladie directe fonctionnent sur la plupart des gens. Cependant, la conquête de la plupart n\'est pas le décret du Maître, c\'est la conquête de tous. Il est vain de dépenser des ressources excessives pour fausser l\'esprit d\'un simple paysan quand une peste ordinaire suffit. Il est tout aussi peu sage de lancer cette même peste ordinaire contre les personnes endurcies et en bonne santé. Une étude stricte de toute difficulté révèlera des faiblesses utilisables.' WHERE `entry` = 3271;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le décret du Fléau\nChapitre 2\n\nL\'idée fausse la plus répandue parmi les vivants au sujet du Fléau est notre penchant pour ce qu\'ils considèrent comme le « mal ». Pour les démoraliser, nous laissons cette erreur se perpétuer, et nous la répandons même parmi les nôtres. La véritable nature du Fléau est notre transcendance des chaînes qui empêchent toutes les races vivantes de devenir aussi prospères que nous.' WHERE `entry` = 3272;
UPDATE `locales_page_text` SET `Text_loc2` = 'Par exemple : Quels bénéfices apportent l\'émotion et l\'honneur ? Ils servent aux vivants pour se justifier, pour les faire se sentir « bien ». Alors, quelles sont leurs faiblesses ? L\'émotion est en corrélation directe avec l\'ignorance de la logique et de la raison. L\'honneur est, la plupart du temps, la seule force qui maintient les ressources capables de combattre dans un combat perdu d\'avance. Il est déroutant même pour moi de voir que les vivants ne sont pas conscients de l\'enthousiasme que nous mettons à jouer les méchants, à nourrir leurs plus gros défauts en devenant l\'incarnation de tout ce qui les incite à un comportement irrationnel.' WHERE `entry` = 3273;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'ambiguïté morale ne peut pas être « nettoyée ». L\'antipathie ne peut pas être « soignée » ou « soulagée ». L\'avidité ne peut pas être « dispersée » et le courroux ne peut pas être « guéri ». Nous avons choisi d\'être l\'incarnation de toutes ces choses, et il y a malgré tout des gens encore assez fous pour croire que le Fléau peut être vaincu et complétement effacé. Aussi longtemps que les péchés les plus attirants des vivants resteront notre porte-drapeau, il y aura toujours des gens impatients de se soumettre à notre volonté. \n\nJe suis moi-même né sous cette doctrine.' WHERE `entry` = 3274;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le décret du Fléau\nChapitre 3\n\nLe potentiel du Fléau est infini. Nous, cependant, ne sommes pas du tout infaillibles. Soyez toujours $gconscient:consciente; de vos limites, ainsi que des ressources à votre disposition. Ayez toujours une stratégie de repli. Le sacrifice de serviteurs avides est la pierre angulaire de la philosophie du Fléau. Toute bravoure, souffrance, ou compatriotisme pour toute autre personne que le Maître est imprudent et sévèrement puni.' WHERE `entry` = 3275;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une autre idée fausse des étrangers à l\'égard du Fléau est que notre pouvoir réside dans la volonté résiduelle des vivants : nous serions une forme avancée de parasites, et sans un hôte régénéré, la vie nous quitterait tout simplement. Une fois encore, nous ne pouvons pas démentir ses idées si mal informées qui nous rendent service, mais cela ne pourrait pas être plus éloigné de la vérité. Nous ne sommes rien de moins qu\'une forme évolutive d\'adaptation à une terre autrefois proie d\'une lutte sans fin. Nous sommes immunisés contre les tribulations de la dissension, de la surpopulation, de l\'individualisme, de l\'égoïsme, de l\'imprudence, et même de la mortalité. Nous obéissons à la volonté du Maître, et le Maître nous offre tout ce que nous pourrons jamais désirer.' WHERE `entry` = 3276;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il est vrai que nous avons emprunté l\'architecture, la technologie et les prouesses physiques des cultures assimilées. Cela, comme les nombreux actes « maléfiques » du Fléau, n\'est fait que par pragmatisme. Le Fléau n\'a ni le temps ni le désir de générer une culture qui lui soit propre. Un membre du Fléau qui crée hors influence aura un attachement inhérent pour ce qu\'il a créé, un droit de voir cette chose considérée et utilisée. Cela mène à l\'individualité, qui mène à la dissension. La créativité est un défaut de culture, mais une obligation mineure quand il s\'agit de besoins simples tels que des bâtiments et de l\'équipement. Ce n\'est pas à nous de questionner ou de nous préoccuper de l\'origine de nos ressources, nous avons juste à savoir comment les utiliser.' WHERE `entry` = 3277;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le Fléau est la volonté de la terre. Tout ce qui vit se plie à notre volonté avec une facilité qui mène à une seule conclusion : ils ont besoin de nous, ils aspirent à notre salut. Il n\'y a aucune résistance à nos pratiques, aucune immunité à nos ordres. Nous sommes l\'inévitabilité que toute vie contient : la mort. La mort est liée à toute vie, et la non-mort est liée à toute mort. Plus vite les vivants reconnaîtront ce fait immuable, plus la transition sera facile.' WHERE `entry` = 3278;
UPDATE `locales_page_text` SET `Text_loc2` = 'Serviteurs, y faut entendre l\'appel de vot\' maître ! Le roi-liche il a donné à Drakuru le contrôle total des armées du Fléau à Zul\'Drak. On a r\'çu l\'ordre de tout tuer.$B$BTOUT DOIT MOURIR !$B$BOn va prendre tous les cadavres du Champ des morts et les envoyer aux gars du prince Navarius pour qu\'ils flaellent grave ! Rien n\'se perd quand Drakuru il est de garde !$B$BBientôt, on boira le sang des prophètes pour devenir plus forts que jamais ! Zul\'Drak et Gundrak ils vont tomber dans les mains du Fléau !' WHERE `entry` = 3279;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il y a longtemps, quand le Vent du Nord régnait seul sur ces sommets, le frère d\'un puissant chef taunka convoitait ces terres.' WHERE `entry` = 3280;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le frère du chef, Sabot-tempête, rassembla ses forces et partit arracher les pics Foudroyés à leur souverain légitime, le Vent du Nord.' WHERE `entry` = 3281;
UPDATE `locales_page_text` SET `Text_loc2` = 'Sabot-tempête tendit une embuscade au Vent du Nord au cœur de son domaine, et faillit réussir. Mais le Vent du Nord survécut à la traîtrise de Sabot-tempête et exécuta le taunka pour le punir de cette invasion.' WHERE `entry` = 3282;
UPDATE `locales_page_text` SET `Text_loc2` = '<La majeure partie des pages du livre est manquante ou tâchée et irrécupérable. Seules quelques-unes des entrées les plus anciennes et le nom « Darius Orion » sont encore lisibles.>' WHERE `entry` = 3284;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les conseils du Mage !\n\nQ : Mes amis me disent qu\'étudier la magie et devenir mage me rendra fragile, faible, et impopulaire auprès des filles. Est-ce que c\'est vrai ?\n-Jimmy « Incertain » Townsend' WHERE `entry` = 3302;
UPDATE `locales_page_text` SET `Text_loc2` = 'R : Ne vous inquiétez pas, Jimmy. Vos amis font référence à un stéréotype injuste qui trouve son origine dans un petit nombre de mages paresseux aux habitudes alimentaires calamiteuses. Il est tout à fait possible de maintenir une forme physique parfaite tout en trouvant du temps pour maîtriser la manipulation de la structure de l\'univers. Même les membres du Kirin Tor, grâce à une alimentation stricte et un programme d\'exercice physique complet, se maintiennent en grande forme malgré leur emploi du temps chargé.\nEt quelle femme n\'aime pas les métamorphoses ?' WHERE `entry` = 3303;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : Mes parents ne me soutiennent pas dans ma décision de devenir mage. Ils disent que la magie des arcanes est dangereuse et mène à d\'autres magies « plus dures » comme la démonologie. Je ne veux pas vendre mon âme. Ont-ils raison ?\n-Joseph Winters\n\nR : L\'inquiétude de vos parents n\'est pas fondée. Même s\'il existe des histoires de mages adorant les démons ou se tournant vers la nécromancie, il y a peu ou pas de preuves soutenant ces affirmations. Rassurez votre famille, tout se passera bien.' WHERE `entry` = 3304;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : J\'ai commencé récemment à étudier les arcanes et jusqu\'à présent, mes amis n\'ont fait que se moquer de moi. Les seules raisons pour lesquelles ils veulent me voir, c\'est pour invoquer de la nourriture et des boissons, et les téléporter. Quand les gens commenceront-ils à me respecter ?\n-Gregory Adams, dit  «Paillasson »\n\nR : Dès que vous aurez appris Explosion pyrotechnique.' WHERE `entry` = 3305;
UPDATE `locales_page_text` SET `Text_loc2` = 'Certains ont accusé les mages d\'être un groupe égocentrique et borné. Nous aimerions que vous sachiez que cela ne pourrait être plus éloigné de la vérité. Les mages sont des individus compatissants dont la constante recherche de la connaissance et de la puissance est centrée sur votre bien-être ! Comment les mages vous servent ? Je suis bien content que vous me posiez la question !' WHERE `entry` = 3306;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les discours en public vous rendent nerveux ? Imaginer votre auditoire en sous-vêtements ne vous aide pas ? Pourquoi ne pas les imaginer en moutons ? Ça ne marche toujours pas ? Nous transformerons les membres de l\'assistance en moutons pour vous.\n\nDemandez à votre ami mage de lancer un charme de ralentissement sur vous et prenez le temps de vous arrêter pour sentir le parfum des roses. Très drôle aussi aux courses !\n\nLa téléportation est le moyen de transport le plus chic. Rien ne ruine vos chances d\'obtenir un deuxième rendez-vous plus vite qu\'un cas aigu d\'O.D.N. (Odeur de Drake du Néant).' WHERE `entry` = 3307;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'ami d\'un mage n\'aura jamais faim ni soif. Et la nourriture invoquée ne contient pas de graisses transmutées !\n\nVous pensez qu\'une chope glacée est le meilleur moyen de conserver votre bière fraîche ? Vous devriez voir une chope qui a reçu un éclair de givre !\n\nDes études montrent que la plupart des chefs-d\'oeuvre de la litterature ont été écrits alors que l\'auteur était sous l\'influence d\'Intelligence des arcanes.' WHERE `entry` = 3308;
UPDATE `locales_page_text` SET `Text_loc2` = 'Obtenez un Voile d\'hiver blanc chaque année grâce aux blizzards invoqués pour vous par des mages. (Attention : rester dehors lors d\'un blizzard des arcanes peut provoquer de légers picotements dans les doigts et une mort douloureuse.)\n\nEt souvenez-vous : si quoi que ce soit ne se passe pas comme prévu, il y aura toujours un mage prêt à se téléporter auprès de vous.\n\nCe pamphlet a été imprimé sur du papier invoqué.' WHERE `entry` = 3309;
UPDATE `locales_page_text` SET `Text_loc2` = 'Il était une fois un mage qui ne pouvait pas lancer de boule de feu, de métamorphose ou d\'éclair de givre. Quand il invoquait de l\'eau, c\'est de la boue qui apparaissait. Ses tentatives pour lancer intelligence des arcanes ont rendu les sujets encore plus stupides. Sans hésitation, ses précepteurs s\'accordèrent à dire que leur élève, Maladorf, était le pire mage de Dalaran.' WHERE `entry` = 3346;
UPDATE `locales_page_text` SET `Text_loc2` = 'Entrée 1\n\nAvant que je puisse comprendre ce qui se passait, la dévastation était sur nous. La terre trembla et notre belle flèche, qui gardait Dalaran depuis la Deuxième guerre, se brisa.\n\nJe me rappelle avoir crié quand le visage de mon père disparut sous l\'échaffaudage qui tombait, mais je n\'ai rien entendu. Malgré ma terreur, je pensais à mon entraînement magique. Si seulement j\'avais pu ralentir ce désastre... mais les sorts mouraient en moi. Des traces de pouvoir démoniaque bloquaient mes efforts. Il n\'y avait pas d\'espoir. J\'ai fermé les yeux et attendu. Je ne sais comment, mais je suis encore là.' WHERE `entry` = 3353;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3358;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le don merveilleux de la magie peut être utilisé pour de nombreuses choses. Nous, les Instruits, avons découvert qu\'il était plus facile de diviser les arts indicibles en huit catégories différentes, de façon à mieux comprendre les fondamentaux de la magie elle-même. Chacune de ces catégories est connue sous le nom « d\'école » de magie, car elles sont souvent apprises séparément et les mages choisissent fréquemment de se spécialiser dans une catégorie ou une autre. D\'autres mages ont tenté de maîtriser toutes les formes de magie, mais peu ont atteint ce noble but. Dans ce volume, nous traiterons de chaque école de magie individuellement et de certains de leurs célèbres adeptes.' WHERE `entry` = 3359;
UPDATE `locales_page_text` SET `Text_loc2` = 'Toutes les écoles de magie peuvent se combiner pour créer de redoutables sorts qui utilisent de multiples sources de puissance. Certains de ces puissants sorts sont expliqués un peu plus loin dans cette série de livres, mais prenez garde, combiner plusieurs types de magie peut être extrêmement dangereux - en particulier pour les débutants.\n\n\\Ici, vous allez apprendre les bases de toutes les écoles de magie. Souvenez-vous qu\'il ne faut pas abuser de la magie. Ne pratiquez pas la magie sans la surveillance appropriée.' WHERE `entry` = 3360;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3361;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'abjuration est l\'étude de la magie protectrice et l\'une des plus importantes écoles qu\'un jeune mage doit étudier. Le sort d\'abjuration le plus généralisé est bouclier de mana, un sort qui transmute le mana brut en un bouclier qui protège le lanceur des attaques. Correctement exécuté, ce sort peut protéger le mage contre même la plus redoutable des attaques - mais le bouclier de mana est très difficile à maîtriser pour un novice. Cela prend souvent des mois à un mage pour parvenir à invoquer ne serait-ce qu\'un simple bouclier de mana, et pour cette raison, il existe des alternatives plus simples.' WHERE `entry` = 3362;
UPDATE `locales_page_text` SET `Text_loc2` = 'Une série de sorts d\'«armures des éléments » fut inventée pour aider le mage voyageur pour le cas malheureux où ce dernier serait attaqué. Ces sorts requièrent beaucoup moins d\'énergie à lancer qu\'un bouclier de mana et sont plus facilement maintenus sur la durée, mais ils n\'ont pas la puissance brute du bouclier de mana. Au lieu d\'arrêter complétement une attaque, un sort d\'armure de givre peut simplement diminuer l\'efficacité de l\'assaut. Les sorts d\'armure plus avancés offrent d\'autres bénéfices ; par exemple, le puissant sort d\'armure du mage aide en fait le lanceur à récupérer sa puissance magique plus facilement.' WHERE `entry` = 3363;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les adeptes des arts arcaniques sont souvent ambitieux, et nous nous retrouvons fréquemment dans des situations où la magie glisse parfois légèrement hors de notre contrôle. C\'est pour cette raison que les sorts de « Garde » existent. Il s\'agit de rapides et puissantes incantations qui protègent le lanceur contre une forme de dégâts élémentaires - dégâts souvent causés parce que le lanceur essaie une des autres écoles de magie. C\'est pourquoi l\'étude précoce de l\'abjuration est tellement importante.' WHERE `entry` = 3364;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'un des plus célèbres adeptes de la magie d\'abjuration est le prince Kael\'thas Haut-soleil. Même si le prince excelle dans toutes les écoles de magie, Kael\'thas a créé une variation de bouclier de mana qui est sans pareille parmi les résidents de Dalaran. L\'arcaniste Doan est également célèbre, pour avoir perfectionné un sort qui associe Évocation et Abjuration pour se protéger un court instant tandis qu\'il prépare un puissant sort à zone d\'effet. Ce sort d\'« Explosion » permet à Doan de bénéficier d\'une quasi-invulnérabilité pendant quelques précieuses secondes, mais seuls quelques mages sont capables de le lancer sans se mettre en danger.' WHERE `entry` = 3365;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3366;
UPDATE `locales_page_text` SET `Text_loc2` = 'La divination est l\'école de magie dédiée à la recherche d\'informations. De puissantes divinations peuvent permettre au mage de voir des cibles depuis une grande distance, ou même voir ce qui normalement serait invisible. L\'une des utilisations les plus communes de la magie de divination est la clairvoyance, qui est l\'art de voir quelque chose qui peut-être très loin - voire même dans un autre plan d\'existence.' WHERE `entry` = 3367;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je voudrais profiter de cet instant pour rappeler à mes apprentis que les bassins de clairvoyance ne sont pas un jouet. Vous n\'avez absolument pas le droit de les utiliser pour espionner les étudiants du sexe opposé sur le principe de la « vision à la demande ». Considérez ceci comme votre dernier avertissement.' WHERE `entry` = 3368;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le légendaire archimage connu sous le nom de Medivh est peut-être le plus grand maître connu de la divination. Ses sorts puissants lui ont permis de regarder le monde de Draenor depuis notre foyer en Azeroth - et peut-être même plus loin. Nous ne pouvons que spéculer sur la véritable puissance détenue à l\'époque par Medivh.' WHERE `entry` = 3369;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3370;
UPDATE `locales_page_text` SET `Text_loc2` = 'La divination est l\'école de magie dédiée à la recherche d\'informations. De puissantes divinations peuvent permettre au mage de voir des cibles depuis une grande distance, ou même voir ce qui normalement serait invisible. L\'une des utilisations les plus communes de la magie de divination est la clairvoyance, qui est l\'art de voir quelque chose qui peut-être très loin - voire même dans un autre plan d\'existence.' WHERE `entry` = 3371;
UPDATE `locales_page_text` SET `Text_loc2` = 'Je voudrais profiter de cet instant pour rappeler à mes apprentis que les bassins de clairvoyance ne sont pas un jouet. Vous n\'avez absolument pas le droit de les utiliser pour espionner les étudiants du sexe opposé sur le principe de la « vision à la demande ». Considérez ceci comme votre dernier avertissement.' WHERE `entry` = 3372;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le légendaire archimage connu sous le nom de Medivh est peut-être le plus grand maître connu de la divination. Ses sorts puissants lui ont permis de regarder le monde de Draenor depuis notre foyer en Azeroth - et peut-être même plus loin. Nous ne pouvons que spéculer sur la véritable puissance détenue à l\'époque par Medivh.' WHERE `entry` = 3373;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3374;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'enchantement est le procédé permettant d\'imprégner un objet - ou une personne - avec des pouvoirs magiques. Certains enchantements sont temporaires, d\'autres permanents. Apprendre à enchanter peut être difficile, mais c\'est l\'une des formes de magie les plus lucratives que l\'on puisse étudier. Je recommande fortement l\'étude de l\'enchantement, car c\'est l\'une des études de magie qui a le moins de chance de mener à ma propre destruction.' WHERE `entry` = 3375;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le désenchantement est le procédé de dissipation ou d\'abolition de la magie. Le sort de dissipation magique est l\'un des plus importants de l\'arsenal d\'un mage, car il peut potentiellement renverser les effets catastrophiques d\'un sort mal lancé. Il est également possible de désenchanter de manière permanente un objet magique. Ceci produit une forme unique de mana cristallisé qui peut être utilisé lors du processus d\'imprégnation de propriétés magiques dans un autre objet. Bien que cela puisse revenir cher, c\'est souvent l\'une des meilleures manières pour un jeune mage d\'étudier le procédé d\'enchantement.' WHERE `entry` = 3376;
UPDATE `locales_page_text` SET `Text_loc2` = 'Bien qu\'il ait été mentionné auparavant pour ses dons d\'abjuration, le prince Kael\'thas Haut-soleil est considéré comme l\'un des plus importants maîtres de l\'enchantement de notre temps. Il a non seulement maîtrisé la création de puissantes armes magiques, mais il a également appris à manipuler ces objets à distance, leur permettant de se battre d\'eux-mêmes. Cela peut permettre rapidement au prince de combattre comme s\'il était défendu par plusieurs gardiens doués alors même qu\'il est complètement seul. De fait, il s\'agit d\'une puissante défense.' WHERE `entry` = 3377;
UPDATE `locales_page_text` SET `Text_loc2` = 'INVINCIBLE\n\nPrécieux destrier du prince Arthas Menethil\n\nLoyal et affectueux dans la vie, puisses-tu trouver la paix dans la mort.\n\nRuisseaux purs et verts paturages, ami dévoué.' WHERE `entry` = 3378;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3379;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'illusion est l\'art de tromper la réalité elle-même. Les brumes de l\'illusion peuvent rendre un mage invisible ou inaudible au monde, ou encore déformer l\'image d\'un endroit en quelque chose de totalement différent. L\'illusion peut être utilisée pour se déguiser ou manipuler, mais prenez garde, il existe des sorts pour contrer les illusions dans l\'école de divination. Il n\'est pas judicieux de fonder toute votre carrière sur la magie illusoire.' WHERE `entry` = 3380;
UPDATE `locales_page_text` SET `Text_loc2` = 'Contrairement aux croyances populaires, les illusions sont bien plus que de simples tours de passe-passe. Le sort d\'invisibilité est l\'un des plus importants dans le répertoire de combat d\'un mage, car vous vous trouverez dans de dangereuses situations et vous aurez besoin d\'une méthode rapide de retrait stratégique. Les illusions peuvent aussi être utilisées pour tromper vos adversaires et leur faire croire que vous êtes ailleurs, ou même les faire se battre entre eux. Ce n\'est pas une tâche facile, mais l\'illusionniste accompli peut transformer des alliés en ennemis - et ses ennemis en alliés.' WHERE `entry` = 3381;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'ancienne archimage Jandice Barov - puisse son âme reposer en paix - était un excellent exemple d\'illusionniste doué. Au cours de sa vie, elle a développé un sort qui affichait plusieurs images de son corps qui étaient pratiquement semblables à sa véritable forme. Ces images copiaient ses actions depuis différents endroits, et il était presque impossible pour ses ennemis de la trouver. C\'était quasiment infaillible. Ce qui m\'amène à une autre leçon - quasiment ne suffit pas.' WHERE `entry` = 3382;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3383;
UPDATE `locales_page_text` SET `Text_loc2` = 'La nécromancie est l\'étude de la magie impliquant les morts. Elle est illégale et devrait être évitée à tout prix. Je parle ici de nécromancie uniquement parce qu\'il est de notre devoir d\'avoir une compréhension minimale de la magie employée par nos ennemis - et ne vous y trompez pas, tout adepte de la nécromancie est votre ennemi. Les nécromanciens et leurs partisans sont les ennemis de toutes les choses vivantes. Leur influence doit être évitée à tout prix.' WHERE `entry` = 3384;
UPDATE `locales_page_text` SET `Text_loc2` = 'La magie nécromantique a de nombreuses fonctions au-delà de la réanimation des morts. Les maîtres de ce domaine impur de la magie peuvent invoquer des maladies purulentes, capter les ombres pour en faire des éclairs d\'énergie incendiaire, et transir les vivants avec le pouvoir de la mort. La nécromancie peut aussi être utilisée pour reconstruire la chair des créatures mortes-vivantes, leur permettant de fonctionner à nouveau même une fois que ces monstres infects ont été détruits.' WHERE `entry` = 3385;
UPDATE `locales_page_text` SET `Text_loc2` = 'L\'ancien archimage Kel\'Thuzad est peut-être l\'exemple le plus notable de nécromancien moderne. Il a fortement contribué à la propagation initiale du Fléau et à la chute de Lordaeron. Aujourd\'hui, Kel\'Thuzad règne en tant que liche depuis la citadelle flottante de Naxxramas. Son existence actuelle fait peser une menace imminente sur nous tous.' WHERE `entry` = 3386;
UPDATE `locales_page_text` SET `Text_loc2` = '<Écrit par le célèbre archimage Ansirem Tisserune>' WHERE `entry` = 3387;
UPDATE `locales_page_text` SET `Text_loc2` = 'La neuvième et dernière école de magie est la transmutation. C\'est l\'une des écoles de magie les plus populaires et les plus utiles, qui permet à un mage de manipuler le temps et l\'espace. Le sort de transmutation le plus emblématique est la Métamorphose, qui permet à un mage de transformer quelque chose - ou quelqu\'un - en quelque chose d\'autre. Heureusement pour nombre de mes étudiants, les effets ne sont pas permanents. J\'ai vu plus d\'un apprenti inattentif transformé en mouton, cochon ou pire - ne me demandez pas ce que « pire » peut signifier.' WHERE `entry` = 3388;
UPDATE `locales_page_text` SET `Text_loc2` = 'La seconde utilisation la plus célèbre de la magie de transmutation est la téléportation. Le sort le plus simple de téléportation est le transfert, et il reste parmi les plus utiles. Le transfert peut être utilisé pour fuir rapidement un ennemi - ou pour sauver un mage accidentellement tombé d\'une falaise. N\'essayez pas d\'utiliser le transfert pour éviter de tomber vers une mort certaine, à moins que ce ne soit absolument nécessaire. C\'est le genre de tour que vous ne pouvez rater qu\'une fois.' WHERE `entry` = 3389;
UPDATE `locales_page_text` SET `Text_loc2` = 'Assurez-vous bien que vous connaissez votre destination avant de tenter de vous téléporter. Il y a une raison pour laquelle nous avons des sorts très spécifiques pour nous téléporter à certains endroits - essayer de lancer une téléportation « sans réfléchir » a souvent pour conséquence un mage très très mort dans un mur, un fauteuil ou un autre mage. Et quand je dis « dans », ça n\'a rien d\'agréable.' WHERE `entry` = 3390;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les sorts qui manipulent le temps rentrent également dans cette catégorie. Le sort toujours très populaire de chute lente est une excellente alternative à une chute vers la mort (et il est plus fiable que le transfert pour cet usage). Les adeptes plus avancés des arts arcaniques peuvent également apprendre un sort qui ralentit les mouvements de leurs ennemis. J\'ai entendu des rumeurs sur un sort qui augmente également la vitesse de déplacement, mais je ne l\'ai jamais vu en pratique.' WHERE `entry` = 3391;
UPDATE `locales_page_text` SET `Text_loc2` = 'Dame Jaina Portvaillant est une adepte extrêmement qualifiée de la magie de transmutation. Elle a développé une variation puissante de la très appréciée téléportation de masse capable de déplacer un pourcentage significatif de son armée avec un minimum d\'efforts. Ce sort contribue à la rendre extrêmement imprévisible sur le champ de bataille.' WHERE `entry` = 3392;
UPDATE `locales_page_text` SET `Text_loc2` = 'Par Ilustriel Vertefeuille\n\nStop ! Pensez à ce que vous êtes en train de faire. La magie des arcanes est-elle vraiment ce qui vous convient ?' WHERE `entry` = 3393;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les faits :\n- La magie est une force dangereuse qui entraîne la mort de millions de gens chaque année.\n- La magie des arcanes crée une forte dépendance. Une fois qu\'on commence à pratiquer la magie, il est presque impossible de s\'arrêter.\n- Apprendre de simples sorts des arcanes peut sembler innocent au début, mais c\'est souvent une passerelle vers des sorts de plus en plus durs. Dans le pire des cas, on peut même finir dépendant à la magie gangrenée.\n- L\'utilisation de la magie pendant la grossesse peut entraîner des risques graves pour la santé de l\'enfant.\n- L\'abus de magie peut mener à la violence et à la nécromancie.\n- La magie des arcanes n\'est pas « cool ».' WHERE `entry` = 3394;
UPDATE `locales_page_text` SET `Text_loc2` = 'C\'est pourquoi nous avons créé le programme S.P.A.M. (Système de Prévention des Abus de Magie) pour informer le public des dangers que représente l\'abus de magie. Notre mission est de montrer à la face du monde qu\'on peut très bien vivre sans magie. On peut toujours s\'amuser autrement, avec des fleurs, ou en tressant des paniers, par exemple. Ça ne donne pas envie, tout ça, peut-être ?' WHERE `entry` = 3395;
UPDATE `locales_page_text` SET `Text_loc2` = 'De plus, notre programme aide les dépendants à la magie en voie de guérison à se réinsérer dans la société. En presque 6 jours d\'existence, notre programme a établi une relation très forte avec la prêtresse d\'Elune et le Cercle cénarien. Ces organisations peuvent - et veulent - vous aider à prendre le chemin d\'une guérison rapide. N\'oubliez pas, il n\'est jamais trop tard pour apprendre à vous aimer vous-même !' WHERE `entry` = 3396;
UPDATE `locales_page_text` SET `Text_loc2` = 'De nos jours, Azeroth nous confronte tous à de nombreux défis qui font qu\'il est très difficile de conserver un style de vie sain et sans magie. Nous sommes là pour vous aider à trouver la voie sûre et confortable que vous recherchez. Des dizaines de personnes ont déjà bénéficié de nos alternatives à la magie pour un meilleur style de vie. Et vous, est-ce que cela vous tente ?' WHERE `entry` = 3397;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vous vous sentez un peu fatigué ? Vous avez besoin d\'énergie ? De beaucoup d\'énergie ? Que diriez-vous d\'une énergie brûlante, ardente, incendiaire ?\n\nEssayez la nouvelle LAVOMALTINE de Pyroco ! Vous allez tomber amoureux de son goût sucré et rafraîchissant, et surtout de la puissance brute qu\'elle contient. Vous avez besoin d\'un remontant rapide ? Pas de problème ! LAVOMALTINE vous revigorera même si vous êtes dans un état d\'épuisement avancé.' WHERE `entry` = 3398;
UPDATE `locales_page_text` SET `Text_loc2` = 'Antonidas n\'était qu\'un jeune garçon quand il devint l\'apprenti de l\'un des membres du Kirin Tor. C\'était un étudiant opiniâtre, toujours penché sur ses livres dans les bibliothèques magiques de Dalaran. Après seulement quelques années d\'étude dans la secte, Antonidas avait surpassé ses pairs et gagné leur admiration, ainsi que le respect de ses aînés.' WHERE `entry` = 3403;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les pressions sur la fragile Alliance s\'alourdirent à la suite de la Deuxième guerre en raison des camps d\'internement bondés et des taxes créées pour les entretenir. L\'ennemi commun ayant disparu, il n\'y avait plus de raison de mettre de côté la politique et les prises de bec mesquines qui occupaient les nations azérothiennes avant l\'invasion des orcs venus de Draenor lors de la Première guerre.' WHERE `entry` = 3409;
UPDATE `locales_page_text` SET `Text_loc2` = 'Pendant qu\'Antonidas enquêtait sur des moyens de mettre fin à la peste, Arthas était devenu fou, rasant Stratholme et voyageant vers le Norfendre pour chasser le seigneur de l\'effroi Mal\'ganis. Avant que l\'archimage vieillissant ne puisse trouver un remède, Arthas, accompagné par la liche Kel\'Thuzad et une armée de morts-vivants, marcha vers le sud en passant par Tirisfal et Lordaeron, détruisant tout sur leur passage et alimentant la machine de guerre dans leur sillage.' WHERE `entry` = 3414;
UPDATE `locales_page_text` SET `Text_loc2` = 'Le lapin Tout-doux         \nPar Jepetto Mizanjoie, fabricant de jouets\n\n<Tout le livre est recouvert de dessins colorés accompagnés de courtes phrases.>' WHERE `entry` = 3419;
UPDATE `locales_page_text` SET `Text_loc2` = '8 oct.\n\nComme je l\'ai dit auparavant, le dangereusement vaste potentiel de certaines écoles de magie m\'inquiète de plus en plus, ainsi que la différence qui règne entre les lois et les limites qui devraient leur être imposées. De plus, des recherches poussées sur ces écoles instables montrent une tendance à une diminution des prérequis académiques pour une augmentation de la puissance des résultats.' WHERE `entry` = 3432;
UPDATE `locales_page_text` SET `Text_loc2` = 'Nous n\'avons pas l\'intention de remplacer les moyens de transport populaires tels que les bateaux, zeppelins et autres maîtres de vol. Ils sont économiques, pratiques et ce sont également des moyens de transport sûrs. Les portails, quand ils sont utilisés de manière appropriée, sont tout aussi sûrs et certainement plus rapides. CEPENDANT, en pratique, les portails ont la réputation d\'être un fardeau pour toute société qui les propose au public. De tous les sorts majeurs développés et maintenus par le Kirin Tor (y compris la métamorphose), la technologie des portails est celle qui est la plus proche d\'être bannie de toute utilisation sauf cas d\'urgence.' WHERE `entry` = 3443;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les altercations de races représentent le plus grand risque d\'abus et de désastres et sont la source du plus grand nombre de plaintes que reçoit le Kirin Tor. L\'étude des métamorphoses a été surveillée et menacée de suppression pendant des années, mais ce n\'est que récemment que ces tribulations ont augmenté de façon exponentielle. Afin que ce sort reste autorisé, certaines directives doivent être mises en place pour diminuer le nombre croissant de tragédies que des métamorphoses hasardeuses ont provoquées.' WHERE `entry` = 3454;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les téléportations instantanées à courte distance du corps entier ont des règles et des limites spécifiques qui doivent être respectées en toutes occasions. Ce livre est une directive pour une utilisation conforme de ce mode de voyage, afin d\'assurer une téléportation conforme et sans risque.' WHERE `entry` = 3461;
UPDATE `locales_page_text` SET `Text_loc2` = 'Comme vous le savez probablement, notre organisation passe régulièrement au peigne fin les abords du néant distordu à la recherche d\'objets déplacés. Nous avons découvert divers trésors, des essences magiques errantes aux éclats de pouvoir mystique crépitants en passant par des ensembles d\'armures complets et des armes très puissantes. Malheureusement, ces derniers temps, nous avons également vu augmenter le nombre d\'ordures et de déchets.' WHERE `entry` = 3469;
UPDATE `locales_page_text` SET `Text_loc2` = 'Note trouvée sur le bureau du très estimé archimage Ansirem Tisserune' WHERE `entry` = 3472;
UPDATE `locales_page_text` SET `Text_loc2` = '<Ce prospectus était encore récemment collé là. Des résidus de colle sur la pierre indiquent que de nombreux prospectus ont occupé cet endroit.>' WHERE `entry` = 3477;
UPDATE `locales_page_text` SET `Text_loc2` = 'Thule Serres-de-corbeau\n\nLes informations dont nous disposons sont contradictoires, mais les événements qui se sont déroulés dans les clairières de Tirisfal et la forêt des Pins argentés semblent indiquer que Thule Serres-de-corbeau s\'est volontairement associé au Fléau. Il serait peut-être possible de trouver des indices dans les ruines du donjon de Fenris, sur l\'une des îles du lac Lordamere, mais pour l\'instant, nous devons concentrer notre attention sur d\'autres problèmes.' WHERE `entry` = 3479;
UPDATE `locales_page_text` SET `Text_loc2` = 'Rheanna,\n\nJe ne peux vivre une seconde en votre absence. Dès que je suis éveillé, je me languis de votre contact, de vos caresses douces et soyeuses.\n\nSi les étoiles tombaient du ciel, je n\'en attraperais pas une seule qui soit plus brillante, brûlante, aveuglante que l\'infinité de vos yeux.' WHERE `entry` = 3482;
UPDATE `locales_page_text` SET `Text_loc2` = 'Où dîner à Dalaran ?\nLorsqu\'Inzi Charmelume a ouvert L\'échoppe des héros, peu après la réinstallation de la ville, tout le monde, résidents et visiteurs, a posé ses armes et s\'est précipité pour mordre à belles dents dans un bon croûton de pain ! Il y a de la place, les prix sont sympathiques et le bar propose des tas de bonnes choses venues de partout, à consommer jusqu\'à ce que vous vous sentiez convenablement héroïque. Ah, et quoi que vous commandiez, gardez un peu de place pour le caribou à l\'hydromel !' WHERE `entry` = 3487;
UPDATE `locales_page_text` SET `Text_loc2` = '<Les entrées du vieux journal sont entrecoupées de pages illisibles ou manquantes.>\n\nQue penserait Nikolaï ? Je n\'oserai jamais lui parler de la voix, j\'arrive à peine à parler quand il est là, à vrai dire. Peut-être que la voix est ma conscience...' WHERE `entry` = 3490;
UPDATE `locales_page_text` SET `Text_loc2` = 'Vous croyiez savoir lancer un sort - Foire aux questions de mages débutants :' WHERE `entry` = 3497;
UPDATE `locales_page_text` SET `Text_loc2` = 'BUR - Appel de mâle passionné et agressif.\nQue puis-je faire pour vous ? - On vend de la bière ici.\nSalut - Cri de guerre menaçant, surtout s\'il est accompagné d\'un geste de la main.\nComment allez-vous ?- Votre mère était-elle vraiment un serpent ?\nEn l\'honneur du roi, l\'ami ! - Je suis affamé !' WHERE `entry` = 3498;
UPDATE `locales_page_text` SET `Text_loc2` = '- Combattez le feu par le feu ! Ce n\'est pas parce que le corps d\'un monstre est entièrement composé de flammes que vos boules de feu ne lui feront rien. Essayez au moins une fois ! Les résultats pourraient vous surprendre.\n\n- L\'habit fait la réussite ! Des études montrent que les mages accordant une attention particulière à leur garde-robe sont bien plus efficaces que ceux portant de vieilles fripes de mauvaise qualité. Avez-vous changé de robe récemment ?\n\n- Connaissez votre rôle ! À première vue, votre bâton semble être une bonne arme, mais c\'est faux. Laissez ceux qui ont une grosse armure brillante se mettre devant vous. Cela étant dit...' WHERE `entry` = 3500;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : Je n\'arrive pas à lancer un seul sort correctement. Que puis-je faire ?\n\nR : Vous souffrez peut-être de déshydratation. Si vous n\'arrivez pas à lancer un sort alors que d\'habitude vous y arrivez, essayez de boire un verre d\'eau ! Une hydratation correcte est primordiale pour une incantation efficace.' WHERE `entry` = 3502;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : Je n\'arrive pas à lancer de traits de feu ! J\'en ai lancé un il y a tout juste deux secondes, et maintenant, ça ne veut plus marcher. Aidez-moi !\n\nR : Vous n\'arrivez pas à lancer un sort ? Vous l\'avez lancé il y a quelques secondes seulement ? Certains sorts ne peuvent être lancés deux fois de suite dans un court laps de temps. Essayez d\'utiliser les autres sorts que vous connaissez en attendant.' WHERE `entry` = 3503;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : J\'ai jeté un coup d\'oeil au grimoire d\'un copain, dans les vestiaires, et il connaît beaucoup plus de sorts que moi ! Qu\'est-ce qui cloche chez moi ?\n\nR : Tout d\'abord, c\'est tout à fait NORMAL. Parlez régulièrement à votre maître de classe. Il pourra vous apprendre régulièrement de nouveaux sorts. S\'il ne peut rien vous enseigner, c\'est que vous devez vous entraîner à utiliser les sorts que vous connaissez déjà. Soyez patient, vous connaîtrez bientôt tous les sorts nécessaires !' WHERE `entry` = 3504;
UPDATE `locales_page_text` SET `Text_loc2` = 'Q : Mes coéquipiers semblent tous faire plus de dégâts que moi. Comment faire pour les rattraper sans passer pour un idiot ?\n\nR : Tout ne tourne pas autour des dégâts. Vous pouvez aider votre groupe de bien d\'autres manières... avec des sorts de rafraîchissement arcanique, par exemple. Si cela ne fonctionne pas, peut-être qu\'une carrière dans les arts arcaniques n\'est pas faite pour vous. Nous avons entendu dire que la chasse était très facile.' WHERE `entry` = 3505;
UPDATE `locales_page_text` SET `Text_loc2` = 'Entrée n° 1 - Tout d\'abord, une introduction. Je suis l\'archimage Vargoth, chef de l\'expédition du Kirin Tor sur Draenor. Nous avons accompagné les forces de l\'Alliance à travers la Porte des ténèbres et nous installons actuellement notre camp de base, Kirin\'Var, dans Farahlon, près du domicile ancestral des orcs du clan du Crâne ricanant. Quel endroit charmant !' WHERE `entry` = 3506;
UPDATE `locales_page_text` SET `Text_loc2` = 'L’almanach du vieux sorcier : l’Alliance\n\nLe Kirin Tor a toujours été lié à l’Alliance. On peut donc s’interroger sur l’utilité d’un chapitre présentant les races qui la composent. Je pense que le contraire est vrai : on ne peut comprendre les autres races qu’après s’être longuement interrogés sur nous-mêmes. - V. S.' WHERE `entry` = 3512;
UPDATE `locales_page_text` SET `Text_loc2` = 'Humains – La race humaine est remarquable pour ses facultés d’adaptation, et les humains font des magiciens hautement compétents. Toutes les listes des mages les plus puissants de l’histoire sont dominées par les humains. Encore plus révélateur, le Vieux sorcier lui-même est un humain.\n\nNains – Les nains sont dotés de modestes talents magiques, mais ils ont du mal à atteindre les échelons les plus élevés de la magie. Ils ont tendance à se concentrer davantage sur la prêtrise, mais certains s’adonnent quand même aux arcanes ou à la nécromancie.' WHERE `entry` = 3513;
UPDATE `locales_page_text` SET `Text_loc2` = 'Gnomes – Les gnomes sont de remarquables arcanistes. Ils ont toujours participé au Kirin Tor et, si l\'on compte au poids, ils présentent l’une des plus importantes concentration de magie du monde.\n\nElfes de la nuit – Les elfes de la nuit ont tendance à éviter l’énergie arcanique à peu près autant que le Fléau, mais leurs pouvoirs druidiques sont sans égal.' WHERE `entry` = 3514;
UPDATE `locales_page_text` SET `Text_loc2` = 'Draeneï – Cette race ancienne a apporté à l’Alliance des mages puissants et d’exceptionnels manieurs de Lumière. Après leur arrivée en Kalimdor, certains draeneï ont adopté le chamanisme.\n\nHauts-elfes – La magie des hauts-elfes est terriblement puissante. Les hauts-elfes possèdent une acuité magique qui rivalise avec celle des humains. Certains des plus grands héros de la bataille du mont Hyjal étaient des prêtres hauts-elfes.' WHERE `entry` = 3515;
UPDATE `locales_page_text` SET `Text_loc2` = 'L’almanach du vieux sorcier : la Horde\n\nJusqu’à une date récente, disons quelques années, l’essentiel de ce que je savais sur la Horde était fait d’anecdotes ou de déductions remontant à mes combats contre ses membres. Désormais, le Kirin Tor ayant adopté une position plus neutre dans les relations entre la Horde et l’Alliance, la somme d’informations disponibles sur la Horde et sa magie a considérablement augmenté. – V.S.' WHERE `entry` = 3516;
UPDATE `locales_page_text` SET `Text_loc2` = 'Orcs – En dépit de leur évidente force physique, les orcs font preuve de talents magiques corrects. Dans l’ensemble, ils font des arcanistes médiocres, mais leur culture est imprégnée de chamanisme, auquel ils ont récemment ajouté des magies plus sombres, celles des démonistes et des nécromants.\n\nTaurens – Rares sont les taurens qui s’intéressent aux arcanes, mais les hommes-taureaux sont en harmonie avec les magies de la terre. Comme beaucoup de tribus d’adorateurs de la nature, ils suivent la voie du chaman. Par ailleurs, leurs plus anciens druides sont aussi puissants que les archidruides de Darnassus.' WHERE `entry` = 3517;
UPDATE `locales_page_text` SET `Text_loc2` = 'Trolls - L\'étonnante affinité avec les arts magiques des trolls vont à l\'encontre de leur réputation et de leur histoire de barbares. Mon expédition dans les « Zul », une visite des empires trolls de Zul’Aman, Zul’Farrak et Zul’Drak, m’a permis de découvrir une étonnante variété de lanceurs de sorts trolls. On y trouve aussi bien des sorciers-docteurs que des prêtres des ombres et de bizarres adorateurs des animaux dont les pratiques évoquent le druidisme.\n\nMorts-vivants - Les morts-vivants ont le même potentiel magique que les humains vivants… voire plus. Les Réprouvés de Sylvanas, en particulier, manient la magie avec un zèle effrayant.' WHERE `entry` = 3518;
UPDATE `locales_page_text` SET `Text_loc2` = 'Elfes de sang - Une exposition prolongée aux énergies magiques a laissé une marque indélébile sur les elfes de sang. Ces créatures élégantes sont littéralement droguées à la magie, et excellent dans toutes les écoles de magie qu’elles décident d’étudier. Récemment, les elfes de sang se sont embarqués sur la voie des « chevaliers de sang », qui correspond à peu près à une forme de paladinisme.\n\nTaunkas – Comme leurs cousins taurens, les taunkas ont tendance à se concentrer sur les magies de la terre.' WHERE `entry` = 3519;
UPDATE `locales_page_text` SET `Text_loc2` = 'Almanach du vieux sorcier : Autres races ayant des affinités avec les arcanes\n\nL\'utilisation de la magie ne se limite pas aux races de la Horde et de l\'Alliance. Nombreuses sont les créatures peuplant Azeroth capables d\'utiliser la magie. - V.S.' WHERE `entry` = 3520;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les nagas - Alors que les mâles sont plus doués pour le combat en mêlée, les femelles nagas sont d\'incroyables ensorceleuses, qui contrôlent totalement le pouvoir de l\'eau et de la glace. Il faut noter que leur ancienne reine Azshara avait la réputation d\'être l\'une des adeptes de la magie la plus puissante de l\'histoire d\'Azeroth.\n\nLes nérubiens - Seuls les dirigeants nérubiens les plus puissants disposent des capacités nécessaires à la magie. Cependant, après l\'assaut du Fléau, de nombreux nérubiens atteints par la peste sont devenus nécromanciens.' WHERE `entry` = 3521;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les ogres - La plupart des expatriés de Draenor choisissent le chemin du guerrier ou du forestier, mais un petit pourcentage choisit une vie de sorcellerie comme ogre-magus. Cho\'gall, un des premiers ogres-magi, était un sorcier immensément puissant (en plus d\'être immense).\n\nLes satyres - Ces dangereuses créatures ont des traits similaires à ceux des elfes de la nuit, mais des corps tordus démoniaques. Maîtres de la magie démoniaque, les satyres sont incroyablement dangereux. Une prudence extrême est conseillée quand on s\'approche d\'eux.' WHERE `entry` = 3522;
UPDATE `locales_page_text` SET `Text_loc2` = 'Les dragons bleus - Les très anciens enfants de Malygos sont parmi les experts les plus importants de la magie des arcanes. Certains, comme l\'aventureux Kalecgos, se montrent amicaux envers le sorcier mortel et peuvent potentiellement faire d\'excellents partenaires de recherche. Malheureusement, de récentes rumeurs provenant du Nord indiquent que certains dragons bleus sont devenus extrêmement hostiles aux autres magiciens. Les raisons de cette agressivité sont inconnues pour le moment.' WHERE `entry` = 3523;
UPDATE `locales_page_text` SET `Text_loc2` = 'Almanach du vieux sorcier : Les races chamaniques\n\nMes voyages m\'ont permis de rencontrer de nombreuses sociétés dans tout Azeroth, dont plusieurs pratiquent le chamanisme. La plupart de ces peuples vivent en osmose avec la terre et fuient les sociétés plus avancées pour une existence beaucoup plus proche de la nature. - V.S.' WHERE `entry` = 3524;
UPDATE `locales_page_text` SET `Text_loc2` = 'Hurans - Le peu de hurans toujours présents en Kalimdor sont connus pour pratiquer la géomancie, une forme rudimentaire de chamanisme. Certains hurans pestiférés pratiquent cette forme de magie même dans la non-mort.\n\nGnolls - Les gnolls sont une race étrange. J\'ai un jour réussi à me faire accepter d\'un chef de clan des Carmines en faisant un petit tour, le genre de ceux qu\'on apprend en primaire à Dalaran. Pendant une journée, il fut mon meilleur ami, jusqu\'à ce qu\'il oublie mon tour et qu\'il lâche ses chasseurs sur moi. Bref, lorsqu\'ils se laissent observer un tant soit peu, les gnolls montrent un certain intérêt pour le chamanisme élémentaire.' WHERE `entry` = 3525;
UPDATE `locales_page_text` SET `Text_loc2` = 'Centaures - Les femelles centaures ont tendance à utiliser une forme sombre et pervertie de chamanisme. En revanche, l\'utilisation de la magie chez les mâles est beaucoup plus rare.\n\nVarleus - Ce peuple ressemblant à des gloutons semble primitif au premier abord. Cependant, les chefs de leurs tribus sont souvent de très puissants chamans.' WHERE `entry` = 3526;
UPDATE `locales_page_text` SET `Text_loc2` = 'Oracles - Bien qu\'ils soient les ennemis jurés des varleus, les oracles semblent être dotés d\'un système social quasi identique. Les tâches basiques sont assignées aux guerriers, mais leurs chefs sont presque toujours de grands pratiquants du chamanisme.\n\nFurbolgs - À notre époque, peu de furbolgs deviennent des chamans. Le chamanisme est plus commun parmi les tribus de furbolgs libres. De plus, certains furbolgs libres sont également capables de changer de forme, comme les druides ayant suivi un apprentissage.' WHERE `entry` = 3527;
UPDATE `locales_page_text` SET `Text_loc2` = 'Almanach du vieux sorcier : Peuples de la terre\n\nLe chamanisme n\'est pas la seule magie centrée sur la nature. Nombreuses sont les races simples d\'esprit d\'Azeroth à lancer des sorts puisant dans les énergies de la terre, mais qu\'on ne peut relier au chamanisme. La source de leurs pouvoirs magiques est-elle la même que celle des chamans ? Voilà un constant sujet de discussion au sein de la citadelle Pourpre. - V.S.' WHERE `entry` = 3528;
UPDATE `locales_page_text` SET `Text_loc2` = 'Roharts - Les hommes-morses pratiquent une sorte de magie qui ressemble fortement au chamanisme en surface. Nos chercheurs n\'ont pas encore découvert la source de la magie des roharts.\n\nKobolds - Ces créatures simplettes ne pratiquent qu\'une magie terrestre basique. Mes exploits de jeunesse m\'ont appris que certains d\'entre eux maîtrisent les boules de feu, mais c\'est à peu près tout.' WHERE `entry` = 3529;
UPDATE `locales_page_text` SET `Text_loc2` = 'Indomptables - Ces créatures à plumes sont connues sous plusieurs noms - séléniens, chouettes-ours et chouettides, pour n\'en citer que quelques-uns. Ces géants placides disposent d\'une énergie naturelle druidique similaire à celle des elfes de la nuit.\n\nHarpies - Ces femmes-oiseaux, qui se caractérisent par leurs caquètements, sont capables d\'utiliser la puissante magie élémentaire de la foudre. On sait très peu sur la source de leur pouvoir, car 1) elles attaquent à vue les intrus, 2) bien peu nombreux sont ceux qui discernent une quelconque signification dans leurs cris stridents, et 3) personne ne peut supporter l\'odeur de leurs nids.' WHERE `entry` = 3530;
UPDATE `locales_page_text` SET `Text_loc2` = 'Murlocs - Pendant de nombreuses années, on a cru ces énigmatiques créatures capables uniquement de la magie la plus primitive. Ne vous laissez pas tromper par leurs visages apparemment dépourvus d\'intelligence. Ces hommes-poissons sont parfaitement capables de lancer de puissants sorts de terre et d\'eau, et ils sont plus nombreux de jour en jour.\n\nMakruras - Cette race similaire au homard, bien que largement considérée comme primitive, a un don inné pour la magie de l\'eau.' WHERE `entry` = 3531;
UPDATE `locales_page_text` SET `Text_loc2` = 'Almanach du vieux sorcier : Créatures non-magiques\n\nLa magie n\'est pas pour tout le monde. Les bêtes non-intelligentes du monde ont survécu pendant des millénaires en comptant uniquement sur leur bon sens, comme nombre des races les plus simples. Quelques races, bien qu\'avancées socialement, préfèrent tout simplement éviter la magie et privilégient d\'autres activités. Il est d\'une importance cruciale que le Kirin Tor comprenne comment ces races prospèrent sans aucune magie. - V.S.' WHERE `entry` = 3532;
UPDATE `locales_page_text` SET `Text_loc2` = 'Gobelins - Les gobelins ont tendance à se méfier de la magie et préfèrent se concentrer sur des projets technologiques. Contrairement à leurs homologues gnomes, ils n\'ont pas la capacité de comprendre et apprécier les arcanes. Cela dit, certains d\'entre eux ont associé leur technologie à la magie et obtenu des résultats intéressants.\n\nTroggs - Ces créatures primitives sont tout simplement dépourvues de l\'intelligence nécessaire à la magie.' WHERE `entry` = 3533;
UPDATE `locales_page_text` SET `Text_loc2` = 'Magnataures - Les énormes magnataures ont peu ou pas d\'affinités avec la magie. Les lanceurs de sorts magnataures sont très rares, et ils suivent généralement la voie chamanique s\'ils pratiquent un tant soit peu la magie. Cependant, je sais par ma propre expérience qu\'ils n\'abandonnent pas sans s\'être battus, et leur cuir épais peut résister à plusieurs des éclairs de givre les plus puissants.\n\nVrykuls - Bien que ces demi-géants du Norfendre ne connaissent de nature très peu ou rien du tout à la magie, certains ont été formés à la nécromancie par le roi-liche. J\'ai également entendu des rumeurs évoquant une certaine "magie runique", mais je n\'ai pas encore eu l\'occasion de me livrer à des recherches approfondies sur ce sujet fascinant.' WHERE `entry` = 3534;
UPDATE `locales_page_text` SET `Text_loc2` = 'Généralissime\n\n$N a mené avec succès la campagne contre le Fléau à Fléaulme et nous a permis d\'être bien placés pour marcher vers la Couronne de glace.\n\nLes maçons ont toutes les fournitures nécessaires à la construction du donjon de la Justice. Nous remercions une fois encore $N pour son aide dans la forêt du Chant de cristal.\n\nIl ne nous reste qu\'un défi à relever : la prise de la cime des Croisés. Comme c\'est à l\'endroit où nous avons le plus souffert, j\'aimerais entamer moi-même ce dernier chapitre.\n\nNous attendons vos ordres, généralissime.\n\nQue la Lumière vous protège.\n\n- Gustav' WHERE `entry` = 3535;
UPDATE `locales_page_text` SET `Text_loc2` = 'Majesté,\n\nÔ roi miséricordieux, puisse la Lumière soutenir votre main et affermir votre cœur. Retenez votre jugement de $gce champion déchu:cette championne déchue; - ne serait-ce que pour un instant - et écoutez mes paroles.\n\n$gLe porteur:la porteuse; de cette lettre est $gun ancien héros:une ancienne héroïne; de l\'Alliance. Bien qu\'$gil:elle; porte sur $glui:elle; la puanteur de la mort et l\'apparence du Fléau, $gil:elle; a l\'âme d\'$gun champion:une championne;. Une âme qui a récemment rejoint à nouveau son corps.' WHERE `entry` = 3536;
UPDATE `locales_page_text` SET `Text_loc2` = '$N représente un nouveau front unifié contre le roi-liche connu sous le nom de la Lame d\'ébène. Oui, majesté, des chevaliers de la mort se sont retournés contre leur ancien maître. Ils sont dirigés par le fils du seigneur Alexandros Mograine - le Porte-cendres. Ils recherchent ce que nous recherchons tous : la fin du Fléau.\n\nJe ne vous demande pas d\'accepter $N et les chevaliers de la Lame d\'ébène dans l\'Alliance, mais seulement que vous montriez de la tolérance.' WHERE `entry` = 3537;
UPDATE `locales_page_text` SET `Text_loc2` = 'Souvenez-vous, sire, par le sang et l\'honneur, nous servons tous.\n\nRespectueusement,\n\nGénéralissime Tirion Fordring' WHERE `entry` = 3538;
UPDATE `locales_page_text` SET `Text_loc2` = 'Chef de guerre,\n\nPuissant chef de guerre, puisse la Lumière soutenir votre main et affermir votre cœur. Retenez votre jugement de $gce champion déchu:cette championne déchue; - ne serait-ce que pour un instant - et écoutez mes paroles.\n\n$gLe porteur:la porteuse; de cette lettre est $gun ancien héros:une ancienne héroïne; de la Horde. Bien qu\'$gil:elle; porte sur $glui:elle; la puanteur de la mort et l\'apparence du Fléau, $gil:elle; a l\'âme d\'$gun champion:une championne;. Une âme qui a récemment rejoint à nouveau son corps.' WHERE `entry` = 3539;
UPDATE `locales_page_text` SET `Text_loc2` = '$N représente un nouveau front unifié contre le roi-liche connu sous le nom de la Lame d\'ébène. Oui, majesté, des chevaliers de la mort se sont retournés contre leur ancien maître. Ils sont dirigés par le fils du seigneur Alexandros Mograine - le Porte-cendres. Ils recherchent ce que nous recherchons tous : la fin du Fléau.\n\nJe ne vous demande pas d\'accepter $N et les chevaliers de la Lame d\'ébène dans l\'Alliance, mais seulement que vous montriez de la tolérance.' WHERE `entry` = 3540;
UPDATE `locales_page_text` SET `Text_loc2` = 'Souvenez-vous, Thrall, par le sang et l\'honneur, nous servons tous.\n\nRespectueusement,\n\nGénéralissime Tirion Fordring\n\nP.S. : Mes respects à Eitrigg. Veuillez l\'informer que j\'aurais bien besoin d\'un bon orc à mes côtés en Norfendre - avec votre permission, bien sûr.' WHERE `entry` = 3541;
UPDATE `locales_page_text` SET `Text_loc2` = 'Archimage Antonidas, grand magus du Kirin Tor\n\nLa grande ville de Dalaran se dresse à nouveau - un testament à la ténacité et la volonté de ses formidables fils.\n\nVos sacrifices n\'auront pas été vains, cher ami.\n\n\nAvec tout mon amour et mon honneur,\n\nJaina Portvaillant' WHERE `entry` = 3542;
UPDATE `locales_page_text` SET `Text_loc2` = 'Depuis des années, je suis prisonnier de cette geôle magique. Mais cette nuit, je vais m\'échapper. J\'ai tout ce qu\'il me faut pour réussir mon évasion.\n\n<Une tache d\'eau sur l\'encre a rendu cette partie de la lettre illisible.>\n\nPourquoi suis-je resté ici aussi longtemps ? Qu\'ai-je fait pour mériter ça ? Cela fait des jours et des jours que j\'y pense. Que peut-on bien faire qui mérite ce traitement ?\n\n<Une tache d\'eau sur l\'encre a rendu cette partie de la lettre illisible.>\n\nPeut-être le découvrirai-je dans une autre vie.' WHERE `entry` = 3543;
UPDATE `locales_page_text` SET `Text_loc2` = 'Feuilleter les pages du livre qui s\'émiettent ne révèle rien d\'autre que des descriptions de la vie à la ferme de Valgan. Le changement des saisons, les semailles et les récoltes sont notés, ainsi que les naissances et les décès d\'enfants.$b$bLes notes s\'arrêtent brusquement avec une courte description de la peste. Aucun des enfants décrits n\'était assez âgé pour suivre une formation de chevalier, s\'il y en a qui ont survécu.' WHERE `entry` = 3545;
UPDATE `locales_page_text` SET `Text_loc2` = 'La première moitié du journal ne semble être rien d\'autre que le livre de compte de monsieur Cooper. Des colonnes manuscrites de chiffres indiquent la fabrication et la vente de barils aux habitants de Ruisselune et même à quelques clients de Hurlevent.$B$BL\'autre moitié du livre semble être le journal de monsieur Cooper, où il parle de laisser l\'affaire familiale à son plus jeune frère. Il n\'est pas fait mention d\'enfant.' WHERE `entry` = 3546;
UPDATE `locales_page_text` SET `Text_loc2` = '<On dirait qu\'Archavon tenait un journal. Il semble écrit dans différentes langues.>\n\nJour 3613785 :\n\nArchavon arrêter journal. Plutôt regarder chétifs petits hommes combattre. Quand ils finissent, Archavon se réveille et voit qui toucher orbe le plus vite. Gagnants jusqu\'à présent :\n\nHorde :   \n  Donjon défendu : $4024w\n  Donjon capturé : $4022w\n\nAlliance :\n  Donjon défendu : $4025w\n  Donjon capturé : $4023w\n\nJour 3613760 :\n\nLa nuit, Archavon sort en cachette et ramasse l\'armure qu\'ils laissent derrière. Archavon essaie de réparer. Dur à faire.' WHERE `entry` = 3547;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 3613725 :\n\nLe petit peuple vient à Joug-d\'hiver depuis autre côté océan. Combattent beaucoup de fois chaque jour. Archavon regarde. \n\nJour 2145617 :\n\nGrandes secousses dehors. Petit peuple semble occupé. Archavon reste dedans.\n\nJour 1051213 :\n\nArchavon se réveille, se rend compte le visage semble doux. Pas important. Retourne dormir.' WHERE `entry` = 3548;
UPDATE `locales_page_text` SET `Text_loc2` = 'Jour 900500 :\n\nÉtait très ennuyeux dehors. Archavon va dormir. Un peu de bruit dehors, il fait froid. \n\nJour 10500 : \n\nArchavon pense journal ennuyeux. Fait une pause.\n\nJour 10 :\n\nLes Faiseurs sont partis. Archavon laissé ici pour surveiller.' WHERE `entry` = 3549;
UPDATE `locales_page_text` SET `Text_loc2` = '<HTML>\n<BODY>\n<P>La première phase de la construction est achevée. L\'amiral Ponevent a ordonné à plusieurs de mes hommes d\'établir un bastion avancé un peu plus au nord. Il n\'a pas voulu entrer dans les détails, disant qu\'il avait été « guidé » dans ses choix.</P>\n<BR/>\n<P>Cet après-midi, nous avons capturé quatre espions de Vexevenin, la ville des Réprouvés. Je vais demander à LeCraft de les torturer pour obtenir des informations. Si nous n\'en avons pris que quatre, combien d\'autres se sont glissés parmi nous ?</P>\n<BR/>\n<P>Pourquoi ai-je l\'impression que tout commence à partir à vau-l\'eau ?</P>\n</BODY>\n</HTML>' WHERE `entry` = 3558;
UPDATE `locales_page_text` SET `Text_loc2` = 'La gnomette se pencha au-dessus de la balustrade pour observer la cour isolée de Dalaran.$b$b« La vue du balcon est stupéfiante. Tu devrais venir voir ! »$b$bLes cuissards de l’armure de Marcus craquèrent quand il s’approcha d’elle. Prenant une longue inspiration, il gratta d’un air distrait son menton mal rasé.$b$b« Le Retour du Héros n’est pas une auberge de bas étage. Et il y a quelque chose dans la chambre qui devrait t’intéresser. »$b$bTavi galopa dans la pièce, ne s’arrêtant qu’un instant avant de sauter sur le lit massif. Elle se retourna vers Marcus pour le regarder de ses immenses yeux mutins, les fermant à demi, et son sourire rayonnant se fit diabolique.' WHERE `entry` = 3562;
